//载入科目数据
function loadData(kmlx,jb,kmbh,xzlx)
{
	//alert(&quot;kmlx=&quot;+kmlx+&quot;,jb=&quot;+jb+&quot;,kmbh=&quot;+kmbh);
	sel_kmlx = kmlx;
	sel_kmjb = jb;
	sel_kmbh = kmbh;

	_this.LoadZXGFile(ZXGFILE0,-1,0);
	_this.SetMainCellRange(0,1,0,_this.GetRowCount(0)-1,_this.GetColCount(0)-1);
	_this.SetFixedRow(0,1);
	_this.SetFixedCol(0,0);


	var xml = _sql.query(&quot;MAIN&quot;,&quot;KMLX=&quot;+kmlx+&quot;&JB=&quot;+jb+&quot;&KMBH=&quot;+kmbh+&quot;&XZLX=&quot;+xzlx);
	_this.SetXmlDS(0,1,0,_this.GetRowCount(0)-1,_this.GetColCount(0)-1,xml);

	for (var r=1;r&lt;=_this.GetMainCellRangeRow2(0);r++) {
		var mjbz = _this.GetCellText(0,r,1);
		var kmbh = _this.GetCellText(0,r,2);
		if(kmbh != &quot;&quot;) {
			if (mjbz == 0) {
				_this.SetCellShowText(0,r,1,&quot;+&quot;);
			}
			else {
				_this.SetCellShowText(0,r,1,&quot; &quot;);
			}

//			_this.SetToBoolBox(0,r,19);
//			_this.SetToBoolBox(0,r,9);
//			_this.SetToBoolBox(0,r,10);
//			_this.SetToBoolBox(0,r,11);
//			_this.SetToBoolBox(0,r,12);
//			_this.SetToBoolBox(0,r,13);
//			_this.SetToBoolBox(0,r,14);
//			_this.SetToBoolBox(0,r,15);
//			_this.SetToBoolBox(0,r,16);
//			_this.SetToBoolBox(0,r,17);
//			_this.SetToBoolBox(0,r,20);
			if (_this.GetCellText(0,r,19) == &quot;1&quot;) _this.SetCellShowText(0,r,19,&quot;√&quot;); else _this.SetCellShowText(0,r,19,&quot;w&quot;);
			if (_this.GetCellText(0,r,9) == &quot;1&quot;)  _this.SetCellShowText(0,r,9,&quot;√&quot;);  else _this.SetCellShowText(0,r,9,&quot;w&quot;);
			if (_this.GetCellText(0,r,10) == &quot;1&quot;) _this.SetCellShowText(0,r,10,&quot;√&quot;); else _this.SetCellShowText(0,r,10,&quot;w&quot;);
			if (_this.GetCellText(0,r,11) == &quot;1&quot;) _this.SetCellShowText(0,r,11,&quot;√&quot;); else _this.SetCellShowText(0,r,11,&quot;w&quot;);
			if (_this.GetCellText(0,r,12) == &quot;1&quot;) _this.SetCellShowText(0,r,12,&quot;√&quot;); else _this.SetCellShowText(0,r,12,&quot;w&quot;);
			if (_this.GetCellText(0,r,13) == &quot;1&quot;) _this.SetCellShowText(0,r,13,&quot;√&quot;); else _this.SetCellShowText(0,r,13,&quot;w&quot;);
			if (_this.GetCellText(0,r,14) == &quot;1&quot;) _this.SetCellShowText(0,r,14,&quot;√&quot;); else _this.SetCellShowText(0,r,14,&quot;w&quot;);
			if (_this.GetCellText(0,r,15) == &quot;1&quot;) _this.SetCellShowText(0,r,15,&quot;√&quot;); else _this.SetCellShowText(0,r,15,&quot;w&quot;);
			if (_this.GetCellText(0,r,16) == &quot;1&quot;) _this.SetCellShowText(0,r,16,&quot;√&quot;); else _this.SetCellShowText(0,r,16,&quot;w&quot;);
			if (_this.GetCellText(0,r,17) == &quot;1&quot;) _this.SetCellShowText(0,r,17,&quot;√&quot;); else _this.SetCellShowText(0,r,17,&quot;w&quot;);
			if (_this.GetCellText(0,r,20) == &quot;1&quot;) _this.SetCellShowText(0,r,20,&quot;√&quot;); else _this.SetCellShowText(0,r,20,&quot;w&quot;);

		}
	}

	//_this.SetColVisible(0,9,-1);
	_this.SetColVisible(0,19,-1);

	_this.AutoFixColWidth(0,600);
	_this.SetAttribe(&quot;SHEET_0&quot;,_this.ATTR_SHEET_SELECTFLAG,_this. SELECTFLAG_SINGLEROW );
	_this.SetAttribe(&quot;SHEET_0&quot;,_this.ATTR_SHOW_ZERO,1);
	_this.AutoFixScreenWidth();

}


//点击菜单项
function _thisOnMenuItemSelect(sheet,row,col,menuitemid)
{
	if (menuitemid.indexOf(&quot;kmlx_&quot;) &gt; -1) {
		var kmlx = menuitemid.split(&quot;_&quot;)[1];
		if (kmlx == &quot;0&quot;) kmlx = &quot;&quot;;
		else if (kmlx == &quot;1&quot;) kmlx = &quot;资产类&quot;;
		else if (kmlx == &quot;2&quot;) kmlx = &quot;负债类&quot;;
		else if (kmlx == &quot;3&quot;) kmlx = &quot;基金类&quot;;
		else if (kmlx == &quot;4&quot;) kmlx = &quot;收入类&quot;;
		else if (kmlx == &quot;5&quot;) kmlx = &quot;支出类&quot;;
		sel_kmlx = kmlx;

		loadData(kmlx,0,&quot;&quot;,&quot;&quot;);
	}
	else if (menuitemid.indexOf(&quot;xzlx_&quot;) &gt; -1) {  //20170207 lwq add-新增按险种查询
		var xzlx = menuitemid.split(&quot;_&quot;)[1];
		if (xzlx == &quot;0&quot;) xzlx = &quot;&quot;;
//		else if (xzlx == &quot;1&quot;) xzlx = &quot;养老&quot;;
//		else if (xzlx == &quot;2&quot;) xzlx = &quot;失业&quot;;
//		else if (xzlx == &quot;3&quot;) xzlx = &quot;基本医疗&quot;;
//		else if (xzlx == &quot;4&quot;) xzlx = &quot;工伤&quot;;
//		else if (xzlx == &quot;5&quot;) xzlx = &quot;生育&quot;;
//		else if (xzlx == &quot;6&quot;) xzlx = &quot;其他医疗&quot;;
//		else if (xzlx == &quot;7&quot;) xzlx = &quot;居民医疗&quot;;
//		else if (xzlx == &quot;8&quot;) xzlx = &quot;机关养老&quot;;
//		else if (xzlx == &quot;9&quot;) xzlx = &quot;职业年金&quot;;
		sel_xzlx = xzlx;

		loadData(&quot;&quot;,0,&quot;&quot;,xzlx);
	}
	else if (menuitemid == &quot;menuMoveKM&quot;) {
		moveKM();
	}
	else if (menuitemid == &quot;menuExtKM&quot;) {
		extKM();
	}
	else if (menuitemid == &quot;menuMergKM&quot;) {
		mergKM();
	}
	else if (menuitemid == &quot;menuPrintKM1&quot;) {
		printKM(1);
	}
	else if (menuitemid == &quot;menuPrintKM2&quot;) {
		printKM(2);
	}
}

//鼠标双击
function _thisOnMouseDClick(sheet,row,col)
{
	if (row == 0) return;

	var mjbz = _this.GetCellText(sheet,row,1);
	var jb = _this.GetCellText(sheet,row,0);
	var kmlx = sel_kmlx;//_this.GetCellText(sheet,row,7);
	var kmbh = _this.GetCellText(sheet,row,2);
	var slh = _this.GetCellText(sheet,row,18);
	//sel_kmlx = kmlx;
	sel_kmjb = jb;
	sel_kmbh = kmbh;
	if (kmbh == &quot;&quot;) return;
	if (jb &lt;= 1) kmlx = &quot;&quot;;
	if (mjbz == 0) {
		if (kmbh == _this.GetCellText(sheet,row+1,18)) {
			jb = jb - 1;
			loadData(kmlx,jb,slh,&quot;&quot;);
		}
		else {
			loadData(kmlx,jb,kmbh,&quot;&quot;);
		}	
	}

}
//科目新增
function NewKM()
{
	var kmjbcf = &quot;&quot;;
	var kmhcf  = &quot;&quot;;
	var kmlxcf = &quot;&quot;;

	for(var i = 1;i&lt;=_this.GetMainCellRangeRow2(0);i++){
		if(_this.GetCellText(0,i,0) == &quot;&quot;) continue;
		var mj = 1;
		kmjbcf = _this,GetCellText(0,i,0);
		kmhcf = _this.GetCellText(0,i,2);
		kmlxcf = _this.GetCellText(0,i,7);
		var fx = _this.GetCellText(0,i,8);
	}
	var xsbz = 0;
	var dwwl = 0;
	var grwl = 0;
	var gdzc = 0;
//	var xjkm = 0;
	var dylj = 0;
	var dybyhj = 0;
	var dybjhj = 0;
	var dbbd = 0;
//	var yhkm = 0;
	var rjzxs = 0;

	var currow = _this.GetCurrentRow(0);
	if (currow &lt; 1) currow = 1;
	var cursel_kmbh = _this.GetCellText(0,currow,2); //当前光标所在行的科目

	var retVal = window.showModalDialog(&quot;show.sp?grdid=CFG_KMBEDIT&kmjbcf=&quot;+kmjbcf+&quot;&kmhcf=&quot;+kmhcf+&quot;&kmlxcf=&quot;+kmlxcf+&quot;&mj=&quot;+mj+&quot;&fx=&quot;+fx+&quot;&xsbz=&quot;+xsbz+&quot;&dwwl=&quot;+dwwl+
		&quot;&grwl=&quot;+grwl+&quot;&gdzc=&quot;+gdzc+&quot;&dybjhj=&quot;+dybjhj+&quot;&dbbd=&quot;+dbbd+&quot;&dylj=&quot;+dylj+&quot;&dybyhj=&quot;+dybyhj+&quot;&rjzxs=&quot;+rjzxs+&quot;&bz=insert&curselkmbh=&quot;+cursel_kmbh,null,&quot;dialogWidth=800px;dialogHeight=400px&quot;);
	if (retVal == 1) {
		//loadData(kmlxcf,kmjbcf,kmhcf.substring(0,4) );
		loadData(sel_kmlx,sel_kmjb,sel_kmbh,&quot;&quot;);
	}
}
//科目删除
function deleteKM()
{
	var currow = _this.GetCurrentRow(0);
	if(currow &lt; 1) return;
	var kmbh = _this.GetCellText(0,currow,2);
	var slh  = _this.GetCellText(0,currow,18);
	var kmmc = _this.GetCellText(0,currow,3);
	var kmlx = _this.GetCellText(0,currow,7);
	var jb   = _this.GetCellText(0,currow,0);
	var mjbz = _this.GetCellText(0,currow,1);

	if(kmbh == &quot;&quot;)	return true;

	kmmc = kmmc.replace(/　/g,&quot;&quot;);

	if(mjbz != 1){
		alert(&quot;科目[&quot;+kmbh+&quot;-&quot;+kmmc+&quot;]不是末级科目，不允许删除&quot;);
		return false;
	}

	if (!confirm(&quot;删除选中的科目[&quot;+kmbh+&quot;-&quot;+kmmc+&quot;]，是否确认？&quot;)) return;
	try {
		var ret = invokeOSFunc(&quot;deleteKM&quot;,&quot;kmbh=&quot;+kmbh+&quot;&thisorgid=&quot;+G_ORGID+&quot;&thisaccid=&quot;+G_ACCID+&quot;&slh=&quot;+slh);
		alert(ret);
		//alert(&quot;kmlx=&quot;+kmlx+&quot;,jb=&quot;+jb+&quot;,slh=&quot;+slh);
		if(jb == 1 || jb == 2) { jb = 0; kmlx = &quot;&quot;; slh = &quot;&quot;; loadData(kmlx,jb,slh,&quot;&quot;);}
		//loadData(kmlx,jb,slh.substring(0,4) );
		else loadData(kmlx,jb-1,slh,&quot;&quot;);
	}
	catch (e) {
		alert(&quot;删除科目失败:&quot;+e.toString());
	}
}
//科目修改
function modifyKM()
{
	var currow = _this.GetCurrentRow(0);
	if (currow &lt; 1) return;
	var kmbh = _this.GetCellText(0,currow,2);
	if(kmbh == &quot;&quot;) return;

	var kmjbcf = _this.GetCellText(0,currow,0);
	var kmhcf = _this.GetCellText(0,currow,2);
	var kmlxcf = _this.GetCellText(0,currow,7);
	var fx = _this.GetCellText(0,currow,8);


	var mj = _this.GetCellText(0,currow,1);	
	var xsbz = _this.GetCellText(0,currow,9);
	var dwwl = _this.GetCellText(0,currow,10);
	var grwl = _this.GetCellText(0,currow,11);
	var gdzc = _this.GetCellText(0,currow,12);
//	var xjkm = _this.GetCellText(0,currow,13);
	var dylj = _this.GetCellText(0,currow,14);
	var dybyhj = _this.GetCellText(0,currow,15);
	var dybjhj = _this.GetCellText(0,currow,16);
	var dbbd = _this.GetCellText(0,currow,17);
//	var yhkm = _this.GetCellText(0,currow,19);
	var rjzxs = _this.GetCellText(0,currow,20);
	//一级科目只能是区社保局可以修改，最多只能允许市级（暂收不提供），县级不允许修改	
	if(kmjbcf == &quot;1&quot;) {
		if(G_ORGID != &quot;1&quot;) {
			alert(&quot;一级科目不允许修改!&quot;);
			return;
		}
	}
	var retVal = window.showModalDialog(&quot;show.sp?grdid=CFG_KMBEDIT&kmjbcf=&quot;+kmjbcf+&quot;&kmhcf=&quot;+kmhcf+&quot;&kmlxcf=&quot;+kmlxcf+&quot;&kmbh=&quot;+kmbh+&quot;&mj=&quot;+mj+&quot;&fx=&quot;+fx+&quot;&xsbz=&quot;+xsbz+&quot;&dwwl=&quot;+dwwl+
	&quot;&grwl=&quot;+grwl+&quot;&gdzc=&quot;+gdzc+&quot;&dybjhj=&quot;+dybjhj+&quot;&dbbd=&quot;+dbbd+&quot;&dylj=&quot;+dylj+&quot;&dybyhj=&quot;+dybyhj+&quot;&rjzxs=&quot;+rjzxs+&quot;&bz=update&quot;,null,&quot;dialogWidth=800px;dialogHeight=400px&quot;);
	if (retVal == 1) {
//		window.location.reload(true);
		//loadData(kmlxcf,kmjbcf,kmhcf.substring(0,4) );
		loadData(sel_kmlx,sel_kmjb,sel_kmbh,&quot;&quot;);
	}
}

//科目延伸
function extKM()
{
	var currow = _this.GetCurrentRow(0);
	if (currow &lt; 1) return;
	var kmbh = _this.GetCellText(0,currow,2);
	if(kmbh == &quot;&quot;) return ;

	var kmmc = _this.GetCellText(0,currow,3);
	var mj = _this.GetCellText(0,currow,1);
	var kmlx = _this.GetCellText(0,currow,7);
	var jb = _this.GetCellText(0,currow,0);
	var yxjefx = _this.GetCellText(0,currow,8);
	var kmjc = _this.GetCellText(0,currow,6);
	var slh = _this.GetCellText(0,currow,18);

	if(mj == 1){	
		//　　　　　工行
		kmmc = kmmc.replace(/  /g,&quot;&quot;);
		kmjc = kmjc.replace(/　/g,&quot;&quot;);

		var retVal = window.showModalDialog(&quot;show.sp?grdid=CFG_KMRS&kmbh=&quot;+kmbh+&quot;&kmmc=&quot;+kmmc+&quot;&mj=&quot;+mj+&quot;&kmlx=&quot;+kmlx+&quot;&jb=&quot;+jb+&quot;&yxjefx=&quot;+yxjefx+&quot;&kmjc=&quot;+kmjc,null,&quot;dialogWidth=600px;dialogHeight=300px&quot;);
		if (retVal == 1) {
//			window.location.reload(true);
			loadData(kmlx,jb,kmbh,substring(0,4),&quot;&quot;);
			return true;
		}
	}else if(mj == 0){
		alert(&quot;该科目不是末级科目,不能延伸科目!&quot;);
		return false;
	}
//	window.location.reload(true);
}
//科目合并
function mergKM()
{
	var currow = _this.GetCurrentRow(0);
	if (currow &lt; 1) return;
	var kmbh = _this.GetCellText(0,currow,2);
	var kmmc = _this.GetCellText(0,currow,3);
	var kmlx = _this.GetCellText(0,currow,7);
	var jb   = _this.GetCellText(0,currow,0);
	var mj = _this.GetCellText(0,currow,1);
	var slh = _this.GetCellText(0,currow,18);

	if(mj==0){
		//检查下级科目是否都是末级科目
		_sql.querytods(&quot;CHECKKM&quot;,&quot;KMBH=&quot;+kmbh);
		var cnt = _this.XMLDS_GetString(0,&quot;CNT&quot;);
		if (cnt != 0) {
			alert(&quot;被合并科目的下级科目含有非末级科目，不允许合并！&quot;);
			return false;
		}

		var retVal = window.showModalDialog(&quot;show.sp?grdid=CFG_XJKMHB&kmbh=&quot;+kmbh+&quot;&kmmc=&quot;+kmmc,null,&quot;dialogWidth=600px;dialogHeight=300px;&quot;);
		if (retVal == 1) {
//			window.location.reload(true);
			if(jb == 1) { jb = 0; kmlx = &quot;&quot;; slh = &quot;&quot;; loadData(kmlx,jb,slh,&quot;&quot;);}
			loadData(kmlx,jb,slh,&quot;&quot;);
			return true;
		}
	}else if(mj== 1){
		alert(&quot;该科目是末级科目,没有可合并的下级科目!&quot;);
		return false;
	}
//	window.location.reload(true);
}

//科目移动
function moveKM()
{
	var currow = _this.GetCurrentRow(0);
	if (currow &lt; 1) return;
	var kmlx = _this.GetCellText(0,currow,7);
	var kmbh = _this.GetCellText(0,currow,2);
	var kmmc = _this.GetCellText(0,currow,3); kmmc = kmmc.replace(/　/g,&quot;&quot;);
	var mjbz = _this.GetCellText(0,currow,1);
	var jb   = _this.GetCellText(0,currow,0);
	var mj = _this.GetCellText(0,currow,1);
	if (mjbz == 1) {
		var retVal = window.showModalDialog(&quot;show.sp?grdid=CFG_KMMV&kmbh=&quot;+kmbh+&quot;&kmmc=&quot;+kmmc,null,&quot;dialogWidth=600px;dialogHeight=300px;&quot;);
		if (retVal == 1) {
			//loadData(kmlx,jb,kmbh.substring(0,4) );
			loadData(sel_kmlx,sel_kmjb,sel_kmbh,&quot;&quot;);
			return true;
		}

	}
	else if (mjbz == 0) {
		alert(&quot;该科目不是末级科目,不能移动!&quot;);
	}
}

function checkKM()
{
	window.showModalDialog(&quot;show.sp?grdid=ZWWH_KMJC&quot;,null,&quot;dialogWidth=800px;dialogHeight=500px&quot;);
}

function printKM(typ)
{
	var msg = (typ == 1 ? &quot;打印当前内容&quot; : &quot;打印全部内容&quot;);
	if (!confirm(msg+&quot;,是否继续？&quot;)) return;
	if (typ == 1) {
		_this.Print(0,0);
	}
	else if (typ == 2) {
		var totalCount = 0;
		var pageCount = 60;

		_this.LoadZXGFile(ZXGFILE1,-1,1);

		_sql.querytods(&quot;ALLKM&quot;,&quot;&quot;);

		totalCount = _this.XMLDS_GetRowCount();
		var totalPage = Math.ceil(totalCount / pageCount);

		for (var i=1;i&lt;=totalPage;i++) {
			_this.LoadZXGFile(ZXGFILE1,-1,1);

			//复制内容到打印页
			var startRow = (i-1)*pageCount; 
			var endRow = i*pageCount + startRow;
			var row = 1;
			for (var n=startRow;n&lt;endRow;n++) {
				for (var c=0;c&lt;_this.GetColCount(1)-1;c++) {
					_this.SetCellText(1,row,c,_this.XMLDS_GetStringAt(n,c));
				}
				row ++;
			}
			_this.SetCellText(1,_this.GetRowCount(1)-1,0,&quot;第&quot;+i+&quot;页 共&quot;+totalPage+&quot;页&quot;);

			//格式化显示
			for (var r=1;r&lt;=_this.GetRowCount(1)-2;r++) {
				var mjbz = _this.GetCellText(1,r,1);
				var kmbh = _this.GetCellText(1,r,2);
				if(kmbh != &quot;&quot;) {
					if (mjbz == 0) {
						_this.SetCellText(1,r,1,&quot;+&quot;);
					}
					else {
						_this.SetCellText(1,r,1,&quot; &quot;);
					}
				}
			}
			nullRow();

			_this.Print(1,0);
		}
	}
}


function nullRow()
{
	for (var r=1;r&lt;_this.GetRowCount(1)-1;r++) {
		var str = _this.GetCellText(1,r,0);
		if (str == &quot;&quot;) {
			for (var c=0;c&lt;_this.GetColCount(1);c++) {
				_this.SetRowVisible(1,r,-1);
			}
		}
	}
}

ROW.GRDOSDS_VALUE:
var pub = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);

function deleteKM()
{
	var db = null;
	try {
		db = new pub.EADatabase();

		var sql = &quot;select count(*) min from cw_kmb where org=&apos;&quot;+thisorgid+&quot;&apos; and acc=&apos;&quot;+thisaccid+&quot;&apos; and slh=&apos;&quot;+slh+&quot;&apos;&quot;;
		var min = db.GetSQL(sql);
		if (min == &quot;1&quot; || min == 1) {
//			throw new Exception(thisorgid+&quot;-&quot;+thisaccid+&quot;-&quot;+slh);
			sql = &quot;update cw_kmb set mjbz= &apos;1&apos; where org=&apos;&quot;+thisorgid+&quot;&apos; and acc=&apos;&quot;+thisaccid+&quot;&apos; and kmbh=&apos;&quot;+slh+&quot;&apos;&quot;;
			db.ExcecutSQL(sql);
		}
//		sql = &quot;delete from cw_qcyeb where org=&apos;&quot;+thisorgid+&quot;&apos; and acc=&apos;&quot;+thisaccid+&quot;&apos; and kmbh=&apos;&quot;+kmbh+&quot;&apos; and abs(ye)&lt;0.001 and abs(jfje)&lt;0.001 and abs(dfje)&lt;0.001&quot;;
		sql = &quot;select count(1) from cw_qcyeb where org=&apos;&quot;+thisorgid+&quot;&apos; and acc=&apos;&quot;+thisaccid+&quot;&apos; and kmbh=&apos;&quot;+kmbh+&quot;&apos; and abs(ye)&lt;0.001 and abs(jfje)&lt;0.001 and abs(dfje)&lt;0.001&quot;;
		//db.ExcecutSQL(sql);			
		var count = db.GetSQL(sql);
		if (count &gt; 0) {
			db.Rollback();
			return &quot;删除失败!---科目已被使用,不能删除!&quot;; 
		}

		sql = &quot;select count(1) from cw_pzmxb where org=&apos;&quot;+thisorgid+&quot;&apos; and acc=&apos;&quot;+thisaccid+&quot;&apos; and kmbh=&apos;&quot;+kmbh+&quot;&apos;&quot;;
		//db.ExcecutSQL(sql);			
		var count = db.GetSQL(sql);
		if (count &gt; 0) {
			db.Rollback();
			return &quot;删除失败!---科目已被使用,不能删除!&quot;; 
		}	

		sql = &quot;select count(1) from cw_rjzb where org=&apos;&quot;+thisorgid+&quot;&apos; and acc=&apos;&quot;+thisaccid+&quot;&apos; and kmbh=&apos;&quot;+kmbh+&quot;&apos;&quot;;
		//db.ExcecutSQL(sql);			
		var count = db.GetSQL(sql);
		if (count &gt; 0) {
			db.Rollback();
			return &quot;删除失败!---科目已被使用,不能删除!&quot;; 
		}				
		sql = &quot;delete from cw_kmb where org=&apos;&quot;+thisorgid+&quot;&apos; and acc=&apos;&quot;+thisaccid+&quot;&apos; and kmbh=&apos;&quot;+kmbh+&quot;&apos;&quot;;
		db.ExcecutSQL(sql);

		db.Commit();
		return &quot;删除科目成功！&quot;;
	}
	catch (e) {
		if (db != null) db.Rollback();
		return &quot;删除失败!&quot;+e.toString(); //e.toString();
	}
	finally {
		if (db != null) db.Close();
	}
}
