//保存
function saveKM()
{
	var param = new Object();
	param.sbh = G_ORGID;
	param.zth = G_ACCID.replace(G_ORGID,&quot;&quot;);
	param.thisorgid = G_ORGID;
	param.thisaccid = G_ACCID;
	param.kmbh = _this.GetCellText(0,3,2);
	param.jb = _this.GetCellText(0,3,6);
	param.kmlx = _this.GetCellText(0,5,2);
	param.mjbz = _this.GetCellText(0,5,6);
	param.kmmc = _this.GetCellText(0,7,2);
	param.kmjc = _this.GetCellText(0,9,2);
	param.zjm = _this.GetCellText(0,11,2);
	param.yefx = _this.GetCellText(0,11,6);
	param.dykmbh = _this.GetCellText(0,13,3);
	param.rjzkm_lrbz = _this.GetCellText(0,13,6);
//	param.xjbz = _this.GetCellText(0,11,8);	
//	param.yhbz = _this.GetCellText(0,13,8);	
	param.xjyhbz = 0;
	param.dwbz = _this.GetCellText(0,5,8);
	param.grbz = _this.GetCellText(0,7,8);
	param.gdzcbz = _this.GetCellText(0,9,8);
	param.kssj = _this.GetCellText(0,17,2);
	param.jssj = _this.GetCellText(0,17,5);
	param.xzlx = _this.GetCellText(0,17,9);
	param.yhzl = _this.GetCellText(0,19,2);

	var kxlxstr = _this.GetCellShowText(0,5,2);
	if (kxlxstr.split(&quot;-&quot;)[0] != (param.kmbh).substring(0,1)) {
		alert(&quot;科目编号的第一位值与科目类型不匹配，请检查！&quot;);
		return;
	}

	if(_this.GetCellText(0,3,6) == 1){
		param.slh = &quot;&quot;;		
	}else{
		param.slh = _this.GetCellText(0,3,2).substring(0,_this.GetCellText(0,3,2).length - 3);
	}
	if (_this.GetCellText(0,11,8) == 1) {
		param.xjyhbz = 1;
	}
	else if (_this.GetCellText(0,13,8) == 1) {
		param.xjyhbz = 2;
	}

	param.qlbz = _this.GetCellText(0,5,12);
	param.zzgs1 = _this.GetCellText(0,7,12);
	param.zzgs2 = _this.GetCellText(0,9,12);
	param.zzgs3 = _this.GetCellText(0,11,12);
	param.zzgs4 = _this.GetCellText(0,13,12);

	if (param.kmbh == &quot;&quot; || param.kmmc == &quot;&quot; || param.kmjc == &quot;&quot; || param.yefx == &quot;&quot; || param.kmlx == &quot;&quot; || param.mjbz == &quot;&quot; || param.jb == &quot;&quot;
		 ||param.kssj == &quot;&quot; || param.xzlx == &quot;&quot;) {
		alert(&quot;星号标注的必填项信息不能为空，请检查！&quot;);
		return;
	}

	var kmlen3 = param.kmbh.substring(0,3);
	if ((kmlen3 == &quot;102&quot; || kmlen3 == &quot;103&quot; || kmlen3 == &quot;104&quot;) && param.yhzl == &quot;&quot;) {
		alert(&quot;银行科目必须选择银行种类！&quot;);
		_this.SetCellFocus(0,19,2);
		return;
	} 

	try {
		var ret = invokeOSFuncExt(&quot;SaveKm&quot;,param);
		if (ret == 1) {
			alert(&quot;保存科目成功！&quot;);
			window.returnValue = &quot;1&quot;;
			window.close();
		}
		else {
			alert(ret);
		}
	}
	catch (e) {
		alert(&quot;保存科目失败!&quot;+e.toString());
	}

}

//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
	if ((row == 11 || row == 13 ) && col == 8) {
		if (newvalue == 1 && row == 11) {
			_this.SetCellText(sheet,13,8,0);
		}
		else if (newvalue == 1 && row == 13) {
			_this.SetCellText(sheet,11,8,0);
		}

	}
	else if (row == 13 && col == 3) {
		_sql.querytods(&quot;DYKMMC&quot;,&quot;KMBH=&quot;+newvalue);
		_this.SetCellText(0,15,3,_this.XMLDS_GetString(0,&quot;DYKMMC&quot;));
	}
	else if (row == 17 && (col == 2 || col == 5)) { //开始截止日期
		if (!checkYYYYMM(newvalue)) {
			alert(&quot;录入格式不正确!正确格式为:YYYYMM(201607).&quot;);
			_this.SetCellText(sheet,row,col,oldvalue);
		}
	}
	else if (row == 7 && col == 2) { //复制到简称
		if (_this.GetCellText(sheet,9,2) == &quot;&quot;) {
			_this.SetCellText(sheet,9,2,newvalue);
		}
	}
	else if (row == 3 && col == 2) { //校验科目编号
		var jb = _this.GetCellText(0,3,6);
		_sql.querytods(&quot;ZTBINFO&quot;);
		var jblen = 0;
		for(var i=1;i&lt;=jb;i++) {
			jblen += 1.0*_this.XMLDS_GetString(0,&quot;KMCD&quot;+i);
		}		
		if (newvalue.length != jblen) {
			alert(&quot;科目长度不正确！科目长度应为&quot;+jblen);
			_this.SetCellText(sheet,row,col,oldvalue);
		}
	}

}

function checkYYYYMM(input) 
{
	var reg = /^\b[1-3]\d{3}(0[1-9]|1[0-2])$/;
	var flg = input.match( reg );
	if( input != null && input != &apos;&apos;) {
		if( flg == null ) {
			return false;
		}
	}
	return true;
}

ROW.GRDOSDS_VALUE:
var pub = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);

function SaveKm()
{
	var db = null;
	try {
		db = new pub.EADatabase();
		//20160831 dxb 如果科目是103开头的（银行支出户），则银行种类用收入户的代码
		var kmlen = kmbh.substring(0,3);
		if(kmlen == &quot;103&quot;) { 
			var sql = &quot;select yhbm from cw_dfdsb where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and yhzl = &apos;&quot;+yhzl+&quot;&apos; and szbz=&apos;2&apos;&quot;;
			var tmpds = db.QuerySQL(sql);
			if (tmpds.getRowCount() &gt; 0) {
				yhzl = tmpds.getStringAt(0,&quot;YHBM&quot;);
			}
			else {
				return &quot;查询cw_dfdsb表找不到银行代码为&quot;+yhzl+&quot;的记录!&quot;;
			}
		}
		var sql = &quot;select * from cw_kmb where org=&apos;&quot;+thisorgid+&quot;&apos; and acc=&apos;&quot;+thisaccid+&quot;&apos; and kmbh=&apos;&quot;+kmbh+&quot;&apos;&quot;;
		var cnt = db.GetSQLRowCount(sql);
		if (cnt == 0) {
			sql = &quot;insert into cw_kmb(org,acc,sbh,zth,kmbh,kmmc,kmjc,zjm,kmlx,slh,jb,mjbz,yxyefx,dwbz,grbz,gdzcbz,qlbz,xjyhbz,zzgs1,zzgs2,zzgs3,zzgs4,rjzkm_lrbz,dykmbh,kssj,jssj,xzlx,yhzl) 
				values(&apos;&quot;+thisorgid+&quot;&apos;,&apos;&quot;+thisaccid+&quot;&apos;,&apos;&quot;+sbh+&quot;&apos;,&apos;&quot;+zth+&quot;&apos;,&apos;&quot;+kmbh+&quot;&apos;,&apos;&quot;+kmmc+&quot;&apos;,&apos;&quot;+kmjc+&quot;&apos;,&apos;&quot;+zjm+&quot;&apos;,&apos;&quot;+kmlx+&quot;&apos;,&apos;&quot;+slh+&quot;&apos;,&apos;&quot;+jb+&quot;&apos;,&apos;&quot;+mjbz+&quot;&apos;,&apos;&quot;+yefx
				+&quot;&apos;,&apos;&quot;+dwbz+&quot;&apos;,&apos;&quot;+grbz+&quot;&apos;,&apos;&quot;+gdzcbz+&quot;&apos;,&apos;&quot;+qlbz+&quot;&apos;,nvl(&apos;&quot;+xjyhbz+&quot;&apos;,&apos;2&apos;),&apos;&quot;+zzgs1+&quot;&apos;,&apos;&quot;+zzgs2+&quot;&apos;,&apos;&quot;+zzgs3+&quot;&apos;,&apos;&quot;+zzgs4+&quot;&apos;,&apos;&quot;+rjzkm_lrbz+&quot;&apos;,&apos;&quot;+dykmbh+&quot;&apos;,&apos;&quot;
				+kssj+&quot;&apos;,&apos;&quot;+jssj+&quot;&apos;,&apos;&quot;+xzlx+&quot;&apos;,&apos;&quot;+yhzl+&quot;&apos;)&quot;;
			db.ExcecutSQL(sql);
		}
		else {
			sql = &quot;update cw_kmb 
				set yxyefx=&apos;&quot;+yefx+&quot;&apos;,kmlx=&apos;&quot;+kmlx+&quot;&apos;,qlbz=&apos;&quot;+qlbz+&quot;&apos;,xjyhbz=&apos;&quot;+xjyhbz+&quot;&apos;,zzgs1=&apos;&quot;+zzgs1+&quot;&apos;,zzgs2=&apos;&quot;+zzgs2+&quot;&apos;,zzgs3=&apos;&quot;+zzgs3+&quot;&apos;,zzgs4=&apos;&quot;+zzgs4+&quot;&apos;,rjzkm_lrbz=&apos;&quot;+rjzkm_lrbz+&quot;&apos;,dykmbh=&apos;&quot;+dykmbh+&quot;&apos;
				    ,kmmc = &apos;&quot;+kmmc+&quot;&apos;,kmjc=&apos;&quot;+kmjc+&quot;&apos;,kssj=&apos;&quot;+kssj+&quot;&apos;,jssj=&apos;&quot;+jssj+&quot;&apos;,xzlx=&apos;&quot;+xzlx+&quot;&apos;,yhzl=&apos;&quot;+yhzl+&quot;&apos;
				where org=&apos;&quot;+thisorgid+&quot;&apos; and acc=&apos;&quot;+thisaccid+&quot;&apos; and kmbh=&apos;&quot;+kmbh+&quot;&apos;&quot;;
			db.ExcecutSQL(sql);
		}
		db.Commit();
		return &quot;1&quot;;
	}
	catch (e) {
		if (db != null) db.Rollback();
		return e.toString();
	}
	finally {
		if (db != null) db.Close();
	}
}

ROW.DATDSC:
select a.*,cw_pack4.kmmc(sbh,zth,DYKMBH) DYKMMC
from cw_kmb a 
where org=&apos;[%SYS_ORGID]&apos; and acc=&apos;[%SYS_ACCID]&apos;
  and kmbh=&apos;[%KMBH]&apos;

select cw_pack4.kmmc(sbh,zth,KMBH) DYKMMC
from cw_kmb a 
where org=&apos;[%SYS_ORGID]&apos; and acc=&apos;[%SYS_ACCID]&apos;
  and kmbh=&apos;[%KMBH]&apos;

select yhzl from cw_dfdsb 
where org = &apos;[%SYS_ORGID]&apos; and acc = &apos;[%SYS_ACCID]&apos; 
  and yhbm=&apos;[%YHBM]&apos;
