ROW.GRDJSDS_VALUE:
var ZXGFILE1;
var dsdfList;
var qdList;
var _szbz = &quot;%&quot;;
var _yhzl = &quot;&quot;;

//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	_this.SplitSheet(0,&quot;H&quot;,&quot;20%&quot;);
	ZXGFILE1 = _this.SaveTempZXGFile(1);

	dsdfList = _this.CreateListValue();
	_this.SetListValue(dsdfList,&quot;%&quot;,&quot;全部&quot;);
	_this.SetListValue(dsdfList,&quot;1&quot;,&quot;代收&quot;);
	_this.SetListValue(dsdfList,&quot;2&quot;,&quot;代发&quot;);
	_this.SetToComboBox(&quot;&quot;,1,0,4,dsdfList);
	_this.SetCellText(1,0,4,&quot;%&quot;);

	qdList = _this.CreateListValue();
	_this.SetListValue(qdList,&quot;0&quot;,&quot;未启用&quot;);
	_this.SetListValue(qdList,&quot;1&quot;,&quot;启用&quot;);

	loadDS1();
	loadDS2(&quot;&quot;,&quot;&quot;);

	_this.SetAttribe(&quot;SHEET_0&quot;,_this.ATTR_SHEET_SELECTFLAG,_this. SELECTFLAG_SINGLEROW );
	_this.SetAttribe(&quot;SHEET_1&quot;,_this.ATTR_SHEET_SELECTFLAG,_this. SELECTFLAG_SINGLEROW );

}


function loadDS1()
{
	var xml = _sql.query(&quot;DS1&quot;,&quot;&quot;);
	_this.SetMainCellRange(0,1,0,11,3);
	_this.SetFixedRow(0,1);
	_this.SetColVisible(0,1,-1);
	_this.SetXmlDS(0,1,0,11,3,xml);
	for (var r=_this.GetMainCellRangeRow1(0);r&lt;=_this.GetMainCellRangeRow2(0);r++) {
		if (_this.GetCellText(0,r,2) != &quot;&quot;) {
			_this.SetToBoolBox(0,r,0);
		}
	}
	_this.AutoFixScreenWidth();
}

function loadDS2(szbz,yhzl)
{
	_this.LoadZXGFile(ZXGFILE1,-1,1);
	_this.SetFixedRow(1,3);
	_this.SetColVisible(1,1,-1);
	_this.SetMainCellRange(1,3,0,_this.GetRowCount(1)-1,_this.GetColCount(1)-1);
	_this.SetToComboBox(&quot;&quot;,1,0,4,dsdfList);
	_this.SetCellText(1,0,4,_szbz);
	var xml = _sql.query(&quot;DS2&quot;,&quot;SZBZ=&quot;+szbz+&quot;&YHZL=&quot;+yhzl);
	_this.SetXmlDS(1,3,0,_this.GetRowCount(1)-1,_this.GetColCount(1)-1,xml);
	for (var r=_this.GetMainCellRangeRow1(1);r&lt;=_this.GetMainCellRangeRow2(1);r++) {
		if (_this.GetCellText(1,r,2) != &quot;&quot;) {
			_this.SetToBoolBox(1,r,0);
			_this.SetToComboBox(&quot;&quot;,1,r,2,dsdfList);
			_this.SetToComboBox(&quot;&quot;,1,r,21,qdList);
			_this.SetToDateBox(&quot;&quot;,1,r,22,_this.GetCellText(1,r,22));
			if (_this.GetCellText(1,r,21) == 0) _this.SetCellColor(1,r,21,255,0,0);
			else _this.SetCellColor(1,r,21,0,0,255);
		}
	}
	_this.AutoFixColWidth(1,600);
}

//鼠标左键点击
function _thisOnMouseLClick(sheet,row,col)
{
	if (sheet == 0 && row &gt;= _this.GetMainCellRangeRow1(0) && row &lt;= _this.GetMainCellRangeRow2(0)) {
		_yhzl = _this.GetCellText(sheet,row,2);
		if (_yhzl != &quot;&quot;) {
			loadDS2(_szbz,_yhzl);
			//单选
			for (var r=_this.GetMainCellRangeRow1(0);r&lt;=_this.GetMainCellRangeRow2(0);r++) {
				_this.SetCellText(0,r,0,0);
			}
			_this.SetCellText(0,row,0,1);
		}
	}
}

//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
	if (sheet == 1 && row == 0 && col == 4) {
		_szbz = newvalue;
		loadDS2(_szbz,_yhzl);
	}
	if (col === 0) {
		selectOneRow(sheet,row);
	}
	//修改标志
//	if (_this.GetCellText(sheet,row,2) != &quot;&quot;) {
//		_this.SetCellText(sheet,row,1,&quot;*&quot;);
//	}

	//修改标志符:只要FLG为添加“+”就屏蔽修改单元格：黄小星2017/8/9
	if (_this.GetCellText(sheet,row,2) != &quot;&quot;&&sheet==0) {
		_this.SetCellText(sheet,row,1,&quot;*&quot;);
	}
	if (_this.GetCellText(sheet,row,2) != &quot;&quot;&&_this.GetCellText(sheet,row,1)!=&quot;+&quot;&&sheet==1) {
		_this.SetCellText(sheet,row,1,&quot;*&quot;);
	}

}
function selectOneRow(sheet,row)
{
	for (var r=_this.GetMainCellRangeRow1(sheet);r&lt;=_this.GetMainCellRangeRow2(sheet);r++) {
		_this.SetCellText(sheet,r,0,&quot;0&quot;);
	}
	_this.SetCellText(sheet,row,0,&quot;1&quot;);
}


//新增银行种类
function newYHZL()
{
	var row = 0;
	for (var r=_this.GetMainCellRangeRow1(0);r&lt;=_this.GetMainCellRangeRow2(0);r++) {
		if (_this.GetCellText(0,r,2) != &quot;&quot;) {
			row = r;
			break;
		}
	}
	_this.AppendRow(0,_this.GetRowCount(0)-1);
	row = _this.GetRowCount(0)-1;
	_this.SetCellFocus(0,row,2);
	_this.SetCellText(0,row,1,&quot;+&quot;);

}
//添加空行直接修改该行值:黄小星2017/8/9
function addlist(row){
	var  list = _this.CreateListValue();
	_this.SetListValue(list,&quot;%&quot;,&quot;全部&quot;);
	_this.SetListValue(list,&quot;1&quot;,&quot;代收&quot;);
	_this.SetListValue(list,&quot;2&quot;,&quot;代发&quot;);
	_this.SetToComboBox(&quot;&quot;,1,row,2,list);
	_this.SetCellText(1,row,2,&quot;1&quot;);

	var qlist = _this.CreateListValue();
	_this.SetListValue(qlist,&quot;0&quot;,&quot;未启用&quot;);
	_this.SetListValue(qlist,&quot;1&quot;,&quot;启用&quot;);
	_this.SetToComboBox(&quot;&quot;,1,row,21,qlist);
	_this.SetCellText(1,row,21,&quot;0&quot;);
	_this.SetToBoolBox(1,row,0);
}
//判断空行即直接在空行添加：黄小星2017/8/9
function pdRow(){ 
	var count=&quot;&quot;;
	var flg=0;
	for(count=3;count&lt;_this.GetRowCount(1);count++){
	var rs =_this.GetCellText(1,count,2);
	if(rs==&quot;&quot;){
		addlist(count);
		//row = _this.GetRowCount(1)-1;
		_this.SetCellFocus(1,count,2);
		_this.SetCellText(1,count,1,&quot;+&quot;);
		flg=1;
		break;
		}			
	}
	if(flg==0){

		_this.AppendRow(1,_this.GetRowCount(1)-1);
		row = _this.GetRowCount(1)-1;
		_this.SetCellFocus(1,row,2);
		_this.SetCellText(1,row,1,&quot;+&quot;);
	}


}

