//删除
function delRow()
{
	var cursheet = _this.GetCurrentSheet();
	if (cursheet &lt; 0)  return;
	for (var r=_this.GetMainCellRangeRow1(cursheet);r&lt;=_this.GetMainCellRangeRow2(cursheet);r++) {
		if (_this.GetCellText(cursheet,r,0) == &quot;1&quot; && cursheet == 0) {
			//检查是否有明细记录
			if (_this.GetCellText(1,3,3) != &quot;&quot;) {
				alert(&quot;该银行已经配置有参数数据，不能删除！&quot;);
				return;
			}

			_this.SetCellText(cursheet,r,1,&quot;-&quot;);
			_this.SetRowVisible(cursheet,r,-1);
			break;
		}
		else if (_this.GetCellText(cursheet,r,0) == &quot;1&quot;) {
			_this.SetCellText(cursheet,r,1,&quot;-&quot;);
			_this.SetRowVisible(cursheet,r,-1);
		}
	}
}

//保存
function saveDSDF()
{
	var param = new Object();
	param.xml1 = _this.GetXml(0,1,0,_this.GetRowCount(0)-1,3);
	//_this.SetCellText(1,0,7,param.xml1);
	param.xml2 = _this.GetXml(1,3,0,_this.GetRowCount(1)-1,_this.GetColCount(1)-1);
	//alert(param.xml2);
	//_this.SetCellText(1,0,6,param.xml2);
	param.thisorgid = G_ORGID;
	param.thisaccid = G_ACCID;
	param.zth = G_ACCID.replace(G_ORGID,&quot;&quot;);

	try {
		var ret = invokeOSFuncExt(&quot;Save&quot;,param);
		alert(ret);
	}
	catch (e) {
		alert(&quot;保存失败:&quot;+e.toString());
	}
}

ROW.GRDOSDS_VALUE:
var pub = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);

function Save()
{
	var db = null;
	var sql = &quot;&quot;;
	var upcnt = 0;
	var adcnt = 0;
	var decnt = 0;

	try {
		db = new pub.EADatabase();

		var yhds = new pub.EAXmlDS(xml1);
		var dsdfds = new pub.EAXmlDS(xml2);

		//银行种类
		for (var i=0;i&lt;yhds.getRowCount();i++) {
			var flg = yhds.getStringAt(i,&quot;FLG&quot;);
			var id = yhds.getStringAt(i,&quot;ID&quot;);
			var name = yhds.getStringAt(i,&quot;NAME&quot;);
			if (id != &quot;&quot;) {
				if (flg == &quot;+&quot;) {
					sql = &quot;insert into aa10(aaa100,AAA102,AAA103) values(&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;)&quot;.format([&quot;BAE010&quot;,id,name]);
					db.ExcecutSQL(sql);
				}
				else if (flg == &quot;-&quot;) {
					sql = &quot;delete from aa10 where aaa100=&apos;%s&apos; and aaa102=&apos;%s&apos;&quot;.format([&quot;BAE010&quot;,id]);
					db.ExcecutSQL(sql);
				}
				else if (flg == &quot;*&quot;) {
					//20161216 lyh modify aa10表
//					sql = &quot;update aaa100 set AAA103=&apos;%s&apos; where aaa100=&apos;%s&apos; and aaa102=&apos;%s&apos;&quot;.format([name,&quot;BAE010&quot;,id]);
					sql = &quot;update aa10 set AAA103=&apos;%s&apos; where aaa100=&apos;%s&apos; and aaa102=&apos;%s&apos;&quot;.format([name,&quot;BAE010&quot;,id]);
					db.ExcecutSQL(sql);
				}
			}
		}

//		select &apos;&apos; sel,&apos;&apos; flg,szbz,yhbm, yhmc, WJM, wjml,nvl(FGFBZ,&apos;0&apos;) FGFBZ, jshhh_head, 
//		  jshhh_len, /*20120703 钱福军 增加医保财务代收拷盘对应结算行行号开头规则*/
//		  ftp_ServerName, /*20130521 qfj ftp服务器名字*/
//		  ftp_ip, /*20130521 qfj ftp服务器IP地址(URL)*/
//		  ftp_port, /*20130521 qfj ftp服务器端口*/
//		  ftp_gxsi_login_name, /*20130521 qfj ftp服务器gxsi社保用户登录名*/
//		  ftp_gxsi_login_passwd, /*20130521 qfj ftp服务器gxsi社保用户登录密码*/
//		  ftp_gxsi_login_path, /*20130521 qfj ftp服务器gxsi社保用户登录默认目录*/
//		  ftp_up_path, /*20130521 qfj 社保ftpup上传目录（权限：社保读写，银行只读）*/
//		  ftp_down_path, /*20130521 qfj 社保ftpdown下载目录（权限：社保只读，银行读写）*/
//		  ftp_file_type /*20130521 qfj 文件类型：未加密，加密无压缩，加密带压缩*/
//		from cw_dfdsb
		//代收代发参数
		for (var i=0;i&lt;dsdfds.getRowCount();i++) {
			var flg = dsdfds.getStringAt(i,&quot;FLG&quot;);
			var szbz = dsdfds.getStringAt(i,&quot;SZBZ&quot;);
			var yhbm = dsdfds.getStringAt(i,&quot;YHBM&quot;);
			var yhmc = dsdfds.getStringAt(i,&quot;yhmc&quot;);
			var wjm = dsdfds.getStringAt(i,&quot;wjm&quot;);
			var wjml = dsdfds.getStringAt(i,&quot;wjml&quot;);
			var fgfbz = dsdfds.getStringAt(i,&quot;fgfbz&quot;);
			var jshhh_head = dsdfds.getStringAt(i,&quot;jshhh_head&quot;);
			var jshhh_len = dsdfds.getStringAt(i,&quot;jshhh_len&quot;);
			var ftp_ServerName = dsdfds.getStringAt(i,&quot;ftp_ServerName&quot;);
			var ftp_ip = dsdfds.getStringAt(i,&quot;ftp_ip&quot;);
			var ftp_port = dsdfds.getStringAt(i,&quot;ftp_port&quot;);
			var ftp_gxsi_login_name = dsdfds.getStringAt(i,&quot;ftp_gxsi_login_name&quot;);
			var ftp_gxsi_login_passwd = dsdfds.getStringAt(i,&quot;ftp_gxsi_login_passwd&quot;);
			var ftp_gxsi_login_path = dsdfds.getStringAt(i,&quot;ftp_gxsi_login_path&quot;);
			var ftp_up_path = dsdfds.getStringAt(i,&quot;ftp_up_path&quot;);
			var ftp_file_type = dsdfds.getStringAt(i,&quot;ftp_file_type&quot;);
			var yhzl = dsdfds.getStringAt(i,&quot;yhzl&quot;);

			if (szbz != &quot;&quot;) {
				if (flg == &quot;+&quot;) {
					//添加yszl_max
					sql = &quot;insert into cw_dfdsb_yszl(sbh,zth,yhzl,org,acc,szbz,yhbm,yhmc,wjm,wjml,fgfbz,jshhh_head,jshhh_len,ftp_ServerName,ftp_ip,ftp_port,ftp_gxsi_login_name,ftp_gxsi_login_passwd,ftp_gxsi_login_path,ftp_up_path,ftp_file_type) 
						values(&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;)&quot;
						.format([thisorgid,zth,yhzl,thisorgid,thisaccid,szbz,yhbm,yhmc,wjm,wjml,fgfbz,jshhh_head,jshhh_len,ftp_ServerName,ftp_ip,ftp_port,ftp_gxsi_login_name,ftp_gxsi_login_passwd,ftp_gxsi_login_path,ftp_up_path,ftp_file_type]);
					adcnt += db.ExcecutSQL(sql);
					//db.Rollback();
					//return sql;
				}
				else if (flg == &quot;-&quot;) {
					sql = &quot;delete from cw_dfdsb_yszl where szbz=&apos;%s&apos; and yhzl=&apos;%s&apos; and yhbm=&apos;%s&apos; and org=&apos;%s&apos; and acc=&apos;%s&apos;&quot;.format([szbz,yhzl,yhbm,thisorgid,thisaccid]);
					decnt += db.ExcecutSQL(sql);
				}
				else if (flg == &quot;*&quot;) {
				//添加yszl_max
					sql = &quot;update cw_dfdsb_yszl set szbz=&apos;%s&apos;,yhbm=&apos;%s&apos;,yhmc=&apos;%s&apos;,wjm=&apos;%s&apos;,wjml=&apos;%s&apos;,fgfbz=&apos;%s&apos;,jshhh_head=&apos;%s&apos;,jshhh_len=&apos;%s&apos;,ftp_ServerName=&apos;%s&apos;,ftp_ip=&apos;%s&apos;,ftp_port=&apos;%s&apos;,ftp_gxsi_login_name=&apos;%s&apos;,ftp_gxsi_login_passwd=&apos;%s&apos;,
						ftp_gxsi_login_path=&apos;%s&apos;,ftp_up_path=&apos;%s&apos;,ftp_file_type=&apos;%s&apos; where org=&apos;%s&apos; and acc=&apos;%s&apos; and yhzl=&apos;%s&apos; and yhbm=&apos;%s&apos;&quot;
						.format([szbz,yhbm,yhmc,wjm,wjml,fgfbz,jshhh_head,jshhh_len,ftp_ServerName,ftp_ip,ftp_port,ftp_gxsi_login_name,ftp_gxsi_login_passwd,ftp_gxsi_login_path,ftp_up_path,ftp_file_type,thisorgid,thisaccid,yhzl,yhbm]);
					upcnt += db.ExcecutSQL(sql);
				}
			}
		}

		db.Commit();

		return &quot;保存成功!新增&quot;+adcnt+&quot;,删除&quot;+decnt+&quot;,更新&quot;+upcnt;
	}
	catch (e) {
		if (db != null) db.Rollback();
		return &quot;保存失败：&quot; + e.toString();
	}
	finally {
		if (db != null) db.Close();
	}
}
