ROW.GRDJSDS_VALUE:
//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	_sql.querytods(&quot;URL&quot;,&quot;&quot;);
	_this.SetCellText(0,4,2,_this.XMLDS_GetString(0,&quot;YSZLURL&quot;));

	_this.AutoFixScreenWidth();

}

//点击按钮
function _thisOnButtonCtrlClick(id,sheet,row,col)
{
	if (id == &quot;保存&quot;) {
		var param = new Object();
		param.url = _this.GetCellText(0,4,2);
		var ret = _this.invokeOSFuncExt(&quot;Save&quot;,param);
		alert(ret);
	}
}

ROW.GRDOSDS_VALUE:
var pub = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);


//保存
function Save()
{
	var db = null;
	try {
		db = new pub.EADatabase();
		var sql = &quot;select * from aa10 where aaa100=&apos;YSZLURL&apos;&quot;;
		var cnt = db.GetSQLRowCount(sql);
		if (cnt == 0) {
			sql = &quot;insert into aa10(aaa100,aaa102,aaa101,aaa103)values(&apos;YSZLURL&apos;,&apos;YSZLURL&apos;,&apos;枢纽平台地址&apos;,&apos;&quot;+url+&quot;&apos;)&quot;;
			db.ExcecutSQL(sql);
		}
		else {
			sql = &quot;update aa10 set aaa103=&apos;&quot;+url+&quot;&apos; where aaa100=&apos;YSZLURL&apos;&quot;;
			db.ExcecutSQL(sql);
		}

		db.Commit();

		return &quot;保存成功！&quot;;
	}
	catch (e) {
		if (db != null) db.Rollback();
		return e.toString();
	}
	finally {
		if (db != null) db.Close();
	}
}

