//////////////////////////////////////////////////////////////////
// 打开单位弹出窗口，选择单位后返回上一个窗口
// 传入参数 ： obj.filter - 过滤条件
// 返回参数值：dwbh+&quot;~&quot;+dwmc+&quot;~&quot;+dwlxbh+&quot;~&quot;+dwjc
//////////////////////////////////////////////////////////////////

var ZXGFILE0 = &quot;&quot;;

var objParam;
var currow = -1;

var sbList;
var xzList;

//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	//_this.ShowToolBar(0);
	//_this.SplitSheet(1,&quot;V&quot;,60);
	_this.SetColVisible(sheet,0,-1);
	_this.SetColVisible(sheet,5,-1);

	ZXGFILE0 = _this.SaveTempZXGFile(0); 
	_this.SetAttribe(&quot;SHEET_0&quot;,_this.ATTR_SHEET_SELECTFLAG,_this. SELECTFLAG_SINGLEROW );

	sbList = _this.CreateListValue();
	_this.SetListValue(sbList,&quot;1&quot;,&quot;社保&quot;);
	_this.SetListValue(sbList,&quot;2&quot;,&quot;非社保&quot;);
	xzList = _this.CreateListValue();
	_this.SetListValue(xzList,&quot;11&quot;,&quot;企业基本养老保险&quot;);
	_this.SetListValue(xzList,&quot;12&quot;,&quot;机关事业养老保险&quot;);
	_this.SetListValue(xzList,&quot;21&quot;,&quot;失业保险&quot;);
	_this.SetListValue(xzList,&quot;31&quot;,&quot;医疗保险&quot;);
	_this.SetListValue(xzList,&quot;41&quot;,&quot;工伤保险&quot;);
	_this.SetListValue(xzList,&quot;51&quot;,&quot;生育保险&quot;);

	//接收传入参数
	var filter = &quot;&quot;;
	try {
		objParam = window.dialogArguments;
		if (objParam.filter != null && objParam.filter != undefined) {
			filter = objParam.filter;
		}
	} catch (e) {}

	loadData(filter);

}

//载入科目数据
function loadData(filter)
{
	_this.LoadZXGFile(ZXGFILE0,-1,0);

	if (filter != &quot;&quot;) filter = &quot;and &quot; + filter;

	var xml = _sql.query(&quot;MAIN&quot;,&quot;filter=&quot;+filter);
	_this.SetMainCellRange(0,1,1,20,12);
	_this.SetXmlDS(0,1,1,20,12,xml);

	for (var r=_this.GetMainCellRangeRow1(0);r&lt;=_this.GetMainCellRangeRow2(0);r++) {
		var xh = _this.GetCellText(0,r,1);
		if (xh != &quot;&quot;) {
			_this.SetToBoolBox(0,r,2);
			_this.SetToComboBox(&quot;&quot;,0,r,3,sbList);
			_this.SetToComboBox(&quot;&quot;,0,r,4,xzList);
		}
	}

	_this.AutoFixScreenWidth();

}


//点击按钮
function _thisOnButtonCtrlClick(id,sheet,row,col)
{
	if (id == &quot;确定&quot;) {
		okAndBack();
	}
	else if (id == &quot;取消&quot;) {
		window.returnValue = &quot;&quot;;
		window.close();
	}
	else if (id == &quot;查询&quot;) {
		queryDW();
	}
	else if(id == &quot;新增&quot;) {
	        add();
	}
}

function queryDW()
{
	var key = _this.GetCellText(1,1,2);
	var filter = &quot;(AAB001 like &apos;%25&quot;+key+&quot;%25&apos; or AAB025 like &apos;%25&quot;+key+&quot;%25&apos; or AAB340 like &apos;%25&quot;+key+&quot;%25&apos; or AAB341 like &apos;%25&quot;+key+&quot;%25&apos;)&quot;;
	if (key == &quot;&quot;) filter = &quot;&quot;;
	loadData(filter);
}

//确定返回
function okAndBack()
{
	var AAB001 = _this.GetCellText(0,currow,5);
	var AAB025 = _this.GetCellText(0,currow,6);
	var AAB340 = _this.GetCellText(0,currow,7);
	var AAB341 = _this.GetCellText(0,currow,8);	
	var AAE339 = _this.GetCellText(0,currow,9);	
	var AAB026 = _this.GetCellText(0,currow,10);	
	var BAE101 = _this.GetCellText(0,currow,11);	
	var BAE102 = _this.GetCellText(0,currow,12);	
	window.returnValue = AAB001+&quot;~&quot;+AAB025+&quot;~&quot;+AAB340+&quot;~&quot;+AAB341+&quot;~&quot;+AAE339+&quot;~&quot;+AAB026+&quot;~&quot;+BAE101+&quot;~&quot;+BAE102;
	window.close();

}

