function getXtbh2()
{
	var zth = G_ACCID.replace(G_ORGID,&quot;&quot;);
	if (1 * G_ORGID &gt;= 100) return G_ORGID + zth;
	else if (1 * G_ORGID &gt;= 10) return &quot;0&quot; + G_ORGID + zth;
	else return &quot;00&quot; + G_ORGID + zth;
}


//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
       if(sheet == 0) {
            if(row == 5 && col == 3) {//校验表头金额
            	   if (!LogicalValue(newvalue,&apos;number&apos;)) {
            	     	 alert(&quot;输入金额格式不正确&quot;);
            	     	 _this.SetCellText(sheet,row,col,oldvalue);
            	   }  	 
            }
            if(row == 5 && col == 7) {//校验表头人数格式
            	   if (!LogicalValue(newvalue,&apos;integer&apos;)) {
            	     	 alert(&quot;输入人数格式不正确&quot;);
            	     	 _this.SetCellText(sheet,row,col,oldvalue);
            	   }  	             		
            }

            if(row &gt; 10 && col == 7) {
            	   if (!LogicalValue(newvalue,&apos;integer&apos;)) {
            	     	 alert(&quot;输入人数格式不正确&quot;);
            	     	 _this.SetCellText(sheet,row,col,oldvalue);
            	   }              		
            }	

            if(row &gt; 10 && col == 8) {
            	   if (!LogicalValue(newvalue,&apos;number&apos;)) {
            	     	 alert(&quot;输入金额格式不正确&quot;);
            	     	 _this.SetCellText(sheet,row,col,oldvalue);
            	   }  	              		
            }	            
       }
}

//删除明细行
function del()
{       
        var bool = window.confirm(&quot;是否删除所选行？&quot;);
        if(!bool) return;
        for(var i=11;i&lt;_this.GetRowCount(0)-2;i++) {
            var bool = _this.GetCellText(0,i,1);          
            if(bool == 1) {
              //先清空行，再隐藏
               for(var col=1;col&lt;=14;col++) {
                    _this.SetCellText(0,i,col,&quot;&quot;);
               }
            }
        }


}


//保存单据数据后
function _thisOnaftersave(success)
{     
      if(success) {
         firstload = false;
         alert(&quot;保存成功&quot;);
         window.close();
      }            
}

//保存单据数据前
function _thisOnbeforesave()
{
     //检查明细数据
     var mxnum = 0;
     for(var row=11;row&lt;_this.GetRowCount(0)-2;row++) {
          var lsh =_this.GetCellText(0,row,14);
          if(lsh != &quot;&quot;) {
	       for(var col=2;col&lt;=9;col++) {
	                var colname = _this.GetCellText(0,10,col);
	                var colvalue = _this.GetCellText(0,row,col);
	                if(colvalue == &quot;&quot;) {
	                        alert(&quot;明细数据[&quot;+colname +&quot;]不能为空&quot;);
	                        return -1;
	                 }        
	          }
	          mxnum ++;          
          }
     }
     if(mxnum == 0 ) {
          alert(&quot;明细数据不能为空&quot;);
          return -1;
     }
     return 0;
}

