ROW.GRDJSDS_VALUE:
//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	// 第一步，定义对应数据库的字段
	// 字段属性： ID,显示列标题,默认值,正则表达式,是否列入查询的字段(=1是)
	var  fields = &quot;GUID,GUID,sys_guid();&quot;+
	    &quot;ID,编号,,.+,1;&quot;+
	    &quot;NOTE,平衡公式名称,,.+,1;&quot;+
	    &quot;FORMULA,表达式,,.+,1;&quot;+
	    &quot;TYPE,公式类型,,.+,1;&quot;+
	    &quot;REPID,报表编号,,.+,1;&quot;+
	    &quot;USEFLG,有效标志,,.+,;&quot;;
	var where = &quot;&quot;;
	var orderby = &quot;ID&quot;;

	// 字段的列表
	// 参数1：基表的名称
	// 参数2：字段列表 格式是通过;分隔不同的字段，每个分隔中使用,来分隔不同的字段属性
	// 参数3：主键
	// 参数4：显示空行标志 =true表示如果结果集为空，隐藏开头的两个空行,强制用户点击“新增”，以保证填入默认值
	// 参数5：查询的表名称，在某些情况下，查询的表名称和基表名称不一样，默认=&quot;&quot;表示查询表名称采用基表名称
	// 参数6：where条件，是不带where的子句如&quot;ID like &apos;01%&apos;&quot;
	// 参数7：orederby，是不带order by的子句如&quot;ID,REFID&quot;, 
	_dbhelper.InitSQL(&quot;FIN_FORMULA&quot;,fields,&quot;GUID&quot;,false,&quot;FIN_FORMULA&quot;,where,orderby);
	_dbhelper.InitColumnList(fields);
	_dbhelper.Load(1,1,2,2);

	// 需要调用该函数，否则按钮不会出来
	setButton();

	//_this.SetToolbarString(&quot;示例说明：F(&apos;1018&apos;,&apos;01&apos;)表示F(&apos;指标&apos;,&apos;指标项&apos;)&quot;);
}

function f_dbeditpostloaddata()
{
	var listid =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_FINFORMULA&quot;),&quot;ID&quot;,&quot;NAME&quot;);

	_this.SetColVisible(sheet,7,-1);
	for (row = _this.GetMainCellRangeRow1(sheet); row &lt;= _this.GetMainCellRangeRow2(sheet); row ++) {
		_this.SetToComboBox(&quot;&quot;, sheet,row,6,listid);
		_this.SetToBoolBox(sheet,row,8);
	}
	_this.AutoFixScreenWidth();
}    

function f_dbeditafternewrow(sheet,nowrow)
{
	_this.SetCellText(sheet,nowrow,8,&quot;1&quot;);
}


//快速定义
function fashDef()
{
	var repid = _this.GetCellText(0,_this.GetCurrentRow(0),7);
	window.open(&quot;show.sp?grdid=FIN_FORMULADEF&REPID=&quot;+repid);
}
