ROW.GRDJSDS_VALUE:
//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	// 第一步，定义对应数据库的字段
	// 字段属性： ID,显示列标题,默认值,正则表达式,是否列入查询的字段(=1是)
	var  fields = &quot;GUID,GUID,sys_guid();&quot;+
	    &quot;ORGANIZEID,编制单位,,.+,1;&quot;+
	    &quot;INDEXID,指标编号,,.+,1;&quot;+
	    &quot;INDEXNAME,指标名称,,.+,1;&quot;+
//	    &quot;PREVIOUSINDEX,汇总归属指标,,.+,;&quot;+
	    &quot;INDEXTYPEID,指标类型,,.+,1;&quot;+
//	    &quot;AMOUNTMONEYUNTI,指标单位,,.+,;&quot;+
	    &quot;FORMULA,指标取数公式,,.+,1;&quot;+
	    &quot;KMBH,对应科目编号,,.+,1;&quot;+
	    &quot;NOTE,备注信息,,.+,1;&quot;+
//	    &quot;INDEXORDER,排序号,,.+,;&quot;+
	    &quot;ISUSED,有效标志,,.+,;&quot;+
	    &quot;YSZTH,原始帐套号,,.+,1;&quot;;
	var where = &quot;&quot;;
	var orderby = &quot; INDEXID&quot;;  

	// 字段的列表
	// 参数1：基表的名称
	// 参数2：字段列表 格式是通过;分隔不同的字段，每个分隔中使用,来分隔不同的字段属性
	// 参数3：主键
	// 参数4：显示空行标志 =true表示如果结果集为空，隐藏开头的两个空行,强制用户点击“新增”，以保证填入默认值
	// 参数5：查询的表名称，在某些情况下，查询的表名称和基表名称不一样，默认=&quot;&quot;表示查询表名称采用基表名称
	// 参数6：where条件，是不带where的子句如&quot;ID like &apos;01%&apos;&quot;
	// 参数7：orederby，是不带order by的子句如&quot;ID,REFID&quot;, 
	_dbhelper.InitSQL(&quot;FIN_INDEX&quot;,fields,&quot;GUID&quot;,false,&quot;FIN_INDEX&quot;,where,orderby);
	_dbhelper.InitColumnList(fields);
	_dbhelper.Load(1,1,2,2);

	// 需要调用该函数，否则按钮不会出来
	setButton();
}

function f_dbeditpostloaddata()
{
	var idxtyp =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_FINIDXTYPE&quot;),&quot;ID&quot;,&quot;NAME&quot;);
	var orglist =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_ORG&quot;),&quot;ID&quot;,&quot;NAME&quot;);
	var ysztlist =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_CW_YSZTB&quot;),&quot;ID&quot;,&quot;NAME&quot;);

	for (row = _this.GetMainCellRangeRow1(sheet); row &lt;= _this.GetMainCellRangeRow2(sheet); row ++) {
		_this.SetToComboBox(&quot;&quot;, sheet,row,3,orglist);
		_this.SetToComboBox(&quot;&quot;, sheet,row,6,idxtyp);
		_this.SetToBoolBox(sheet,row,10);
		_this.SetToComboBox(&quot;&quot;, sheet,row,11,ysztlist);
	}

	_this.SetColWidth(sheet,3,200);
}    


function f_dbeditafternewrow(sheet,nowrow)
{
	if (_this.GetRowCount(0) &gt; 3) //not empty content
		_this.SetCellText(sheet,nowrow,3,G_ORGID);
	_this.SetCellText(sheet,nowrow,10,&quot;1&quot;);
}

//从科目表检查
function checkIndexByKmb()
{
	var ret = invokeOSFunc(&quot;checkIndexFromKmb&quot;,&quot;SBH=&quot;+G_ORGID+&quot;&ZTH=&quot;+G_ACCID.replace(G_ORGID,&quot;&quot;)+&quot;&ACCID=&quot;+G_ACCID);
	alert(ret);
}

ROW.GRDOSDS_VALUE:
var pub = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);

function checkIndexFromKmb()
{
	var db = null;
	try {
		db = new pub.EADatabase();

		var sql = &quot;insert into fin_index(guid,organizeid,indexid,indexname,indextypeid,amountmoneyunti,isused,kmbh)
				select sys_guid(),&apos;1&apos;,kmbh,kmmc,kmlx,1,&apos;1&apos;,kmbh 
				from cw_kmb
				where sbh=&apos;&quot;+SBH+&quot;&apos; and zth=&apos;&quot;+ZTH+&quot;&apos;
				  and org=&apos;&quot;+SBH+&quot;&apos; and acc=&apos;&quot;+ACCID+&quot;&apos;
				  and kmbh not in (select kmbh from fin_index) &quot;;
		var rows = db.ExcecutSQL(sql);
		return &quot;插入指标定义表记录数 &quot; + rows + &quot;.&quot;;
	}
	catch(e) {
		if (db != null) db.Rollback();
		return e.toString();
	}
	finally {
		if (db != null) db.Close();
	}
}
