ROW.GRDJSDS_VALUE:
//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	// 第一步，定义对应数据库的字段
	// 字段属性： ID,显示列标题,默认值,正则表达式,是否列入查询的字段(=1是)
	var  fields = &quot;GUID,GUID,sys_guid();&quot;+	
            &quot;ORGANIZEID,编制单位,,.+,1;&quot;+ 
            &quot;INDEXITEMID,编号,,.+,1;&quot;+ 
            &quot;ITEMNAME,名称,,.+,1;&quot;+ 
            &quot;FORMULA,表达式,,.+,1;&quot;+
            &quot;JOINYS,对应预算,,.+,1;&quot;+
            &quot;MEMO,备注,,.+,1;&quot;+
            &quot;YSZTH,原始帐套号,,.+,1;&quot;;
	var where = &quot;&quot;;
	var orderby = &quot;INDEXITEMID&quot;;      

	// 字段的列表
	// 参数1：基表的名称
	// 参数2：字段列表 格式是通过;分隔不同的字段，每个分隔中使用,来分隔不同的字段属性
	// 参数3：主键
	// 参数4：显示空行标志 =true表示如果结果集为空，隐藏开头的两个空行,强制用户点击“新增”，以保证填入默认值
	// 参数5：查询的表名称，在某些情况下，查询的表名称和基表名称不一样，默认=&quot;&quot;表示查询表名称采用基表名称
	// 参数6：where条件，是不带where的子句如&quot;ID like &apos;01%&apos;&quot;
	// 参数7：orederby，是不带order by的子句如&quot;ID,REFID&quot;, 
	_dbhelper.InitSQL(&quot;FIN_INDEXITEM&quot;,fields,&quot;GUID&quot;,false,&quot;FIN_INDEXITEM&quot;,where,orderby);
	_dbhelper.InitColumnList(fields );
	_dbhelper.Load(1,1,2,2);                     

	// 需要调用该函数，否则按钮不会出来
	setButton();
}

function f_dbeditpostloaddata()
{
	var orglist =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_ORG&quot;),&quot;ID&quot;,&quot;NAME&quot;);
	var ysztlist =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_CW_YSZTB&quot;),&quot;ID&quot;,&quot;NAME&quot;);
	for(row=_this.GetMainCellRangeRow1(sheet);row&lt;=_this.GetMainCellRangeRow2(sheet);row++)
	{ 
             _this.SetToComboBox(&quot;&quot;, sheet,row,3,orglist);
             _this.SetToComboBox(&quot;&quot;, sheet,row,9,ysztlist);
	}

	_this.SetColWidth(sheet,3,200);
}    


function f_dbeditafternewrow(sheet,newrow)
{
	_this.SetCellText(sheet,newrow,3,G_ORGID);

}
