//点击按钮
function _thisOnButtonCtrlClick(id,sheet,row,col)
{
	if ( id== &quot;确定&quot; ) {
		var val = &quot;&quot;;
		var fn = _this.GetCellText(0,4,2);
		if(fn != &quot;&quot;) {
			val = _this.LoadTextFile(fn);
		}

		if ( val == &quot;&quot; ) {
	        	alert(&apos;请选择导入文件！&apos;);
	        	return;
		}
		var param = new Object();
		param.txt = val;
		var ret = invokeOSFuncExt(&quot;importText&quot;,param);
		alert(ret);	

	}  
}

