var pub = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);
var xmldb = new JavaPackage(&quot;com.xlsgrid.net.xmldb&quot;);
var web = new JavaPackage(&quot;com.xlsgrid.net.web&quot;);

function importText()
{
	if(txt == null || txt == &quot;&quot;) return &quot;&quot;;
	var db = null;
	var ps = null;
  	var sr = null;
	var br = null;
	var sql = &quot;&quot;;  
	var row = 0;

	try {
		var usr = web.EASession.GetLoginInfo(request); //获取操作用户信息
		var acc = usr.getAccid();
		var username = usr.getUsrnam();

		sql = &quot;insert into bildtl(syt,org,acc,biltyp,bilid,dat,stat,untnum,seqid,itmid,refid2,price)
 			values(&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;LQ&apos;,&apos;%s&apos;,to_date(&apos;%s&apos;,&apos;yyyy-mm-dd&apos;),&apos;1&apos;,&apos;1&apos;,?,?,?,?)&quot;
 			.format([syt,org,acc,bilid,dat]);
		ps = db.prepareStatement(sql); //初始化

		var sr = new java.io.StringReader(txt);
		var br = new java.io.BufferedReader(sr);
		var line = &quot;&quot;;
		while( (line=br.readLine()) != null) { //读取文本行记录
			row++;
			if(line == &quot;&quot;) continue;

			var str = line.split(&quot;,&quot;); //分隔行记录 处理
       			ps.setString(1,&quot;&quot;+row); //设置SQL参数值  
    			ps.setString(2,itmid);

    			ps.execute(); //执行SQL语句

		}
		db.Commit();
		return &quot;导入成功！&quot;;
	}
	catch(e) {
		if (db != null) db.Rollback();
		return e.toString();
	}
	finally {
		if (db != null) db.Close();
	}
}
