//录入附件
function editFJ()
{
	var obj = new Object();
	obj.bqsj = _this.GetCellText(0,35,10);
	obj.yj = _this.GetCellText(0,36,10);
	obj.bj = _this.GetCellText(0,37,10);
	obj.qq = _this.GetCellText(0,38,10);
	obj.qt = _this.GetCellText(0,39,10);
	obj.ljtzzc = _this.GetCellText(0,40,10);

	var ret = window.showModalDialog(&quot;show.sp?grdid=REPFJ_862&quot;,obj,&quot;dialogWidth=400px;dialogHeight=300px;&quot;);
	if (ret != null) {
		var bqsj = (1.0 * ret.bqsj).toFixed(2);
		var yj = (1.0 * ret.yj).toFixed(2);
		var bj = (1.0 * ret.bj).toFixed(2);
		var qq = (1.0 * ret.qq).toFixed(2);
		var qt = (1.0 * ret.qt).toFixed(2);
		var ljtzzc = (1.0 * ret.ljtzzc).toFixed(2);

		_this.SetCellText(0,35,10,bqsj);
		_this.SetCellText(0,36,10,yj);
		_this.SetCellText(0,37,10,bj);
		_this.SetCellText(0,38,10,qq);
		_this.SetCellText(0,39,10,qt);
		_this.SetCellText(0,40,10,ljtzzc);
//		_this.SetCellText(0,13,9,ljtzzc);

		_this.SetCellText(0,38,1,&quot;　　　　　　　　　　　　　　1、本期实缴 &quot;+bqsj+&quot; ， 2、预缴 &quot;+yj+&quot; ，3、补缴 &quot;+bj+&quot; ，4、清欠（不含核销） &quot;+qq+&quot; ，5、其他  &quot;+qt+&quot; 元&quot;);
		_this.SetCellText(0,40,1,&quot;　　　　　　　　　　　　　(二)累计调整待遇支出 &quot;+ljtzzc+&quot; 元.  &quot;);
		alert(&quot;附件已录入，请保存报表数据！&quot;);
	}
}

//设置附件显示值
function setFjStr()
{
	var bqsj = 0;
	var yj = 0;
	var bj = 0;
	var qq = 0;
	var qt = 0;
	var ljtzzc = 0;

	//新制表状态下附录取数
	if (G_GUID == &quot;&quot;) {
		var values = invokeOSFunc(&quot;GetFLValue&quot;,&quot;orgid=&quot;+G_ORGID+&quot;&accid=&quot;+G_ACCID+&quot;&repid=&quot;+repid+&quot;&yymm=&quot;+yymm+&quot;&replocid=&quot;+replocid);
		bqsj = values.split(&quot;@&quot;)[0];
		yj = values.split(&quot;@&quot;)[1];
		bj = values.split(&quot;@&quot;)[2];
		qq = values.split(&quot;@&quot;)[3];
		qt = values.split(&quot;@&quot;)[4];
		ljtzzc = values.split(&quot;@&quot;)[5];

		_this.SetCellText(0,35,10,bqsj);
		_this.SetCellText(0,36,10,yj);
		_this.SetCellText(0,37,10,bj);
		_this.SetCellText(0,38,10,qq);
		_this.SetCellText(0,39,10,qt);
		_this.SetCellText(0,40,10,ljtzzc);
//		_this.SetCellText(0,13,9,ljtzzc);
	}
	else {
		bqsj = (1.0 * _this.GetCellText(0,35,10)).toFixed(2);
		yj = (1.0 * _this.GetCellText(0,36,10)).toFixed(2);
		bj = (1.0 * _this.GetCellText(0,37,10)).toFixed(2);
		qq = (1.0 * _this.GetCellText(0,38,10)).toFixed(2);
		qt = (1.0 * _this.GetCellText(0,39,10)).toFixed(2);
		ljtzzc = (1.0 * _this.GetCellText(0,40,10)).toFixed(2);
	}

	_this.SetCellText(0,38,1,&quot;　　　　　　　　　　　　　　1、本期实缴 &quot;+bqsj+&quot; ， 2、预缴 &quot;+yj+&quot; ，3、补缴 &quot;+bj+&quot; ，4、清欠（不含核销） &quot;+qq+&quot; ，5、其他  &quot;+qt+&quot; 元&quot;);
	_this.SetCellText(0,40,1,&quot;　　　　　　　　　　　　　(二)累计调整待遇支出 &quot;+ljtzzc+&quot; 元.  &quot;);

}

ROW.GRDOSDS_VALUE:
var pub = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);

//附录取数处理
function GetFLValue()
{
	var db = null;
	var sbcw = new SBCW_PUBDFFIN();
	var ret = &quot;&quot;;
	try {
		db = new pub.EADatabase();

		var indexitemid = &quot;04&quot;; //累计数

		//基本养老保险费收入
		//1、本期实缴 4018001001001 + 4018001009001 + 4018002001001 + 4018002009001 
		var val1 = 1.0 * sbcw.getIdxValue(db,orgid,accid,repid,yymm,&quot;4018001001001&quot;,indexitemid,replocid); //单位机关本期实缴
		var val2 = 1.0 * sbcw.getIdxValue(db,orgid,accid,repid,yymm,&quot;4018001009001&quot;,indexitemid,replocid); //单位事业本期实缴
		var val3 = 1.0 * sbcw.getIdxValue(db,orgid,accid,repid,yymm,&quot;4018002001001&quot;,indexitemid,replocid); //个人机关本期实缴
		var val4 = 1.0 * sbcw.getIdxValue(db,orgid,accid,repid,yymm,&quot;4018002009001&quot;,indexitemid,replocid); //个人事业本期实缴
		var val = val1 + val2 + val3 + val4;		
		if (ret == &quot;&quot;) ret += val; else ret += &quot;@&quot;+val;

		//2、预缴 4018001001002 + 4018001009002 + 4018002001002 + 4018002009002 
		var val1 = 1.0 * sbcw.getIdxValue(db,orgid,accid,repid,yymm,&quot;4018001001002&quot;,indexitemid,replocid); //单位机关预缴
		var val2 = 1.0 * sbcw.getIdxValue(db,orgid,accid,repid,yymm,&quot;4018001009002&quot;,indexitemid,replocid); //单位事业预缴
		var val3 = 1.0 * sbcw.getIdxValue(db,orgid,accid,repid,yymm,&quot;4018002001002&quot;,indexitemid,replocid); //个人机关预缴
		var val4 = 1.0 * sbcw.getIdxValue(db,orgid,accid,repid,yymm,&quot;4018002009002&quot;,indexitemid,replocid); //个人事业预缴
		var val = val1 + val2 + val3 + val4;		
		if (ret == &quot;&quot;) ret += val; else ret += &quot;@&quot;+val;

		//3、补缴 4018001001003 + 4018001009003 + 4018002001003 + 4018002009003 
		var val1 = 1.0 * sbcw.getIdxValue(db,orgid,accid,repid,yymm,&quot;4018001001003&quot;,indexitemid,replocid); //单位机关补缴
		var val2 = 1.0 * sbcw.getIdxValue(db,orgid,accid,repid,yymm,&quot;4018001009003&quot;,indexitemid,replocid); //单位事业补缴
		var val3 = 1.0 * sbcw.getIdxValue(db,orgid,accid,repid,yymm,&quot;4018002001003&quot;,indexitemid,replocid); //个人机关补缴
		var val4 = 1.0 * sbcw.getIdxValue(db,orgid,accid,repid,yymm,&quot;4018002009003&quot;,indexitemid,replocid); //个人事业补缴
		var val = val1 + val2 + val3 + val4;		
		if (ret == &quot;&quot;) ret += val; else ret += &quot;@&quot;+val;

		//4、清欠 4018001001004 + 4018001009004 + 4018002001004 + 4018002009004 
		var val1 = 1.0 * sbcw.getIdxValue(db,orgid,accid,repid,yymm,&quot;4018001001004&quot;,indexitemid,replocid); //单位机关清欠
		var val2 = 1.0 * sbcw.getIdxValue(db,orgid,accid,repid,yymm,&quot;4018001009004&quot;,indexitemid,replocid); //单位事业清欠
		var val3 = 1.0 * sbcw.getIdxValue(db,orgid,accid,repid,yymm,&quot;4018002001004&quot;,indexitemid,replocid); //个人机关清欠
		var val4 = 1.0 * sbcw.getIdxValue(db,orgid,accid,repid,yymm,&quot;4018002009004&quot;,indexitemid,replocid); //个人事业清欠
		var val = val1 + val2 + val3 + val4;		
		if (ret == &quot;&quot;) ret += val; else ret += &quot;@&quot;+val;

		//5、其他 4018001001009 + 4018001009009 + 4018002001009 + 4018002009009 
		var val1 = 1.0 * sbcw.getIdxValue(db,orgid,accid,repid,yymm,&quot;4018001001009&quot;,indexitemid,replocid); //单位机关其他
		var val2 = 1.0 * sbcw.getIdxValue(db,orgid,accid,repid,yymm,&quot;4018001009009&quot;,indexitemid,replocid); //单位事业其他
		var val3 = 1.0 * sbcw.getIdxValue(db,orgid,accid,repid,yymm,&quot;4018002001009&quot;,indexitemid,replocid); //个人机关其他
		var val4 = 1.0 * sbcw.getIdxValue(db,orgid,accid,repid,yymm,&quot;4018002009009&quot;,indexitemid,replocid); //个人事业其他
		var val = val1 + val2 + val3 + val4;		
		if (ret == &quot;&quot;) ret += val; else ret += &quot;@&quot;+val;

		//累计调整待遇支出
		val = 0;
		ret += &quot;@&quot;+val;

	}
	catch (e) {
		if (db != null) db.Rollback();
		return &quot;&quot;;
	}
	finally {
		if (db != null) db.Close();
	}

	return ret;
}
