ROW.GRDJSDS_VALUE:
var cur_sbh  = &quot;&quot;;
var cur_zth  = &quot;&quot;;
var cur_yyyy = &quot;&quot;;
var cur_mm   = &quot;&quot;;
var cur_pzh  = &quot;&quot;;
var cur_kmbh = &quot;&quot;;
var cur_czxh = &quot;&quot;;

var sheet_pzfj  = 0;
var sheet_param = 1;
var sheet_rjzmxb1 = 2;
var sheet_rjzmxb2 = 3;

var ZXGFILE0 = &quot;&quot;;
var ZXGFILE1 = &quot;&quot;;
var ZXGFILE2 = &quot;&quot;;
var paramObj = &quot;&quot;;

//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	initLayout();

	try{
		paramObj = window.dialogArguments;
		cur_sbh  = paramObj.sbh;
		cur_zth  = paramObj.zth;
		cur_yyyy = paramObj.yy;
		cur_mm   = paramObj.mm;
		cur_pzh  = paramObj.pzh;
		cur_czxh = paramObj.czxh;
	}catch(e){
		cur_sbh  = G_ORGID;
		cur_zth  = G_ACCID.replace(G_ORGID,&quot;&quot;);
		cur_yyyy = G_LOGDAT.substring(0,4);
		cur_mm   = 1*(G_LOGDAT.substring(5,7));
		cur_pzh  = &quot;&quot;;
		cur_czxh = &quot;&quot;;
	}
	if(cur_pzh == &quot;&quot;){
		alert(&quot;凭证号为空!!!&quot;);
	}	
	//获取科目编号
	var KmList = _this.CreateListValue();
	var kmbh = &quot;&quot;;
	var kmmc = &quot;&quot;;
	_sql.querytods(&quot;getKmbh&quot;,&quot;SBH=&quot;+cur_sbh+&quot;&ZTH=&quot;+cur_zth+&quot;&YYYY=&quot;+cur_yyyy+&quot;&MM=&quot;+cur_mm+&quot;&PZH=&quot;+cur_pzh+&quot;&CZXH=&quot;+cur_czxh);
	for(var i = 0;i&lt;_this.XMLDS_GetRowCount();i++){
		kmbh = _this.XMLDS_GetString(i,&quot;KMBH&quot;);
		kmmc = _this.XMLDS_GetString(i,&quot;KMMC&quot;);
		_this.SetListValue(KmList,kmbh,kmbh+&quot;-&quot;+kmmc);
		if(i == 0){
			cur_kmbh = kmbh;
		}
	}
	_this.SetToComboBox(&quot;&quot;,sheet_param,1,6,KmList);
	_this.SetCellText(sheet_param,1,6,cur_kmbh);

	pzfjData(cur_sbh,cur_zth,cur_yyyy,cur_mm,cur_pzh,cur_kmbh,&quot;&quot;);
}

//页面布局
function initLayout(){
	_this.SplitSheet(sheet_param,&quot;V&quot;,&quot;8%&quot;);
	_this.SplitSheet(sheet_pzfj,&quot;V&quot;,&quot;50%&quot;);
	_this.SplitSheet(sheet_rjzmxb1,&quot;H&quot;,&quot;50%&quot;);

	ZXGFILE0 = _this.SaveTempZXGFile(sheet_pzfj); 
	ZXGFILE1 = _this.SaveTempZXGFile(sheet_rjzmxb1);
	ZXGFILE2 = _this.SaveTempZXGFile(sheet_rjzmxb2);

	_this.SetAttribe(&quot;SHEET_0&quot;,_this.ATTR_SHEET_SELECTFLAG,_this. SELECTFLAG_SINGLEROW );
	_this.SetAttribe(&quot;SHEET_1&quot;,_this.ATTR_SHEET_SELECTFLAG,_this. SELECTFLAG_SINGLEROW );
	_this.SetAttribe(&quot;SHEET_2&quot;,_this.ATTR_SHEET_SELECTFLAG,_this. SELECTFLAG_SINGLEROW );

	_this.AddToolbarButton(&quot;udf_btnGl&quot;,&quot;过滤&quot;,&quot;过滤&quot;,&quot;&quot;,0,1,1,60);
	_this.AddToolbarButton(&quot;udf_btnSearch&quot;,&quot;查找&quot;,&quot;查找&quot;,&quot;&quot;,0,7,7,60);
	_this.AddToolbarButton(&quot;udf_btnSearchPrev&quot;,&quot;上一个&quot;,&quot;上一个&quot;,&quot;&quot;,0,7,7,60);
	_this.AddToolbarButton(&quot;udf_btnSearchNext&quot;,&quot;下一个&quot;,&quot;下一个&quot;,&quot;&quot;,0,7,7,60);
	_this.AddToolbarButton(&quot;udf_btnPrintFJ&quot;,&quot;打印附件&quot;,&quot;打印附件&quot;,&quot;&quot;,0,6,6,60);

}

//加载凭证附件数据
function pzfjData(sbh,zth,yy,mm,pzh,kmbh,filter){
	_this.SetToolbarString(&quot;正在加载数据...&quot;);

	_this.LoadZXGFile(ZXGFILE0,-1,sheet_pzfj);//清空主数据区
	_this.SetFixedRow(sheet_pzfj,1);
	_this.SetAttribe(&quot;SHEET_0&quot;,_this.ATTR_SHEET_SELECTFLAG,_this. SELECTFLAG_SINGLEROW );	
	_this.SetMainCellRange(sheet_pzfj,1,0,9,16);//设置主数据区

	var xml = _sql.query(&quot;pzfjData&quot;,&quot;SBH=&quot;+sbh+&quot;&ZTH=&quot;+zth+&quot;&YYYY=&quot;+yy+&quot;&MM=&quot;+mm+&quot;&PZH=&quot;+pzh+&quot;&KMBH=&quot;+kmbh+&quot;&FILTER=&quot;+filter);
	_this.SetXmlDS(sheet_pzfj,1,0,_this.GetRowCount(sheet_pzfj)-2,_this.GetColCount(sheet_pzfj)-1,xml);
	for (var r=_this.GetMainCellRangeRow1(sheet_pzfj);r&lt;=_this.GetMainCellRangeRow2(sheet_pzfj);r++) {
		if (_this.GetCellText(sheet_pzfj,r,0) == &quot;&quot;) _this.SetRowVisible(sheet_pzfj,r,-1);
	}

	//获取第一行数据的值
	_this.SetCellFocus(sheet_pzfj,1,1);
	var lsh  = _this.GetCellText(sheet_pzfj,1,13);
	var dwbh = _this.GetCellText(sheet_pzfj,1,15);
	var dwmc = _this.GetCellText(sheet_pzfj,1,16);
	var djh  = _this.GetCellText(sheet_pzfj,1,12);
	_this.SetCellText(sheet_param,1,2,dwbh+&quot;-&quot;+dwmc);
	_this.SetCellText(sheet_param,1,9,djh);

	//获取单位类型
	if(dwbh != &quot;&quot;){
		_sql.querytods(&quot;getDwlx&quot;,&quot;SBH=&quot;+sbh+&quot;&ZTH=&quot;+zth+&quot;&DWBH=&quot;+dwbh);
		var dwlxbh = _this.XMLDS_GetString(0,&quot;DWLXBH&quot;);
		var dwlx   = _this.XMLDS_GetString(0,&quot;DWLX&quot;);
		_this.SetCellText(sheet_param,1,3,dwlxbh+&quot;-&quot;+dwlx);
	}

	//加载明细数据
	loadRJZMXData(lsh);

	_this.AutoFixScreenWidth();
	_this.SetToolbarString(&quot;&quot;);
	_this.RefreshFormula();
}

//日记帐明细
function loadRJZMXData(lsh)
{	
	_this.SetToolbarString(&quot;正在加载数据...&quot;);

	_this.LoadZXGFile(ZXGFILE1,-1,sheet_rjzmxb1);
	_this.LoadZXGFile(ZXGFILE2,-1,sheet_rjzmxb2);
	_this.SetFixedRow(sheet_rjzmxb1,1);
	_this.SetFixedRow(sheet_rjzmxb2,1);

	_this.SetColVisible(sheet_rjzmxb1,6,-1);
	_this.SetColVisible(sheet_rjzmxb1,7,-1);
	_this.SetColVisible(sheet_rjzmxb2,6,-1);
	_this.SetColVisible(sheet_rjzmxb2,7,-1);

	_this.SetMainCellRange(sheet_rjzmxb1,1,0,_this.GetRowCount(sheet_rjzmxb1)-2,_this.GetColCount(sheet_rjzmxb1)-1);
	_this.SetMainCellRange(sheet_rjzmxb2,1,0,_this.GetRowCount(sheet_rjzmxb2)-2,_this.GetColCount(sheet_rjzmxb2)-1);

	_sql.querytods(&quot;rjzmxData&quot;,&quot;LSH=&quot;+lsh);
	var sumrow = _this.XMLDS_GetRowCount();
	var midrow = Math.ceil(sumrow / 2);
	//alert(&quot;sumrow=&quot;+sumrow+&quot;,midrow=&quot;+midrow);
	var row1 = _this.GetMainCellRangeRow1(sheet_rjzmxb1);
	var row2 = _this.GetMainCellRangeRow2(sheet_rjzmxb1);
	var n = 0;
	for (var i=0;i&lt;midrow;i++) {
		n ++;
		if (n &gt; row2) {
			_this.AppendRow(sheet_rjzmxb1,n-1);
		}
		if(_this.XMLDS_GetString(i,&quot;KMBH&quot;) != &quot;&quot;){
			_this.SetCellText(sheet_rjzmxb1,row1,0,_this.XMLDS_GetString(i,&quot;KMBH&quot;));
			_this.SetCellText(sheet_rjzmxb1,row1,1,_this.XMLDS_GetString(i,&quot;KMMC&quot;));
		}else{
			_this.SetCellText(sheet_rjzmxb1,row1,0,_this.XMLDS_GetString(i,&quot;RJLX&quot;));
			_this.SetCellText(sheet_rjzmxb1,row1,1,_this.XMLDS_GetString(i,&quot;KXMC&quot;));
		}
		_this.SetCellText(sheet_rjzmxb1,row1,2,_this.XMLDS_GetString(i,&quot;YM1&quot;));
		_this.SetCellText(sheet_rjzmxb1,row1,3,_this.XMLDS_GetString(i,&quot;YM2&quot;));
		_this.SetCellText(sheet_rjzmxb1,row1,4,_this.XMLDS_GetString(i,&quot;JE&quot;));
		_this.SetCellText(sheet_rjzmxb1,row1,5,_this.XMLDS_GetString(i,&quot;JEFX&quot;));
		_this.SetCellText(sheet_rjzmxb1,row1,6,_this.XMLDS_GetString(i,&quot;ORG&quot;));
		_this.SetCellText(sheet_rjzmxb1,row1,7,_this.XMLDS_GetString(i,&quot;ACC&quot;));
		_this.SetCellText(sheet_rjzmxb1,row1,8,_this.XMLDS_GetString(i,&quot;LSH&quot;));
		_this.SetCellText(sheet_rjzmxb1,row1,9,_this.XMLDS_GetString(i,&quot;LXBH&quot;));
		_this.SetCellText(sheet_rjzmxb1,row1,10,_this.XMLDS_GetString(i,&quot;LXXH&quot;));
		row1 ++;
	}//for (var i=0;i&lt;midrow;i++)
	row1 = _this.GetMainCellRangeRow1(sheet_rjzmxb2);
	row2 = _this.GetMainCellRangeRow2(sheet_rjzmxb2);
	n = 0;
	for (var i=midrow;i&lt;sumrow;i++) {
	 	n ++;
		if (n &gt; row2) {
			_this.AppendRow(sheet_rjzmxb2,n-1);
		}
		if(_this.XMLDS_GetString(i,&quot;KMBH&quot;) != &quot;&quot;){
			_this.SetCellText(sheet_rjzmxb2,row1,0,_this.XMLDS_GetString(i,&quot;KMBH&quot;));
			_this.SetCellText(sheet_rjzmxb2,row1,1,_this.XMLDS_GetString(i,&quot;KMMC&quot;));
		}else{
			_this.SetCellText(sheet_rjzmxb2,row1,0,_this.XMLDS_GetString(i,&quot;RJLX&quot;));
			_this.SetCellText(sheet_rjzmxb2,row1,1,_this.XMLDS_GetString(i,&quot;KXMC&quot;));
		}
		_this.SetCellText(sheet_rjzmxb2,row1,2,_this.XMLDS_GetString(i,&quot;YM1&quot;));
		_this.SetCellText(sheet_rjzmxb2,row1,3,_this.XMLDS_GetString(i,&quot;YM2&quot;));
		_this.SetCellText(sheet_rjzmxb2,row1,4,_this.XMLDS_GetString(i,&quot;JE&quot;));
		_this.SetCellText(sheet_rjzmxb2,row1,5,_this.XMLDS_GetString(i,&quot;JEFX&quot;));
		_this.SetCellText(sheet_rjzmxb2,row1,6,_this.XMLDS_GetString(i,&quot;ORG&quot;));
		_this.SetCellText(sheet_rjzmxb2,row1,7,_this.XMLDS_GetString(i,&quot;ACC&quot;));
		_this.SetCellText(sheet_rjzmxb2,row1,8,_this.XMLDS_GetString(i,&quot;LSH&quot;));
		_this.SetCellText(sheet_rjzmxb2,row1,9,_this.XMLDS_GetString(i,&quot;LXBH&quot;));
		_this.SetCellText(sheet_rjzmxb2,row1,10,_this.XMLDS_GetString(i,&quot;LXXH&quot;));

		row1 ++;
	}//for (var i=midrow;i&lt;sumrow;i++)

	jsSumJE(sheet_rjzmxb1);
	jsSumJE(sheet_rjzmxb2);

	_this.SetToolbarString(&quot;&quot;);
	_this.AutoFixScreenWidth();
}

//鼠标左键点击
function _thisOnMouseLClick(sheet,row,col)
{
	if(sheet == sheet_pzfj){
		if( row &gt;= _this.GetMainCellRangeRow1(sheet) && row &lt;= _this.GetMainCellRangeRow2(sheet) ){
			var lsh  = _this.GetCellText(sheet_pzfj,row,13);
			var dwbh = _this.GetCellText(sheet_pzfj,row,15);
			var dwmc = _this.GetCellText(sheet_pzfj,row,16);
			var djh  = _this.GetCellText(sheet_pzfj,row,12);

			_this.SetCellText(sheet_param,1,2,dwbh+&quot;-&quot;+dwmc);
			_this.SetCellText(sheet_param,1,9,djh);

			loadRJZMXData(lsh);

			//获取单位类型
			if(dwbh != &quot;&quot;){
				_sql.querytods(&quot;getDwlx&quot;,&quot;SBH=&quot;+cur_sbh+&quot;&ZTH=&quot;+cur_zth+&quot;&DWBH=&quot;+dwbh);
				var dwlxbh = _this.XMLDS_GetString(0,&quot;DWLXBH&quot;);
				var dwlx   = _this.XMLDS_GetString(0,&quot;DWLX&quot;);
				_this.SetCellText(sheet_param,1,3,dwlxbh+&quot;-&quot;+dwlx);
			}
		}//if( row &gt;= _this.GetMainCellRangeCol1(sheet) && row &lt;= _this.GetMainCellRangeCol1(sheet) )
	}//if(sheet == sheet_pzfj)
}

//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
	if(sheet == sheet_param){
		if(row == 1 && col == 6){
			var kmbh = _this.GetCellText(sheet_param,1,6);
			pzfjData(cur_sbh,cur_zth,cur_yyyy,cur_mm,cur_pzh,kmbh,&quot;&quot;);
		}
	}
}


//计算合计金额
function jsSumJE(sheet_yhrjzb)
{
	var jfje_sum = 0.00;
	var dfje_sum = 0.00;
	var ye_sum   = 0.00;
	var jfbs     = 0;
	var dfbs     = 0;
	for(var i =_this.GetMainCellRangeRow1(sheet_yhrjzb);i&lt;=_this.GetMainCellRangeRow2(sheet_yhrjzb);i++){
		var jefx = _this.GetCellText(sheet_yhrjzb,i,5); //使用金额方向计算
		var jfje = 0;
		var dfje = 0;
		if (jefx == &quot;借&quot;) jfje = _this.GetCellText(sheet_yhrjzb,i,4)*1.00;
		if (jefx == &quot;贷&quot;) dfje = _this.GetCellText(sheet_yhrjzb,i,4)*1.00;

		var ye   = 0;
		var qcbz = 0;
		var sblsh = _this.GetCellText(sheet_yhrjzb,i,8);

		if(ye == &quot;&quot;) ye = 0.00;
		if(jfje == &quot;&quot;) jfje = 0.00;
		if(dfje == &quot;&quot;) dfje = 0.00;

		if(sblsh != &quot;&quot;){
			if(qcbz == 1) {
				ye_sum = 1.0* round(ye,2);
			}else{
				if(jefx == &quot;借&quot;){
					jfbs ++;
					jfje_sum = 1.00 * round(jfje_sum,2) + 1.00 * round(jfje,2);
					ye_sum = 1.00 * round(ye_sum,2) + 1.00 * round(jfje,2);
					//_this.SetCellText(sheet_yhrjzb,i,10,ye_sum);
				}else if(jefx == &quot;贷&quot;){
					dfbs ++;
					dfje_sum = 1.00 * round(dfje_sum,2) + 1.00 * round(dfje,2);
					ye_sum = 1.00 * round(ye_sum,2) - 1.00 * round(dfje,2);
					//_this.SetCellText(sheet_yhrjzb,i,10,ye_sum);
				}
			}
		}

	}

	var retje_sum = jfje_sum - dfje_sum;
	var retjefx = &quot;&quot;;
	if (retje_sum &gt; 0) {
		retjefx = &quot;借&quot;;
	}
	else {
		retjefx = &quot;贷&quot;;
		retje_sum = Math.abs(retje_sum);
	}


	_this.SetCellText(sheet_yhrjzb,_this.GetRowCount(sheet_yhrjzb)-1,4,retje_sum);
	_this.SetCellText(sheet_yhrjzb,_this.GetRowCount(sheet_yhrjzb)-1,5,retjefx);
}

//过滤
function btnGl(){
	var obj = new Object();
	obj.yyyy = cur_yyyy;
	obj.mm = cur_mm;
	obj.kmbh = cur_kmbh;	

	_sql.querytods(&quot;getKmmc&quot;,&quot;SBH=&quot;+cur_sbh+&quot;&ZTH=&quot;+cur_zth+&quot;&KMBH=&quot;+cur_kmbh);
	var kmmc = _this.XMLDS_GetString(0,&quot;KMMC&quot;);

	obj.kmmc = kmmc;
	var retVal = window.showModalDialog(&quot;show.sp?grdid=RJZ_FILTER&quot;,obj,&quot;dialogWidth=600px;dialogHeight=300px&quot;);
	var param = retVal;
//	alert(param.dat1+&quot;,kmbh=&quot;+param.kmbh);
//	if (filter != &quot;&quot; && filter != &quot;undefined&quot; && filter != undefined) {
//		alert(&quot;过滤&quot;);
//	}

	try {
		var filter = &quot;&quot;;
		if (param.reset == &quot;1&quot;) { //恢复
			pzfjData(cur_sbh,cur_zth,cur_yyyy,cur_mm,cur_pzh,cur_kmbh,&quot;&quot;);
		}
		else {
			if (param.dat1 != &quot;&quot;) {
				var dat1 = param.dat1;
				var dat2 = param.dat2;
				//cur_yyyy = dat1.split(&quot;-&quot;)[0];
				//cur_mm = 1 * (dat1.split(&quot;-&quot;)[1]);
				var dd1 = 1 * (dat1.split(&quot;-&quot;)[2]);
				var dd2 = 1 * (dat2.split(&quot;-&quot;)[2]);
				filter = &quot; and (A.dd&gt;=&quot;+dd1+&quot; and A.dd&lt;=&quot;+dd2+&quot;)&quot;;
			}
			if (param.jsfs != &quot;&quot;) {
				filter += &quot; and (A.jsfs=&apos;&quot;+param.jsfs+&quot;&apos;)&quot;;
			}
			if (param.kmbh != &quot;&quot;) {
				_this.SetCellText(sheet_param,1,6,param.kmbh);
				cur_kmbh = param.kmbh;
			}
			if (param.dwbh != &quot;&quot;) {
				filter += &quot; and (A.dwbh=&apos;&quot;+param.dwbh+&quot;&apos;)&quot;;
			}
			if (param.zy != &quot;&quot;) {
				filter += &quot; and (A.zy like &apos;%25&quot;+param.zy+&quot;%25&apos;)&quot;;
			}
			if (param.mny1 != &quot;&quot;) {
				filter += &quot; and A.je&gt;=&quot;+param.mny1;
			}
			if (param.mny2 != &quot;&quot;) {
				filter += &quot; and A.je&lt;=&quot;+param.mny2;
			}
			if (param.printbz != &quot;&quot;) {
				filter += &quot; and A.dybz=&apos;&quot;+param.printbz+&quot;&apos;&quot;;
			}
			if (param.djh != &quot;&quot;) {
				filter += &quot; and A.djh like &apos;%25&quot;+param.djh+&quot;%25&apos;&quot;;
			}
			if (param.pch != &quot;&quot;) {
				filter += &quot; and A.CWPCH like &apos;%25&quot;+param.pch+&quot;%25&apos;&quot;;
			}

			pzfjData(cur_sbh,cur_zth,cur_yyyy,cur_mm,cur_pzh,cur_kmbh,filter);
		}
	}
	catch(e) {}
}


//查找
var cur_search_param;    //当前查找条件
var cur_search_row = -1; //查找到的当前行
function btnSearch()
{
	var obj = new Object();
	obj.yyyy = cur_yyyy;
	obj.mm = cur_mm;
	obj.kmbh = cur_kmbh;	

	_sql.querytods(&quot;getKmmc&quot;,&quot;SBH=&quot;+cur_sbh+&quot;&ZTH=&quot;+cur_zth+&quot;&KMBH=&quot;+cur_kmbh);
	var kmmc = _this.XMLDS_GetString(0,&quot;KMMC&quot;);

	obj.kmmc = kmmc;
	var retVal = window.showModalDialog(&quot;show.sp?grdid=RJZ_SEARCH&quot;,obj,&quot;dialogWidth=600px;dialogHeight=300px&quot;);
	try {
		cur_search_row = -1;
		cur_search_param = retVal;
		doSearch(&quot;down&quot;);
	} catch (e) {

	}

}

//查找上一个
function btnSearchPrev()
{
	try { doSearch(&quot;up&quot;); } catch (e) { }
}

//查找下一个
function btnSearchNext()
{
	try { doSearch(&quot;down&quot;); } catch (e) { }
}

//执行查找
function doSearch(searchFx)
{
	var sdat1 = cur_search_param.dat1;
	var sdat2 = cur_search_param.dat2;
	var skmbh = cur_search_param.kmbh;
	var skxlx = cur_search_param.kxlx;
	var sdwbh = cur_search_param.dwbh;
	var sdwlx = cur_search_param.dwlx;
	var szy   = cur_search_param.zy;
	var smny1 = cur_search_param.mny1;
	var smny2 = cur_search_param.mny2;
	var sdjh  = cur_search_param.djh;
	var spch  = cur_search_param.pch;
	var syy = 1*sdat1.split(&quot;-&quot;)[0];
	var smm = 1*sdat1.split(&quot;-&quot;)[1];
	var sdd1 = 1*sdat1.split(&quot;-&quot;)[2];
	var sdd2 = 1*sdat2.split(&quot;-&quot;)[2];

	if (syy != cur_yyyy || smm != cur_mm || skmbh != cur_kmbh) {
		alert(&quot;未找到符合条件的日记帐！&quot;);
		return false;
	} 

	if (smny1 != &quot;&quot; && smny2 == &quot;&quot;) smny2 = &quot;999999999999999999.99&quot;;
	if (smny2 != &quot;&quot; && smny1 == &quot;&quot;) smny1 = &quot;0.00&quot;;

	var row1 = _this.GetMainCellRangeRow1(sheet_pzfj)-1;
	var row2 = _this.GetMainCellRangeRow2(sheet_pzfj)+1;
	var bFind = false;
	if (searchFx == &quot;down&quot;) {
		if (cur_search_row != -1) row1 = cur_search_row;
		for (var row=row1+1; row&lt;=row2;row++) {
			bFind = doSearchRowValue(sheet_pzfj,row,sdd1,sdd2,spch,szy,sdjh,sdwbh,sdwlx,smny1,smny2,skxlx);
			if (bFind == true) break;
		}
	}
	else if (searchFx == &quot;up&quot;) {
		for (var row=cur_search_row-1; row&gt;=1;row--) {
			bFind = doSearchRowValue(sheet_pzfj,row,sdd1,sdd2,spch,szy,sdjh,sdwbh,sdwlx,smny1,smny2,skxlx);
			if (bFind == true) break;
		}
	}

	//如果查找到，滚动到记录行
	if (bFind) {
		//alert(&quot;找到记录行&quot;+cur_search_row);
		ScrollToFindRow(sheet_pzfj,cur_search_row);
		_thisOnMouseLClick(sheet_pzfj,cur_search_row,2);
	}
	else {
		alert(&quot;未找到符合条件的日记帐！&quot;);
	}
}

//查找记录行
function doSearchRowValue(sheet_rjz1,row,sdd1,sdd2,spch,szy,sdjh,sdwbh,sdwlx,smny1,smny2,skxlx)
{
	var dd = _this.GetCellText(sheet_rjz1,row,2);
	if (dd &gt;= sdd1 && dd &lt;= sdd2) { //查找日期
		var pch = _this.GetCellText(sheet_rjz1,row,3); //批次号
		var zy = _this.GetCellText(sheet_rjz1,row,6); //摘要
		var jfmny = _this.GetCellText(sheet_rjz1,row,7); //金额
		var dfmny = _this.GetCellText(sheet_rjz1,row,8); //金额
		var djh = _this.GetCellText(sheet_rjz1,row,12); //单据号
		var dwbh = _this.GetCellText(sheet_rjz1,row,15); //主体编号
		//var dwlx = _this.GetCellText(sheet_rjz1,row,12); //主体类型
		//var kxlx = _this.GetCellText(sheet_rjz1,row,9); //主体类型

		if (spch  != &quot;&quot;) { if (spch == pch) { cur_search_row = row; return true; } else return false; }
		if (szy   != &quot;&quot;) { if (zy.indexOf(szy) != -1) { cur_search_row = row; return true; } else return false; }
		if (sdjh  != &quot;&quot;) { if (sdjh == djh) { cur_search_row = row; return true; } else return false; }
		if (sdwbh != &quot;&quot;) { if (sdwbh == dwbh) { cur_search_row = row; return true; } else return false; }
		//if (sdwlx != &quot;&quot;) { if (sdwlx == dwlx) { cur_search_row = row; return true; } else return false; }
		//if (skxlx != &quot;&quot;) { if (kxlx.indexOf(skxlx) != -1) { cur_search_row = row; return true; } else return false; }
		if (smny1 != &quot;&quot; || smny2 != &quot;&quot;) { 
			if ((1.0*smny1 &lt;= 1.0*jfmny && 1.0*smny2 &gt;= 1.0*jfmny) || (1.0*smny1 &lt;= 1.0*dfmny && 1.0*smny2 &gt;= 1.0*dfmny)) { 
				cur_search_row = row; 
				return true; 
			}
			else {
				return false;
			} 
		}

		cur_search_row = row; 
		return true;
	}

	return false;


}

//滚动到行
function ScrollToFindRow(sheet,scroll_row)
{
	var scroll_height = -30;
	for (var r=0;r&lt;scroll_row;r++) {
		if (r == scroll_row) break;
		scroll_height += _this.GetRowHeight(sheet,r);
	}
	_this.ScrollToPos(sheet,scroll_height,0);
	//_this.SetCellColor(sheet,scroll_row,2,255,0,0);
	_this.SetCellFocus(sheet,scroll_row,6);
}


//打印附件
function btnPrintFJ()
{
	var obj = new Object();
	var retVal = window.showModalDialog(&quot;show.sp?grdid=RJZ_PRINTFJ&quot;,obj,&quot;dialogWidth=400px;dialogHeight=300px&quot;);
	try {
		//alert(&quot;result=&quot;+retVal.result+&quot;,onlyyl=&quot;+retVal.onlyyl);
		var result = retVal.result; //按单位或摘要
		var onlyyl = retVal.onlyyl; //只打印养老保险
		if (result == 0) return; //取消

		var url = &quot;&quot;;
		if (result == 1) { //按单位名称打印
			if (onlyyl == 0) {
				url = &quot;show.sp?grdid=PZGL_PZQD&SBH=&quot;+cur_sbh+&quot;&ZTH=&quot;+cur_zth+&quot;&YY=&quot;+cur_yyyy+&quot;&MM=&quot;+cur_mm+&quot;&PZH=&quot;+cur_pzh+&quot;&CZXH=&quot;+cur_czxh;				
			}
			else if (onlyyl == 1) {
				url = &quot;show.sp?grdid=PZGL_PZQD_YL&SBH=&quot;+cur_sbh+&quot;&ZTH=&quot;+cur_zth+&quot;&YY=&quot;+cur_yyyy+&quot;&MM=&quot;+cur_mm+&quot;&PZH=&quot;+cur_pzh+&quot;&CZXH=&quot;+cur_czxh;
			}

			//window.open(url);
			//window.open(url+&quot;&callfunc=f_print_and_close&quot;);
			window.showModalDialog(url,null,&quot;dialogWidth=1200px;dialogHeight=800px&quot;);
		}
		else if (result == 2) { //按摘要打印(用于个体工商户)
			if (onlyyl == 0) {
				url = &quot;show.sp?grdid=PZGL_PZQD_ZY&SBH=&quot;+cur_sbh+&quot;&ZTH=&quot;+cur_zth+&quot;&YY=&quot;+cur_yyyy+&quot;&MM=&quot;+cur_mm+&quot;&PZH=&quot;+cur_pzh+&quot;&CZXH=&quot;+cur_czxh;				
			}
			else if (onlyyl == 1) {
				url = &quot;show.sp?grdid=PZGL_PZQD_ZYYL&SBH=&quot;+cur_sbh+&quot;&ZTH=&quot;+cur_zth+&quot;&YY=&quot;+cur_yyyy+&quot;&MM=&quot;+cur_mm+&quot;&PZH=&quot;+cur_pzh+&quot;&CZXH=&quot;+cur_czxh;
			}

			//window.open(url+&quot;&callfunc=f_print_and_close&quot;);
			window.showModalDialog(url,null,&quot;dialogWidth=1200px;dialogHeight=800px&quot;);
		}

	} catch (e) {

	}
}

ROW.DATDSC:
SELECT DISTINCT KMBH,CW_PACK4.KMMC(SBH, ZTH, KMBH) KMMC
  FROM CW_RJZB
 WHERE SBH = &apos;[%SBH]&apos;
   AND ZTH = &apos;[%ZTH]&apos;
   AND YY = &apos;[%YYYY]&apos;
   AND MM = &apos;[%MM]&apos;
   AND PZH = &apos;[%CZXH]&apos;   
ORDER BY KMBH

SELECT A.DWLXBH DWLXBH, B.DWLX DWLX
  FROM SI_DWB A, SI_DWLXB B
 WHERE A.SBH = &apos;[%SBH]&apos;
   AND A.ZTH = &apos;[%ZTH]&apos;
   AND A.DWBH = &apos;[%DWBH]&apos;
   AND A.DWLXBH = B.DWLXBH
