ROW.GRDJSDS_VALUE:
var bhTotalPage = 0;

//数据加载完毕
function _thisOnpost_loaddata(sheet)
{	
	_this.ShowToolBar(0);

	try {
		_this.SetCellText(0,2,3,printkm);
		_this.SetCellText(0,3,3,&quot;本户第 &quot;+bhpageno+&quot; 页，本户共 &quot;+bhtotal+&quot; 页，本帐第 &quot;+bzpage+&quot; 页，请放入纸张。&quot;);
	}
	catch (e) {
		_this.SetCellText(0,2,3,&quot;打印操作&quot;);
		_this.SetCellText(0,3,3,&quot;&quot;);
	}

	_this.AutoFixScreenWidth();

}

//确定
function queding()
{
	var obj = new Object();
	obj.result = 1;
	obj.bhpage = _this.GetCellText(0,7,4);
	obj.bzpage = _this.GetCellText(0,9,4);

	window.returnValue = obj;
	window.close();
}

//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
	if (row == 7 || row == 9) {
		if (!!isNaN(newvalue)) {
			_this.SetCellText(sheet,row,col,oldvalue);
		}
		if (1*_this.GetCellText(sheet,7,4) &gt; bhTotalPage) {
			alert(&quot;输入页数不正确！&quot;);
			_this.SetCellText(sheet,7,4,oldvalue);
		}
	}

}

//点击按钮
function _thisOnButtonCtrlClick(id,sheet,row,col)
{
	var obj = new Object();
	obj.result = &quot;&quot;;

	if (id == &quot;打印并提示&quot;) {
		obj.result = 1;
	}
	else if (id == &quot;连续打印不提示&quot;) {
		obj.result = 2;
	}
	else if (id == &quot;取消打印&quot;) {
		obj.result = 0;			
	}
	window.returnValue = obj;
	window.close();	
}
