function _thisOnTreeItemClick(id, hItem, tagid, attribe, value)
{
	var typ = getXmlAttribute(attribe,&quot;typ&quot;);
	var orgid = getXmlAttribute(attribe,&quot;orgid&quot;);
	var accid = getXmlAttribute(attribe,&quot;accid&quot;);
	var replocid = getXmlAttribute(attribe,&quot;replocid&quot;);
	cursel_orgid = orgid;
	cursel_replocid = replocid;

	try {var nextlevel_itemid=_this.XMLCTL_GetItem(id,hItem,2);if(nextlevel_itemid!=&quot;&quot;)return;} catch ( e ){  return;}//说明该节点已经有数据


	if (typ == &quot;ORG&quot;) {
		var accxml = invokeOSFunc(&quot;GetAccList&quot;,&quot;thisorgid=&quot;+orgid);
		_this.XMLCTL_Append(id,accxml,hItem,2);
	}
	else if (typ == &quot;ACC&quot;) {
		cursel_accid = accid;
		var yymm = _this.GetCellText(1,1,2);
		loadReport(yymm,accid,cursel_replocid);
	}


}

function getXmlAttribute(attribe,attribename)
{
            var loc = attribe.indexOf( attribename+&quot;=\&quot;&quot; );
            if(loc&lt;0) return &quot;&quot;;
            var loc1 = attribe.indexOf( &quot;\&quot;&quot; , loc+attribename.length+2);
            var ret = attribe.substring( loc+attribename.length+2,loc1 ) ;

            return ret;
}

