function loadReport(yymm,accid,replocid)
{	
	_this.LoadZXGFile(ZXGFILE1,-1,1);
	_this.SetFixedRow(1,3);

	_this.SetCellText(1,0,2,cursel_replocid);
	_this.SetCellText(1,0,3,getReplocName(replocid));
	_this.SetToComboBox(&quot;&quot;,1,1,2,yymmList);
	_this.SetCellText(1,1,2,yymm);
	_this.SetAttribe(&quot;SHEET_1&quot;,_this.ATTR_SHEET_SELECTFLAG,_this. SELECTFLAG_SINGLEROW );

	var xml = _sql.query(&quot;MAIN&quot;,&quot;ACCID=&quot;+accid+&quot;&REPLOCID=&quot;+replocid+&quot;&YYMM=&quot;+yymm);
	_this.SetMainCellRange(1,3,1,17,5);
	_this.SetXmlDS(1,3,1,17,5,xml);
	for (var r=_this.GetMainCellRangeRow1(1);r&lt;=_this.GetMainCellRangeRow2(1);r++) {
		_this.SetCellText(1,r,0,(r-2));
		if (_this.GetCellText(1,r,1) == &quot;&quot;) {
			_this.SetRowVisible(1,r,-1);
		}
	}

	_this.AutoFixColWidth(1,250);
	_this.AutoFixScreenWidth();

}

//开始检查
function startCheck()
{
	//增加权限控制
	var author = invokeOSFunc(&quot;CheckPHReport&quot;,&quot;&quot;);
	if (author != 1) {
		alert(&quot;对不起，你没有该操作权限！&quot;);
		return;
	}

	if (!confirm(&quot;检查报表平衡公式，是否继续？&quot;)) return;
	var noph = 0;
	_this.SetCellText(1,1,4,&quot;正在检查报表平衡公式 ...&quot;);

	var yymm = _this.GetCellText(1,1,2);
	//返回不平衡的公式
	var ret = invokeOSFunc(&quot;checkPH&quot;,&quot;orgid=&quot;+cursel_orgid+&quot;&accid=&quot;+cursel_accid+&quot;&yymm=&quot;+yymm+&quot;&replocid=&quot;+cursel_replocid);
	if (ret == undefined || ret == &quot;undefined&quot; || ret == &quot;&quot;) {
		ret = &quot;&quot;;
	}

	for (var r=_this.GetMainCellRangeRow1(1);r&lt;=_this.GetMainCellRangeRow2(1);r++) {
		_this.SetCellText(1,r,5,&quot;√ 平衡&quot;);
		_this.SetCellColor(1,r,5,0,0,255);

		//返回值：报表编号~公式编号~公式~公式名称~不平衡差异金额;
		var rstArr = ret.split(&quot;;&quot;);
		for (var i=0;i&lt;rstArr.length;i++) {
			var rstInfo = rstArr[i].split(&quot;~&quot;);
			if (rstInfo.length != 5) continue;

			var repid = rstInfo[0];
			var fid = rstInfo[1];
			var formula = rstInfo[2];
			var fname = rstInfo[3];
			var cxje = 1.0 * rstInfo[4];
			//for (var r=3;r&lt;_this.GetRowCount(1);r++) {
				if (_this.GetCellText(1,r,1) == repid && _this.GetCellText(1,r,2) == fid) {
					var phrest = &quot;&quot;;
					if (cxje == 0) {
						phrest = &quot;√ 平衡&quot;;
						_this.SetCellColor(1,r,5,0,0,255);	
					}
					else {
						noph ++;
						phrest = &quot;w 不平！差异金额:&quot; + cxje.toFixed(2);
						_this.SetCellColor(1,r,5,255,0,0);
					}
					_this.SetCellText(1,r,5,phrest);
				}
			//}
		}
	}


	var chkRst = &quot;检查完成!共有 &quot;+noph+&quot; 条公式不平衡。&quot;;
	alert(chkRst);
	_this.SetCellText(1,1,4,chkRst);

	var phList = _this.CreateListValue();
	_this.SetListValue(phList,&quot;0&quot;,&quot;不平的&quot;);
	_this.SetListValue(phList,&quot;1&quot;,&quot;全部&quot;);
	_this.SetToRadioBox(&quot;&quot;,1,0,5,phList);
	_this.SetCellText(1,0,5,&quot;1&quot;);

}

//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
	if (sheet == 1 && row == 1 && col == 2) {
		var yymm = _this.GetCellText(1,1,2);
		if (yymm.indexOf(&quot;-&quot;) == -1) {
			var yy = yymm.substring(0,4);
			var mm = yymm.substring(4);
			if (1*mm &lt; 10) mm = &quot;0&quot;+ (1*mm);
			yymm = yy + &quot;-&quot; + mm;
			_this.SetCellText(1,1,2,yymm);
		}
		loadReport(yymm,cursel_accid,cursel_replocid);
	}
	else if (sheet == 1 && row == 0 && col == 5) {
		var flag = _this.GetCellText(1,0,5);
		showPHFormula(flag);
	}
}

function showPHFormula(flag)
{
	for (var r=_this.GetMainCellRangeRow1(1);r&lt;=_this.GetMainCellRangeRow2(1);r++) {
		var result = _this.GetCellText(1,r,5);
		if (flag == 0) {
			if (result.indexOf(&quot;w&quot;) == -1) {
				_this.SetRowVisible(1,r,-1);
			}
		}
		else if (flag == 1) {
			_this.SetRowVisible(1,r,1);
		}
	}

}

