//组织树
function GetReportTree()
{
	var xml = &quot;&quot;;
	var db = null;
	try {
		db = new pub.EADatabase();
		var sql = &quot;select * from oa_reploc where (sjbmbh is null or sjbmbh=&apos;&apos;) &quot;;
		if (thisorgid != &quot;1&quot;) {
			sql += &quot; and org=&apos;&quot;+thisorgid+&quot;&apos;&quot;;
		}
		sql += &quot; order by bmbh, sjbmbh&quot;;	

		var ds = db.QuerySQL(sql);
		for(var i=0;i&lt;ds.getRowCount();i++) {
			var bmbh = ds.getStringAt(i,&quot;BMBH&quot;);
			var bmmc = ds.getStringAt(i,&quot;BMMC&quot;);
			var orgid = ds.getStringAt(i,&quot;ORG&quot;);
			var accid = ds.getStringAt(i,&quot;ACC&quot;);
			var zth = ds.getStringAt(i,&quot;ZTH&quot;);
			var typ = (zth == &quot;&quot; ? &quot;ORG&quot; : &quot;ACC&quot;);
			xml += &quot;&lt;&quot;+bmbh +&quot;.&quot;+bmmc+&quot; imageid=\&quot;0\&quot; orgid=\&quot;&quot;+orgid+&quot;\&quot; accid=\&quot;&quot;+accid+&quot;\&quot; replocid=\&quot;&quot;+bmbh+&quot;\&quot; typ=\&quot;&quot;+typ+&quot;\&quot;&gt;&quot;;	
			xml += getAccXml(db,bmbh);
			xml += &quot;&lt;/&quot;+bmbh+&quot;.&quot;+bmmc+&quot;&gt;&quot;;
  		}

	        return xml;
	}
	catch (e) {
		if (db != null) db.Rollback();
		return e.toString();
	}
	finally {
		if (db != null) db.Close();	
	}       
}

