function getAccXml(db,bmbh)
{
	var xml = &quot;&quot;;
	var imageid = 0;
	var sql = &quot;select * from oa_reploc where sjbmbh=&apos;&quot;+bmbh+&quot;&apos; order by bmbh&quot;;
	var ds = db.QuerySQL(sql);
	for(var i=0;i&lt;ds.getRowCount();i++) {
		var bmbh = ds.getStringAt(i,&quot;BMBH&quot;);
		var bmmc = ds.getStringAt(i,&quot;BMMC&quot;);
		var orgid = ds.getStringAt(i,&quot;ORG&quot;);
		var accid = ds.getStringAt(i,&quot;ACC&quot;);
		var zth = ds.getStringAt(i,&quot;ZTH&quot;);
		var typ = (zth == &quot;&quot; ? &quot;ORG&quot; : &quot;ACC&quot;);
		imageid = (zth == &quot;&quot; ? &quot;0&quot; : &quot;1&quot;);
		xml += &quot;&lt;&quot;+bmbh +&quot;.&quot;+bmmc+&quot; imageid=\&quot;&quot;+imageid+&quot;\&quot; orgid=\&quot;&quot;+orgid+&quot;\&quot; accid=\&quot;&quot;+accid+&quot;\&quot; replocid=\&quot;&quot;+bmbh+&quot;\&quot; typ=\&quot;&quot;+typ+&quot;\&quot;&gt;&quot;;	
		xml += getAccXml(db,bmbh);
		xml += &quot;&lt;/&quot;+bmbh+&quot;.&quot;+bmmc+&quot;&gt;&quot;;		
	}
	return xml;
}


function getRepGuid()
{
	var sql = &quot;&quot;;
	var eagrdxmldb = new xmldb.EAGRDXmlDB(&quot;SBCW&quot;,mwid);
	var grdds = eagrdxmldb.getGrdDS();
	var grdcolds = eagrdxmldb.getGrdColDS();
	var bilhdrtable = grdds.getStringAt(0,&quot;BILHDRTABLE&quot;);
	var bildtltable = grdds.getStringAt(0,&quot;BILDTLTABLE&quot;);

	try {
		sql = &quot;select * from %s where acc=&apos;%s&apos; and yymm=&apos;%s&apos;&quot;.format([bilhdrtable,accid,yymm]);
		var ds = pub.EADbTool.QuerySQL(sql);
		if (ds.getRowCount() &gt; 0)
			return ds.getStringAt(0,&quot;GUID&quot;);
		else 
			return &quot;&quot;;
	} catch ( e ) {
		return e.toString();
	}
	return &quot;&quot;;
}

//删除报表数据
function deleteReportData()
{
	var db = null;
	try {
		db = new pub.EADatabase();
		var sql = &quot;select * from fin_repmodel where reportid=&apos;&quot;+repid+&quot;&apos;&quot;;
		var ds = db.QuerySQL(sql);
		if (ds.getRowCount() &lt;= 0) {
			return &quot;找不到报表相关定义!&quot;;
		}

		var grdid = ds.getStringAt(0,&quot;GRDID&quot;);
		var hdrtable = ds.getStringAt(0,&quot;HDRTABID&quot;);
		var dtltable = ds.getStringAt(0,&quot;DTLTABID&quot;);

		sql = &quot;select guid,stat from &quot; + hdrtable + &quot; where org=&apos;&quot;+orgid+&quot;&apos; and acc=&apos;&quot;+accid+&quot;&apos; and replocid=&apos;&quot;+replocid+&quot;&apos; and yymm=&apos;&quot;+yymm+&quot;&apos;&quot;;
		ds = db.QuerySQL(sql);
		var stat = ds.getStringAt(0,&quot;STAT&quot;);
		if (stat == &quot;2&quot;) { //已核的报表不允许删除
			return &quot;报表数据已审核，不能删除！&quot;;
		}

		var repguid = ds.getStringAt(0,&quot;GUID&quot;);
		sql = &quot;delete from &quot; + hdrtable + &quot; where guid=&apos;&quot;+repguid+&quot;&apos;&quot;;
		db.ExcecutSQL(sql);
		sql = &quot;delete from &quot; + dtltable + &quot; where formguid=&apos;&quot;+repguid+&quot;&apos;&quot;;
		db.ExcecutSQL(sql);
		sql = &quot;delete from repmodel where guid=&apos;&quot;+repguid+&quot;&apos; and viewtyp=&apos;&quot;+repid+&quot;&apos; and org=&apos;&quot;+orgid+&quot;&apos; and acc=&apos;&quot;+accid+&quot;&apos; and replocid=&apos;&quot;+replocid+&quot;&apos; and yymm=&apos;&quot;+yymm+&quot;&apos;&quot;;
		db.ExcecutSQL(sql);

		db.Commit();

		return &quot;删除报表数据成功!&quot;;
	}
	catch (e) {
		if (db != null) db.Rollback();
		return &quot;删除报表数据出错：&quot;+e.toString();
	}
	finally {
		if (db != null) db.Close();
	}
}

function checkPH()
{
	var db = null;
	var ret = &quot;&quot;;
	try {
		db = new pub.EADatabase();
		var sql = &quot;select distinct b.repid
			from fin_formula a,fin_formularep b,v_repmodel c 
			where a.guid=b.formulaguid and b.repid=c.repid
			  and c.replocid=&apos;%s&apos; 
			  and c.yymm=&apos;%s&apos; and c.acc=&apos;%s&apos;
			  and a.useflg=&apos;1&apos; /*and c.STAT=&apos;1&apos;*/&quot;.format([replocid,yymm,accid]);

		var sbcw = new SBCW_PUBDFFIN();
		var pubjs = new SBCW_T0PubJS();
		var ds = db.QuerySQL(sql);

		//先写进指标库表
		for (var i=0;i&lt;ds.getRowCount();i++) {
			var repid = ds.getStringAt(i,&quot;REPID&quot;);
			pubjs.beforeCheckPH(db,&quot;SBCW&quot;,orgid,accid,repid,yymm,replocid);
		}
		for (var i=0;i&lt;ds.getRowCount();i++) {
			var repid = ds.getStringAt(i,&quot;REPID&quot;);
			ret += sbcw.checkReportPH(db,orgid,accid,repid,yymm,replocid);
		}

		return ret;

	}
	catch (e) {
		if (db != null) db.Rollback();
		return e.toString();
	}
	finally {
		if (db != null) {
			db.Rollback(); //平衡检查最后回滚掉
			db.Close();
		}
	}
}


