ROW.DATDSC:
select b.repid,a.id,a.note,a.formula,&apos;&apos; rst
from fin_formula a,fin_formularep b,v_repmodel c 
where a.guid=b.formulaguid and b.repid=c.repid(+)
  and c.replocid(+)=&apos;[%REPLOCID]&apos; 
  and c.yymm(+)=&apos;[%YYMM]&apos; and c.acc(+)=&apos;[%ACCID]&apos;
  and a.useflg=&apos;1&apos; 
order by b.repid,a.id

select &apos;&apos; sel,yymm,stat from v_repmodel
where acc=&apos;[%ACCID]&apos; and viewtyp=&apos;[%REPID]&apos; and yymm like &apos;[%YYYY]%&apos;
order by yymm desc
