ROW.GRDJSDS_VALUE:
var printType = &quot;1&quot;;

//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	_this.ShowToolBar(0);
	_this.AutoFixScreenWidth();

	var obj = window.dialogArguments;
	_this.SetCellText(sheet,3,4,obj.bqsj);
	_this.SetCellText(sheet,5,4,obj.yj);
	_this.SetCellText(sheet,7,4,obj.bj);
	_this.SetCellText(sheet,9,4,obj.qq);
	_this.SetCellText(sheet,11,4,obj.qt);
	_this.SetCellText(sheet,13,4,obj.ljtzzc);


}

//确定
function queding()
{
	var sheet = 0;
	var obj = new Object();
	obj.bqsj = _this.GetCellText(sheet,3,4);
	obj.yj = _this.GetCellText(sheet,5,4);
	obj.bj = _this.GetCellText(sheet,7,4);
	obj.qq = _this.GetCellText(sheet,9,4);
	obj.qt = _this.GetCellText(sheet,11,4);
	obj.ljtzzc = _this.GetCellText(sheet,13,4);

	window.returnValue = obj;
	window.close();
}

//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
	if (0) {
		_this.SetCellText(sheet,row,col,oldvalue);
	}
}

//点击按钮
function _thisOnButtonCtrlClick(id,sheet,row,col)
{
	if (id == &quot;确定&quot;) {
		queding();
	}
	else if (id == &quot;取消&quot;) {
		window.returnValue = null;
		window.close();
	}
}
