ROW.GRDJSDS_VALUE:
//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	//报表编号~公式编号~公式~公式名称~不平衡差异金额
	var obj = window.dialogArguments;
	var phinfos = obj.phinfos;
	_this.SetCellText(0,0,0,&quot;报表（&quot;+obj.repid+&quot;-&quot;+obj.repnam+&quot;）平衡检查详细信息&quot;);

	var xml = _sql.query(&quot;MAIN&quot;,&quot;REPID=&quot;+obj.repid);
	_this.SetMainCellRange(0,2,0,_this.GetRowCount(0)-1,6);
	_this.SetXmlDS(sheet,2,0,_this.GetRowCount(0)-1,6,xml);
	for (var row =_this.GetMainCellRangeRow1(0);row&lt;=_this.GetMainCellRangeRow2(0);row++) {
		if (_this.GetCellText(0,row,5) == &quot;√&quot;) {
			_this.SetCellColor(sheet,row,5,0,0,255);	
			_this.SetCellColor(sheet,row,5,0,0,255);
		}
	}

	var rstArr = phinfos.split(&quot;;&quot;);
	var xh = 0;
	var row = 1;
	for (var i=0;i&lt;rstArr.length;i++) {
		var rstInfo = rstArr[i].split(&quot;~&quot;);
		if (rstInfo.length != 5) continue;
		xh ++;
		row ++;

		var repid = rstInfo[0];
		var fid = rstInfo[1];
		var formula = rstInfo[2];
		var fname = rstInfo[3];
		var cxje = 1.0 * rstInfo[4];

		for (var row =_this.GetMainCellRangeRow1(0);row&lt;=_this.GetMainCellRangeRow2(0);row++) {
			if (repid == _this.GetCellText(sheet,row,1) && fid == _this.GetCellText(sheet,row,2)) {
//				if (row &gt; 3) _this.AppendRow(sheet,row-1);
//				_this.SetCellText(sheet,row,0,xh);
//				_this.SetCellText(sheet,row,1,repid);
//				_this.SetCellText(sheet,row,2,fid);
//				_this.SetCellText(sheet,row,3,fname);
//				_this.SetCellText(sheet,row,4,formula);

				var phrest = &quot;&quot;;
				if (cxje == 0 || cxje == &quot;&quot;) {
					phrest = &quot;√ 平衡&quot;;
					_this.SetCellText(sheet,row,5,&quot;√&quot;);
					_this.SetCellColor(sheet,row,5,0,0,255);	
					_this.SetCellColor(sheet,row,5,0,0,255);
				}
				else {
					//noph ++;
					phrest = &quot;w 不平！差异金额:&quot; + cxje.toFixed(2);
					_this.SetCellText(sheet,row,5,&quot;w&quot;);
					_this.SetCellText(sheet,row,6,cxje.toFixed(2));
					_this.SetCellColor(sheet,row,5,255,0,0);
					_this.SetCellColor(sheet,row,6,255,0,0);
				}
				//_this.SetCellText(sheet,row,5,phrest);
			}
		}

	}

	showNoPH();

	_this.SetAttribe(&quot;SHEET_0&quot;,_this.ATTR_SHEET_SELECTFLAG,_this. SELECTFLAG_SINGLEROW );


	_this.AutoFixColWidth(sheet,200);
	_this.AutoFixScreenWidth();

}

//显示不平
function showNoPH()
{
	for (var row =_this.GetMainCellRangeRow1(0);row&lt;=_this.GetMainCellRangeRow2(0);row++) {
		var fid = _this.GetCellText(0,row,2);
		var isph = _this.GetCellText(0,row,5);
		if (fid != &quot;&quot; && isph == &quot;w&quot;) {
			_this.SetRowVisible(0,row,1);
		}
		else {
			_this.SetRowVisible(0,row,-1);
		}
	}
}

function showAll()
{
	for (var row =_this.GetMainCellRangeRow1(0);row&lt;=_this.GetMainCellRangeRow2(0);row++) {
		var fid = _this.GetCellText(0,row,2);
		var isph = _this.GetCellText(0,row,5);
		if (fid != &quot;&quot;) {
			_this.SetRowVisible(0,row,1);
		}
		else {
			_this.SetRowVisible(0,row,-1);
		}
	}
}
