ROW.GRDJSDS_VALUE:
var sheet_yhrjzb = 0;
var sheet_rjzb   = 1;
var sheet_rjzmxb = 2;
var sheet_param  = 3;
var sheet_tsxx   = 4;

var cur_sbh  = &quot;&quot;;
var cur_zth  = &quot;&quot;;
var cur_yyyy = &quot;&quot;;
var cur_mm   = &quot;&quot;;
var cur_kmbh = &quot;&quot;;

var ZXGFILE0=&quot;&quot;;
var ZXGFILE1=&quot;&quot;;
var ZXGFILE2=&quot;&quot;;

var cur_ztlxlist = &quot;&quot;;
var g_modifyflag = -1;

var is_dzxz = &quot;01&quot;;
var cur_action = &quot;query&quot;; //当前操作是修改还是查询

//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	var yyList =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_CW_YYYY&quot;),&quot;ID&quot;,&quot;NAME&quot;);
	var mmList =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_CW_MM&quot;),&quot;ID&quot;,&quot;NAME&quot;);
	var logym = G_LOGDAT.split(&quot;-&quot;)[0] + G_LOGDAT.split(&quot;-&quot;)[1]; 
	//var kmList =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_ACCITEM&where=mjbz=1 and XJYHBZ&lt;&gt;0 and QLBZ&lt;&gt;1 and nvl(RJZKM_LRBZ,&apos;1&apos;) = &apos;1&apos;&quot;),&quot;ID&quot;,&quot;NAME&quot;);
	var kmList =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_ACCITEM&where=mjbz=1 and XJYHBZ&lt;&gt;0 and QLBZ&lt;&gt;1 and nvl(RJZKM_LRBZ,&apos;1&apos;)=&apos;1&apos; and (nvl(jssj,&apos;999999&apos;)&gt;=&quot;+logym+&quot; and nvl(kssj,&apos;190001&apos;)&lt;=&quot;+logym+&quot;)&quot;),&quot;ID&quot;,&quot;NAME&quot;);
	var cur_ztlxlist = _this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_CW_CSB&where=LXDM=&apos;ZTLX&apos;&quot;),&quot;ID&quot;,&quot;NAME&quot;);

	cur_sbh = G_ORGID;
	cur_zth = G_ACCID.replace(G_ORGID,&quot;&quot;);
	cur_yyyy = G_LOGDAT.substring(0,4);
	cur_mm = 1*(G_LOGDAT.substring(5,7));

	try { cur_action = action; } catch (e) { }

	_this.SetToComboBox(&quot;&quot;,sheet_param,1,1,yyList); _this.SetCellText(sheet_param,1,1,cur_yyyy);
	_this.SetToComboBox(&quot;&quot;,sheet_param,1,3,mmList); _this.SetCellText(sheet_param,1,3,cur_mm);
	_this.SetToComboBox(&quot;&quot;,sheet_param,1,6,kmList);

	initLayout();
	setBoolBox();	
	_this.AutoFixScreenWidth();
}

function setBoolBox(){
	for(var i = 0;i&lt;= _this.GetRowCount(sheet_yhrjzb) - 2;i ++){
		_this.SetToBoolBox(sheet_yhrjzb,i,0);
		_this.SetCellText(sheet_yhrjzb,i,0,0);
	}
}

//页面布局
