		xmlmenu = &quot;&lt;item id = \&quot;yejc1\&quot; name= \&quot;余额检查(从启动年月开始)\&quot;/&gt;&lt;item id = \&quot;yejc2\&quot; name= \&quot;余额检查(从当前年月开始)\&quot;/&gt;&lt;item id = \&quot;jejc\&quot; name= \&quot;金额检查\&quot;/&gt;&quot;;
		_this.AddToolbarButton(&quot;btn_jc&quot;,&quot;检查&quot;,&quot;检查&quot;,xmlmenu,1,3,3,80);	
