	}

}

//加载银行日记账表
function loadDataYhrjzb(sbh,zth,yy,mm,kmbh,filter)
{
	_this.SetToolbarString(&quot;正在加载银行日记账表数据...&quot;);

	_this.LoadZXGFile(ZXGFILE0,-1,sheet_yhrjzb);
	_this.SetFixedRow(sheet_yhrjzb,1);
	_this.SetMainCellRange(sheet_yhrjzb,1,0,_this.GetRowCount(sheet_yhrjzb)-2,_this.GetColCount(sheet_yhrjzb)-1);

	//加载银行日记账数据
	var xml = _sql.query(&quot;loadDataYhrjzb&quot;,&quot;YYYY=&quot;+yy+&quot;&MM=&quot;+mm+&quot;&KMBH=&quot;+kmbh+&quot;&FILTER=&quot;+filter+&quot;&SBH=&quot;+sbh+&quot;&ZTH=&quot;+zth);
	_this.SetXmlDS(sheet_yhrjzb,1,0,_this.GetRowCount(sheet_yhrjzb)-2,_this.GetColCount(sheet_yhrjzb)-1,xml);			

	//光标定位焦点
//	_this.SetFocusCell(sheet_yhrjzb,2,1);	
	//设置显示信息
	var ywpch  = _this.GetCellText(sheet_yhrjzb,2,13);
	var ztlx   = _this.GetCellText(sheet_yhrjzb,2,14);
	var ztmc   = _this.GetCellText(sheet_yhrjzb,2,16);
	var jsfs   = _this.GetCellText(sheet_yhrjzb,2,19);
	var yszllx = _this.GetCellText(sheet_yhrjzb,2,27);

	_sql.querytods(&quot;getZtlx&quot;,&apos;ZTLX=&apos;+ztlx);
	var ztlxmc = _this.XMLDS_GetString(0,&apos;aaa103&apos;);

	_this.SetCellText(sheet_tsxx,0,2 ,ywpch  );
	_this.SetCellText(sheet_tsxx,0,4 ,ztlxmc );
	_this.SetCellText(sheet_tsxx,0,6 ,ztmc   );
	_this.SetCellText(sheet_tsxx,0,8 ,jsfs   );
	_this.SetCellText(sheet_tsxx,0,10,yszllx );

	var jfje_sum = 0.00;
	var dfje_sum = 0.00;
	var ye_sum   = 0.00;
	var jfbs     = 0;
	var dfbs     = 0;
	_this.SetAttribe(&quot;SHEET_0&quot;,_this.ATTR_SHOW_ZERO,1); //设置0也显示
	for(var i =_this.GetMainCellRangeRow1(sheet_yhrjzb);i&lt;=_this.GetMainCellRangeRow2(sheet_yhrjzb);i++){
		var jfje = _this.GetCellText(sheet_yhrjzb,i,7)*1.00;
		var dfje = _this.GetCellText(sheet_yhrjzb,i,8)*1.00;
		var jefx = _this.GetCellText(sheet_yhrjzb,i,43); //使用金额方向计算
		var ye   = _this.GetCellText(sheet_yhrjzb,i,10)*1.00;
		var qcbz = _this.GetCellText(sheet_yhrjzb,i,42);
		var sblsh = _this.GetCellText(sheet_yhrjzb,i,11);

		if(ye == &quot;&quot;) ye = 0.00;
		if(jfje == &quot;&quot;) jfje = 0.00;
		if(dfje == &quot;&quot;) dfje = 0.00;

		if(sblsh != &quot;&quot;){
			if(qcbz == 1) {
				ye_sum = 1.0* round(ye,2);
				if (jefx == &quot;借&quot;) _this.SetCellText(sheet_yhrjzb,i,8,&quot;&quot;); //处理贷方金额不显示0
				if (jefx == &quot;贷&quot;) _this.SetCellText(sheet_yhrjzb,i,7,&quot;&quot;); //处理借方金额不显示0
			}else{
				if(jefx == &quot;借&quot;){
					jfbs ++;
					jfje_sum = 1.00 * round(jfje_sum,2) + 1.00 * round(jfje,2);
//					ye_sum += 1.00 * round(jfje,2);
					ye_sum = (1.00 * round(ye_sum,2) + 1.00 * round(jfje,2)).toFixed(2);
					_this.SetCellText(sheet_yhrjzb,i,10,ye_sum);
					_this.SetCellText(sheet_yhrjzb,i,8,&quot;&quot;); //处理贷方金额不显示0
				}else if(jefx == &quot;贷&quot;){
					dfbs ++;
					dfje_sum = 1.00 * round(dfje_sum,2) + 1.00 * round(dfje,2);
//					ye_sum -= 1.00 * round(dfje,2);
					ye_sum = (1.00 * round(ye_sum,2) - 1.00 * round(dfje,2)).toFixed(2);
					_this.SetCellText(sheet_yhrjzb,i,10,ye_sum);
					_this.SetCellText(sheet_yhrjzb,i,7,&quot;&quot;); //处理借方金额不显示0
				}
			}//if(qcbz == 1)
		}//if(sblsh != &quot;&quot;)

		//设置选择控件
		if (_this.GetCellText(sheet_yhrjzb,i,2) != &quot;&quot;) {
			_this.SetToBoolBox(sheet_yhrjzb,i,0);
		}

	}//for(var i =_this.GetMainCellRangeRow1(sheet_yhrjzb);i&lt;=_this.GetMainCellRangeRow2(sheet_yhrjzb);i++)

	_this.SetCellText(sheet_param,1,10,&quot;借：&quot;+jfbs+&quot;笔；   贷：&quot;+dfbs+&quot;笔；&quot;);
	_this.SetCellText(sheet_yhrjzb,_this.GetRowCount(sheet_yhrjzb)-1,7,jfje_sum);
	_this.SetCellText(sheet_yhrjzb,_this.GetRowCount(sheet_yhrjzb)-1,8,dfje_sum);


	//加载日记账表数据
	var siseqno = _this.GetCellText(sheet_yhrjzb,2,11);
	loadDataRjzb(sbh,zth,yy,mm,kmbh,siseqno);

	//setBoolBox();
	_this.AutoFixScreenWidth();
	_this.SetToolbarString(&quot;&quot;);
}

//加载日记账表
function loadDataRjzb(sbh,zth,yy,mm,kmbh,siseqno){
	_this.SetToolbarString(&quot;正在加载日记账表数据...&quot;);

	_this.LoadZXGFile(ZXGFILE1,-1,sheet_rjzb);
	_this.SetFixedRow(sheet_rjzb,1);
	_this.SetMainCellRange(sheet_rjzb,1,0,_this.GetRowCount(sheet_rjzb)-2,_this.GetColCount(sheet_rjzb)-1);

	//加载银行日记账数据
	var xml = _sql.query(&quot;loadDataRjzb&quot;,&quot;YYYY=&quot;+yy+&quot;&MM=&quot;+mm+&quot;&KMBH=&quot;+kmbh+&quot;&SISEQNO=&quot;+siseqno+&quot;&SBH=&quot;+sbh+&quot;&ZTH=&quot;+zth);
	_this.SetXmlDS(sheet_rjzb,1,0,_this.GetRowCount(sheet_rjzb)-2,_this.GetColCount(sheet_rjzb)-1,xml);	

	var jfje_sum = 0.00;
	var dfje_sum = 0.00;
	_this.SetAttribe(&quot;SHEET_1&quot;,_this.ATTR_SHOW_ZERO,1); //设置0也显示
	for(var i = _this.GetMainCellRangeRow1(sheet_rjzb);i&lt;= _this.GetMainCellRangeRow2(sheet_rjzb);i++){	
		var jfje = _this.GetCellText(sheet_rjzb,i,5);
		var dfje = _this.GetCellText(sheet_rjzb,i,6);
		var lsh  = _this.GetCellText(sheet_rjzb,i,8);
		var jefx  = _this.GetCellText(sheet_rjzb,i,18);

		if(lsh != &quot;&quot;){
			jfje_sum += 1.00*jfje;
			dfje_sum += 1.00*dfje;
			if (jefx == &quot;借&quot;) _this.SetCellText(sheet_rjzb,i,6,&quot;&quot;); //处理贷方金额不显示0
			if (jefx == &quot;贷&quot;) _this.SetCellText(sheet_rjzb,i,5,&quot;&quot;); //处理借方金额不显示0
		}
	}
	_this.SetCellText(sheet_rjzb,_this.GetRowCount(sheet_rjzb)-1,5,jfje_sum);
	_this.SetCellText(sheet_rjzb,_this.GetRowCount(sheet_rjzb)-1,6,dfje_sum);

	//加载日记账表数据
	var lsh = _this.GetCellText(sheet_rjzb,1,8);
	loadDataRjzmxb(lsh);

	_this.AutoFixScreenWidth();
	_this.SetToolbarString(&quot;&quot;);
}

//加载日记账明细表
function loadDataRjzmxb(lsh){
	_this.SetToolbarString(&quot;正在加载日记账明细表数据...&quot;);

	_this.LoadZXGFile(ZXGFILE2,-1,sheet_rjzmxb);
	_this.SetFixedRow(sheet_rjzmxb,1);
	_this.SetMainCellRange(sheet_rjzmxb,1,0,_this.GetRowCount(sheet_rjzmxb)-2,_this.GetColCount(sheet_rjzmxb)-1);

	//加载银行日记账数据
	var xml = _sql.query(&quot;loadDataRjzmxb&quot;,&quot;LSH=&quot;+lsh);
	_this.SetXmlDS(sheet_rjzmxb,1,0,_this.GetRowCount(sheet_rjzmxb)-2,_this.GetColCount(sheet_rjzmxb)-1,xml);
	_this.SetAttribe(&quot;SHEET_2&quot;,_this.ATTR_SHOW_ZERO,1); //设置0也显示

	var je_sum = 0.00;
	for(var i =_this.GetMainCellRangeRow1(sheet_rjzmxb);i&lt;=_this.GetMainCellRangeRow2(sheet_rjzmxb);i++){
		var je   = _this.GetCellText(sheet_rjzmxb,i,5);
		var jefx = _this.GetCellText(sheet_rjzmxb,i,4);
		var lsh  = _this.GetCellText(sheet_rjzmxb,i,8);
		if(lsh != &quot;&quot;){
			if(jefx == &quot;借&quot;){
				je_sum += 1.00*je;
			}else if(jefx == &quot;贷&quot;){
				je_sum -= 1.00*je;
			}
		}
	}

	if(je_sum &gt;= 0 ){		
		_this.SetCellText(sheet_rjzmxb,_this.GetRowCount(sheet_rjzmxb)-1,4,&quot;借&quot;);
		_this.SetCellText(sheet_rjzmxb,_this.GetRowCount(sheet_rjzmxb)-1,5,je_sum);
	}else{
		_this.SetCellText(sheet_rjzmxb,_this.GetRowCount(sheet_rjzmxb)-1,4,&quot;贷&quot;);
		_this.SetCellText(sheet_rjzmxb,_this.GetRowCount(sheet_rjzmxb)-1,5,-1*je_sum);
	}

	_this.AutoFixScreenWidth();
	_this.SetToolbarString(&quot;&quot;);
}

//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
	if(sheet == sheet_param){
		if(row == 1 && (col == 6 || col == 1 || col == 3)){
			cur_yyyy = _this.GetCellText(sheet_param,row,1);
			cur_mm   = _this.GetCellText(sheet_param,row,3);
			cur_kmbh = _this.GetCellText(sheet_param,row,6);

			loadDataYhrjzb(cur_sbh,cur_zth,cur_yyyy,cur_mm,cur_kmbh,&quot;&quot;);
		}
	}
	else if (sheet == sheet_yhrjzb) {
		if (col != 0) {
			var checkRet = checkCzyxm(row );
			if(checkRet != 1) {
				_this.SetCellText(sheet_yhrjzb,row,col,oldvalue);
				return false;
			}

			_this.SetCellText(sheet_yhrjzb,row,40,&quot;*&quot;); //设置标志列为修改*
			g_modifyflag = 1;
		}	
		else if (col == 0) {
			if (row == 0) { //全选
				for (var r=_this.GetMainCellRangeRow1(sheet_yhrjzb);r&lt;=_this.GetMainCellRangeRow2(sheet_yhrjzb);r++) {
					_this.SetCellText(sheet_yhrjzb,r,0,newvalue);
					if (newvalue == 0) {
						setOneRowBkColor(sheet,r,255,255,255);		
					}
					else {
						setOneRowBkColor(sheet,r,255,236,139);
					}
				}
			}
			else {
				if (newvalue == 0) {
					setOneRowBkColor(sheet,row,255,255,255);		
				}
				else {
					setOneRowBkColor(sheet,row,255,236,139);
				}
			}
		}
	}
}

//鼠标左键点击
function _thisOnMouseLClick(sheet,row,col)
{
	if(sheet == sheet_yhrjzb){
		if(row &gt; 0){
			var siseqno = _this.GetCellText(sheet_yhrjzb,row,11);
			loadDataRjzb(cur_sbh,cur_zth,cur_yyyy,cur_mm,cur_kmbh,siseqno);	

			var ywpch  = _this.GetCellText(sheet,row,13);
			var ztlx   = _this.GetCellText(sheet,row,14);
			var ztmc   = _this.GetCellText(sheet,row,16);
			var jsfs   = _this.GetCellText(sheet,row,19);
			var yszllx = _this.GetCellText(sheet,row,27);

			_sql.querytods(&quot;getZtlx&quot;,&apos;ZTLX=&apos;+ztlx);
			var ztlxmc = _this.XMLDS_GetString(0,&apos;aaa103&apos;);

			_this.SetCellText(sheet_tsxx,0,2 ,ywpch  );
			_this.SetCellText(sheet_tsxx,0,4 ,ztlxmc );
			_this.SetCellText(sheet_tsxx,0,6 ,ztmc   );
			_this.SetCellText(sheet_tsxx,0,8 ,jsfs   );
			_this.SetCellText(sheet_tsxx,0,10,yszllx );

			//检查是否有对应流水
			_sql.querytods(&quot;getDykm&quot;,&quot;SBLSH=&quot;+siseqno+&quot;&KMBH=&quot;+cur_kmbh);
			if(_this.XMLDS_GetRowCount() &gt; 0){
				var dykmbh = _this.XMLDS_GetString(0,&quot;KMBH&quot;);
				var dykmmc = _this.XMLDS_GetString(0,&quot;KMMC&quot;);
				_this.SetToolbarString(&quot; &gt;&gt; 对应科目：&quot;+dykmbh +&quot;-&quot;+dykmmc);
			}
		}
	}else if(sheet == sheet_rjzb){
		if(row &gt; 0){
			var lsh = _this.GetCellText(sheet_rjzb,row,8);
			loadDataRjzmxb(lsh);
		}
	}
	else if (sheet == sheet_param) {
		if (row == 1 && col == 7) {
			showKM();
		}
		else if (row == 1 && col == 8) {
			showFilter(); //过滤
		}
	}
}

//点击按钮
function _thisOnButtonCtrlClick(id,sheet,row,col)
{
	if(id == &quot;选择科目&quot;){
		showKM();
	}else if(id == &quot;过滤&quot;){
		showFilter(); //过滤
	}
}

//选择科目
function showKM()
{
	var obj = new Object();
        obj.filter = &quot;xjyhbz &lt;&gt; 0&quot;;
        obj.mjbz = &quot;1&quot;;
        obj.bz = &quot;&quot;;
        obj.jb = 0;
        obj.kmbh = &quot;&quot;;
	var retVal = window.showModalDialog(&quot;show.sp?grdid=CW_KMSEL&quot;,obj,&quot;dialogWidth=600px;dialogHeight=500px&quot;);
	cur_kmbh = retVal;
	if (cur_kmbh != &quot;&quot; && cur_kmbh != &quot;undefined&quot; && cur_kmbh != undefined) {
		_this.SetCellText(sheet_param,1,6,cur_kmbh);
		loadDataYhrjzb(cur_sbh,cur_zth,cur_yyyy,cur_mm,cur_kmbh,&quot;&quot;);
	}
}

//日记帐过滤条件
function showFilter()
{
	var obj = new Object();
	obj.yyyy = cur_yyyy;
	obj.mm = cur_mm;
	obj.kmbh = cur_kmbh;	
	obj.kmmc = _this.GetCellShowText(sheet_param,1,6);
	var retVal = window.showModalDialog(&quot;show.sp?grdid=RJZ_FILTER&quot;,obj,&quot;dialogWidth=600px;dialogHeight=300px&quot;);
	var param = retVal;
//	alert(&quot;retVal=&quot;+retVal );
//	if (filter != &quot;&quot; && filter != &quot;undefined&quot; && filter != undefined) {
//		alert(&quot;过滤&quot;);
//	}

	try {
		var filter = &quot;&quot;;
		if (param.reset == &quot;1&quot;) { //恢复
			loadDataYhrjzb(cur_sbh,cur_zth,cur_yyyy,cur_mm,cur_kmbh,&quot;&quot;);
		}
		else {
			if (param.dat1 != &quot;&quot;) {
				var dat1 = param.dat1;
				var dat2 = param.dat2;
				//cur_yyyy = dat1.split(&quot;-&quot;)[0];
				//cur_mm = 1 * (dat1.split(&quot;-&quot;)[1]);
				var dd1 = 1 * (dat1.split(&quot;-&quot;)[2]);
				var dd2 = 1 * (dat2.split(&quot;-&quot;)[2]);
				filter = &quot; and (a.dd&gt;=&quot;+dd1+&quot; and a.dd&lt;=&quot;+dd2+&quot;)&quot;;
			}
			if (param.jsfs != &quot;&quot;) {
				filter += &quot; and (a.jsfs=&apos;&quot;+param.jsfs+&quot;&apos;)&quot;;
			}
			if (param.kmbh != &quot;&quot;) {
				_this.SetCellText(sheet_param,1,6,param.kmbh);
				cur_kmbh = param.kmbh;
			}
			if (param.dwbh != &quot;&quot;) {
				//filter += &quot; and (a.dwbh=&apos;&quot;+param.dwbh+&quot;&apos;)&quot;;
				filter += &quot; and (a.ztbh=&apos;&quot;+param.dwbh+&quot;&apos;)&quot;;
			}
			if (param.zy != &quot;&quot;) {
				filter += &quot; and (a.zy like &apos;%25&quot;+param.zy+&quot;%25&apos;)&quot;;
			}
			if (param.mny1 != &quot;&quot;) {
				filter += &quot; and a.je&gt;=&quot;+param.mny1;
			}
			if (param.mny2 != &quot;&quot;) {
				filter += &quot; and a.je&lt;=&quot;+param.mny2;
			}
			if (param.printbz != &quot;&quot;) {
				filter += &quot; and a.dybz=&apos;&quot;+param.printbz+&quot;&apos;&quot;;
			}
			if (param.djh != &quot;&quot;) {
				filter += &quot; and a.djh like &apos;%25&quot;+param.djh+&quot;%25&apos;&quot;;
			}
			if (param.pch != &quot;&quot;) {
				filter += &quot; and a.cwpch like &apos;%25&quot;+param.pch+&quot;%25&apos;&quot;;
			}
			//alert(filter);
			loadDataYhrjzb(cur_sbh,cur_zth,cur_yyyy,cur_mm,cur_kmbh,filter);
		}
	}
	catch(e) {}
}

//点击菜单项
function _thisOnMenuItemSelect(sheet,row,col,menuitemid)
{
	if (menuitemid == &quot;rjztjb1&quot; || menuitemid == &quot;rjztjb2&quot; || menuitemid == &quot;rjztjb3&quot; || menuitemid == &quot;rjztjb4&quot; || menuitemid == &quot;rjztjb5&quot; || menuitemid == &quot;rjztjb6&quot;)  {
		//window.open(&quot;show.sp?grdid=RJZ_TJ&quot;);	
		var yyyy = _this.GetCellText(sheet_param,1,1);
		var mm = formatMD(_this.GetCellText(sheet_param,1,3));
		var dat1 = yyyy + &quot;-&quot; + mm + &quot;-01&quot;;
		_sql.querytods(&quot;LASTDAY&quot;,&quot;DAT1=&quot;+dat1);
		var dat2 = _this.XMLDS_GetString(0,&quot;DAT2&quot;);
		var kmbh = _this.GetCellText(sheet_param,1,6);

		var repmwid = &quot;RJZ_TJ&quot;;
		if (menuitemid == &quot;rjztjb1&quot;) repmwid = &quot;RJZ_TJ&quot;;	//日记帐款项统计
		else if (menuitemid == &quot;rjztjb2&quot;) repmwid = &quot;RJZ_TJ2&quot;;	//零星医疗费报账支出明细表
		else if (menuitemid == &quot;rjztjb3&quot;) repmwid = &quot;RJZ_TJ3&quot;;	//现金支出汇总表
		else if (menuitemid == &quot;rjztjb4&quot;) repmwid = &quot;RJZ_TJ4&quot;;	//享受大额医疗补助人员名单
		else if (menuitemid == &quot;rjztjb5&quot;) repmwid = &quot;RJZ_TJ5&quot;;	//费用申报表
//		else if (menuitemid == &quot;rjztjb6&quot;) repmwid = &quot;RJZ_TJ6&quot;;	//享受大额医疗补助人员名单

		window.showModalDialog(&quot;show.sp?grdid=&quot;+repmwid+&quot;&DAT1=&quot;+dat1+&quot;&DAT2=&quot;+dat2+&quot;&KMBH=&quot;+kmbh,&quot;&quot;,&quot;dialogWidth=1000px;dialogHeight=600px;&quot;);
	}
	else if(menuitemid == &quot;yejc1&quot;){
		yejc(1);
	        loadDataYhrjzb(cur_sbh,cur_zth,cur_yyyy,cur_mm,cur_kmbh,&quot;&quot;);
	}else if(menuitemid == &quot;yejc2&quot;){
		yejc(2);
	        loadDataYhrjzb(cur_sbh,cur_zth,cur_yyyy,cur_mm,cur_kmbh,&quot;&quot;);
	}else if(menuitemid == &quot;jejc&quot;){
		jejc();
	}else if(menuitemid == &quot;csye&quot;){
		check_next_qcye();
	}
}

function formatMD(mmdd) 
{
	if (mmdd.length == 1) return &quot;0&quot; + mmdd;
	else return mmdd;
}

//单位明细
function rjzDwmx()
{
	var rjz_currow = _this.GetCurrentRow(sheet_yhrjzb);
	var dwbh = _this.GetCellText(sheet_yhrjzb,rjz_currow,15);
        if (rjz_currow == -1 || dwbh == &quot;&quot;) {
		alert(&quot;没有选中日记账记录或者没有单位编号！&quot;+rjz_currow);
		return;
	}
	var url = &quot;show.sp?grdid=RJZ_DWMXZ&yyyy=&quot;+cur_yyyy+&quot;&dwbh=&quot;+dwbh;
	//window.open(&quot;show.sp?grdid=RJZ_DWMXZ&yyyy=&quot;+cur_yyyy+&quot;&dwbh=&quot;+dwbh);
	window.parent.parent.parent.j_openWindow(10,10,1000,-1,url,&quot;参保对象明细账&quot;,&quot;参保对象明细账&quot;);

}

//新增日记帐
function rjzNew()
{
	//检查是否已经结账
	var jz_yy = G_LOGDAT.split(&quot;-&quot;)[0];
	var jz_mm = G_LOGDAT.split(&quot;-&quot;)[1]*1;
	var jz_sbh = G_ORGID;
	var jz_zth = G_ACCID.replace(G_ORGID,&quot;&quot;);

	var param = new Object();
	param.yy = jz_yy;
	param.mm = jz_mm;
	param.sbh = jz_sbh;
	param.zth = jz_zth;
	var jz_retVal = invokeOSFuncExt(&quot;check_jz&quot;,param);
	var jz_ret = jz_retVal.split(&quot;@&quot;)[0];
	var jz_msg = jz_retVal.split(&quot;@&quot;)[1];
	if(jz_ret != 1 ){
		alert(jz_retVal);
		return false;
	}

	if (g_modifyflag == 1) {
		if (confirm(&quot;日记帐已被修改，是否先进行保存？&quot;) == true) {
			rjzSave();
		}		
	}

	//window.open(&quot;show.sp?grdid=RJZ_LR&LSH=&YM1=&YM2=&quot;);	

	var obj = new Object();
	obj.yy    = cur_yyyy;
	obj.mm    = cur_mm;
	obj.dd    = G_LOGDAT.split(&quot;-&quot;)[2];
	obj.kmbh  = cur_kmbh;

	var retVal = window.showModalDialog(&quot;show.sp?grdid=RJZ_LR&quot;,obj,&quot;dialogWidth=1300px;dialogHeight=800px;&quot;);
	if (retVal != &quot;&quot; && retVal != &quot;undefined&quot; && retVal != undefined) {
		return false;
	}
	loadDataYhrjzb(cur_sbh,cur_zth,cur_yyyy,cur_mm,cur_kmbh,&quot;&quot;);

}

//修改款项
function rjzModifyMX(){
	//检查是否已经结账
	var jz_yy = G_LOGDAT.split(&quot;-&quot;)[0];
	var jz_mm = G_LOGDAT.split(&quot;-&quot;)[1]*1;
	var jz_sbh = G_ORGID;
	var jz_zth = G_ACCID.replace(G_ORGID,&quot;&quot;);

	var param = new Object();
	param.yy = jz_yy;
	param.mm = jz_mm;
	param.sbh = jz_sbh;
	param.zth = jz_zth;
	var jz_retVal = invokeOSFuncExt(&quot;check_jz&quot;,param);
	var jz_ret = jz_retVal.split(&quot;@&quot;)[0];
	var jz_msg = jz_retVal.split(&quot;@&quot;)[1];
	if(jz_ret != 1 ){
		alert(jz_retVal);
		return false;
	}

	if (rjzModifyCheck() == false) return;
	var currow = _this.GetCurrentRow(sheet_yhrjzb);
	var sblsh  = _this.GetCellText(sheet_yhrjzb,currow,11);
	var kmbh   = _this.GetCellText(sheet_yhrjzb,currow,17);
	var pch    = _this.GetCellText(sheet_yhrjzb,currow,4);

	var checkRet = checkCzyxm(currow );
	if(checkRet != 1) return false;

	if(sblsh == &quot;&quot;) return false;

	_sql.querytods(&quot;rjzmxModify&quot;,&quot;SBH=&quot;+cur_sbh+&quot;&ZTH=&quot;+cur_zth+&quot;&KMBH=&quot;+kmbh+&quot;&SBLSH=&quot;+sblsh);
	if(_this.XMLDS_GetRowCount()&gt;0){
		var lsh   = _this.XMLDS_GetString(0,&quot;LSH&quot;);		
		var dylsh = _this.XMLDS_GetString(0,&quot;DYLSH&quot;);
		var yy    = _this.XMLDS_GetString(0,&quot;YY&quot;);
		var mm    = _this.XMLDS_GetString(0,&quot;MM&quot;);
		var dd    = _this.XMLDS_GetString(0,&quot;DD&quot;);		
		var jsfs  = _this.XMLDS_GetString(0,&quot;JSFS&quot;);
		var dwbh  = _this.XMLDS_GetString(0,&quot;DWBH&quot;);
		var dwmc  = _this.XMLDS_GetString(0,&quot;DWMC&quot;);
		var dwlxbh = _this.XMLDS_GetString(0,&quot;DWLXBH&quot;);
		var je    = _this.XMLDS_GetString(0,&quot;JE&quot;);
		var jefx  = _this.XMLDS_GetString(0,&quot;JEFX&quot;);
		var ym1   = _this.XMLDS_GetString(0,&quot;YM1&quot;);
		var ym2   = _this.XMLDS_GetString(0,&quot;YM2&quot;);
		var zy    = _this.XMLDS_GetString(0,&quot;ZY&quot;);
		var zyrymd= _this.XMLDS_GetString(0,&quot;ZYRYMD&quot;);

		var obj = new Object();
		obj.yy    = yy;
		obj.mm    = mm;
		obj.dd    = dd;
		obj.kmbh  = kmbh;
		obj.pch   = pch;
		obj.jsfs  = jsfs;
		obj.dwbh  = dwbh;
		obj.dwmc  = dwmc;
		obj.dwlxbh = dwlxbh;
		obj.zy    = zy;
		obj.je    = je*1.00;
		obj.jefx  = jefx;
		obj.zyrymd= zyrymd;
		obj.sblsh = sblsh;

		//alert(&quot;show.sp?grdid=RJZ_LR&LSH=&quot;+lsh+&quot;&YM1=&quot;+ym1+&quot;&YM2=&quot;+ym2+&quot;&xgkxbz=2&DYLSH=&quot;+dylsh);
		var retVal = window.showModalDialog(&quot;show.sp?grdid=RJZ_LR&LSH=&quot;+lsh+&quot;&YM1=&quot;+ym1+&quot;&YM2=&quot;+ym2+&quot;&xgkxbz=2&DYLSH=&quot;+dylsh,obj,&quot;dialogWidth=1300px;dialogHeight=800px;&quot;);
		if (retVal != &quot;&quot; && retVal != &quot;undefined&quot; && retVal != undefined) {
			return false;
		}
		loadDataYhrjzb(cur_sbh,cur_zth,cur_yyyy,cur_mm,cur_kmbh,&quot;&quot;);
	}else{
		alert(&quot;查询无此数据,流水号:&quot;+sblsh);
		return false;
	}

}
//修改检查
function rjzModifyCheck()
{
	var currow = _this.GetCurrentRow(sheet_yhrjzb);
	if (currow &lt;= 0) return false;
	var qcbz = _this.GetCellText(sheet_yhrjzb,currow,42); 
	var pzh  = _this.GetCellText(sheet_yhrjzb,currow,5); 
	var djh  = _this.GetCellText(sheet_yhrjzb,currow,12);
	var dzbz  = _this.GetCellText(sheet_yhrjzb,currow,45); //对账标志
	if (dzbz == &quot;1&quot;) {
		alert(&quot;银社对账是平的，不允许修改！&quot;);
		return false;
	}
	else if (dzbz == &quot;2&quot;) {
		alert(&quot;银社对账结果是【银行无社保有】，不允许修改但可以删除！&quot;);
		return false;
	}
	else if (dzbz == &quot;&quot;) {
		if(djh != &quot;&quot;){
			alert(&quot;跟业务数据有关的记录不允许修改款项，单据号：&quot;+djh);
			return false;
		}
	}

	if (pzh != &quot;&quot;) {
		alert(&quot;当前日记帐纪录已出凭证不能修改!\n请跟上级领导审批请示?&quot;);
		return false;
	}
	if (qcbz == &quot;1&quot;) {
		alert(&quot;上年结转或上月结转不能修改!&quot;);
		return false;
	}

	return true;
}

