//插入
function rjzInsert(){
	//检查是否已经结账
	var jz_yy = G_LOGDAT.split(&quot;-&quot;)[0];
	var jz_mm = G_LOGDAT.split(&quot;-&quot;)[1]*1;
	var jz_sbh = G_ORGID;
	var jz_zth = G_ACCID.replace(G_ORGID,&quot;&quot;);

	var param = new Object();
	param.yy = jz_yy;
	param.mm = jz_mm;
	param.sbh = jz_sbh;
	param.zth = jz_zth;
	var jz_retVal = invokeOSFuncExt(&quot;check_jz&quot;,param);
	var jz_ret = jz_retVal.split(&quot;@&quot;)[0];
	var jz_msg = jz_retVal.split(&quot;@&quot;)[1];
	if(jz_ret != 1 ){
		alert(jz_retVal);
		return false;
	}

	if (g_modifyflag == 1) {
		alert(&quot;日记帐已修改，请先保存！&quot;);
		return;
	}
	var currow = _this.GetCurrentRow(sheet_yhrjzb);
	if (currow &lt; 1) return;
	var insertsxh = _this.GetCellText(sheet_yhrjzb,currow,41); //做账顺序号
	if(insertsxh == &quot;&quot; || insertsxh == &quot;0&quot;) return false;

	var obj = new Object();
