	obj.jsfs = &quot;&quot;;
	var retVal = window.showModalDialog(&quot;show.sp?grdid=RJZ_LR&INSERTSXH=&quot;+insertsxh,obj,&quot;dialogWidth=1200px;dialogHeight=800px;&quot;);
	if (retVal != &quot;&quot; && retVal != &quot;undefined&quot; && retVal != undefined) {

	}
	loadDataYhrjzb(cur_sbh,cur_zth,cur_yyyy,cur_mm,cur_kmbh,&quot;&quot;);
}
//删除
function rjzDelete(){
	//检查是否已经结账
	var jz_yy = G_LOGDAT.split(&quot;-&quot;)[0];
	var jz_mm = G_LOGDAT.split(&quot;-&quot;)[1]*1;
	var jz_sbh = G_ORGID;
	var jz_zth = G_ACCID.replace(G_ORGID,&quot;&quot;);

	var param = new Object();
	param.yy = jz_yy;
	param.mm = jz_mm;
	param.sbh = jz_sbh;
	param.zth = jz_zth;
	var jz_retVal = invokeOSFuncExt(&quot;check_jz&quot;,param);
	var jz_ret = jz_retVal.split(&quot;@&quot;)[0];
	var jz_msg = jz_retVal.split(&quot;@&quot;)[1];
	if(jz_ret != 1 ){
		alert(jz_retVal);
		return false;
	}

	var selcnt = 0;
	var selectlsh = &quot;&quot;;
	var mkjmbz = 0;
	for (var row=_this.GetMainCellRangeRow1(sheet_yhrjzb);row&lt;=_this.GetMainCellRangeRow2(sheet_yhrjzb);row++) {
		var flg    = _this.GetCellText(sheet_yhrjzb,row,0);
		var ysywlx = _this.GetCellText(sheet_yhrjzb,row,27);
		var pzh    = _this.GetCellText(sheet_yhrjzb,row,5);
		var qcbz   = _this.GetCellText(sheet_yhrjzb,row,42);

		if (flg == 1) {
			//校验是否本人记录
			var checkRet = checkCzyxm(row);
			if(checkRet != 1) return false;

			selcnt ++;
			if (selectlsh == &quot;&quot;) selectlsh += _this.GetCellText(sheet_yhrjzb,row,11);
			else selectlsh += &quot;,&quot; + _this.GetCellText(sheet_yhrjzb,row,11);

			if (pzh != &quot;&quot;) {
				alert(&quot;当前日记帐纪录已出凭证，不能删除，请核对！&quot;);
				return;
			}
			if (qcbz == 1) {
				alert(&quot;第一条记录为上年结转或上月结转时不能删除，请核对！&quot;);
				return;
			}
			if(ysywlx != &quot;&quot;){
				var dzbz  = _this.GetCellText(sheet_yhrjzb,row,45); //对账标志
				if (dzbz != &quot;2&quot;) { //对账结果是银行无社保有的才允许删除
					alert(&quot;银社对账结果不为【银行无社保有】，不允许删除！&quot;);
					return;
				}

				alert(&quot;第&quot;+row+&quot;行选中记录为银社直接数据，不允许删除！&quot;);
				return;
			}
		}
	}
	if (selcnt == 0) {
		alert(&quot;没有选中行！&quot;);
		return;
	}
	if (!confirm(&quot;删除所选中的行，是否继续？&quot;)) return;

	try {
		var param = new Object();
		param.thisorgid = G_ORGID;
		param.thisaccid = G_ACCID;
		param.sblshs = selectlsh;
		param.usrid = G_USRID;
		param.czyxm = G_USRNAM;
//		param.is_dzxz = is_dzxz;
		param.sbh = cur_sbh;
		param.zth = cur_zth;
		param.kmbh = cur_kmbh;
		param.doyy = cur_yyyy;
		param.domm = cur_mm;

		var ret = invokeOSFuncExt(&quot;Delete&quot;,param);
		if ( 1*ret &gt; 0) {
			alert(&quot;删除日记帐成功！操作记录数&quot;+ret);
			loadDataYhrjzb(cur_sbh,cur_zth,cur_yyyy,cur_mm,cur_kmbh,&quot;&quot;);
		}
		else {
			alert(&quot;删除失败！出错信息：&quot;+ret);
		}
	}
	catch (e) {
		alert(e.toString());
	}
}
//保存
function rjzSave()
{
	//检查是否已经结账
	var jz_yy = G_LOGDAT.split(&quot;-&quot;)[0];
	var jz_mm = G_LOGDAT.split(&quot;-&quot;)[1]*1;
	var jz_sbh = G_ORGID;
	var jz_zth = G_ACCID.replace(G_ORGID,&quot;&quot;);

	var param = new Object();
	param.usrid = G_USRID;
	param.usrnam = G_USRNAM;
	param.thisaccid = G_ACCID;
	param.thisorgid = G_ORGID;
	param.yy = jz_yy;
	param.mm = jz_mm;
	param.kmbh = cur_kmbh;
	param.sbh = jz_sbh;
	param.zth = jz_zth;
	param.rjzxml = getRJZSaveXml();

	var jz_retVal = invokeOSFuncExt(&quot;check_jz&quot;,param);
	var jz_ret = jz_retVal.split(&quot;@&quot;)[0];
	var jz_msg = jz_retVal.split(&quot;@&quot;)[1];
	if(jz_ret != 1 ){
		alert(jz_retVal);
		return false;
	}

	try {
		var ret = invokeOSFuncExt(&quot;Save&quot;,param);
		if (ret == 1) {
			alert(&quot;保存成功！&quot;);
			g_modifyflag = 0;
			loadDataYhrjzb(cur_sbh,cur_zth,cur_yyyy,cur_mm,cur_kmbh,&quot;&quot;);
		}
		else if (ret == 0) {
			alert(&quot;没有内容修改！&quot;);
		}
		else {
			alert(&quot;保存日记帐出错:&quot;+ret);
		}
	}
	catch (e) {
		alert(&quot;保存日记帐出错:&quot;+e.toString());
	}

}

//获取修改的数据xml
function getRJZSaveXml()
{
	var xml = &quot;&lt;?xml version = &apos;1.0&apos;?&gt;&lt;ROWSET&gt;\n&quot;;
	var num = 0;
	for (var r=_this.GetMainCellRangeRow1(sheet_yhrjzb);r&lt;=_this.GetMainCellRangeRow2(sheet_yhrjzb);r++) {
		var flag = _this.GetCellText(sheet_yhrjzb,r,40);
		var yy = _this.GetCellText(sheet_yhrjzb,r,1);
		if (flag == &quot;*&quot; && yy != &quot;&quot;) {
			num ++;
			xml += &quot;&lt;ROW num=\&quot;&quot;+num+&quot;\&quot;&gt;\n&quot;;
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_yhrjzb,1)+&quot;&gt;&quot; + _this.GetCellText(sheet_yhrjzb,r,1) +&quot;&lt;/&quot;+_this.GetColName(sheet_yhrjzb,1)+&quot;&gt;\n&quot;;	//年
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_yhrjzb,2)+&quot;&gt;&quot; + _this.GetCellText(sheet_yhrjzb,r,2) +&quot;&lt;/&quot;+_this.GetColName(sheet_yhrjzb,2)+&quot;&gt;\n&quot;;	//月
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_yhrjzb,3)+&quot;&gt;&quot; + _this.GetCellText(sheet_yhrjzb,r,3) +&quot;&lt;/&quot;+_this.GetColName(sheet_yhrjzb,3)+&quot;&gt;\n&quot;;	//日
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_yhrjzb,6)+&quot;&gt;&quot; + _this.GetCellText(sheet_yhrjzb,r,6) +&quot;&lt;/&quot;+_this.GetColName(sheet_yhrjzb,6)+&quot;&gt;\n&quot;;	//摘要
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_yhrjzb,11)+&quot;&gt;&quot; + _this.GetCellText(sheet_yhrjzb,r,11) +&quot;&lt;/&quot;+_this.GetColName(sheet_yhrjzb,11)+&quot;&gt;\n&quot;;	//社保流水号
			xml += &quot;&lt;/ROW&gt;\n&quot;;		
		}
	}
	xml += &quot;&lt;/ROWSET&gt;&quot;;
	return xml;
}
//变更选择颜色
function setOneRowBkColor(sheet,row,r,g,b)
{
	for (var c=0;c&lt;_this.GetColCount(sheet);c++) {
		_this.SetCellBkColor(sheet,row,c,r,g,b);
	}
	_this.SetCellFocus(sheet,row,0);
	_this.Redraw();
}

//重算余额
function check_next_qcye()
{
//	var kmbh = _this.GetCellText(sheet_param,1,6);
//	var yy = _this.GetCellText(sheet_param,1,1);
//	var mm = _this.GetCellText(sheet_param,1,3);
	if (cur_kmbh == &quot;&quot;) {
		alert(&quot;请选择科目！&quot;);
		return;
	}
	if (!confirm(&quot;余额重算，是否继续？&quot;)) return;
	var ret = invokeOSFunc(&quot;CheckNextQcye&quot;,&quot;thisorgid=&quot;+G_ORGID+&quot;&thisaccid=&quot;+G_ACCID+&quot;&kmbh=&quot;+cur_kmbh+&quot;&yy=&quot;+cur_yyyy+&quot;&mm=&quot;+cur_mm);
	if (ret == 0) {
		alert(&quot;重算余额成功！&quot;);
	}
	else {
		alert(&quot;重算余额失败！&quot;+ret);
	}
}

//余额检查 typ1:从启动年月检查 typ2:从当前年月检查
function yejc(typ)
{
       var ret = &quot;&quot;;
       var dqyy = _this.GetCellText(sheet_param,1,1);
       var dqmm = _this.GetCellText(sheet_param,1,3);
       var kmbh = _this.GetCellText(sheet_param,1,6);
       var sbh = G_ORGID;
       var zth = G_ACCID.replace(sbh,&quot;&quot;);
       if (kmbh == &quot;&quot;) {
	   alert(&quot;请选择科目！&quot;);
	   return;
       }              
       ret = _this.invokeOSFunc(&quot;yejc&quot;,&quot;typ=&quot;+typ+&quot;&dqyy=&quot;+dqyy+&quot;&dqmm=&quot;+dqmm+&quot;&kmbh=&quot;+kmbh+&quot;&sbh=&quot;+sbh+&quot;&zth=&quot;+zth+&quot;&thisorgid=&quot;+G_ORGID+&quot;&thisaccid=&quot;+G_ACCID);
       if(ret == 0) {
           alert(&quot;余额检查完成&quot;);
       }
       else {
           alert(&quot;余额检查失败，出错信息：&quot;+ret);
       }
}

//金额检查
function jejc()
{      
       var kmbh = _this.GetCellText(sheet_param,1,6);
       if (kmbh == &quot;&quot;) {
	   alert(&quot;请选择科目！&quot;);
	   return;
       }         
       var m = 0; //检查的记录数
       var n = 0; //出错记录数
       for(var row=1;row&lt;_this.GetRowCount(0)-1;row++) {
           var siseqno = _this.GetCellText(0,row,11);
           if(siseqno== &quot;&quot;) continue;

           var ret = &quot;&quot;;
           var zy = _this.GetCellText(0,row,6);           
           var param = new Object();
           param.sbh = G_ORGID;
           param.zth = G_ACCID.replace(G_ORGID,&quot;&quot;);
           param.siseqno = siseqno;
           param.zy = zy;
           //param.je = Math.abs(1.0*_this.GetCellText(0,row,9)-1.0*_this.GetCellText(0,row,10));
           param.jefx = _this.GetCellText(0,row,9);
           if (param.jefx == &quot;借&quot;) {
           	param.je = _this.GetCellText(0,row,7);
           }
           else {
           	param.je = _this.GetCellText(0,row,8);
           }

           ret = invokeOSFuncExt(&quot;jejc&quot;,param);
           if(ret == 0) continue;           
           else if(ret.indexOf(&quot;出错信息&quot;) != -1) {
                 alert(ret);
                 return;
           }
           else if(ret == 1) {
                m++;
           }
           else {
                m++;
                n++;
                var bool = window.confirm( &quot;第&quot;+row+&quot;笔记录&quot;+ret+&quot;是否确定继续检查？&quot;);
                if(bool) continue;
                else break;
           }
      }
       alert(&quot;共检查&quot;+m+&quot;笔日记账\r\n其中有：&quot;+n+&quot;笔金额不对&quot;);
}

//鼠标双击
function _thisOnMouseDClick(sheet,row,col)
{
	if (sheet == sheet_yhrjzb && row &gt;= _this.GetMainCellRangeRow1(sheet_yhrjzb) && row &lt;= _this.GetMainCellRangeRow2(sheet_yhrjzb) && col != 6) {
		selectOneRow(sheet_yhrjzb,row);
	}
}


function selectOneRow(sheet,row)
{
	var flg = _this.GetCellText(sheet,row,0);
	if (flg == 1) {
		_this.SetCellText(sheet,row,0,0);
		setOneRowBkColor(sheet,row,255,255,255);		
	}
	else {
		_this.SetCellText(sheet,row,0,1);
		setOneRowBkColor(sheet,row,255,236,139);
	}
}

function setOneRowBkColor(sheet,row,r,g,b)
{
	for (var c=0;c&lt;_this.GetColCount(sheet);c++) {
		_this.SetCellBkColor(sheet,row,c,r,g,b);
	}
	_this.SetCellFocus(sheet,row,0);
	_this.Redraw();
}

function checkCzyxm(row){
	var czyxm = _this.GetCellText(sheet_yhrjzb,row,18);
	var ysywlx = _this.GetCellText(sheet_yhrjzb,row,27);
	if(czyxm != G_USRNAM && ysywlx == &quot;&quot;){
		alert(&quot;不是本人日记账，不允许操作！&quot;);
		return -1;
	}

	return 1;
}

function rjzModifyKMBH() {
	//检查是否已经结账
	var jz_yy = G_LOGDAT.split(&quot;-&quot;)[0];
	var jz_mm = G_LOGDAT.split(&quot;-&quot;)[1]*1;
	var jz_sbh = G_ORGID;
	var jz_zth = G_ACCID.replace(G_ORGID,&quot;&quot;);

	var param = new Object();
	param.yy = jz_yy;
	param.mm = jz_mm;
	param.sbh = jz_sbh;
	param.zth = jz_zth;
	var jz_retVal = invokeOSFuncExt(&quot;check_jz&quot;,param);
	var jz_ret = jz_retVal.split(&quot;@&quot;)[0];
	var jz_msg = jz_retVal.split(&quot;@&quot;)[1];
	if(jz_ret != 1 ){
		alert(jz_retVal);
		return false;
	}		
	var kmbh = _this.GetCellText(3,1,6);
	if(kmbh == &quot;&quot;) {
		alert(&quot;科目编号不能为空&quot;);
		return;
	}

	var xml = &quot;&lt;?xml version = &apos;1.0&apos;?&gt;\n&lt;ROWSET&gt;\n&quot;;
	var num = 0;
	var minyy = &quot;&quot;;
	var mindd = &quot;&quot;;
	var minmm = &quot;&quot;;
	for (var r=_this.GetMainCellRangeRow1(sheet_yhrjzb);r&lt;=_this.GetMainCellRangeRow2(sheet_yhrjzb);r++) {
		var flag = _this.GetCellText(sheet_yhrjzb,r,0);
		var yy = _this.GetCellText(sheet_yhrjzb,r,1);
		if (flag == &quot;1&quot; && yy != &quot;&quot;) {
			num ++;
			var ysywlx = _this.GetCellText(sheet_yhrjzb,r,27);
			if(ysywlx != &quot;&quot;) {
				alert(&quot;银社数据不能修改!&quot;);
				return false;
			}
			var qcbz = _this.GetCellText(sheet_yhrjzb,r,42);
			if(qcbz == &quot;1&quot;) {
				alert(&quot;期初余额不能修改!&quot;);
				return false;				
			}
			xml += &quot;&lt;ROW num=\&quot;&quot;+num+&quot;\&quot;&gt;\n&quot;;
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_yhrjzb,1)+&quot;&gt;&quot; + _this.GetCellText(sheet_yhrjzb,r,1) +&quot;&lt;/&quot;+_this.GetColName(sheet_yhrjzb,1)+&quot;&gt;\n&quot;;	//年
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_yhrjzb,2)+&quot;&gt;&quot; + _this.GetCellText(sheet_yhrjzb,r,2) +&quot;&lt;/&quot;+_this.GetColName(sheet_yhrjzb,2)+&quot;&gt;\n&quot;;	//月
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_yhrjzb,3)+&quot;&gt;&quot; + _this.GetCellText(sheet_yhrjzb,r,3) +&quot;&lt;/&quot;+_this.GetColName(sheet_yhrjzb,3)+&quot;&gt;\n&quot;;	//日
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_yhrjzb,6)+&quot;&gt;&quot; + _this.GetCellText(sheet_yhrjzb,r,6) +&quot;&lt;/&quot;+_this.GetColName(sheet_yhrjzb,6)+&quot;&gt;\n&quot;;	//摘要
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_yhrjzb,11)+&quot;&gt;&quot; + _this.GetCellText(sheet_yhrjzb,r,11) +&quot;&lt;/&quot;+_this.GetColName(sheet_yhrjzb,11)+&quot;&gt;\n&quot;;	//社保流水号
			xml += &quot;&lt;/ROW&gt;\n&quot;;	
			if(minyy == &quot;&quot;) {
				minyy = _this.GetCellText(sheet_yhrjzb,r,1);
			}
			if(minmm == &quot;&quot;) {
				minmm = _this.GetCellText(sheet_yhrjzb,r,2);
			}	
			if(mindd == &quot;&quot;) {
				mindd = _this.GetCellText(sheet_yhrjzb,r,3);
			}
		}
	}	
	xml += &quot;&lt;/ROWSET&gt;&quot;;
	if(num == 0) {
		alert(&quot;请选择记录再修改!&quot;);
		return;
	}
	var obj = new Object();
	obj.in_xml = xml;	
	var retVal = window.showModalDialog(&quot;show.sp?grdid=RJZ_KMBHEdit&in_kmbh=&quot;+kmbh+&quot;&in_yy=&quot;+minyy+&quot;&in_mm=&quot;+minmm+&quot;&in_dd=&quot;+mindd,obj,&quot;dialogWidth=500px;dialogHeight=200px;&quot;);	
	if(retVal == 1) {
		loadDataYhrjzb(cur_sbh,cur_zth,cur_yyyy,cur_mm,cur_kmbh,&quot;&quot;);
	}	

}

ROW.GRDOSDS_VALUE:
var pub = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);
//检查结账
function check_jz(){
var Pub = new SBCW_CW_PUBLIC();
var retVal = Pub.Pub_check_jz(sbh,zth,yy,mm);
return retVal;
}

//保存日记帐修改
function Save()
{
	var db = null;
	var sql = &quot;&quot;;

	try {
		db = new pub.EADatabase();
		var ds = new pub.EAXmlDS(rjzxml);
		if (ds.getRowCount() &lt;= 0) return &quot;0&quot;;
		for (var i=0;i&lt;ds.getRowCount();i++) {
			var yy = ds.getStringAt(i,&quot;YY&quot;);
			var mm = ds.getStringAt(i,&quot;MM&quot;);
			var dd = ds.getStringAt(i,&quot;DD&quot;);
			var zy = ds.getStringAt(i,&quot;ZY&quot;);
			var sblsh = ds.getStringAt(i,&quot;SISEQNO&quot;);
			sql = &quot;update cw_yhrjzb set dd=&apos;&quot;+dd+&quot;&apos;,zy=&apos;&quot;+zy+&quot;&apos; where sbh=&apos;&quot;+sbh+&quot;&apos; and zth=&apos;&quot;+zth+&quot;&apos; and siseqno=&apos;&quot;+sblsh+&quot;&apos;&quot;;
			db.ExcecutSQL(sql);

			sql = &quot;update cw_rjzb set dd=&apos;&quot;+dd+&quot;&apos;,zy=&apos;&quot;+zy+&quot;&apos; where sbh=&apos;&quot;+sbh+&quot;&apos; and zth=&apos;&quot;+zth+&quot;&apos; and siseqno=&apos;&quot;+sblsh+&quot;&apos;&quot;;
			db.ExcecutSQL(sql);

		}

		//记录操作日志
		var note = &quot;保存日记帐修改：&quot;+yy+&quot;年&quot;+mm+&quot;月&quot;+&quot;，日记帐科目：&quot;+kmbh;
		sql = &quot;insert into oplog(grdid,type,action,opdat,opusr,opusrnam,note,acc,syt,org)
			values(&apos;RJZ_YHRJZB&apos;,&apos;日记帐修改&apos;,&apos;保存日记帐修改&apos;,sysdate,&apos;&quot;+usrid+&quot;&apos;,&apos;&quot;+usrnam+&quot;&apos;,&apos;&quot;+note+&quot;&apos;,&apos;&quot;+thisaccid+&quot;&apos;,&apos;SBCW&apos;,&apos;&quot;+thisorgid+&quot;&apos;)&quot;;
		db.ExcecutSQL(sql);

		//重算余额
		try { checkRjzYE(sbh,zth,kmbh,yy,mm); } catch (e) { }

		db.Commit();
		return &quot;1&quot;;
	}
	catch (e) {
		if (db != null) db.Rollback();
		return e.toString();	
	}
	finally {
		if (db != null) db.Close();
	}
}

//日记账删除
function Delete()
{
var db = null;
var retcnt = 0;
var sql = &quot;&quot;;
try {
	db = new pub.EADatabase();

	var iuo_ywjk = new SBCW_IYWJK(); //业务接口类
	var ywjkbz = iuo_ywjk.getYWJKBZ(db,thisaccid);
	//获取统筹区编码		
	sql = &quot;select tcqbm from cw_sbsb where sbh=&apos;&quot;+thisorgid+&quot;&apos;&quot;;
	var tcqbm = db.GetSQL(sql);
	//查询数据
	sql = &quot;select a.*,trim(to_char(a.mm,&apos;00&apos;)) mm2,trim(to_char(a.dd,&apos;00&apos;)) dd2 from cw_yhrjzb a where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and kmbh = &apos;&quot;+kmbh+&quot;&apos;
		and siseqno in (&quot;+pub.EAFunc.SQLIN(sblshs)+&quot;) ORDER BY A.DD, A.SXH, A.SISEQNO&quot;;
	var ds = db.QuerySQL(sql);
	for(var i = 0;i&lt;ds.getRowCount();i++){
		var yy     = ds.getStringAt(i,&quot;YY&quot;);
		var mm     = ds.getStringAt(i,&quot;MM&quot;);
		var dd     = ds.getStringAt(i,&quot;DD&quot;);
		var mm2    = ds.getStringAt(i,&quot;MM2&quot;);
		var dd2    = ds.getStringAt(i,&quot;DD2&quot;);
		var je     = 1.0 * ds.getStringAt(i,&quot;JE&quot;);	//日记账总金额
		var djh    = ds.getStringAt(i,&quot;DJH&quot;);
		var qcbz   = ds.getStringAt(i,&quot;QCBZ&quot;); 		//期初标志
		var fkbz   = ds.getStringAt(i,&quot;FKBZ&quot;); 		//分款标志
		var pzh    = ds.getStringAt(i,&quot;PZH&quot;);   	//凭证号
		var kmbh   = ds.getStringAt(i,&quot;KMBH&quot;);
		var sblsh  = ds.getStringAt(i,&quot;SISEQNO&quot;);
		var ysywlx = ds.getStringAt(i,&quot;YSZL_YWLX&quot;);	//银社直连业务类型
		var ywhtbz = ds.getStringAt(i,&quot;DYYWJK_LX&quot;);	//业务回退标志（调用业务接口）

		if (qcbz == &quot;1&quot;) {
			db.Rollback();
			return &quot;流水号:&quot;+sblsh+&quot;记录为上年结转或上月结转时不能删除，请核对！&quot;;
		}
		if (pzh != &quot;&quot;) {
			db.Rollback();
			return &quot;流水号:&quot;+sblsh+&quot;当前日记帐纪录已出凭证，不能删除，请核对！&quot;;
		}
		if (ysywlx != &quot;&quot; ){
			db.Rollback();
			return &quot;流水号:&quot;+sblsh+&quot;为银社直连数据，不允许删除&quot;;
		}

		//获取模块界面标志  录单笔返回多行记录
		sql = &quot;select distinct nvl(max(mkjmbz),&apos;&apos;) mkjmbz from cw_rjzb where sbh = &apos;&quot;+sbh+&quot;&apos; and ZTH = &apos;&quot;+zth+&quot;&apos; and siseqno = &apos;&quot;+sblsh+&quot;&apos;&quot;;
		var mkjmbz = db.GetSQL(sql);

		var ls_hkrq = yy+mm2+dd2;
		var ls_scny = yy+mm2; //取生成年月
//			throw new Exception(djh);
		if(djh != &quot;&quot;){	//与业务系统及银社直连有关数据
//				var djh_len = djh.length();
			if(ywhtbz != &quot;&quot;){
				if(mkjmbz == &quot;3&quot;){
					//判断是否存在日记账明细
					sql = &quot;select count(1) from cw_rjzmxb where sbh = &apos;&quot;+sbh+&quot;&apos; and ZTH = &apos;&quot;+zth+&quot;&apos; 
						and lsh in (select lsh from cw_rjzb where sbh = &apos;&quot;+sbh+&quot;&apos; and ZTH = &apos;&quot;+zth+&quot;&apos; and siseqno = &apos;&quot;+sblsh+&quot;&apos;)&quot;;
					var mxcou = db.GetSQL(sql);
					if(mxcou &lt;= 0){
						db.Rollback();
						return &quot;流水号:&quot;+sblsh+&quot;没有日记账明细，不允许删除,请到[日记管理-&gt;未分款界面]删除！！！！&quot;;
					}
					//获取原录入的摘要
					sql = &quot;select zy,dwbh from v_si_djb_tmp where djh=&quot;+sblsh+&quot; and sbh = &apos;&quot;+sbh+&quot;&apos; and ZTH = &apos;&quot;+zth+&quot;&apos; and zbbz = &apos;1&apos;&quot;;
					var zy_ds = db.QuerySQL(sql);
					var old_zy = zy_ds.getStringAt(0,&quot;ZY&quot;);
					var dwbh   = zy_ds.getStringAt(0,&quot;DWBH&quot;);

					//判断是否挂暂存
					sql = &quot;select count(1) cou,sum(round(je,2)) je from cw_rjzmxb where sbh = &apos;&quot;+sbh+&quot;&apos; and ZTH = &apos;&quot;+zth+&quot;&apos; 
						and lxbh = &apos;87&apos; and je &gt; 0 and lsh in (select lsh from cw_rjzb where sbh = &apos;&quot;+sbh+&quot;&apos; and ZTH = &apos;&quot;+zth+&quot;&apos; and siseqno = &apos;&quot;+sblsh+&quot;&apos;)&quot;;
					var mx_ds = db.QuerySQL(sql);					
					var mxcou1 = mx_ds.getStringAt(0,&quot;COU&quot;);	//判断是否挂账
					var gzje   = mx_ds.getStringAt(0,&quot;JE&quot;);		//挂账金额						
					if(mxcou1 &gt; 0){	//挂暂存
						//判断是否使用过暂存款
						sql = &quot;select count(1) from v_si_djb_tmp where djh = &apos;&quot;+djh+&quot;&apos; and sbh = &apos;&quot;+sbh+&quot;&apos; and ZTH = &apos;&quot;+zth+&quot;&apos; and zbbz = 0 and lxbh = &apos;87&apos; 
							HAVING sum(NVL(JE1, 0) +  NVL(JE2, 0) +  NVL(JE3, 0) +  NVL(JE4, 0) +  NVL(JE5, 0) +  
								NVL(JE6, 0) +  NVL(JE7, 0) +  NVL(JE8, 0) +  NVL(JE9, 0) +  NVL(JE10,0) +  
								NVL(JE11,0) +  NVL(JE12,0) +  NVL(JE13,0) +  NVL(JE14,0) +  NVL(JE15,0)) &lt; 0&quot;;
						var zcksy_ds = db.QuerySQL(sql);
						if(zcksy_ds.getRowCount() &gt; 0 ){
							db.Rollback();
							return &quot;单据号&quot;+sblsh+&quot;已使用过暂存款，不允许回退!!!&quot;;
						}

						if(1.0*gzje == 1.0*je){
							sql = &quot;update v_si_djb_tmp set lsh = null,rzbz = &apos;0&apos;,rzr = null,rzsj = null,zfbz = &apos;1&apos;,zfr = &apos;&quot;+czyxm+&quot;&apos;,zfsj=sysdate,fzbz = &apos;0&apos; where djh=&apos;&quot;+djh+&quot;&apos; and sbh = &apos;&quot;+sbh+&quot;&apos; and ZTH = &apos;&quot;+zth+&quot;&apos; and lsh =  &apos;&quot;+sblsh+&quot;&apos; and zbbz = 0&quot;;
							db.ExcecutSQL(sql);

							sql = &quot;update cw_rjzb set fkbz =&apos;0&apos;,czlx = &apos;&apos;,gzlx = &apos;&apos; where siseqno=&apos;&quot;+sblsh+&quot;&apos; and sbh = &apos;&quot;+sbh+&quot;&apos; and ZTH = &apos;&quot;+zth+&quot;&apos;&quot;;
							db.ExcecutSQL(sql);

							sql = &quot;update cw_yhrjzb set zy = &apos;&quot;+old_zy+&quot;&apos;,fkbz = &apos;0&apos;,fkrxm = &apos;&apos;,fkrq = &apos;&apos; where siseqno=&apos;&quot;+sblsh+&quot;&apos; and sbh = &apos;&quot;+sbh+&quot;&apos; and ZTH = &apos;&quot;+zth+&quot;&apos;&quot;;
							db.ExcecutSQL(sql);
						}else {
							sql = &quot;update v_si_djb_tmp set lsh = null,rzbz = &apos;0&apos;,rzr = null,rzsj = null,zfbz =decode(lxbh,87,&apos;1&apos;,&apos;0&apos;),zfr = &apos;&quot;+czyxm+&quot;&apos;,zfsj=sysdate,fzbz = &apos;1&apos; where djh=&apos;&quot;+djh+&quot;&apos; and sbh = &apos;&quot;+sbh+&quot;&apos; and ZTH = &apos;&quot;+zth+&quot;&apos; and lsh =  &apos;&quot;+sblsh+&quot;&apos; and zbbz = 0&quot;;
							db.ExcecutSQL(sql);

							sql = &quot;update cw_rjzb set fkbz =&apos;1&apos;,czlx = &apos;&apos;,gzlx = &apos;&apos; where siseqno=&apos;&quot;+sblsh+&quot;&apos; and sbh = &apos;&quot;+sbh+&quot;&apos; and ZTH = &apos;&quot;+zth+&quot;&apos;&quot;;
							db.ExcecutSQL(sql);

							sql = &quot;update cw_yhrjzb set zy = &apos;&quot;+old_zy+&quot;&apos;,fkbz = &apos;1&apos;,fkrxm = &apos;&apos;,fkrq = &apos;&apos; where siseqno=&apos;&quot;+sblsh+&quot;&apos; and sbh = &apos;&quot;+sbh+&quot;&apos; and ZTH = &apos;&quot;+zth+&quot;&apos;&quot;;
							db.ExcecutSQL(sql);
						}

						var yh_ywjk = new SBCW_YWJK_YH();
						var yhjk_msg = yh_ywjk.yhjk_PREPAY(db,ywjkbz,&quot;1&quot;,dwbh,&quot;4&quot;,gzje,ls_hkrq,czyxm,tcqbm,sblsh,sbh,zth );
						var ret = yhjk_msg.split(&quot;@&quot;)[0];
						var retmes = &quot;&quot;;//yhjk_msg.split(&quot;@&quot;)[1];
						if (yhjk_msg.split(&quot;@&quot;).length == 2) {
							retmes = yhjk_msg.split(&quot;@&quot;)[1];
						}
						if(ret!=&quot;NOERROR&quot;){
							db.Rollback();
							return &quot;1、调用征收业务接口出错！单据号:&quot;+sblsh+&quot;;出错码：&quot;+ret+&quot;出错原因:&quot;+retmes+&quot;.\n&quot;;
						}

						//删除日记账明细表记录
						sql = &quot;delete from cw_rjzmxb where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and lsh in (select lsh from cw_rjzb where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos;
							and siseqno = &apos;&quot;+sblsh+&quot;&apos;)&quot;;
						db.ExcecutSQL(sql);

					}else{		//未挂账
						sql = &quot;update v_si_djb_tmp set lsh = null,rzbz = &apos;0&apos;,rzr = null,rzsj = null,fzbz = &apos;1&apos; where djh=&apos;&quot;+djh+&quot;&apos; and sbh = &apos;&quot;+sbh+&quot;&apos; and ZTH = &apos;&quot;+zth+&quot;&apos; and lsh =  &apos;&quot;+sblsh+&quot;&apos; and zbbz = 0&quot;;
						db.ExcecutSQL(sql);

						//删除日记账明细表记录
						sql = &quot;delete from cw_rjzmxb where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and lsh in (select lsh from cw_rjzb where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos;
							and siseqno = &apos;&quot;+sblsh+&quot;&apos;)&quot;;
						db.ExcecutSQL(sql);

						sql = &quot;update cw_yhrjzb set zy = &apos;&quot;+old_zy+&quot;&apos;,fkbz = &apos;1&apos; where siseqno=&apos;&quot;+sblsh+&quot;&apos; and sbh = &apos;&quot;+sbh+&quot;&apos; and ZTH = &apos;&quot;+zth+&quot;&apos;&quot;;
						db.ExcecutSQL(sql);
					}

				}else{	
					//判断是否有记录
					sql = &quot;select a.dwbh,a.dwmc,a.djh,a.zjpch,a.ywlx,sum(je1+je2+je3+je4+je5+je6+je7+je8+je9+je10+je11+je12+je13+je14+je15) sumje from v_si_djb_tmp a 
						where LSH=&quot;+sblsh+&quot; and sbh = &apos;&quot;+sbh+&quot;&apos; and ZTH = &apos;&quot;+zth+&quot;&apos;
						group by a.dwbh,a.dwmc,a.djh,a.zjpch,a.ywlx&quot;;
					var si_ds= db.QuerySQL(sql);
					if(si_ds.getRowCount() &gt; 0){
						if( ywhtbz.substring(0,3) == &quot;ZS_&quot; ){
							var dwbh = si_ds.getStringAt(0,&quot;DWBH&quot;);	

							//银海在过程外更新数据，东软在过程内更新数据
							if(ywjkbz == &quot;YH&quot;){
								//更新SI_DJB_TMP
								sql = &quot;update v_si_djb_tmp set RZBZ=&apos;0&apos;,RZR=null,RZSJ=null,LSH=null where LSH=&quot;+sblsh+&quot; and sbh = &apos;&quot;+sbh+&quot;&apos; and ZTH = &apos;&quot;+zth+&quot;&apos; and djh = &apos;&quot;+djh+&quot;&apos;&quot;;
								db.ExcecutSQL(sql);

								//修改银行回盘AC95标志
								sql = &quot;update ac95 set prc_out_xh=&apos;&apos;,prc_out_code=&apos;&apos;,prc_out_text=&apos;&apos;,err_cwlsh=&apos;&apos; 
									where err_cwlsh=&apos;&quot;+sblsh+&quot;&apos; and zth=&apos;&quot;+zth+&quot;&apos; and aaa027=&apos;&quot;+tcqbm+&quot;&apos;&quot;;
								db.ExcecutSQL(sql);
							}

							var dsrlx = &quot;1&quot;;
							var ywlxhtbz = &quot;&quot;;

							if(ywjkbz == &quot;YH&quot;){
								dsrlx = &quot;1&quot;;
								ywlxhtbz = &quot;5&quot;;
							}else if(ywjkbz == &quot;DR&quot;){
								dsrlx = &quot;1&quot;;
								ywlxhtbz = &quot;DZHT&quot;;
							}


							var yh_ywjk = new SBCW_YWJK_YH();				
							var yhjk_msg = yh_ywjk.yhjk_PREPAY(db,ywjkbz,dsrlx ,dwbh,ywlxhtbz,je,ls_hkrq,czyxm,tcqbm,sblsh,sbh,zth );
							var ret = yhjk_msg.split(&quot;@&quot;)[0];
							var retmes = &quot;&quot;;//yhjk_msg.split(&quot;@&quot;)[1];
							if (yhjk_msg.split(&quot;@&quot;).length == 2) {
								retmes = yhjk_msg.split(&quot;@&quot;)[1];
							}
							if(ret!=&quot;NOERROR&quot;){
								db.Rollback();
								return &quot;1、调用征收业务接口出错！流水号:&quot;+sblsh+&quot;;出错码：&quot;+ret+&quot;出错原因:&quot;+retmes;
							}
						}else if( ywhtbz.substring(0,3) == &quot;DY_&quot; ){
							var sum_je = si_ds.getStringAt(0,&quot;SUMJE&quot;);
							var ywlx   = si_ds.getStringAt(0,&quot;YWLX&quot;);
							if(sum_je &gt;= 0){
								if(ywlx == &quot;E110&quot;){
									//更新SI_DJB_TMP
									sql = &quot;update v_si_djb_tmp set RZBZ=&apos;0&apos;,RZR=null,RZSJ=null,LSH=null where LSH=&quot;+sblsh+&quot; and sbh = &apos;&quot;+sbh+&quot;&apos; and ZTH = &apos;&quot;+zth+&quot;&apos; and djh = &apos;&quot;+djh+&quot;&apos;&quot;;
									db.ExcecutSQL(sql);

									//修改银行回盘AC95标志
									sql = &quot;update ac95 set prc_out_xh=&apos;&apos;,prc_out_code=&apos;&apos;,prc_out_text=&apos;&apos;,err_cwlsh=&apos;&apos; 
										where err_cwlsh=&apos;&quot;+sblsh+&quot;&apos; and zth=&apos;&quot;+zth+&quot;&apos; and aaa027=&apos;&quot;+tcqbm+&quot;&apos;&quot;;
									db.ExcecutSQL(sql);
								}else{
									db.Rollback();
									return &quot;1、发放单据不允许回退!流水号:&quot;+sblsh;
								}
							}else{
								//银海在过程外更新数据，东软在过程内更新数据
								if(ywjkbz == &quot;YH&quot;){
									//更新SI_DJB_TMP
									sql = &quot;update v_si_djb_tmp set RZBZ=&apos;0&apos;,RZR=null,RZSJ=null,LSH=null where LSH=&quot;+sblsh+&quot; and sbh = &apos;&quot;+sbh+&quot;&apos; and ZTH = &apos;&quot;+zth+&quot;&apos; and djh = &apos;&quot;+djh+&quot;&apos;&quot;;
									db.ExcecutSQL(sql);

									//修改银行回盘AC95标志
									sql = &quot;update ac95 set prc_out_xh=&apos;&apos;,prc_out_code=&apos;&apos;,prc_out_text=&apos;&apos;,err_cwlsh=&apos;&apos; 
										where err_cwlsh=&apos;&quot;+sblsh+&quot;&apos; and zth=&apos;&quot;+zth+&quot;&apos; and aaa027=&apos;&quot;+tcqbm+&quot;&apos;&quot;;
									db.ExcecutSQL(sql);
								}

								//负数是业务回写进来的数据（银行回盘出错的记录）
								//调用业务接口
								var li_ret = iuo_ywjk.RJZ_Delete(db,sbh,zth,sblsh,czyxm,ywjkbz,djh);
								var ret = li_ret.split(&quot;@&quot;)[0];
								var retmes = &quot;&quot;;//li_ret.split(&quot;@&quot;)[1];
								if (li_ret.split(&quot;@&quot;).length == 2) {
									retmes = li_ret.split(&quot;@&quot;)[1];
								}
								if (ret != &quot;NOERROR&quot;) {
									db.Rollback();
									return &quot;调用发放业务函数失败！流水号:&quot;+sblsh+&quot;;出错码：&quot;+ret+&quot;出错原因:&quot;+retmes;
								}
							}
						}//if( ywhtbz.substring(0,3) == &quot;ZS_&quot; )

						//删除日记账明细表记录
						sql = &quot;delete from cw_rjzmxb where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and lsh in (select lsh from cw_rjzb where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos;
							and siseqno = &apos;&quot;+sblsh+&quot;&apos;)&quot;;
						db.ExcecutSQL(sql);

						//删除日记账表记录
						sql = &quot;delete from cw_rjzb where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and yy = &apos;&quot;+yy+&quot;&apos; and mm = &apos;&quot;+mm+&quot;&apos; and siseqno = &apos;&quot;+sblsh+&quot;&apos;&quot;;
						db.ExcecutSQL(sql);

						//删除银行日记账表记录	
						sql = &quot;delete from cw_yhrjzb where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and yy = &apos;&quot;+yy+&quot;&apos; and mm = &apos;&quot;+mm+&quot;&apos; and siseqno = &apos;&quot;+sblsh+&quot;&apos;&quot;;
						db.ExcecutSQL(sql);

					}else{
						db.Rollback();
						return &quot;程序出错!请联系系统开发商&quot;+sql;
					}
				}
			}else{
				//更新SI_DJB_TMP
				sql = &quot;update v_si_djb_tmp set RZBZ=&apos;0&apos;,RZR=null,RZSJ=null,LSH=null where LSH=&quot;+sblsh+&quot; and sbh = &apos;&quot;+sbh+&quot;&apos; and ZTH = &apos;&quot;+zth+&quot;&apos; and djh = &apos;&quot;+djh+&quot;&apos;&quot;;
				db.ExcecutSQL(sql);

				//删除日记账明细表记录
				sql = &quot;delete from cw_rjzmxb where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and lsh in (select lsh from cw_rjzb where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos;
					and siseqno = &apos;&quot;+sblsh+&quot;&apos;)&quot;;
				db.ExcecutSQL(sql);

				//删除日记账表记录
				sql = &quot;delete from cw_rjzb where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and yy = &apos;&quot;+yy+&quot;&apos; and mm = &apos;&quot;+mm+&quot;&apos; and siseqno = &apos;&quot;+sblsh+&quot;&apos;&quot;;
				db.ExcecutSQL(sql);

				//删除银行日记账表记录	
				sql = &quot;delete from cw_yhrjzb where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and yy = &apos;&quot;+yy+&quot;&apos; and mm = &apos;&quot;+mm+&quot;&apos; and siseqno = &apos;&quot;+sblsh+&quot;&apos;&quot;;
				db.ExcecutSQL(sql);
			}
		}else{	//财务内部做帐数据或跟业务无对接的数据
			sql = &quot;select count(1) from cw_yhrjzb where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and yy = &apos;&quot;+yy+&quot;&apos; and mm = &apos;&quot;+mm+&quot;&apos; and siseqno = &apos;&quot;+sblsh+&quot;&apos;&quot;;
			var cout = db.GetSQL(sql);
			if(cout &gt;= 1){	//录单笔记录//手工录记录

				//判断是否是转移收入记录
				sql = &quot;select count(1) from cw_rjzmxb where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and lsh in (select lsh from cw_rjzb where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos;
					and siseqno = &apos;&quot;+sblsh+&quot;&apos;) and lxbh = &apos;83&apos;&quot;;
				var zysr_cout = db.GetSQL(sql);
				if(zysr_cout &gt; 0){	//转移收入记录
					var yh_ywjk = new SBCW_YWJK_YH();
					var yhjk_msg = yh_ywjk.yhjk_PayInterface(db,djh,&quot;ZY&quot;,je ,ls_hkrq,&quot;&quot;,&quot;&quot;,czyxm,tcqbm ,thisorgid ,thisaccid,ywjkbz,sblsh,&quot;&quot;,&quot;&quot;); 
					var ret = yhjk_msg.split(&quot;@&quot;)[0];
					var retmes = &quot;&quot;;//yhjk_msg.split(&quot;@&quot;)[1];
					if (yhjk_msg.split(&quot;@&quot;).length == 2) {
						retmes = yhjk_msg.split(&quot;@&quot;)[1];
					}
					if(ret!=&quot;NOERROR&quot;){
						db.Rollback();
						return &quot;1、调用征收业务接口出错！流水号:&quot;+sblsh+&quot;;出错码：&quot;+ret+&quot;出错原因:&quot;+retmes;
					}
				}

				//删除日记账明细表记录
				sql = &quot;delete from cw_rjzmxb where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and lsh in (select lsh from cw_rjzb where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos;
					and siseqno = &apos;&quot;+sblsh+&quot;&apos;)&quot;;
				db.ExcecutSQL(sql);

				//删除日记账表记录
				sql = &quot;delete from cw_rjzb where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and yy = &apos;&quot;+yy+&quot;&apos; and mm = &apos;&quot;+mm+&quot;&apos; and siseqno = &apos;&quot;+sblsh+&quot;&apos;&quot;;
				db.ExcecutSQL(sql);
				//删除银行日记账表记录										
				sql = &quot;delete from cw_yhrjzb where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and yy = &apos;&quot;+yy+&quot;&apos; and mm = &apos;&quot;+mm+&quot;&apos; and siseqno = &apos;&quot;+sblsh+&quot;&apos;&quot;;
				db.ExcecutSQL(sql);

				//记录操作日志
				var note = &quot;删除日记帐：&quot;+yy+&quot;年&quot;+mm+&quot;月&quot;+&quot;，社保流水号：&quot;+sblsh;
				sql = &quot;insert into oplog(grdid,type,action,opdat,opusr,opusrnam,note,acc,syt,org)
					values(&apos;RJZ_YHRJZB&apos;,&apos;日记帐修改&apos;,&apos;删除日记帐&apos;,sysdate,&apos;&quot;+usrid+&quot;&apos;,&apos;&quot;+czyxm+&quot;&apos;,&apos;&quot;+note+&quot;&apos;,&apos;&quot;+thisaccid+&quot;&apos;,&apos;SBCW&apos;,&apos;&quot;+thisorgid+&quot;&apos;)&quot;;
				db.ExcecutSQL(sql);

			}else {	
				db.Rollback();
				return &quot;程序出错！&quot;+cout+&quot;流水号:&quot;+sblsh+&quot;\n语句:&quot;+sql;
			}
		}
	}

	//重算余额
	try { checkRjzYE(sbh,zth,kmbh,doyy,domm); } catch (e) { }

	db.Commit();
	return 1;

}catch (e) {
	if (db != null) db.Rollback();
	return &quot;删除失败！\n&quot;+e.toString()+sql;	
}finally {
	if (db != null) db.Close();
}
}

//新增修改删除后进行余额检查,从当前年月开始
function checkRjzYE(sbh,zth,kmbh,yy,mm)
{
	var db = null;
	try {   
		db = new pub.EADatabase();
		var thisaccid = sbh+&quot;&quot;+zth;

		sql = &quot;update cw_yhrjzb set qcbz = 0 
	                where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and kmbh = &apos;&quot;+kmbh+&quot;&apos; and ( yy &gt; &quot;+yy+&quot; or ( yy = &quot;+yy+&quot; and mm &gt;= &quot;+mm+&quot; ) ) and qcbz is null&quot;;
	        db.ExcecutSQL(sql);

                sql = &quot;update cw_yhrjzb set ye=0 
                	where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and kmbh = &apos;&quot;+kmbh+&quot;&apos; and qcbz=1 and  ye is null&quot;;
                db.ExcecutSQL(sql);

		//调用f_check_next_qcye更新期初余额
		var pubfunc = new SBCW_PUBFUNC();
		var ret = pubfunc.f_check_next_qcye(db,sbh,thisaccid,kmbh,yy,mm);
		db.Commit();
		return ret;

	}
	catch (e) {
		if (db != null) db.Rollback();
		return e.toString();	
	}
	finally {
		if (db != null) db.Close();
	}
}

//余额检查
//&quot;typ=&quot;+typ+&quot;&dqyy=&quot;+dqyy+&quot;&dqmm=&quot;+dqmm+&quot;&kmbh=&quot;+kmbh+&quot;&sbh=&quot;+sbh+&quot;&zth=&quot;+zth+&quot;&thisorgid=&quot;+G_ORGID+&quot;&thisaccid=&quot;+G_ACCID
function yejc()
{      
var db = null;
try {   
	db = new pub.EADatabase();
	var yy = &quot;&quot;;
	var mm = &quot;&quot;;
	var sql = &quot;&quot;;
        if(typ == 1) {
               sql = &quot;update cw_yhrjzb set qcbz = 0 
                       where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and kmbh = &apos;&quot;+kmbh+&quot;&apos; and qcbz is null&quot; ;
               db.ExcecutSQL(sql);     
               sql =  &quot;update cw_yhrjzb set ye=0 
                       where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and kmbh = &apos;&quot;+kmbh+&quot;&apos; and qcbz=1 and  ye is null&quot; ; 
               db.ExcecutSQL(sql);
               sql = &quot;select substr(qdym,1,4) qdyy,substr(qdym,5) qdmm from cw_ztb where sbh =&apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos;&quot;;
               var ds = db.QuerySQL(sql);
               yy = ds.getStringAt(0,&quot;qdyy&quot;);
               mm = ds.getStringAt(0,&quot;qdmm&quot;);        
        }
        else if(typ == 2) {
                sql = &quot;update cw_yhrjzb set qcbz = 0 
                where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and kmbh = &apos;&quot;+kmbh+&quot;&apos; and ( yy &gt; &quot;+dqyy+&quot; or ( yy = &quot;+dqyy+&quot; and mm &gt;= &quot;+dqmm+&quot; ) ) and qcbz is null&quot;;
                db.ExcecutSQL(sql);
                sql = &quot;	update cw_yhrjzb set ye=0 
                where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and kmbh = &apos;&quot;+kmbh+&quot;&apos; and qcbz=1 and  ye is null&quot;;
                db.ExcecutSQL(sql);
                yy = dqyy;
                mm = dqmm;
        }
	//调用f_check_next_qcye更新期初余额
	var pubfunc = new SBCW_PUBFUNC();
	var ret = pubfunc.f_check_next_qcye(db,thisorgid,thisaccid,kmbh,yy,mm);
	db.Commit();
	return ret;

}
catch (e) {
	if (db != null) db.Rollback();
	return e.toString();	
}
finally {
	if (db != null) db.Close();
}
}


//金额检查
function jejc()
{
var db = null;
try {
	db = new pub.EADatabase();
	var ds = &quot;&quot;;
	var ret = &quot;&quot;;
	var jfje1 = 0.0;
	var dfje1 = 0.0;
	var jdje = 0.0;
	var jdfx = &quot;&quot;;
	var sum_je = pub.EAFunc.Round(1.0*je,2);
	var sum_jefx = jefx;
	var sql = &quot;select count(*) from cw_rjzb where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and siseqno = &quot;+siseqno+&quot; and qcbz = 1&quot;;
	var cnt = 0;
	var m = 0;
	var n = 0;
	var lsh = &quot;&quot;;

	try{cnt = db.GetSQL(sql);}catch(e){return &quot;出错信息：查询日记账表出错\r\n&quot;+e.toString();}
	if(cnt &gt; 0) return 0;

	//根据社保流水号取出日记帐LSH
	sql = &quot;select * from cw_rjzb where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and siseqno = &quot;+siseqno+&quot; &quot;;
	var tmpds = db.QuerySQL(sql);
	if (tmpds.getRowCount() &gt; 0) {
		lsh = tmpds.getStringAt(0,&quot;LSH&quot;);
	}
	if(lsh == &quot;&quot;) return &quot;出错信息：查询不到日记帐流水记录&quot;;
	if(zy == &quot;&quot;) return &quot;出错信息：摘要不能为空&quot;;
	sql = &quot;select round(sum(decode(jefx,&apos;借&apos;,nvl(je,0),0)),2) jfje,round(sum(decode(jefx,&apos;贷&apos;,nvl(je,0),0)),2) dfje
               from cw_rjzmxb
               where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and lsh =&quot;+lsh;
        ds = db.QuerySQL(sql);  
        try{     
	        jfje1 = 1.0*ds.getStringAt(0,&quot;jfje&quot;);
	        dfje1 = 1.0*ds.getStringAt(0,&quot;dfje&quot;);
	}catch(e) {
		return e.toString();
	}
	jdje = jfje1 = dfje1; 
        if(jdje &gt;= 0) {
	        jdje = pub.EAFunc.Round(jfje1 - dfje1,2);   	        
                jdfx = &quot;借&quot;;
        }else {
                jdje = pub.EAFunc.Round(jfje1 - dfje1,2);   
                jdfx = &quot;贷&quot;;
        }
        if(!(sum_je == 0) && !(jdje == 0)) {
               if((sum_je != jdje) || (sum_jefx != jdfx)) {
                      ret = zy+&quot;\r\n主表金额(sum_je)=&quot;+sum_jefx+&quot;,&quot;+sum_je+&quot;&lt;&gt;明细表金额(jdje)=&quot;+jdfx+&quot;,&quot;+jdje;
                      return ret;
               }
        }
        return 1;//返回1表示正常		
}
catch (e) {
	if (db != null) db.Rollback();
	return &quot;出错信息：&quot;+e.toString();	
}
finally {
	if (db != null) db.Close();
}    
}


