ROW.DATDSC:
select aaa103
  from aa10
 where aaa100 = &apos;ZTLX&apos;
   and aaa102 = &apos;[%ZTLX]&apos;

SELECT KMBH, CW_PACK4.KMMC(SBH, ZTH, KMBH) KMMC
  FROM CW_YHRJZB
 WHERE SISEQNO = &apos;[%SBLSH]&apos;
   AND KMBH &lt;&gt; &apos;[%KMBH]&apos;
