//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	//_this.ShowToolBar(0);
	_this.SetToolbarString(&quot;提示：&lt;F5&gt;打印 &lt;F8&gt;选择科目 &lt;PageUp&gt;上一个科目 &lt;PageDown&gt;下一个科目 &lt;Ctrl+PageUp&gt;第一个科目 &lt;Ctrl+PageDown&gt;最后一个科目&quot;);

	sbh = G_ORGID;
	zth = G_ACCID.replace(sbh,&quot;&quot;);
	initLayout();
	_this.AutoFixScreenWidth();
	_this.SetCellText(1,20,2,G_LOGDAT.split(&quot;-&quot;)[0]);
	_this.SetCellText(0,4,1,G_LOGDAT.split(&quot;-&quot;)[0]);
	_this.SetCellText(2,1,1,G_LOGDAT.split(&quot;-&quot;)[0]);
	_this.SetColVisible(2,6,0);	
	_this.SetColVisible(0,6,0);
	yyList = _this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_CW_YYYY&quot;),&quot;ID&quot;,&quot;NAME&quot;);
	_this.SetToComboBox(&quot;&quot;,1,20,2,yyList);
	//_this.SetToCheckBox(&quot;&quot;,1,27,2,&quot;按日排序&quot;,&quot;1&quot;,&quot;0&quot;);
	_this.SetToBoolBox(1,27,1);
	_this.SetCellText(1,27,1,&quot;1&quot;);
	//_this.SetToCheckBox(&quot;&quot;,1,28,2,&quot;提示放纸&quot;,&quot;1&quot;,&quot;0&quot;); 
	_this.SetToBoolBox(1,28,1);
	_this.SetRowVisible(1,28,-1);
	//_this.SetToCheckBox(&quot;&quot;,1,29,2,&quot;空页不打印&quot;,&quot;1&quot;,&quot;0&quot;);
	_this.SetToBoolBox(1,29,1);
	_this.SetCellText(1,29,1,&quot;1&quot;);
	//_this.SetToCheckBox(&quot;&quot;,1,30,2,&quot;打印当前科目&quot;,&quot;1&quot;,&quot;0&quot;);
	_this.SetToBoolBox(1,30,1);

	var fyList = _this.CreateListValue();
	_this.SetListValue(fyList ,&quot;按年分页&quot;,&quot;按年分页&quot;);
	_this.SetListValue(fyList ,&quot;按月分页&quot;,&quot;按月分页&quot;);
	_this.SetListValue(fyList ,&quot;按月打印&quot;,&quot;按月打印&quot;);
	_this.SetToRadioBox(&quot;&quot;,1,33,2,fyList);
	_this.SetCellText(1,33,2,&quot;按月分页&quot;);	

	_this.SetToBoolBox(1,42,1);
	_this.SetCellText(1,42,1,&quot;1&quot;);


	ZXGFILE0 = _this.SaveTempZXGFile(0);
	ZXGFILE2 = _this.SaveTempZXGFile(2); 

	loadKM();
	cur_kmbh = _this.GetCellText(3,2,1);
	cur_kmmc = _this.invokeOSFunc(&quot;getKMMC&quot;,&quot;sbh=&quot;+sbh+&quot;&zth=&quot;+zth+&quot;&kmbh=&quot;+cur_kmbh);
	cur_title = cur_kmbh + &quot;-&quot;+cur_kmmc;
	pageCount = 1;
	pageNow = 1;
	loadData();
	loadFenYe();
	_this.SetCellText(1,13,2,cur_kmbh);
	_this.SetCellText(0,3,5,cur_title);		

	//监听按键事件
	document.onkeydown = controlkeydown;
	document.onkeyup = controlkeyup;

}

//布局初始化
function initLayout()
{
	_this.SplitSheet(&quot;0&quot;,&quot;H&quot;,&quot;90%&quot;);
	//_this.SplitSheet(3,&quot;V&quot;,&quot;50%&quot;);
	//_this.HideSheet(0);
}
//点击按钮
function _thisOnButtonCtrlClick(id,sheet,row,col)
{
	if(id == &quot;选择科目&quot;) {
		selKMBH();
	}
	else if(id == &quot;上页&quot;) {
		if(pageNow == 1) return;
		pageNow = pageNow - 1;
		loadFenYe();
	}
	else if(id == &quot;下页&quot;) {
		if(pageNow == pageCount) return;
		pageNow = pageNow + 1;
		loadFenYe();	
	}
	else if(id == &quot;下个科目&quot;) {
		scrollKM(1);
	}
	else if(id == &quot;上个科目&quot;) {
		scrollKM(-1);
	}
	else if(id == &quot;打印&quot;) {
//		var retVal = window.showModalDialog(&quot;show.sp?grdid=ZB_ZZTips&quot;,&quot;&quot;,&quot;dialogWidth=350px;dialogHeight=100px;&quot;); 
//		if(retVal != &quot;&quot; && retVal != &quot;undefined&quot; && retVal != null) {
//			print(retVal);
//		}
		var url = &quot;show.sp?grdid=PZGL_ZBDYCS&bzpage=&quot;+_this.GetCellText(1,43,3)+&quot;&km=&quot;+_this.GetCellText(0,3,5)+&quot;&bhtotal=&quot;+pageCount;
		var param = window.showModalDialog(url,&quot;&quot;,&quot;dialogWidth=360px;dialogHeight=300px;&quot;); 
		try {
			if (param.result == 0) return;
			//alert(param.bhpage+&quot;,&quot;+param.bzpage);
			if (param.bhpage != &quot;&quot; && param.bhpage &gt; 0) pageNow = param.bhpage;
			if (param.bzpage != &quot;&quot; && param.bzpage &gt; 0) cur_bzPage = param.bzpage;

			print(1);
		}
		catch(e) {}

	}
	else if(id == &quot;导出excel&quot;) {
		alert(&quot;功能尚未开放&quot;);
		return;
	}
}


// 3 打印当前页 2 打印全部 1 从当前页打印
function print(bz)
{
	printType = &quot;&quot;;
	var nullPrint = _this.GetCellText(1,29,1); //空页不打印

	if(bz == 3 ) {
		_this.Print(0,1);	
	}
	else if(bz == 2){
		var bfirst = 0;
		for(var i = 1;i&lt;= pageCount;i++) {
			loadFenYe();
			_this.SetCellText(1,13,2,cur_kmbh);
			_this.SetCellText(0,3,5,cur_title);

			if (checkNull() == true && nullPrint == 1) continue; //空页不打印
			if (bfirst == 0) {
				bfirst ++;
				var param = window.showModalDialog(&quot;show.sp?grdid=PZGL_ZBDYTX&bhpageno=&quot;+pageNow+&quot;&bhtotal=&quot;+pageCount+&quot;&bzpage=&quot;+cur_bzPage+&quot;&printkm=&quot;+cur_title,&quot;&quot;,&quot;dialogWidth=400px;dialogHeight=200px;&quot;); 
				try {
					printType = param.result;
					if (param.result == 0) return;
				}catch (e) { return; }
			}

			_this.Print(0,1);				
			pageNow++;
			cur_bzPage ++;
		}

		if (_this.GetCellText(1,30,1) == 1) return; //只打印当前科目
		//打印下一个科目
		printNextKM();

	}
	else if(bz == 1) {
		var bfirst = 0;
		for(var i = pageNow;i&lt;= pageCount;i++) {
			loadFenYe();
			_this.SetCellText(1,13,2,cur_kmbh);
			_this.SetCellText(0,3,5,cur_title);	

			if (checkNull() == true && nullPrint == 1) continue; //空页不打印	
			if (bfirst == 0) {
				bfirst ++;
				var param = window.showModalDialog(&quot;show.sp?grdid=PZGL_ZBDYTX&bhpageno=&quot;+pageNow+&quot;&bhtotal=&quot;+pageCount+&quot;&bzpage=&quot;+cur_bzPage+&quot;&printkm=&quot;+cur_title,&quot;&quot;,&quot;dialogWidth=400px;dialogHeight=200px;&quot;); 
				try {
					printType = param.result;
					if (param.result == 0) return;
				}catch (e) { return; }
			}

			_this.Print(0,1);
			pageNow++;
			cur_bzPage ++;

//			if (printType == 1 && i != pageCount) {	//打印并提示
//				var param = window.showModalDialog(&quot;show.sp?grdid=PZGL_ZBDYTX&bhpageno=&quot;+pageNow+&quot;&bhtotal=&quot;+pageCount+&quot;&bzpage=&quot;+cur_bzPage+&quot;&printkm=&quot;+cur_title,&quot;&quot;,&quot;dialogWidth=400px;dialogHeight=200px;&quot;); 
//				try {
//					printType = param.result;
//					if (param.result == 0) return;
//				} catch(e) {}
//			}
		}

		if (_this.GetCellText(1,30,1) == 1) return; //只打印当前科目
		//打印下一个科目
		printNextKM();

	}
}

//检查是否空数据
function checkNull()
{
	for (var c=1;c&lt;=10;c++) {
		var val = _this.GetCellText(0,6,c);
		if (val != &quot;&quot; && val != &quot;0&quot; && val != &quot;0.00&quot; && c != 5) {
			return false;
		}
	}
	return true;
}

//打印下一个科目
function printNextKM()
{
	var nullPrint = _this.GetCellText(1,29,1); //空页不打印
	var cur_kmbh_row = -1;
	for (var r=_this.GetMainCellRangeRow1(3);r&lt;=_this.GetMainCellRangeRow2(3);r++) {
		var kmbh = _this.GetCellText(3,r,1);
		if (kmbh == cur_kmbh) {
			cur_kmbh_row = r;
			continue;
		}
		if (cur_kmbh_row != -1) { //开始打印下一个科目
			//加载科目分页数据，重新计算本户页数等
			cur_kmbh = kmbh;
			cur_kmmc = _this.GetCellText(3,r,2); //_this.invokeOSFunc(&quot;getKMMC&quot;,&quot;sbh=&quot;+sbh+&quot;&zth=&quot;+zth+&quot;&kmbh=&quot;+cur_kmbh);
			cur_title = cur_kmbh + &quot;-&quot;+cur_kmmc;

			pageCount = 1;
			pageNow = 1;
			loadData();
			loadFenYe();
			_this.SetCellText(1,13,2,cur_kmbh);
			_this.SetCellText(0,3,5,cur_title);	

			if (checkNull() == true && nullPrint == 1) continue; //空页不打印

			if (printType == &quot;&quot; || printType == 1) {
				var param = window.showModalDialog(&quot;show.sp?grdid=PZGL_ZBDYTX&bhpageno=&quot;+pageNow+&quot;&bhtotal=&quot;+pageCount+&quot;&bzpage=&quot;+cur_bzPage+&quot;&printkm=&quot;+cur_title,&quot;&quot;,&quot;dialogWidth=400px;dialogHeight=200px;&quot;); 
				try {
					printType = param.result;
					if (param.result == 0) return;
				}
				catch (e) { return; }
			}

			//打印本户所有页
			for(var i = pageNow;i&lt;= pageCount;i++) {
				loadFenYe();
				_this.SetCellText(1,13,2,cur_kmbh);
				_this.SetCellText(0,3,5,cur_title);		

				if (checkNull() == true && nullPrint == 1) continue; //空页不打印						
				_this.Print(0,1);
				pageNow++;
				cur_bzPage ++;

			}
		}
	}
}


//选择科目编号
function selKMBH()
{
	var obj = new Object();
	obj.filter = &quot;xjyhbz &lt;&gt; 0&quot;; 
        obj.mjbz = &quot;1&quot;;
        obj.bz = &quot;&quot;;
        obj.jb = 0;
        obj.kmbh = &quot;&quot;;
	var retVal = window.showModalDialog(&quot;show.sp?grdid=CW_KMSEL&quot;,obj,&quot;dialogWidth=600px;dialogHeight=500px&quot;);
	cur_kmbh = retVal;
	if (cur_kmbh != &quot;&quot; && cur_kmbh != &quot;undefined&quot; && cur_kmbh != undefined) {
		cur_kmmc = _this.invokeOSFunc(&quot;getKMMC&quot;,&quot;sbh=&quot;+sbh+&quot;&zth=&quot;+zth+&quot;&kmbh=&quot;+cur_kmbh);
		cur_title = cur_kmbh + &quot;-&quot;+cur_kmmc;
		pageCount = 1;
		pageNow = 1;
		loadData();
		loadFenYe();
		_this.SetCellText(1,13,2,cur_kmbh);
		_this.SetCellText(0,3,5,cur_title);
	}			
}

//分页加载数据
function loadFenYe()
{
	var kmbh = _this.GetCellText(0,3,5);
	_this.LoadZXGFile(ZXGFILE0,-1,0);
	row1 = 3+(pageNow - 1) * pageSize;
	row2 = row1 + 20;
	CopyRow(row1,row2);
	var yy = _this.GetCellText(1,20,2);
	if(_this.GetCellText(1,33,2) != &quot;按年分页&quot;) {
		//求当前页对应的月份
		var cur_mm = _this.GetCellText(0,6,1);
		if(cur_mm == &quot;&quot;) {
			cur_mm = _this.GetCellText(0,7,1);
		}
		_this.SetCellText(1,39,3,cur_mm);
	}
	_this.SetCellText(0,4,1,yy);
	_this.SetCellText(0,2,11,pageNow);	
	_this.SetCellText(1,16,1,&quot;当前 &quot;+pageNow+&quot;/&quot;+pageCount+&quot; 页&quot;);
	_this.SetCellText(0,1,11,cur_bzPage);
	_this.SetCellText(0,3,5,kmbh);
	_this.AutoFixScreenWidth();
}

function loadData()
{
	_this.LoadZXGFile(ZXGFILE2,-1,2);
	var yy = _this.GetCellText(1,20,2);
	_sql.querytods(&quot;qcye&quot;,&quot;sbh=&quot;+sbh+&quot;&zth=&quot;+zth+&quot;&yy=&quot;+yy+&quot;&kmbh=&quot;+cur_kmbh);	
	var my_ye = _this.XMLDS_GetStringAt(0,0)*1.0;
	var my_yefx = _this.XMLDS_GetStringAt(0,1);
	var jj = &quot;&quot;;
	var last_jj = &quot;&quot;;
	var old_my_ye = &quot;&quot;;
	var old_my_yefx = &quot;&quot;;
	var curr_row = 3;
	var ljjfje = 0;
	var ljdfje = 0;
	var tmp_ye = 0.0;
	var tmp_yefx = &quot;借&quot;;
	var my_ye = 0.0;
	var my_yefx = &quot;借&quot;;
	var last_mm = &quot;&quot;;
	var byljjfje = 0;
	var byljdfje = 0;
	var bjljjfje = 0;
	var bjljdfje = 0;
	var ybz = 0; //第一个月标志
	var ljbz = 0;	
	var byhjbz = 0;//本月合计标志	
	for(var i = 1;i&lt;=12;i++) {
		//判断是否选择了按月打印
		var flg = _this.GetCellText(0,33,2);
		_sql.querytods(&quot;main&quot;,&quot;sbh=&quot;+sbh+&quot;&zth=&quot;+zth+&quot;&yy=&quot;+yy+&quot;&kmbh=&quot;+cur_kmbh+&quot;&mm=&quot;+i);
			for(var row=0;row&lt;_this.XMLDS_GetRowCount();row++) {
				var mm = i;
				var dd = _this.XMLDS_GetString(row,&quot;dd&quot;);
				var pzlx = _this.XMLDS_GetString(row,&quot;pzlx&quot;);
				var pzh = _this.XMLDS_GetString(row,&quot;str_pzh&quot;);
				var jfje= _this.XMLDS_GetString(row,&quot;jfje&quot;)*1.0;
				var dfje = _this.XMLDS_GetString(row,&quot;dfje&quot;)*1.0;
				var zy = _this.XMLDS_GetString(row,&quot;zy&quot;);
				var qcbz = _this.XMLDS_GetString(row,&quot;qcbz&quot;);
				tmp_ye = 1.0*_this.XMLDS_GetString(row,&quot;ye&quot;);
				tmp_yefx = _this.XMLDS_GetString(row,&quot;yefx&quot;);
				jj = Math.floor((1*mm-1)/3)+1;
				if(last_mm != &quot;&quot;) {
					//有数据才打印本月合计
					if(last_mm != mm && byhjbz == 1) {//换月 合计月
						_this.SetCellText(2,curr_row,5,&quot;本月合计&quot;);
						_this.SetCellText(2,curr_row,7,fmoney(byljjfje,2));	
						_this.SetCellText(2,curr_row,8,fmoney(byljdfje,2));					
						curr_row ++;
						if(curr_row &gt; 22) {
							_this.AppendRow(2,curr_row-1);
						}
						if((curr_row -3 + 1) % 20 == 0) {
							_this.SetCellText(2,curr_row,5,&quot;过次页&quot;);
							_this.SetCellText(2,curr_row,7,fmoney(ljjfje,2));	
							_this.SetCellText(2,curr_row,8,fmoney(ljdfje,2));				
							curr_row ++;
							_this.AppendRow(2,curr_row-1);
							pageCount++;
							_this.SetCellText(2,curr_row,5,&quot;承上页&quot;);
							_this.SetCellText(2,curr_row,7,fmoney(ljjfje,2));	
							_this.SetCellText(2,curr_row,8,fmoney(ljdfje,2));					
							curr_row ++;
							_this.AppendRow(2,curr_row-1);								
						}										
						byljjfje = 0;	
						byljdfje = 0;
						ljbz =  1;								
					}
				}
				if(last_jj != &quot;&quot;) {
					if(last_jj != jj) {//换季 
						_this.SetCellText(2,curr_row,5,&quot;本季合计&quot;);
						_this.SetCellText(2,curr_row,7,fmoney(bjljjfje,2));	
						_this.SetCellText(2,curr_row,8,fmoney(bjljdfje,2));					
						curr_row ++;
						if(curr_row &gt; 22) {
							_this.AppendRow(2,curr_row-1);
						}
						if((curr_row -3 + 1) % 20 == 0) {
							_this.SetCellText(2,curr_row,5,&quot;过次页&quot;);
							_this.SetCellText(2,curr_row,7,fmoney(ljjfje,2));	
							_this.SetCellText(2,curr_row,8,fmoney(ljdfje,2));				
							curr_row ++;
							_this.AppendRow(2,curr_row-1);
							pageCount++;
							_this.SetCellText(2,curr_row,5,&quot;承上页&quot;);
							_this.SetCellText(2,curr_row,7,fmoney(ljjfje,2));	
							_this.SetCellText(2,curr_row,8,fmoney(ljdfje,2));					
							curr_row ++;
							_this.AppendRow(2,curr_row-1);								
						}						
						bjljjfje = 0;	
						bjljdfje = 0;
						ljbz = 1;							
					}				
				}
				if(mm != last_mm && ljbz != 0 && last_mm != 1) {
					_this.SetCellText(2,curr_row,5,&quot;本年累计&quot;);
					_this.SetCellText(2,curr_row,7,fmoney(ljjfje,2));	
					_this.SetCellText(2,curr_row,8,fmoney(ljdfje,2));
					curr_row ++;
					if(curr_row &gt; 22) {
						_this.AppendRow(2,curr_row-1);
					}
					if((curr_row -3 + 1) % 20 == 0) {
						_this.SetCellText(2,curr_row,5,&quot;过次页&quot;);
						_this.SetCellText(2,curr_row,7,fmoney(ljjfje,2));	
						_this.SetCellText(2,curr_row,8,fmoney(ljdfje,2));				
						curr_row ++;
						_this.AppendRow(2,curr_row-1);
						pageCount++;
						_this.SetCellText(2,curr_row,5,&quot;承上页&quot;);
						_this.SetCellText(2,curr_row,7,fmoney(ljjfje,2));	
						_this.SetCellText(2,curr_row,8,fmoney(ljdfje,2));					
						curr_row ++;
						_this.AppendRow(2,curr_row-1);								
					}																			
				}	
				if(last_mm != &quot;&quot; && mm != last_mm && _this.GetCellText(1,33,2) != &quot;按年分页&quot;) {
					if(mm != 12) {
						if(my_ye == 0) {
							_this.SetCellText(2,curr_row,9,&quot;平&quot;);
						}
						else {
							_this.SetCellText(2,curr_row,9,my_yefx);
						}
						_this.SetCellText(2,curr_row,5,&quot;结转下月&quot;);	
						curr_row ++;
						if(curr_row &gt; 22) {
							_this.AppendRow(2,curr_row-1);
						}
						if((curr_row -3 + 1) % 20 == 0) {
							_this.SetCellText(2,curr_row,5,&quot;过次页&quot;);
							_this.SetCellText(2,curr_row,7,fmoney(ljjfje,2));	
							_this.SetCellText(2,curr_row,8,fmoney(ljdfje,2));				
							curr_row ++;
							_this.AppendRow(2,curr_row-1);
							pageCount++;
							_this.SetCellText(2,curr_row,5,&quot;承上页&quot;);
							_this.SetCellText(2,curr_row,7,fmoney(ljjfje,2));	
							_this.SetCellText(2,curr_row,8,fmoney(ljdfje,2));					
							curr_row ++;
							_this.AppendRow(2,curr_row-1);								
						}											

					}					
				}
				if(_this.GetCellText(1,33,2) != &quot;按年分页&quot;) {
					if(mm != last_mm && last_mm != &quot;&quot;) {
						while((curr_row -3 + 1) % 20 != 1) {
							curr_row ++;
							_this.AppendRow(2,curr_row-1);						
						}
						pageCount++;				
					}				
				}			
				if(qcbz == 1) {
					my_ye = tmp_ye;
					my_yefx = tmp_yefx;
				}
				else {

					if(my_yefx == &quot;借&quot;) {
						my_ye = my_ye + jfje -dfje;
					}
					else if(my_yefx == &quot;贷&quot;) {
						my_ye = my_ye + dfje - jfje;
					}				
				}								
				if(my_ye &lt; 0) {
					my_ye = -1 * my_ye;
					if(my_yefx == &quot;贷&quot;) {
						my_yefx = &quot;借&quot;;
					}else {
						my_yefx = &quot;贷&quot;;
					}
				}
				else if(my_ye ==0) {
					my_yefx = &quot;借&quot;;
				}
				if(my_ye == 0) {
					_this.SetCellText(2,curr_row,9,&quot;平&quot;);
				}
				else {
					_this.SetCellText(2,curr_row,9,my_yefx);
				}
				_this.SetCellText(2,curr_row,10,fmoney(my_ye,2));				
				_this.SetCellText(2,curr_row,8,fmoney(dfje,2));
				_this.SetCellText(2,curr_row,7,fmoney(jfje,2));
				_this.SetCellText(2,curr_row,1,mm);
				_this.SetCellText(2,curr_row,2,dd);
				_this.SetCellText(2,curr_row,3,pzlx);
				_this.SetCellText(2,curr_row,4,pzh);
				_this.SetCellText(2,curr_row,5,zy);
				_this.SetCellText(2,curr_row,6,&quot;√&quot;);
				_this.SetCellColor(2,curr_row,5,255,0,0);
				if(jfje != &quot;&quot;) {
					ljjfje += jfje;
					byljjfje += jfje;
					bjljjfje += jfje;
				}
				if(dfje != &quot;&quot;) {
					ljdfje += dfje;
					byljdfje += dfje;
					bjljdfje += dfje;
				}
				curr_row ++;
				if(curr_row &gt; 22) {
					_this.AppendRow(2,curr_row-1);
				}
				if((curr_row -3 + 1) % 20 == 0) {
					_this.SetCellText(2,curr_row,5,&quot;过次页&quot;);
					_this.SetCellText(2,curr_row,7,fmoney(ljjfje,2));	
					_this.SetCellText(2,curr_row,8,fmoney(ljdfje,2));				
					curr_row ++;
					_this.AppendRow(2,curr_row-1);
					pageCount++;
					_this.SetCellText(2,curr_row,5,&quot;承上页&quot;);
					_this.SetCellText(2,curr_row,7,fmoney(ljjfje,2));	
					_this.SetCellText(2,curr_row,8,fmoney(ljdfje,2));					
					curr_row ++;
					_this.AppendRow(2,curr_row-1);								
				}							
				last_mm = mm;	
				last_jj = jj;
				ljbz = 0;
				if(dd != &quot;&quot; && qcbz != 1) {
					byhjbz = 1;
				}		
			}// end for _this.xmlds
	}	
	_this.SetCellText(2,curr_row,5,&quot;本月合计&quot;);
	_this.SetCellText(2,curr_row,7,fmoney(byljjfje,2));	
	_this.SetCellText(2,curr_row,8,fmoney(byljdfje,2));
	curr_row ++;
	if(curr_row &gt; 22) {
		_this.AppendRow(2,curr_row-1);
	}
	if((curr_row -3 + 1) % 20 == 0) {
		_this.SetCellText(2,curr_row,5,&quot;过次页&quot;);
		_this.SetCellText(2,curr_row,7,fmoney(ljjfje,2));	
		_this.SetCellText(2,curr_row,8,fmoney(ljdfje,2));				
		curr_row ++;
		_this.AppendRow(2,curr_row-1);
		pageCount++;
		_this.SetCellText(2,curr_row,5,&quot;承上页&quot;);
		_this.SetCellText(2,curr_row,7,fmoney(ljjfje,2));	
		_this.SetCellText(2,curr_row,8,fmoney(ljdfje,2));					
		curr_row ++;
		_this.AppendRow(2,curr_row-1);								
	}	
	_this.SetCellText(2,curr_row,5,&quot;本年累计&quot;);
	_this.SetCellText(2,curr_row,7,fmoney(ljjfje,2));	
	_this.SetCellText(2,curr_row,8,fmoney(ljdfje,2));

	old_my_yefx = my_yefx;
	old_my_ye = my_ye;
	_sql.querytods(&quot;qcye&quot;,&quot;sbh=&quot;+sbh+&quot;&zth=&quot;+zth+&quot;&yy=&quot;+(1*yy+1)+&quot;&kmbh=&quot;+cur_kmbh);	
	if(_this.XMLDS_GetRowCount() &gt; 0) {
		if(my_ye &lt; 0) {
			my_ye = -1 * my_ye;
			if(my_yefx == &quot;贷&quot;) {
				my_yefx = &quot;借&quot;;
			}else {
				my_yefx = &quot;贷&quot;;
			}
		}
		curr_row ++;
		if(curr_row &gt; 23) {
			_this.AppendRow(2,curr_row-1);
		}
		if((curr_row -3) % 19 == 0) {
			_this.SetCellText(2,curr_row,5,&quot;过次页&quot;);
			curr_row ++;
			_this.AppendRow(2,curr_row-1);					
		}
		if((curr_row -3 + 1) % 20 == 0) {
			pageCount++;
			_this.SetCellText(2,curr_row,5,&quot;承上页&quot;);
			curr_row ++;
			_this.AppendRow(2,curr_row-1);												
		}				
		_this.SetCellText(2,curr_row,5,&quot;结转下年&quot;);
		_this.SetCellText(2,curr_row,9,my_yefx);
		_this.SetCellText(2,curr_row,10,fmoney(my_ye,2));				
	}			
	_this.AutoFixScreenWidth();
}

