//根据科目翻页 bz 1下一个科目 bz -1上一个科目
function scrollKM(bz)
{
	if(bz == 1) {
		for(var i=2;i&lt;=_this.GetRowCount(3)-1;i++) {
			var kmbh = _this.GetCellText(3,i,1);
			if(kmbh == cur_kmbh) {
				if (i == _this.GetRowCount(3)-1) {
					alert(&quot;已经是最后一个科目&quot;);
					return;			
				}				
				var nextkmbh = _this.GetCellText(3,(i+1),1);
				cur_kmbh = nextkmbh;
				cur_kmmc = _this.invokeOSFunc(&quot;getKMMC&quot;,&quot;sbh=&quot;+sbh+&quot;&zth=&quot;+zth+&quot;&kmbh=&quot;+cur_kmbh);
				cur_title = cur_kmbh + &quot;-&quot;+cur_kmmc;
				pageCount = 1;
				pageNow = 1;
				loadData();
				loadFenYe();
				_this.SetCellText(1,13,2,cur_kmbh);
				_this.SetCellText(0,3,5,cur_title);	
				return;			
			}			
		}
	}
	else {
		for(var i=2;i&lt;=_this.GetRowCount(3)-1;i++) {
			var kmbh = _this.GetCellText(3,i,1);
			if(kmbh == cur_kmbh) {
				if (i == 2) {
					alert(&quot;已经是第一个科目&quot;);
					return;			
				}				
				var nextkmbh = _this.GetCellText(3,(i-1),1);
				cur_kmbh = nextkmbh;
				cur_kmmc = _this.invokeOSFunc(&quot;getKMMC&quot;,&quot;sbh=&quot;+sbh+&quot;&zth=&quot;+zth+&quot;&kmbh=&quot;+cur_kmbh);
				cur_title = cur_kmbh + &quot;-&quot;+cur_kmmc;
				pageCount = 1;
				pageNow = 1;
				loadData();
				loadFenYe();
				_this.SetCellText(1,13,2,cur_kmbh);
				_this.SetCellText(0,3,5,cur_title);	
				return;			
			}			
		}		
	}
}

//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
	if(sheet == 1) {
		if(row == 20 && col == 2 ) {
			_this.SetCellText(2,1,1,newvalue);
			_this.SetCellText(0,4,2,newvalue);
			pageCount = 1;
			pageNow = 1;
			loadData();
			loadFenYe();			
			_this.SetCellText(1,13,2,cur_kmbh);
			_this.SetCellText(0,3,5,cur_title);				
		}
		else if(row == 16 && col == 1) {
			if((!IsNumber(newvalue)) || newvalue == 0 || newvalue &gt; pageCount) {
				_this.SetCellText(sheet,row,col,oldvalue);
				return;
			}
			pageNow = newvalue;
			loadFenYe();
		}
		else if(row == 33 && col == 2) {
			if(newvalue == &quot;按年分页&quot;) {
				_this.SetCellLock(1,39,3,1);
			}
			else {
				_this.SetCellLock(1,39,3,0);
			}
			pageCount = 1;
			pageNow = 1;
			loadData();
			loadFenYe();			
			_this.SetCellText(1,13,2,cur_kmbh);
			_this.SetCellText(0,3,5,cur_title);	
		}
		else if(row == 39 && col == 3) {
			if(!IsNumber(newvalue) || newvalue &lt; 1 || newvalue &gt; 12) {
				alert(&quot;输入的月份格式不正确&quot;);
				_this.SetCellText(sheet,row,col,oldvalue);
				return;
			}
			var tmp_pageCount = 1;
			var bool = false;
			for(var i =3;i&lt;=_this.GetRowCount(2)-1;i++) {
				var mm = _this.GetCellText(2,i,1);
				if((i-3+1) % 20 == 0) {
					tmp_pageCount++;
				}
				if(mm == newvalue) {
					bool = true;
					break;
				}
			}
			if(bool) {
				pageNow = tmp_pageCount;
				loadFenYe();			
			}
			else {
				_this.SetCellText(sheet,row,col,oldvalue);
				return;
			}
		}
		else if (row == 42 && col == 1) {
			var bzyprintbz = _this.GetCellText(1,42,1);
			if (bzyprintbz == 1) {
				_this.SetRowVisible(1,43,1);
			}
			else {
				_this.SetRowVisible(1,43,-1);
			}
		}
		else if (row == 43 && col == 3) {
			if (!!isNaN(newvalue)) {
				_this.SetCellText(sheet,row,col,oldvalue);
				return;
			}
			_this.SetCellText(0,1,11,newvalue); //设置本帐页
			cur_bzPage = newvalue;
		}

	}
}
