function getLastKmRow(sheet_km)
{
	var lastrow = -1;
	for(var i=_this.GetMainCellRangeRow1(sheet_km);i&lt;=_this.GetMainCellRangeRow2(sheet_km);i++) {
		lastrow = i;
		var kmbh = _this.GetCellText(sheet_km,i,1);
		if (kmbh != &quot;&quot;) continue;
		else {
			return i - 1;
		}
	}
	return lastrow;
}

//按键
function _thisOnKeyin(nChar,nFlags,sheet,row,col)
{
	if (ctrlKey == true) {
		if (nChar == 33) { //第一个科目
			cur_kmbh = _this.GetCellText(3,2,1);
			cur_kmmc = _this.GetCellText(3,2,2);//_this.invokeOSFunc(&quot;getKMMC&quot;,&quot;sbh=&quot;+sbh+&quot;&zth=&quot;+zth+&quot;&kmbh=&quot;+cur_kmbh);
			cur_title = cur_kmbh + &quot;-&quot;+cur_kmmc;
			pageCount = 1;
			pageNow = 1;
			loadData();
			loadFenYe();
			_this.SetCellText(1,13,2,cur_kmbh);
			_this.SetCellText(0,3,5,cur_title);
		}
		else if (nChar == 34) { //最后一个科目
			var lastrow = getLastKmRow(3);
			cur_kmbh = _this.GetCellText(3,lastrow,1);
			cur_kmmc = _this.GetCellText(3,lastrow,2);//_this.invokeOSFunc(&quot;getKMMC&quot;,&quot;sbh=&quot;+sbh+&quot;&zth=&quot;+zth+&quot;&kmbh=&quot;+cur_kmbh);
			cur_title = cur_kmbh + &quot;-&quot;+cur_kmmc;
			pageCount = 1;
			pageNow = 1;
			loadData();
			loadFenYe();
			_this.SetCellText(1,13,2,cur_kmbh);
			_this.SetCellText(0,3,5,cur_title);
		}
	}
	else {
		if (nChar == 33) { //pageUp
			_thisOnButtonCtrlClick(&quot;上个科目&quot;,sheet,row,col);
		}
		else if (nChar == 34) { //pageDown
			_thisOnButtonCtrlClick(&quot;下个科目&quot;,sheet,row,col);
		}
	}
}

//按键按下
function controlkeydown()
{
	altKey = event.altKey;
	ctrlKey = event.ctrlKey;
	shiftKey = event.shiftKey;

}

//按键放开
function controlkeyup()
{
	altKey = false;
	ctrlKey = false;
	shiftKey = false;
}

