function getKMMC() {
      var db= null;
      var ret= 0;
      try{
          	 var db = new pub.EADatabase();
		var sql = &quot;select cw_pack4.kmmc(sbh,zth,kmbh) from cw_kmb 
        				where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and kmbh = &apos;&quot;+kmbh+&quot;&apos;&quot;;		
          	 var kmmc = &quot;&quot;;
          	 kmmc = db.GetSQL(sql);
           	return kmmc;                              
      }catch(e) {
          if(db != null) db.Rollback();
          return e.toString();
      }finally {
          if(db != null) db.Close();
      }
}

