select ye, yefx  
from cw_qcyeb 
where zth = &apos;[%zth]&apos;and sbh = &apos;[%sbh]&apos; and  mm = 1 and yy = [%yy] and kmbh = &apos;[%kmbh]&apos;

select pzh1, pzh2  
from cw_pzhzb 
where sbh = &apos;[%sbh]&apos; and zth = &apos;[%zth]&apos;
			and yy = [%yy] and mm = [%mm]
			and zzh = [%zzh] and pzlx = &apos;[%pzlx]&apos;

select max(pzh) pzh1, min(pzh) pzh2 
from cw_pzmxb
where sbh = &apos;[%sbh]&apos; and zth = &apos;[%zth]&apos;
			and yy = [%yy] and mm = [%mm]
			and zzh = [%zzh] and pzlx = &apos;[%pzlx]&apos;
