//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	initLayout();

	var yyList =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_CW_YYYY&quot;),&quot;ID&quot;,&quot;NAME&quot;);
	var mmList =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_CW_MM&quot;),&quot;ID&quot;,&quot;NAME&quot;);
	var kmList =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_ACCITEM&where=mjbz=1 and XJYHBZ&lt;&gt;0 and QLBZ&lt;&gt;1 and nvl(RJZKM_LRBZ,&apos;1&apos;) = &apos;1&apos;&quot;),&quot;ID&quot;,&quot;NAME&quot;);
	fkBZ =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_CW_FKBZ&quot;),&quot;ID&quot;,&quot;NAME&quot;);

	cur_yyyy = G_LOGDAT.substring(0,4);
	cur_mm = 1*(G_LOGDAT.substring(5,7));

	_this.SetToComboBox(&quot;&quot;,sheet_param,1,1,yyList); _this.SetCellText(sheet_param,1,1,cur_yyyy);
	_this.SetToComboBox(&quot;&quot;,sheet_param,1,3,mmList); _this.SetCellText(sheet_param,1,3,cur_mm);
	_this.SetToComboBox(&quot;&quot;,sheet_param,1,6,kmList);

	//_this.SetToButton(&quot;&quot;,sheet_param,1,7,&quot;选择科目&quot;,&quot;&quot;,&quot;&quot;,2,16,16);

	printBZ = _this.CreateListValue();
	_this.SetListValue(printBZ,&quot;0&quot;,&quot;未打印&quot;);
	_this.SetListValue(printBZ,&quot;1&quot;,&quot;已打印&quot;);

	cur_kmbh = _this.GetCellText(sheet_param,1,6);
	_this.SetColVisible(sheet_rjz,1,-1); 
	if (cur_kmbh != &quot;&quot; && cur_yyyy != &quot;&quot; && cur_mm != &quot;&quot;) {
		loadRJZData(cur_yyyy,cur_mm,cur_kmbh,&quot; &quot;);
	}

	_this.LoadScene(sheet_param,10);
	_this.AutoFixScreenWidth();

	addToolButtons();

	sheet_print = _this.AddSheet(&quot;打印单据&quot;);

	//监听按键事件
	document.onkeydown = controlkeydown;
	document.onkeyup = controlkeyup;

	try { is_dzxz = invokeOSFunc(&quot;&quot;,&quot;thisorgid=&quot;+G_ORGID+&quot;&thisaccid=&quot;+G_ACCID); } catch (e) { }


}


//页面布局初始化
function initLayout()
{
//	_this.SplitSheet(sheet_param,&quot;V&quot;,45);
//	_this.SplitSheet(sheet_rjz,&quot;V&quot;,&quot;50%&quot;);
//	_this.SplitSheet(sheet_tips,&quot;V&quot;,30);
//	_this.SplitSheet(sheet_rjzmx1,&quot;H&quot;,&quot;50%&quot;);

	_this.SplitSheet(sheet_param,&quot;V&quot;,&quot;10%&quot;);
	_this.SplitSheet(sheet_rjz,&quot;V&quot;,&quot;50%&quot;);
	_this.SplitSheet(sheet_tips,&quot;V&quot;,&quot;10%&quot;);
	_this.SplitSheet(sheet_rjzmx1,&quot;H&quot;,&quot;50%&quot;);

	ZXGFILE0 = _this.SaveTempZXGFile(0); 
	ZXGFILE1 = _this.SaveTempZXGFile(1);
	ZXGFILE2 = _this.SaveTempZXGFile(2);	

	_this.SetAttribe(&quot;SHEET_0&quot;,_this.ATTR_SHEET_SELECTFLAG,_this. SELECTFLAG_SINGLEROW );
	_this.SetAttribe(&quot;SHEET_1&quot;,_this.ATTR_SHEET_SELECTFLAG,_this. SELECTFLAG_SINGLEROW );
	_this.SetAttribe(&quot;SHEET_2&quot;,_this.ATTR_SHEET_SELECTFLAG,_this. SELECTFLAG_SINGLEROW );

}

var show_flag = 0;
function showHideDetailSheet()
{

	if (show_flag == 1) {
		show_flag = 0;
		_this.ShowSheet(sheet_rjzmx1);
		_this.ShowSheet(sheet_rjzmx2);
	}
	else if (show_flag == 0) {
		show_flag = 1;
		_this.HideSheet(sheet_rjzmx1);
		_this.HideSheet(sheet_rjzmx2);
	}

}

