function rjztj()
{
	var yyyy = _this.GetCellText(sheet_param,1,1);
	var mm = _this.GetCellText(sheet_param,1,3);
	var km = _this.GetCellText(sheet_param,1,6);
	window.showModalDialog(&quot;show.sp?grdid=RJZ_TJ&yyyy=&quot;+yyyy+&quot;&mm=&quot;+mm+&quot;&km=&quot;+km,&quot;&quot;,&quot;dialogWidth=1000px;dialogHeight=700px;&quot;);
//	window.showModalDialog(&quot;show.sp?grdid=SZTHLR&TYPE=&quot;+szbz,&quot;&quot;,&quot;dialogWidth=800px;dialogHeight=350px;&quot;);
}

//加载日记帐数据
function loadRJZData(yyyy,mm,kmbh,filter)
{
	_this.SetToolbarString(&quot;正在加载数据...&quot;);
	_this.SetRedraw(0);

	//清除前面的数据
	_this.LoadZXGFile(ZXGFILE0,-1,0);
	_this.SetFixedRow(sheet_rjz,1);	
	_this.SetColVisible(sheet_rjz,1,-1);
	_this.SetMainCellRange(sheet_rjz,1,0,_this.GetRowCount(sheet_rjz)-2,_this.GetColCount(sheet_rjz)-1);	

	if (kmbh != &quot;&quot;) {
		filter = &quot; and kmbh=&apos;&quot;+kmbh+&quot;&apos; &quot; + filter;
	}

	//加载日记帐
	var xml = _sql.query(&quot;RJZ&quot;,&quot;YYYY=&quot;+yyyy+&quot;&MM=&quot;+mm+&quot;&KMBH=&quot;+kmbh+&quot;&FILTER=&quot;+filter);
	_this.SetXmlDS(sheet_rjz,1,0,_this.GetRowCount(sheet_rjz)-2,_this.GetColCount(sheet_rjz)-1,xml);

	//加载日记帐明细
	var lsh = _this.GetCellText(sheet_rjz,1,15);
	loadRJZMXData(lsh);

	//计算合计数
	var jfje_sum = 0.00;
	var dfje_sum = 0.00;
	var ye_sum = 0.0;
	var jfbs = 0;
	var dfbs = 0;
	_this.SetToBoolBox(sheet_rjz,0,0);
	for (var r=_this.GetMainCellRangeRow1(sheet_rjz);r&lt;=_this.GetMainCellRangeRow2(sheet_rjz);r++) {
	        //显示的方向从金额方向修改为余额方向 by dxb 2016.3.25
		//var fx = _this.GetCellText(sheet_rjz,r,11);
		var fx = _this.GetCellText(sheet_rjz,r,34);
		var lxbh = _this.GetCellText(sheet_rjz,r,29);
		var jfmny = 1.0*_this.GetCellText(sheet_rjz,r,9);
		var dfmny = 1.0*_this.GetCellText(sheet_rjz,r,10);

		if (_this.GetCellText(sheet_rjz,r,2) == &quot;&quot;) break;

		//计算余额
		var qcbz = 1.0*_this.GetCellText(sheet_rjz,r,30);
		if (qcbz == &quot;1&quot;) {
			ye_sum = 1.0*_this.GetCellText(sheet_rjz,r,12);
		}
		if (qcbz != &quot;1&quot; && (ye_sum == 0 || ye_sum == &quot;&quot;)) {
			ye_sum = ye_sum + jfmny - dfmny;
			_this.SetCellText(sheet_rjz,r,12,ye_sum);
		}
		else {
			_this.SetCellText(sheet_rjz,r,12,ye_sum+jfmny-dfmny);
			ye_sum = 1.0*_this.GetCellText(sheet_rjz,r,12);
		}

		jfje_sum += jfmny;
		dfje_sum += dfmny;

		if (fx == &quot;借&quot;) jfbs ++;
		else if (fx == &quot;贷&quot;) dfbs ++;

		_this.SetToBoolBox(sheet_rjz,r,0);
		_this.SetToComboBox(&quot;&quot;,sheet_rjz,r,21,fkBZ);
		_this.SetToComboBox(&quot;&quot;,sheet_rjz,r,25,printBZ);
		if (_this.GetCellText(sheet_rjz,r,25) == 1) {
			_this.SetCellColor(sheet_rjz,r,25,0,0,255);
		}
		else if (_this.GetCellText(sheet_rjz,r,25) == 0) {
			_this.SetCellColor(sheet_rjz,r,25,255,0,0);
		}

	}
	_this.SetCellText(sheet_rjz,_this.GetRowCount(sheet_rjz)-1,9,jfje_sum);
	_this.SetCellText(sheet_rjz,_this.GetRowCount(sheet_rjz)-1,10,dfje_sum);
	_this.SetCellText(sheet_param,1,9,&quot;借：&quot;+jfbs+&quot;笔  贷：&quot;+dfbs+&quot;笔&quot;);

	_this.AutoFixColWidth(sheet_rjz,600);
	_this.AutoFixScreenWidth();
	_this.SetToolbarString(&quot;&quot;);
	_this.SetRedraw(1);
}

//日记帐明细
function loadRJZMXData(lsh)
{	
	_this.SetToolbarString(&quot;正在加载数据...&quot;);

	_this.LoadZXGFile(ZXGFILE1,-1,1);
	_this.LoadZXGFile(ZXGFILE2,-1,2);
	_this.SetFixedRow(sheet_rjzmx1,1);
	_this.SetFixedRow(sheet_rjzmx2,1);

	_this.SetColVisible(sheet_rjzmx1,6,-1);
	_this.SetColVisible(sheet_rjzmx1,7,-1);
	_this.SetColVisible(sheet_rjzmx2,6,-1);
	_this.SetColVisible(sheet_rjzmx2,7,-1);

	_this.SetMainCellRange(sheet_rjzmx1,1,0,_this.GetRowCount(sheet_rjzmx1)-2,_this.GetColCount(sheet_rjzmx1)-1);
	_this.SetMainCellRange(sheet_rjzmx2,1,0,_this.GetRowCount(sheet_rjzmx2)-2,_this.GetColCount(sheet_rjzmx2)-1);

	_sql.querytods(&quot;RJZMX&quot;,&quot;LSH=&quot;+lsh);
	var sumrow = _this.XMLDS_GetRowCount();
	var midrow = Math.ceil(sumrow / 2);
	//alert(&quot;sumrow=&quot;+sumrow+&quot;,midrow=&quot;+midrow);
	var row1 = _this.GetMainCellRangeRow1(sheet_rjzmx1);
	var row2 = _this.GetMainCellRangeRow2(sheet_rjzmx1);
	var n = 0;
	for (var i=0;i&lt;midrow;i++) {
		n ++;
		if (n &gt; row2) {
			_this.AppendRow(sheet_rjzmx1,n-1);
		}
		if(_this.XMLDS_GetString(i,&quot;KMBH&quot;) != &quot;&quot;){
			_this.SetCellText(sheet_rjzmx1,row1,0,_this.XMLDS_GetString(i,&quot;KMBH&quot;));
			_this.SetCellText(sheet_rjzmx1,row1,1,_this.XMLDS_GetString(i,&quot;KMMC&quot;));
		}else{
			_this.SetCellText(sheet_rjzmx1,row1,0,_this.XMLDS_GetString(i,&quot;RJLX&quot;));
			_this.SetCellText(sheet_rjzmx1,row1,1,_this.XMLDS_GetString(i,&quot;KXMC&quot;));
		}
		_this.SetCellText(sheet_rjzmx1,row1,2,_this.XMLDS_GetString(i,&quot;YM1&quot;));
		_this.SetCellText(sheet_rjzmx1,row1,3,_this.XMLDS_GetString(i,&quot;YM2&quot;));
		_this.SetCellText(sheet_rjzmx1,row1,4,_this.XMLDS_GetString(i,&quot;JEFX&quot;));
		_this.SetCellText(sheet_rjzmx1,row1,5,_this.XMLDS_GetString(i,&quot;JE&quot;));
		_this.SetCellText(sheet_rjzmx1,row1,6,_this.XMLDS_GetString(i,&quot;ORG&quot;));
		_this.SetCellText(sheet_rjzmx1,row1,7,_this.XMLDS_GetString(i,&quot;ACC&quot;));
		_this.SetCellText(sheet_rjzmx1,row1,8,_this.XMLDS_GetString(i,&quot;LSH&quot;));
		_this.SetCellText(sheet_rjzmx1,row1,9,_this.XMLDS_GetString(i,&quot;LXBH&quot;));
		_this.SetCellText(sheet_rjzmx1,row1,10,_this.XMLDS_GetString(i,&quot;LXXH&quot;));
		row1 ++;
	}
	row1 = _this.GetMainCellRangeRow1(sheet_rjzmx2);
	row2 = _this.GetMainCellRangeRow2(sheet_rjzmx2);
	n = 0;
	for (var i=midrow;i&lt;sumrow;i++) {
	 	n ++;
		if (n &gt; row2) {
			_this.AppendRow(sheet_rjzmx2,n-1);
		}
		if(_this.XMLDS_GetString(i,&quot;KMBH&quot;) != &quot;&quot;){
			_this.SetCellText(sheet_rjzmx2,row1,0,_this.XMLDS_GetString(i,&quot;KMBH&quot;));
			_this.SetCellText(sheet_rjzmx2,row1,1,_this.XMLDS_GetString(i,&quot;KMMC&quot;));
		}else{
			_this.SetCellText(sheet_rjzmx2,row1,0,_this.XMLDS_GetString(i,&quot;RJLX&quot;));
			_this.SetCellText(sheet_rjzmx2,row1,1,_this.XMLDS_GetString(i,&quot;KXMC&quot;));
		}
		_this.SetCellText(sheet_rjzmx2,row1,2,_this.XMLDS_GetString(i,&quot;YM1&quot;));
		_this.SetCellText(sheet_rjzmx2,row1,3,_this.XMLDS_GetString(i,&quot;YM2&quot;));
		_this.SetCellText(sheet_rjzmx2,row1,4,_this.XMLDS_GetString(i,&quot;JEFX&quot;));
		_this.SetCellText(sheet_rjzmx2,row1,5,_this.XMLDS_GetString(i,&quot;JE&quot;));
		_this.SetCellText(sheet_rjzmx2,row1,6,_this.XMLDS_GetString(i,&quot;ORG&quot;));
		_this.SetCellText(sheet_rjzmx2,row1,7,_this.XMLDS_GetString(i,&quot;ACC&quot;));
		_this.SetCellText(sheet_rjzmx2,row1,8,_this.XMLDS_GetString(i,&quot;LSH&quot;));
		_this.SetCellText(sheet_rjzmx2,row1,9,_this.XMLDS_GetString(i,&quot;LXBH&quot;));
		_this.SetCellText(sheet_rjzmx2,row1,10,_this.XMLDS_GetString(i,&quot;LXXH&quot;));

		row1 ++;
	}
	_this.SetToolbarString(&quot;&quot;);
	_this.AutoFixScreenWidth();
	_this.RefreshFormula();

}

//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
	if (sheet == sheet_param) {
		cur_yyyy = _this.GetCellText(sheet_param,1,1);
		cur_mm = _this.GetCellText(sheet_param,1,3);
		cur_kmbh = _this.GetCellText(sheet_param,1,6);
		if (cur_yyyy !=&quot;&quot; && cur_mm != &quot;&quot; && cur_kmbh != &quot;&quot;) {
			loadRJZData(cur_yyyy,cur_mm,cur_kmbh,&quot; &quot;);
		}

	}
	else if (sheet == sheet_rjz) {
		if (col != 0) {
			_this.SetCellText(sheet_rjz,row,1,&quot;*&quot;); //设置标志列为修改*
			g_modifyflag = 1;
		}	
		else if (col == 0) {
			if (row == 0) { //全选
				for (var r=_this.GetMainCellRangeRow1(sheet_rjz);r&lt;=_this.GetMainCellRangeRow2(sheet_rjz);r++) {
					if (_this.GetCellText(sheet_rjz,r,2) == &quot;&quot;) break;
					_this.SetCellText(sheet_rjz,r,0,newvalue);
					if (newvalue == 0) {
						setOneRowBkColor(sheet,r,255,255,255);		
					}
					else {
						setOneRowBkColor(sheet,r,255,236,139);
					}
				}
			}
			else {
				if (newvalue == 0) {
					_this.SetCellText(sheet,0,0,0);
					setOneRowBkColor(sheet,row,255,255,255);		
				}
				else {
					setOneRowBkColor(sheet,row,255,236,139);

					var n = 0;
					var m = 0;
					for (var r=_this.GetMainCellRangeRow1(sheet);r&lt;=_this.GetMainCellRangeRow2(sheet);r++) {
						if (_this.GetCellText(sheet,r,2) != &quot;&quot;) {
							n ++;
							 if (_this.GetCellText(sheet,r,0) == &quot;1&quot;) {
							 	m ++;
							 }	
						}
					}
					if (n == m) {
						_this.SetCellText(sheet,0,0,1);
					}

				}

			}
		}
	}
}

//鼠标左键点击
function _thisOnMouseLClick(sheet,row,col)
{
	if (sheet == sheet_rjz && row &gt;= _this.GetMainCellRangeRow1(sheet_rjz) && row &lt;= _this.GetMainCellRangeRow2(sheet_rjz)) {
		var lsh = _this.GetCellText(sheet_rjz,row,15);		
		if (lsh != &quot;&quot;) {
			loadRJZMXData(lsh);
			setDataRowTips(row);

			var dylsh = _this.GetCellText(sheet_rjz,row,16);
			if (dylsh != &quot;&quot;) {
				_sql.querytods(&quot;DYKM&quot;,&quot;DYLSH=&quot;+dylsh);
				_this.SetCellTip(sheet_rjz,1,9,_this.XMLDS_GetString(0,&quot;NAME&quot;));
				_this.SetToolbarString(&quot; &gt;&gt; 对应科目：&quot;+_this.XMLDS_GetString(0,&quot;NAME&quot;));
			}
			cur_kmbh = _this.GetCellText(sheet_rjz,row,32);
			if (ctrlKey == true) { //按住Ctrl键多选
				selectOneRow(sheet_rjz,row);
			}
		}
	}
	else if (sheet == sheet_param) {
		if (row == 1 && col == 7) {
			showKM(); //选择科目
		}
		else if (row == 1 && col == 8) {
			showFilter(); //过滤
		}
		else if (row == 1 && col == 12) {
			//check_next_qcye(); //重算余额
		}
	}
}

//提示行信息
function setDataRowTips(row)
{
	_this.SetCellText(sheet_tips,0,2,_this.GetCellText(sheet_rjz,row,5));
	_this.SetCellText(sheet_tips,0,5,_this.GetCellText(sheet_rjz,row,18)+&quot;-&quot;+_this.GetCellText(sheet_rjz,row,19));
	_this.SetCellText(sheet_tips,0,7,_this.GetCellText(sheet_rjz,row,13));
	_this.SetCellText(sheet_tips,0,9,_this.GetCellText(sheet_rjz,row,29));

}

function showKM()
{
	var obj = new Object();
        obj.filter = &quot;xjyhbz &lt;&gt; 0&quot;;
        obj.mjbz = &quot;1&quot;;
        obj.bz = &quot;&quot;;
        obj.jb = 0;
        obj.kmbh = &quot;&quot;;
	var retVal = window.showModalDialog(&quot;show.sp?grdid=CW_KMSEL&quot;,obj,&quot;dialogWidth=600px;dialogHeight=500px&quot;);
	cur_kmbh = retVal;
	if (cur_kmbh != &quot;&quot; && cur_kmbh != &quot;undefined&quot; && cur_kmbh != undefined) {
		_this.SetCellText(sheet_param,1,6,cur_kmbh);
		_thisOnCellModify(sheet_param,1,6,&quot;&quot;,cur_kmbh);
	}

}

function rjzModifyCheck()
{
	var currow = _this.GetCurrentRow(sheet_rjz);
	if (currow &lt;= 0) return false;
	var qcbz = _this.GetCellText(sheet_rjz,currow,30); 
	var pzh  = _this.GetCellText(sheet_rjz,currow,31); 
	if (pzh != &quot;&quot;) {
		alert(&quot;当前日记帐纪录已出凭证不能修改!\n请跟上级领导审批请示?&quot;);
		return false;
	}
	if (qcbz == &quot;1&quot;) {
		alert(&quot;上年结转或上月结转不能修改!&quot;);
		return false;
	}

	return true;
}

//修改日记帐
function rjzModify()
{
	//检查是否已经结账
	var jz_yy = G_LOGDAT.split(&quot;-&quot;)[0];
	var jz_mm = G_LOGDAT.split(&quot;-&quot;)[1]*1;
	var jz_sbh = G_ORGID;
	var jz_zth = G_ACCID.replace(G_ORGID,&quot;&quot;);

	var param = new Object();
	param.yy = jz_yy;
	param.mm = jz_mm;
	param.sbh = jz_sbh;
	param.zth = jz_zth;
	var jz_retVal = invokeOSFuncExt(&quot;check_jz&quot;,param);
	var jz_ret = jz_retVal.split(&quot;@&quot;)[0];
	var jz_msg = jz_retVal.split(&quot;@&quot;)[1];
	if(jz_ret != 1 ){
		alert(jz_retVal);
		return false;
	}

	if (rjzModifyCheck() == false) return;
	//var currow = _this.GetCurrentRow(sheet_rjz);
	for (var currow=_this.GetMainCellRangeRow1(sheet_rjz);currow&lt;=_this.GetMainCellRangeRow2(sheet_rjz);currow++) {
		var qcbz = _this.GetCellText(sheet_rjz,currow,30); 
		if (_this.GetCellText(sheet_rjz,currow,2) != &quot;&quot; && qcbz != &quot;1&quot;) {
			_this.SetCellLock(sheet_rjz,currow,4,0); //日
			_this.SetCellLock(sheet_rjz,currow,8,0); //摘要
			_this.SetCellLock(sheet_rjz,currow,17,0); //做帐顺序号
		}
	}
}

//修改款项
function rjzModifyMX()
{
	//检查是否已经结账
	var jz_yy = G_LOGDAT.split(&quot;-&quot;)[0];
	var jz_mm = G_LOGDAT.split(&quot;-&quot;)[1]*1;
	var jz_sbh = G_ORGID;
	var jz_zth = G_ACCID.replace(G_ORGID,&quot;&quot;);

	var param = new Object();
	param.yy = jz_yy;
	param.mm = jz_mm;
	param.sbh = jz_sbh;
	param.zth = jz_zth;
	var jz_retVal = invokeOSFuncExt(&quot;check_jz&quot;,param);
	var jz_ret = jz_retVal.split(&quot;@&quot;)[0];
	var jz_msg = jz_retVal.split(&quot;@&quot;)[1];
	if(jz_ret != 1 ){
		alert(jz_retVal);
		return false;
	}

	if (rjzModifyCheck() == false) return;
	var currow = _this.GetCurrentRow(sheet_rjz);
	var lsh = _this.GetCellText(sheet_rjz,currow,15); 
	var dylsh = _this.GetCellText(sheet_rjz,currow,16); //对应流水号
	var ym1 = _this.GetCellText(sheet_rjzmx1,1,2); 
	var ym2 = _this.GetCellText(sheet_rjzmx1,1,3);
	var mkjmbz = _this.GetCellText(sheet_rjz,currow,35);
	if(mkjmbz == 3) {
		alert(&quot;未分款界面数据，不允许修改款项!!&quot;);
		return false;
	}
	var obj = new Object();
	obj.yy = _this.GetCellText(sheet_rjz,currow,2);
	obj.mm = _this.GetCellText(sheet_rjz,currow,3);
	obj.dd = _this.GetCellText(sheet_rjz,currow,4);
	obj.kmbh = _this.GetCellText(sheet_rjz,currow,32);
	obj.pch = _this.GetCellText(sheet_rjz,currow,5);
	obj.jsfs = _this.GetCellText(sheet_rjz,currow,13);
	obj.dwbh = _this.GetCellText(sheet_rjz,currow,18);
	obj.dwmc = _this.GetCellText(sheet_rjz,currow,19);
	obj.zy = _this.GetCellText(sheet_rjz,currow,8);
	//显示的方向从金额方向修改为余额方向 by dxb 2016.3.25
	//obj.jefx = _this.GetCellText(sheet_rjz,currow,11);
	obj.jefx = _this.GetCellText(sheet_rjz,currow,34);
	if (obj.jefx == &quot;借&quot;) {
		obj.je = _this.GetCellText(sheet_rjz,currow,9);
	}
	else {
		obj.je = _this.GetCellText(sheet_rjz,currow,10);
	}
	_sql.querytods(&quot;DWLX&quot;,&quot;DWBH=&quot;+obj.dwbh);	
	obj.dwlxbh = _this.XMLDS_GetString(0,&quot;DWLXBH&quot;);
	if (lsh == &quot;&quot;) return;
	var retVal = window.showModalDialog(&quot;show.sp?grdid=RJZ_LR&LSH=&quot;+lsh+&quot;&YM1=&quot;+ym1+&quot;&YM2=&quot;+ym2+&quot;&xgkxbz=2&DYLSH=&quot;+dylsh,obj,&quot;dialogWidth=1300px;dialogHeight=800px;&quot;);
	if (retVal != &quot;&quot; && retVal != &quot;undefined&quot; && retVal != undefined) {

	}
	loadRJZData(cur_yyyy,cur_mm,cur_kmbh,&quot;&quot;);
}

//保存
function rjzSave()
{
	//检查是否已经结账
	var jz_yy = G_LOGDAT.split(&quot;-&quot;)[0];
	var jz_mm = G_LOGDAT.split(&quot;-&quot;)[1]*1;
	var jz_sbh = G_ORGID;
	var jz_zth = G_ACCID.replace(G_ORGID,&quot;&quot;);

	var param = new Object();
	param.yy = jz_yy;
	param.mm = jz_mm;
	param.sbh = jz_sbh;
	param.zth = jz_zth;
	var jz_retVal = invokeOSFuncExt(&quot;check_jz&quot;,param);
	var jz_ret = jz_retVal.split(&quot;@&quot;)[0];
	var jz_msg = jz_retVal.split(&quot;@&quot;)[1];
	if(jz_ret != 1 ){
		alert(jz_retVal);
		return false;
	}

	var param = new Object();
	param.rjzxml = getRJZSaveXml();
	param.thisorgid = G_ORGID;
	param.thisaccid = G_ACCID;
	try {
		var ret = invokeOSFuncExt(&quot;Save&quot;,param);
		if (ret == 1) {
			alert(&quot;保存成功！&quot;);
			g_modifyflag = 0;
			loadRJZData(cur_yyyy,cur_mm,cur_kmbh,&quot; &quot;);
		}
		else if (ret == 0) {
			alert(&quot;没有内容修改！&quot;);
		}
		else {
			alert(&quot;保存日记帐出错:&quot;+ret);
		}
	}
	catch (e) {
		alert(&quot;保存日记帐出错:&quot;+e.toString());
	}

}

//获取修改的数据xml
function getRJZSaveXml()
{
	var xml = &quot;&lt;?xml version = &apos;1.0&apos;?&gt;&lt;ROWSET&gt;\n&quot;;
	var num = 0;
	for (var r=_this.GetMainCellRangeRow1(sheet_rjz);r&lt;=_this.GetMainCellRangeRow2(sheet_rjz);r++) {
		var flag = _this.GetCellText(sheet_rjz,r,1);
		var yy = _this.GetCellText(sheet_rjz,r,2);
		if (flag == &quot;*&quot; && yy != &quot;&quot;) {
			num ++;
			xml += &quot;&lt;ROW num=\&quot;&quot;+num+&quot;\&quot;&gt;\n&quot;;
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_rjz,2)+&quot;&gt;&quot; + _this.GetCellText(sheet_rjz,r,2) +&quot;&lt;/&quot;+_this.GetColName(sheet_rjz,2)+&quot;&gt;\n&quot;;	//年
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_rjz,3)+&quot;&gt;&quot; + _this.GetCellText(sheet_rjz,r,3) +&quot;&lt;/&quot;+_this.GetColName(sheet_rjz,3)+&quot;&gt;\n&quot;;	//月
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_rjz,4)+&quot;&gt;&quot; + _this.GetCellText(sheet_rjz,r,4) +&quot;&lt;/&quot;+_this.GetColName(sheet_rjz,4)+&quot;&gt;\n&quot;;	//日
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_rjz,8)+&quot;&gt;&quot; + _this.GetCellText(sheet_rjz,r,8) +&quot;&lt;/&quot;+_this.GetColName(sheet_rjz,8)+&quot;&gt;\n&quot;;	//摘要
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_rjz,15)+&quot;&gt;&quot; + _this.GetCellText(sheet_rjz,r,15) +&quot;&lt;/&quot;+_this.GetColName(sheet_rjz,15)+&quot;&gt;\n&quot;;	//流水号
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_rjz,17)+&quot;&gt;&quot; + _this.GetCellText(sheet_rjz,r,17) +&quot;&lt;/&quot;+_this.GetColName(sheet_rjz,17)+&quot;&gt;\n&quot;;	//做帐顺序号
			xml += &quot;&lt;/ROW&gt;\n&quot;;		
		}
	}
	xml += &quot;&lt;/ROWSET&gt;&quot;;
	return xml;
}



//单位明细
function rjzDwmx()
{
	var rjz_currow = _this.GetCurrentRow(sheet_rjz);
	var dwbh = _this.GetCellText(sheet_rjz,rjz_currow,18);
        if (rjz_currow == -1 || dwbh == &quot;&quot;) {
		alert(&quot;没有选中日记账记录或者没有单位编号！&quot;);
		return;
	}
	var url = &quot;show.sp?grdid=RJZ_DWMXZ&yyyy=&quot;+cur_yyyy+&quot;&dwbh=&quot;+dwbh;
	window.open(&quot;show.sp?grdid=RJZ_DWMXZ&yyyy=&quot;+cur_yyyy+&quot;&dwbh=&quot;+dwbh);
	//f_openWindow(-1,-1,-1,-1,url,null,&quot;&quot;,&quot;&quot;);

}

//新增日记帐
function rjzNew()
{
	//检查是否已经结账
	var jz_yy = G_LOGDAT.split(&quot;-&quot;)[0];
	var jz_mm = G_LOGDAT.split(&quot;-&quot;)[1]*1;
	var jz_sbh = G_ORGID;
	var jz_zth = G_ACCID.replace(G_ORGID,&quot;&quot;);

	var param = new Object();
	param.yy = jz_yy;
	param.mm = jz_mm;
	param.sbh = jz_sbh;
	param.zth = jz_zth;
	var jz_retVal = invokeOSFuncExt(&quot;check_jz&quot;,param);
	var jz_ret = jz_retVal.split(&quot;@&quot;)[0];
	var jz_msg = jz_retVal.split(&quot;@&quot;)[1];
	if(jz_ret != 1 ){
		alert(jz_retVal);
		return false;
	}

	if (g_modifyflag == 1) {
		if (confirm(&quot;日记帐已被修改，是否先进行保存？&quot;) == true) {
			rjzSave();
		}		
	}
	window.open(&quot;show.sp?grdid=RJZ_LR&LSH=&YM1=&YM2=&quot;);	
}

//点击菜单项
function _thisOnMenuItemSelect(sheet,row,col,menuitemid)
{
	//日记帐打印
	if (menuitemid == &quot;print_selected&quot;) {
		printRJZ(1);
	}
	else if (menuitemid == &quot;print_fromcurrow&quot;) {
		printRJZ(2);
	}
	else if (menuitemid == &quot;rjztjb1&quot; || menuitemid == &quot;rjztjb2&quot; || menuitemid == &quot;rjztjb3&quot; || menuitemid == &quot;rjztjb4&quot; || menuitemid == &quot;rjztjb5&quot; || menuitemid == &quot;rjztjb6&quot;)  {
		//window.open(&quot;show.sp?grdid=RJZ_TJ&quot;);	
		var yyyy = _this.GetCellText(sheet_param,1,1);
		var mm = formatMD(_this.GetCellText(sheet_param,1,3));
		var dat1 = yyyy + &quot;-&quot; + mm + &quot;-01&quot;;
		_sql.querytods(&quot;LASTDAY&quot;,&quot;DAT1=&quot;+dat1);
		var dat2 = _this.XMLDS_GetString(0,&quot;DAT2&quot;);
		var kmbh = _this.GetCellText(sheet_param,1,6);

		var repmwid = &quot;RJZ_TJ&quot;;
		if (menuitemid == &quot;rjztjb1&quot;) repmwid = &quot;RJZ_TJ&quot;;	//日记帐款项统计
		else if (menuitemid == &quot;rjztjb2&quot;) repmwid = &quot;RJZ_TJ2&quot;;	//零星医疗费报账支出明细表
		else if (menuitemid == &quot;rjztjb3&quot;) repmwid = &quot;RJZ_TJ3&quot;;	//现金支出汇总表
		else if (menuitemid == &quot;rjztjb4&quot;) repmwid = &quot;RJZ_TJ4&quot;;	//享受大额医疗补助人员名单
		else if (menuitemid == &quot;rjztjb5&quot;) repmwid = &quot;RJZ_TJ5&quot;;	//费用申报表
//		else if (menuitemid == &quot;rjztjb6&quot;) repmwid = &quot;RJZ_TJ6&quot;;	//享受大额医疗补助人员名单

		window.showModalDialog(&quot;show.sp?grdid=&quot;+repmwid+&quot;&DAT1=&quot;+dat1+&quot;&DAT2=&quot;+dat2+&quot;&KMBH=&quot;+kmbh,&quot;&quot;,&quot;dialogWidth=1000px;dialogHeight=600px;&quot;);
	}
	else if (menuitemid == &quot;csye&quot;) {
	        check_next_qcye();
	}
	else if(menuitemid == &quot;yejc1&quot;) {
	        yejc(1);
	        loadRJZData(cur_yyyy,cur_mm,cur_kmbh,&quot; &quot;);
	}
	else if(menuitemid == &quot;yejc2&quot;) {
	        yejc(2);
	        loadRJZData(cur_yyyy,cur_mm,cur_kmbh,&quot; &quot;);
	}
	else if(menuitemid == &quot;jejc&quot;) {
		alert(&quot;未开发&quot;);
	        jejc();
	}
}

//打印日记帐
//mod=1打印选中行 mod=2从当前行开始打印
function printRJZ(mod)
{
	//增加权限控制
	var author = invokeOSFunc(&quot;Print&quot;,&quot;&quot;);
	if (author != 1) return;

	_this.LoadZXGFile(zxgRoot+&quot;RJZ_PRINT.zxg&quot;,-1,sheet_print);

	if (mod == 2) {
		var currow = _this.GetCurrentRow(sheet_rjz);
		for (var r=currow;r&lt;=_this.GetMainCellRangeRow2(sheet_rjz);r++) {
			var djh = _this.GetCellText(0,r,14);
			var lsh = _this.GetCellText(sheet_rjz,r,15);
			var dybz = _this.GetCellText(sheet_rjz,r,25);
			if (lsh != &quot;&quot;) {
				if (dybz == 1) {
					if (!confirm(&quot;该日记帐（流水号&quot;+lsh+&quot;）已打印[&quot;+_this.GetCellText(sheet_rjz,r,26)+&quot;]，是否重新打印？&quot;)) {
						continue;
					}
				}
				//给打印模板赋值
				setRJZPrintData(djh,lsh,sheet_print);
				//发送打印作业
				//_this.SetPrintMode(1);
				_this.Print(sheet_print,0);
			}
		}
	}
	else if (mod == 1) {
		for (var r=_this.GetMainCellRangeRow1(sheet_rjz);r&lt;=_this.GetMainCellRangeRow2(sheet_rjz);r++) {
			var flag = _this.GetCellText(sheet_rjz,r,0);
			var djh = _this.GetCellText(0,r,14);
			var lsh = _this.GetCellText(sheet_rjz,r,15);
			var dybz = _this.GetCellText(sheet_rjz,r,25);
			if (flag == &quot;1&quot; && lsh != &quot;&quot;) {
				if (dybz == 1) {
					if (!confirm(&quot;该日记帐（流水号&quot;+lsh+&quot;）已打印[&quot;+_this.GetCellText(sheet_rjz,r,26)+&quot;]，是否重新打印？&quot;)) {
						continue;
					}
				}
				//给打印模板赋值
				setRJZPrintData(djh,lsh,sheet_print);
				//发送打印作业
				_this.Print(sheet_print,0);

				//更新打印标志
				_sql.run(&quot;UPDATEPRINT&quot;,&quot;LSH=&quot;+lsh+&quot;&DYR=&quot;+G_USRNAM);
				_this.SetCellText(sheet_rjz,r,25,&quot;1&quot;);
				_this.SetCellColor(sheet_rjz,r,25,0,0,255);
				_this.SetCellText(sheet_rjz,r,26,getNowFormatDate());
				_this.SetCellText(sheet_rjz,r,27,G_USRNAM);
			}
		}

	}
}

//给日记帐打印模板赋值
function setRJZPrintData(djh,lsh,sheet_print)
{
	//_sql.querytods(&quot;PRINTDS&quot;,&quot;LSH=&quot;+lsh);
	var xmlstr = invokeOSFunc(&quot;getRJZPrintData&quot;,&quot;thisaccid=&quot;+G_ACCID+&quot;&thisorgid=&quot;+G_ORGID+&quot;&printlsh=&quot;+lsh);
	_this.XMLDS_Parse(xmlstr);

	var yy = _this.XMLDS_GetString(0,&quot;YY&quot;);
	var mm = _this.XMLDS_GetString(0,&quot;MM&quot;);
	var dd = _this.XMLDS_GetString(0,&quot;DD&quot;);
	var zy = _this.XMLDS_GetString(0,&quot;ZY&quot;);
	var kmbh = _this.XMLDS_GetString(0,&quot;KMBH&quot;);
	var dwbh = _this.XMLDS_GetString(0,&quot;DWBH&quot;);
	var dwmc = _this.XMLDS_GetString(0,&quot;DWMC&quot;);
	var ym1 = _this.XMLDS_GetString(0,&quot;YM1&quot;);
	var ym2 = _this.XMLDS_GetString(0,&quot;YM2&quot;);
	var jsfs = &quot;银行托收&quot;;//_this.XMLDS_GetString(0,&quot;JSFS&quot;);
	var dwkmyhmc = _this.XMLDS_GetString(0,&quot;DWKMYHMC&quot;);
	var sumje = 0.0;//_this.XMLDS_GetString(0,&quot;JE&quot;);

	_this.SetCellText(sheet_print,1,1,&quot;单位编号:&quot;+dwbh+&quot;　　　　　　　　&quot;+yy+&quot;　 　   &quot;+mm+&quot;　　　&quot;+dd+&quot;　　　　　单据号:&quot;+djh+&quot;　打印日期:&quot;+getCurrDate());
	_this.SetCellText(sheet_print,2,2,dwmc);
	_this.SetCellText(sheet_print,2,6,yy+&quot;        &quot;+mm);
	//_this.SetCellText(sheet_print,3,2,&quot;--单位科目银行名称&quot;+dwkmyhmc); //单位科目银行名称 //不需要打印的

	//_this.SetCellText(sheet_print,17,1,zy);
	_this.SetCellText(sheet_print,13,1,&quot;　　　收据联与托收凭证同时使用才有效&quot;);  //备注
	_this.SetCellText(sheet_print,13,7,&quot;　&quot;+jsfs);
	//_this.SetCellText(sheet_print,13,9,yy+&quot;-&quot;+mm+&quot;-&quot;+dd);

	//缴费项目	期限	单位缴	个人缴	
	var rowhead = formatPrintStr(&quot;缴费项目&quot;,16,0)+formatPrintStr(&quot;期限&quot;,12,0)+formatPrintStr(&quot;单位缴&quot;,12,1)+formatPrintStr(&quot;个人缴&quot;,10,1);
	_this.SetCellText(sheet_print,5,1,rowhead);
	_this.SetCellText(sheet_print,5,5,rowhead);

	var rowcnt = _this.XMLDS_GetRowCount();
	if (rowcnt &lt;= 12) {
		var rowstr = &quot;&quot;;
		for (var i=0;i&lt;rowcnt;i++) {
			var rjlx = _this.XMLDS_GetString(i,&quot;RJLX&quot;);
			var kxmc = _this.XMLDS_GetString(i,&quot;KXMC&quot;);
			var ym1 = _this.XMLDS_GetString(i,&quot;YM1&quot;);
			var ym2 = _this.XMLDS_GetString(i,&quot;YM2&quot;);
			var mxjefx = _this.XMLDS_GetString(i,&quot;JEFX&quot;);
			var mxje = _this.XMLDS_GetString(i,&quot;MXJE&quot;);
			var dwje = _this.XMLDS_GetString(i,&quot;DWJE&quot;);
			var grje = _this.XMLDS_GetString(i,&quot;GRJE&quot;);
			if (1 * dwje == 0) dwje = &quot;0.00&quot;;
			if (1 * grje == 0) grje = &quot;0.00&quot;;
			sumje += (1.0 * mxje);

			rowstr += formatPrintStr(rjlx+kxmc,16,0) //缴费项目
				+ formatPrintStr(ym1+ym2,12,0) //期限
				+ formatPrintStr(dwje,12,1) //单位缴
				+ formatPrintStr(grje,10,1) //个人缴
				+ &quot;\n&quot;;
		}
		_this.SetCellText(sheet_print,6,1,rowstr);
	}
	else {
		//第一列的内容		
		var rowstr = &quot;&quot;;
		for (var i=0;i&lt;12;i++) {
			var rjlx = _this.XMLDS_GetString(i,&quot;RJLX&quot;);
			var kxmc = _this.XMLDS_GetString(i,&quot;KXMC&quot;);
			var ym1 = _this.XMLDS_GetString(i,&quot;YM1&quot;);
			var ym2 = _this.XMLDS_GetString(i,&quot;YM2&quot;);
			var mxjefx = _this.XMLDS_GetString(i,&quot;JEFX&quot;);
			var mxje = _this.XMLDS_GetString(i,&quot;MXJE&quot;);
			var dwje = _this.XMLDS_GetString(i,&quot;DWJE&quot;);;
			var grje = _this.XMLDS_GetString(i,&quot;GRJE&quot;);;
			if (1 * dwje == 0) dwje = &quot;0.00&quot;;
			if (1 * grje == 0) grje = &quot;0.00&quot;;
			sumje += (1.0 * mxje);

			rowstr += formatPrintStr(rjlx+kxmc,16,0) //缴费项目
				+ formatPrintStr(ym1+ym2,12,0) //期限
				+ formatPrintStr(dwje,12,1) //单位缴
				+ formatPrintStr(grje,10,1) //个人缴
				+ &quot;\n&quot;;
		}		
		_this.SetCellText(sheet_print,6,1,rowstr);

		//第二列的内容
		rowstr = &quot;&quot;;
		for (var i=12;i&lt;rowcnt;i++) {
			var rjlx = _this.XMLDS_GetString(i,&quot;RJLX&quot;);
			var kxmc = _this.XMLDS_GetString(i,&quot;KXMC&quot;);
			var ym1 = _this.XMLDS_GetString(i,&quot;YM1&quot;);
			var ym2 = _this.XMLDS_GetString(i,&quot;YM2&quot;);
			var mxjefx = _this.XMLDS_GetString(i,&quot;JEFX&quot;);
			var mxje = _this.XMLDS_GetString(i,&quot;MXJE&quot;);
			var dwje = _this.XMLDS_GetString(i,&quot;DWJE&quot;);;
			var grje = _this.XMLDS_GetString(i,&quot;GRJE&quot;);;
			if (1 * dwje == 0) dwje = &quot;0.00&quot;;
			if (1 * grje == 0) grje = &quot;0.00&quot;;
			sumje += (1.0 * mxje);

			rowstr += formatPrintStr(rjlx+kxmc,16,0) //缴费项目
				+ formatPrintStr(ym1+ym2,12,0) //期限
				+ formatPrintStr(dwje,12,1) //单位缴
				+ formatPrintStr(grje,10,1) //个人缴
				+ &quot;\n&quot;;
		}		
		_this.SetCellText(sheet_print,6,5,rowstr);
	}

	var dxje = convertCurrency(sumje);
	dxje = dxje.replace(&quot;人民币&quot;,&quot; &quot;) + &quot;（￥&quot;+fmoney(sumje,2)+&quot;）&quot;;
	_this.SetCellText(sheet_print,4,2,dxje); //大写金额


}

//转为带,号分隔的格式
function fmoney(s, n)   
{   
   n = n &gt; 0 && n &lt;= 20 ? n : 2;   
   s = parseFloat((s + &quot;&quot;).replace(/[^\d\.-]/g, &quot;&quot;)).toFixed(n) + &quot;&quot;;   
   var l = s.split(&quot;.&quot;)[0].split(&quot;&quot;).reverse(),   
   r = s.split(&quot;.&quot;)[1];   
   t = &quot;&quot;;   
   for(i = 0; i &lt; l.length; i ++ )   
   {   
      t += l[i] + ((i + 1) % 3 == 0 && (i + 1) != l.length ? &quot;,&quot; : &quot;&quot;);   
   }   
   return t.split(&quot;&quot;).reverse().join(&quot;&quot;) + &quot;.&quot; + r;   
} 

///&lt;summary&gt;获得字符串实际长度，中文2，英文1&lt;/summary&gt;
///&lt;param name=&quot;str&quot;&gt;要获得长度的字符串&lt;/param&gt;  
function getStrLength(str) 
{
	var realLength = 0, len = str.length, charCode = -1;
	for (var i = 0; i &lt; len; i++) {
		charCode = str.charCodeAt(i);
		if (charCode &gt;= 0 && charCode &lt;= 128) realLength += 1;
		else realLength += 2;
	}
	return realLength;
}

//在前面或者后面补足空格
function formatPrintStr(str,len,bef)
{
	str = &quot;&quot;+str;
	var slen = getStrLength(str);//str.length();
	if (slen &gt; len) return str.substring(0,len);
	var trim = &quot;&quot;;
	for (var i=0;i&lt;(len-slen);i++) {
		trim += &quot; &quot;;
	}
	if (bef == 1) return trim + str;
	else return str + trim;

}

//取当前日期
function getCurrDate()
{
	var now = new Date();
        var year = now.getFullYear();       //年
        var month = now.getMonth() + 1;     //月
        var day = now.getDate();            //日
        var clock = year + &quot;-&quot;;

        if(month &lt; 10) clock += &quot;0&quot;;
        clock += month + &quot;-&quot;;
	if(day &lt; 10) clock += &quot;0&quot;;
        clock += day + &quot; &quot;;
        return(clock); 
} 


//日记帐过滤条件
function showFilter()
{
	var obj = new Object();
	obj.yyyy = cur_yyyy;
	obj.mm = cur_mm;
	obj.kmbh = cur_kmbh;	
	obj.kmmc = _this.GetCellShowText(sheet_param,1,6);
	var retVal = window.showModalDialog(&quot;show.sp?grdid=RJZ_FILTER&quot;,obj,&quot;dialogWidth=600px;dialogHeight=300px&quot;);
	var param = retVal;
//	alert(param.dat1+&quot;,kmbh=&quot;+param.kmbh);
//	if (filter != &quot;&quot; && filter != &quot;undefined&quot; && filter != undefined) {
//		alert(&quot;过滤&quot;);
//	}

	try {
		var filter = &quot;&quot;;
		if (param.reset == &quot;1&quot;) { //恢复
			loadRJZData(cur_yyyy,cur_mm,cur_kmbh,&quot;&quot;);
		}
		else {
			if (param.dat1 != &quot;&quot;) {
				var dat1 = param.dat1;
				var dat2 = param.dat2;
				//cur_yyyy = dat1.split(&quot;-&quot;)[0];
				//cur_mm = 1 * (dat1.split(&quot;-&quot;)[1]);
				var dd1 = 1 * (dat1.split(&quot;-&quot;)[2]);
				var dd2 = 1 * (dat2.split(&quot;-&quot;)[2]);
				filter = &quot; and (dd&gt;=&quot;+dd1+&quot; and dd&lt;=&quot;+dd2+&quot;)&quot;;
			}
			if (param.jsfs != &quot;&quot;) {
				filter += &quot; and (jsfs=&apos;&quot;+param.jsfs+&quot;&apos;)&quot;;
			}
			if (param.kmbh != &quot;&quot;) {
				_this.SetCellText(sheet_param,1,6,param.kmbh);
				cur_kmbh = param.kmbh;
			}
			if (param.dwbh != &quot;&quot;) {
				filter += &quot; and (dwbh=&apos;&quot;+param.dwbh+&quot;&apos;)&quot;;
			}
			if (param.zy != &quot;&quot;) {
				filter += &quot; and (zy like &apos;%25&quot;+param.zy+&quot;%25&apos;)&quot;;
			}
			if (param.mny1 != &quot;&quot;) {
				filter += &quot; and je&gt;=&quot;+param.mny1;
			}
			if (param.mny2 != &quot;&quot;) {
				filter += &quot; and je&lt;=&quot;+param.mny2;
			}
			if (param.printbz != &quot;&quot;) {
				filter += &quot; and dybz=&apos;&quot;+param.printbz+&quot;&apos;&quot;;
			}
			if (param.djh != &quot;&quot;) {
				filter += &quot; and djh like &apos;%25&quot;+param.djh+&quot;%25&apos;&quot;;
			}
			if (param.pch != &quot;&quot;) {
				filter += &quot; and pch like &apos;%25&quot;+param.pch+&quot;%25&apos;&quot;;
			}
			//alert(filter);
			loadRJZData(cur_yyyy,cur_mm,param.kmbh,filter);
		}
	}
	catch(e) {}
}

