	param.yy = jz_yy;
	param.mm = jz_mm;
	param.sbh = jz_sbh;
	param.zth = jz_zth;
	var jz_retVal = invokeOSFuncExt(&quot;check_jz&quot;,param);
	var jz_ret = jz_retVal.split(&quot;@&quot;)[0];
	var jz_msg = jz_retVal.split(&quot;@&quot;)[1];
	if(jz_ret != 1 ){
		alert(jz_retVal);
		return false;
	}

	//alert(&quot;未完成！&quot;);
	if (g_modifyflag == 1) {
		alert(&quot;日记帐已修改，请先保存！&quot;);
		return;
	}
	var currow = _this.GetCurrentRow(sheet_rjz);
	if (currow &lt; 1) return;
	var insertsxh = _this.GetCellText(sheet_rjz,currow,17); //做账顺序号
