	if (retVal != &quot;&quot; && retVal != &quot;undefined&quot; && retVal != undefined) {

	}
	loadRJZData(cur_yyyy,cur_mm,cur_kmbh,&quot;&quot;);

}

//删除日记账
function rjzDelete()
{
	//检查是否已经结账
	var jz_yy = G_LOGDAT.split(&quot;-&quot;)[0];
	var jz_mm = G_LOGDAT.split(&quot;-&quot;)[1]*1;
	var jz_sbh = G_ORGID;
	var jz_zth = G_ACCID.replace(G_ORGID,&quot;&quot;);

	var param = new Object();
	param.yy = jz_yy;
	param.mm = jz_mm;
	param.sbh = jz_sbh;
	param.zth = jz_zth;
	var jz_retVal = invokeOSFuncExt(&quot;check_jz&quot;,param);
	var jz_ret = jz_retVal.split(&quot;@&quot;)[0];
	var jz_msg = jz_retVal.split(&quot;@&quot;)[1];
	if(jz_ret != 1 ){
		alert(jz_retVal);
		return false;
	}

	var selcnt = 0;
	var selectlsh = &quot;&quot;;

	for (var row=_this.GetMainCellRangeRow1(sheet_rjz);row&lt;=_this.GetMainCellRangeRow2(sheet_rjz);row++) {
		var flg = _this.GetCellText(sheet_rjz,row,0);
		var czlx = _this.GetCellText(sheet_rjz,row,20);
		var fkbz = _this.GetCellText(sheet_rjz,row,21);
		var pzh = _this.GetCellText(sheet_rjz,row,31);
		var qcbz = _this.GetCellText(sheet_rjz,row,30);
		if (flg == 1) {
			selcnt ++;
			if (selectlsh == &quot;&quot;) selectlsh += _this.GetCellText(sheet_rjz,row,15);
			else selectlsh += &quot;,&quot; + _this.GetCellText(sheet_rjz,row,15);

			if (fkbz == 4) {
				alert(&quot;当前日记帐记录在业务已分款，不能删除，请在业务先取消分款后再做删除！&quot;);
				return;
			}
			if (pzh != &quot;&quot;) {
				alert(&quot;当前日记帐纪录已出凭证，不能删除，请核对！&quot;);
				return;
			}
			if (qcbz == 1) {
				alert(&quot;第一条记录为上年结转或上月结转时不能删除，请核对！&quot;);
				return;
			}

			if (czlx == &quot;挂暂存&quot;) {
				if (fkbz == 1) {
					alert(&quot;此暂存款已经分款，不能回退。请核实！&quot;);
					return;
				}
//				else if (fkbz == 3) {
//					alert(&quot;此款项已经冲暂存，请先回退冲暂存。请核实！&quot;);
//					return;
//				}
			}
		}
	}
	if (selcnt == 0) {
		alert(&quot;没有选中行！&quot;);
		return;
	}
	if (!confirm(&quot;删除所选中的行，是否继续？&quot;)) return;


	try {
		var param = new Object();
		param.thisorgid = G_ORGID;
		param.thisaccid = G_ACCID;
		param.lshs = selectlsh;
		param.czyxm = G_USRNAM;
		param.is_dzxz = is_dzxz;
		var ret = invokeOSFuncExt(&quot;Delete&quot;,param);
		if ( 1*ret &gt; 0) {
			alert(&quot;删除日记帐成功！操作记录数&quot;+ret);
			loadRJZData(cur_yyyy,cur_mm,cur_kmbh,&quot;&quot;);
		}
		else {
			alert(&quot;删除失败！出错信息：&quot;+ret);
		}
	}
	catch (e) {
		alert(e.toString());
	}
}

//按键按下
function controlkeydown()
{
	altKey = event.altKey;
	ctrlKey = event.ctrlKey;
	shiftKey = event.shiftKey;

	if (shiftKey == true) {
		shiftSelectRow1 = _this.GetCurrentRow(sheet_rjz);
	}
}

//按键放开
function controlkeyup()
{
	//处理多选行
	if (shiftKey == true) {
		shiftSelectRow2 = _this.GetCurrentRow(sheet_rjz);
		if (!(shiftSelectRow1 == shiftSelectRow2 && _this.GetCellText(sheet_rjz,shiftSelectRow1,0) == &quot;1&quot;)) {
			for (var r=shiftSelectRow1;r&lt;=shiftSelectRow2;r++) {
				if (_this.GetCellText(sheet_rjz,r,2) != &quot;&quot;) {
					selectOneRow(sheet_rjz,r);
				}
			}
		}
	}

	altKey = false;
	ctrlKey = false;
	shiftKey = false;
}

//鼠标双击
function _thisOnMouseDClick(sheet,row,col)
{
	if (sheet == sheet_rjz && row &gt;= _this.GetMainCellRangeRow1(sheet_rjz) && row &lt;= _this.GetMainCellRangeRow2(sheet_rjz)) {
		var readOnly = _this.GetAttribe(&quot;CELL_&quot;+sheet+&quot;_&quot;+row+&quot;_&quot;+col,_this.ATTR_CELL_READONLY);
		var dd = _this.GetCellText(sheet_rjz,row,4)
		if(dd == &quot;&quot;) return true;//双击空行不选中
		if (readOnly == &quot;true&quot;) {
			selectOneRow(sheet_rjz,row);
		}
	}
}

function selectOneRow(sheet,row)
{
	var flg = _this.GetCellText(sheet,row,0);
	if (flg == 1) {
		_this.SetCellText(sheet,row,0,0);
		setOneRowBkColor(sheet,row,255,255,255);		
	}
	else {
		_this.SetCellText(sheet,row,0,1);
		setOneRowBkColor(sheet,row,255,236,139);
	}
}

function setOneRowBkColor(sheet,row,r,g,b)
{
	for (var c=0;c&lt;_this.GetColCount(sheet);c++) {
		_this.SetCellBkColor(sheet,row,c,r,g,b);
	}
	_this.SetCellFocus(sheet,row,0);
	_this.Redraw();
}

function getNowFormatDate() {
    var date = new Date();
    var seperator1 = &quot;-&quot;;
    var seperator2 = &quot;:&quot;;
    var month = date.getMonth() + 1;
    var strDate = date.getDate();
    if (month &gt;= 1 && month &lt;= 9) {
        month = &quot;0&quot; + month;
    }
    if (strDate &gt;= 0 && strDate &lt;= 9) {
        strDate = &quot;0&quot; + strDate;
    }
    var currentdate = date.getFullYear() + seperator1 + month + seperator1 + strDate
            + &quot; &quot; + date.getHours() + seperator2 + date.getMinutes()
            + seperator2 + date.getSeconds();
    return currentdate;
}

//重算余额
function check_next_qcye()
{
	var kmbh = _this.GetCellText(sheet_param,1,6);
	var yy = _this.GetCellText(sheet_param,1,1);
	var mm = _this.GetCellText(sheet_param,1,3);
	if (kmbh == &quot;&quot;) {
		alert(&quot;请选择科目！&quot;);
		return;
	}
	if (!confirm(&quot;余额重算，是否继续？&quot;)) return;
	var ret = invokeOSFunc(&quot;CheckNextQcye&quot;,&quot;thisorgid=&quot;+G_ORGID+&quot;&thisaccid=&quot;+G_ACCID+&quot;&kmbh=&quot;+kmbh+&quot;&yy=&quot;+yy+&quot;&mm=&quot;+mm);
	if (ret == 0) {
		alert(&quot;重算余额成功！&quot;);
	}
	else {
		alert(&quot;重算余额失败！&quot;+ret);
	}
}

//余额检查 typ1:从启动年月检查 typ2:从当前年月检查
function yejc(typ)
{
       var ret = &quot;&quot;;
       var dqyy = _this.GetCellText(sheet_param,1,1);
       var dqmm = _this.GetCellText(sheet_param,1,3);
       var kmbh = _this.GetCellText(sheet_param,1,6);
       var sbh = G_ORGID;
       var zth = G_ACCID.replace(sbh,&quot;&quot;);
       if (kmbh == &quot;&quot;) {
	   alert(&quot;请选择科目！&quot;);
	   return;
       }              
       ret = _this.invokeOSFunc(&quot;yejc&quot;,&quot;typ=&quot;+typ+&quot;&dqyy=&quot;+dqyy+&quot;&dqmm=&quot;+dqmm+&quot;&kmbh=&quot;+kmbh+&quot;&sbh=&quot;+sbh+&quot;&zth=&quot;+zth+&quot;&thisorgid=&quot;+G_ORGID+&quot;&thisaccid=&quot;+G_ACCID);
       if(ret == 0) {
           alert(&quot;余额检查完成&quot;);
       }
       else {
           alert(&quot;余额检查失败，出错信息：&quot;+ret);
       }
}

//金额检查
function jejc()
{      
       var kmbh = _this.GetCellText(sheet_param,1,6);
       if (kmbh == &quot;&quot;) {
	   alert(&quot;请选择科目！&quot;);
	   return;
       }         
       var m = 0; //检查的记录数
       var n = 0; //出错记录数
       for(var row=1;row&lt;_this.GetRowCount(0)-1;row++) {
           var lsh = &quot;&quot;;
           var ret = &quot;&quot;;
           var zy = _this.GetCellText(0,row,8);
           lsh = _this.GetCellText(0,row,15);
           if(lsh == &quot;&quot;) continue;
           var param = new Object();
           param.sbh = G_ORGID;
           param.zth = G_ACCID.replace(G_ORGID,&quot;&quot;);
           param.lsh = lsh;
           param.zy = zy;
           param.je = Math.abs(1.0*_this.GetCellText(0,row,9)-1.0*_this.GetCellText(0,row,10));
           param.jefx = _this.GetCellText(0,row,11);
           ret = invokeOSFuncExt(&quot;jejc&quot;,param);
           if(ret == 0) continue;           
           else if(ret.indexOf(&quot;出错信息&quot;) != -1) {
                 alert(ret);
                 return;
           }
           else if(ret == 1) {
                m++;
           }
           else {
                m++;
                n++;
                var bool = window.confirm( &quot;第&quot;+row+&quot;笔记录&quot;+ret+&quot;是否确定继续检查？&quot;);
                if(bool) continue;
                else break;
           }
      }
       alert(&quot;共检查&quot;+m+&quot;笔日记账\r\n其中有：&quot;+n+&quot;笔金额不对&quot;);
}

function formatMD(mmdd) 
{
	if (mmdd.length == 1) return &quot;0&quot; + mmdd;
	else return mmdd;
}

