function getDzxz()
{
	var is_dzxz = &quot;01&quot;;
	var sql = &quot;select distinct dzxz from cw_dfdsb where org=&apos;&quot;+thisorgid+&quot;&apos; and acc=&apos;&quot;+thisaccid+&quot;&apos;&quot;;
	var tmpds = pub.EADbTool.QuerySQL(sql);
	if (tmpds.getRowCount() &gt; 0) {
		is_dzxz = tmpds.getStringAt(0,&quot;DZXZ&quot;);
	}
	return is_dzxz;
}

//保存日记帐修改
function Save()
{
	var db = null;
	try {
		db = new pub.EADatabase();
		var ds = new pub.EAXmlDS(rjzxml);
		if (ds.getRowCount() &lt;= 0) return &quot;0&quot;;
		for (var i=0;i&lt;ds.getRowCount();i++) {
			var yy = ds.getStringAt(i,&quot;YY&quot;);
			var mm = ds.getStringAt(i,&quot;MM&quot;);
			var dd = ds.getStringAt(i,&quot;DD&quot;);
			var zy = ds.getStringAt(i,&quot;ZY&quot;);
			var sxh = ds.getStringAt(i,&quot;SXH&quot;);
			var lsh = ds.getStringAt(i,&quot;LSH&quot;);
			var sql = &quot;update cw_rjzb set dd=&apos;&quot;+dd+&quot;&apos;,zy=&apos;&quot;+zy+&quot;&apos;,sxh=&apos;&quot;+sxh+&quot;&apos; where org=&apos;&quot;+thisorgid+&quot;&apos; and acc=&apos;&quot;+thisaccid+&quot;&apos; and lsh=&apos;&quot;+lsh+&quot;&apos;&quot;;
			db.ExcecutSQL(sql);
		}
		db.Commit();
		return &quot;1&quot;;
	}
	catch (e) {
		if (db != null) db.Rollback();
		return e.toString();	
	}
	finally {
		if (db != null) db.Close();
	}
}

//删除日记帐
function Delete()
{
	var db = null;
	var retcnt = 0;
	var sql = &quot;&quot;;
	try {
		db = new pub.EADatabase();

		var iuo_ywjk = new SBCW_IYWJK(); //业务接口类
		var ywjkbz = iuo_ywjk.getYWJKBZ(db,thisaccid);
		//获取统筹区编码		
		sql = &quot;select tcqbm from cw_sbsb where sbh=&apos;&quot;+thisorgid+&quot;&apos;&quot;;
		var tcqbm = db.GetSQL(sql);

		sql = &quot;select sbh,zth,yy,mm,dd,trim(to_char(mm,&apos;00&apos;)) mm2,trim(to_char(dd,&apos;00&apos;)) dd2,czlx,lsh,czlsh,dwbh,upper(dwbh) upper_dwbh,
				round(je,2) je,dylsh,fkbz,lxbh,jsph,pch,old_pch,zy,kmbh,djh,pzh,qcbz,mkjmbz,siseqno
			from cw_rjzb 
			where org=&apos;&quot;+thisorgid+&quot;&apos; and acc=&apos;&quot;+thisaccid+&quot;&apos; and lsh in (&quot;+pub.EAFunc.SQLIN(lshs)+&quot;)&quot;;	
		var ds = db.QuerySQL(sql);
		for (var i=0;i&lt;ds.getRowCount();i++) {
			var yy = ds.getStringAt(i,&quot;YY&quot;);
			var mm = ds.getStringAt(i,&quot;MM&quot;);
			var dd = ds.getStringAt(i,&quot;DD&quot;);
			var mm2 = ds.getStringAt(i,&quot;MM2&quot;);
			var dd2 = ds.getStringAt(i,&quot;DD2&quot;);
			var sbh = ds.getStringAt(i,&quot;SBH&quot;);
			var zth = ds.getStringAt(i,&quot;ZTH&quot;);			
			var ls_czlx = ds.getStringAt(i,&quot;CZLX&quot;);
			var lsh = ds.getStringAt(i,&quot;LSH&quot;);
			var ll_dylsh = ds.getStringAt(i,&quot;DYLSH&quot;);
			var czlsh = ds.getStringAt(i,&quot;CZLSH&quot;);
			var ls_upper_dwbh = ds.getStringAt(i,&quot;UPPER_DWBH&quot;);
			var ldb_je = 1.0 * ds.getStringAt(i,&quot;JE&quot;);//日记账总金额
			var djh = ds.getStringAt(i,&quot;DJH&quot;);
			var qcbz = ds.getStringAt(i,&quot;QCBZ&quot;); //期初标志
			var fkbz = ds.getStringAt(i,&quot;FKBZ&quot;); //分款标志
			var pzh = ds.getStringAt(i,&quot;PZH&quot;);   //凭证号
			var ls_kmbh = ds.getStringAt(i,&quot;KMBH&quot;);
			var ls_dwbh = ds.getStringAt(i,&quot;DWBH&quot;);
			var ls_pch = ds.getStringAt(i,&quot;PCH&quot;);
			var mkjmbz = ds.getStringAt(i,&quot;MKJMBZ&quot;);
			var sblsh  = ds.getStringAt(i,&quot;SISEQNO&quot;);

			if (qcbz == &quot;1&quot;) {
				return &quot;记录为上年结转或上月结转时不能删除，请核对！&quot;;
			}
			if (pzh != &quot;&quot;) {
				return &quot;当前日记帐纪录已出凭证，不能删除，请核对！&quot;;
			}
			if (fkbz == 4) {
				return &quot;当前日记帐记录在业务已分款，不能删除，请在业务先取消分款后再做删除！&quot;;
			}

			/*begin 20121206 add 删除批量录入，如果删除第一条批量录入记录，则张数往下移动到同一附件张数批次号的位置*/
			//查询原日记账流水的附件张数,批次号
			sql = &quot;select count(*) cnt,max(nvl(yspz_fjzs,0)) fjzs,max(nvl(yspz_fjpch,0)) fjpch from cw_rjzb where org=&apos;&quot;+thisorgid+&quot;&apos; and acc=&apos;&quot;+thisaccid+&quot;&apos; and lsh=&quot;+lsh;
			var tmpds = db.QuerySQL(sql);
			if (lsh == &quot;&quot; || tmpds.getRowCount() &lt;= 0) {
				db.Rollback();
				return &quot;查询原日记账流水号[&quot; + lsh + &quot;]的附件张数出错!&quot;;
			}
			var ll_count = tmpds.getStringAt(0,&quot;CNT&quot;);
			var ll_yspz_fjzs = tmpds.getStringAt(0,&quot;FJZS&quot;);
			var ll_yspz_fjpch = tmpds.getStringAt(0,&quot;FJPCH&quot;);
			var li_scxy_bz = 0;
			//同一附件批次号的行数
			var li_count = db.GetSQL(&quot;select  count(*) from cw_rjzb where org=&apos;&quot;+thisorgid+&quot;&apos; and acc=&apos;&quot;+thisaccid+&quot;&apos; and yspz_fjpch=&apos;&quot;+ll_yspz_fjpch+&quot;&apos;&quot;); //该语句慢
			if (li_count &gt; 1) { 
				li_scxy_bz = 1;
			}
			if (ll_count == 1 && ll_yspz_fjzs == 0 && ll_yspz_fjpch &gt; 0) { 
				li_scxy_bz = 0; //（选中修改不为第一条记录，即第一条有附件张数时，不用修改同一附件批次号张数）
			}
			if (li_scxy_bz == 1) { 
				sql = &quot;select nvl(min(lsh),&apos;-1&apos;) fjcl_lsh from cw_rjzb where org=&apos;&quot;+thisorgid+&quot;&apos; and acc=&apos;&quot;+thisaccid+&quot;&apos; and yspz_fjpch=&apos;&quot;+ll_yspz_fjpch+&quot;&apos; and lsh&lt;&gt;&quot;+lsh+&quot; and lsh&gt;&quot;+lsh ;
				var ll_fjcl_lsh = &quot;&quot;;
				ll_fjcl_lsh = db.GetSQL(sql);
				if (lsh == &quot;&quot; || ll_fjcl_lsh == &quot;-1&quot;) {
//					db.Rollback();
//					return &quot;查询原日记账流水号[&quot; + lsh + &quot;]下移流水号出错!&quot;;
				}

				sql = &quot;select * from cw_rjzb where org=&apos;&quot;+thisorgid+&quot;&apos; and acc=&apos;&quot;+thisaccid+&quot;&apos; and lsh=&quot;+ll_fjcl_lsh;
				var tmpds = db.QuerySQL(sql);
				// 下移行号，张数，PCH 
				if (tmpds.getRowCount() &gt; 0) {
					var ll_yspz_fjzs = tmpds.getStringAt(0,&quot;YSPZ_FJZS&quot;);
					var ll_yspz_fjpch = tmpds.getStringAt(0,&quot;YSPZ_FJPCH&quot;);
					var uplsh = tmpds.getStringAt(0,&quot;LSH&quot;);
					sql = &quot;update cw_rjzb set yspz_fjzs=&apos;&quot;+ll_yspz_fjzs+&quot;&apos;,yspz_fjpch=&apos;&quot;+ll_yspz_fjpch+&quot;&apos; where org=&apos;&quot;+thisorgid+&quot;&apos; and acc=&apos;&quot;+thisaccid+&quot;&apos; and lsh=&quot;+uplsh;
					db.ExcecutSQL(sql);
				} 
			}
			/*end 20121206 add 删除批量录入，如果删除第一条批量录入记录，则张数往下移动到同一附件张数批次号的位置*/	

			var ls_hkrq = yy+mm2+dd2;
			var ls_scny = yy+mm2; //取生成年月
			//begin 调用接口回退代码
			if(mkjmbz == &quot;3&quot;){
				sql = &quot;select count(1) from cw_rjzmxb where LSH=&quot;+lsh+&quot; and sbh = &apos;&quot;+sbh+&quot;&apos; and ZTH = &apos;&quot;+zth+&quot;&apos;&quot;;
				var mxcou = db.GetSQL(sql);
				if(mxcou &lt;= 0){
					db.Rollback();
					return &quot;该日记账没有明细，不允许删除!!!&quot;;
				}

				sql = &quot;select zy from v_si_djb_tmp where djh=&quot;+lsh+&quot; and sbh = &apos;&quot;+sbh+&quot;&apos; and ZTH = &apos;&quot;+zth+&quot;&apos; and zbbz = &apos;1&apos;&quot;;
				var old_zy = db.GetSQL(sql);

				sql = &quot;select count(1) from cw_rjzmxb where LSH=&quot;+lsh+&quot; and sbh = &apos;&quot;+sbh+&quot;&apos; and ZTH = &apos;&quot;+zth+&quot;&apos; and lxbh = &apos;87&apos;&quot;;
				var mxcou1 = db.GetSQL(sql);								

				if(mxcou1 &gt; 0){	//转账通知回退分两种：1、挂暂存回退（此功能回退在日记账修改界面），2、未挂账回退（此功能回退在未分款界面）
					sql = &quot;select count(1) from v_si_djb_tmp where djh = &apos;&quot;+djh+&quot;&apos; and sbh = &apos;&quot;+sbh+&quot;&apos; and ZTH = &apos;&quot;+zth+&quot;&apos; and zbbz = 0 and lxbh = &apos;87&apos; 
						HAVING sum(NVL(JE1, 0) +  NVL(JE2, 0) +  NVL(JE3, 0) +  NVL(JE4, 0) +  NVL(JE5, 0) +  
							NVL(JE6, 0) +  NVL(JE7, 0) +  NVL(JE8, 0) +  NVL(JE9, 0) +  NVL(JE10,0) +  
							NVL(JE11,0) +  NVL(JE12,0) +  NVL(JE13,0) +  NVL(JE14,0) +  NVL(JE15,0)) &lt; 0&quot;;
					var zcksy_ds = db.QuerySQL(sql);
					if(zcksy_ds.getRowCount() &gt; 0 ){
						db.Rollback();
						return &quot;该单据号已使用过暂存款，不允许回退!!!&quot;;
					}

					sql = &quot;select round(je,2) je from cw_rjzmxb where LSH=&quot;+lsh+&quot; and sbh = &apos;&quot;+sbh+&quot;&apos; and ZTH = &apos;&quot;+zth+&quot;&apos; and je &gt; 0 and lxbh = &apos;87&apos; and jefx = &apos;借&apos;&quot;;
					var gzje = db.GetSQL(sql);

					if(1.0*gzje == 1.0*ldb_je){
						sql = &quot;update v_si_djb_tmp set lsh = null,rzbz = &apos;0&apos;,rzr = null,rzsj = null,zfbz = &apos;1&apos;,zfr = &apos;&quot;+czyxm+&quot;&apos;,zfsj=sysdate,fzbz = &apos;0&apos; where djh=&apos;&quot;+djh+&quot;&apos; and sbh = &apos;&quot;+sbh+&quot;&apos; and ZTH = &apos;&quot;+zth+&quot;&apos; and lsh =  &apos;&quot;+lsh+&quot;&apos; and zbbz = 0&quot;;
						db.ExcecutSQL(sql);

						sql = &quot;update cw_rjzb set zy = &apos;&quot;+old_zy+&quot;&apos;, czlx = null,gzlx = null,fkbz = &apos;0&apos; where LSH=&apos;&quot;+lsh+&quot;&apos; and sbh = &apos;&quot;+sbh+&quot;&apos; and ZTH = &apos;&quot;+zth+&quot;&apos;&quot;;
						db.ExcecutSQL(sql);
					}else {
						sql = &quot;update v_si_djb_tmp set lsh = null,rzbz = &apos;0&apos;,rzr = null,rzsj = null,zfbz = &apos;1&apos;,zfr = &apos;&quot;+czyxm+&quot;&apos;,zfsj=sysdate,fzbz = &apos;1&apos; where djh=&apos;&quot;+djh+&quot;&apos; and sbh = &apos;&quot;+sbh+&quot;&apos; and ZTH = &apos;&quot;+zth+&quot;&apos; and lsh =  &apos;&quot;+lsh+&quot;&apos; and zbbz = 0&quot;;
						db.ExcecutSQL(sql);

						sql = &quot;update cw_rjzb set zy = &apos;&quot;+old_zy+&quot;&apos;, czlx = null,gzlx = null,fkbz = &apos;1&apos; where LSH=&apos;&quot;+lsh+&quot;&apos; and sbh = &apos;&quot;+sbh+&quot;&apos; and ZTH = &apos;&quot;+zth+&quot;&apos;&quot;;
						db.ExcecutSQL(sql);
					}

					var yh_ywjk = new SBCW_YWJK_YH();				
					var yhjk_msg = yh_ywjk.yhjk_PREPAY(db,ywjkbz,&quot;1&quot;,ls_dwbh,&quot;4&quot;,gzje,ls_hkrq,czyxm,tcqbm,sblsh,sbh,zth );
					var ret = yhjk_msg.split(&quot;@&quot;)[0];
					var retmes = yhjk_msg.split(&quot;@&quot;)[1];
					if(ret!=&quot;NOERROR&quot;){
						db.Rollback();
						return &quot;1、调用业务接口prc_prepayCheck出错！单据号:&quot;+lsh+&quot;;出错码：&quot;+ret+&quot;出错原因:&quot;+retmes+&quot;.\n&quot;;
					}
				}else{
					sql = &quot;update v_si_djb_tmp set lsh = null,rzbz = &apos;0&apos;,rzr = null,rzsj = null,fzbz = &apos;1&apos; where djh=&apos;&quot;+lsh+&quot;&apos; and sbh = &apos;&quot;+sbh+&quot;&apos; and ZTH = &apos;&quot;+zth+&quot;&apos; and lsh =  &apos;&quot;+lsh+&quot;&apos; and zbbz = 0&quot;;
					db.ExcecutSQL(sql);

					sql = &quot;update cw_rjzb set zy = &apos;&quot;+old_zy+&quot;&apos;, czlx = null,gzlx = null,fkbz = &apos;1&apos; where LSH=&apos;&quot;+lsh+&quot;&apos; and sbh = &apos;&quot;+sbh+&quot;&apos; and ZTH = &apos;&quot;+zth+&quot;&apos;&quot;;
					db.ExcecutSQL(sql);

				}
			}else if(mkjmbz == 1){
				if(djh != &quot;&quot;){
					//调用业务接口
//					var yh_ywjk = new SBCW_YWJK_YH();
//					var yhjk_msg = yh_ywjk.yhjk_rollback(db,djh,czyxm,tcqbm,thisorgid ,thisaccid );
//					var ret = yhjk_msg.split(&quot;@&quot;)[0];
//					var retmes = yhjk_msg.split(&quot;@&quot;)[1];
//					if(ret!=&quot;NOERROR&quot;){
//						db.Rollback();
//						return &quot;2、调用业务接口prc_rollback出错！单据号:&quot;+djh+&quot;;出错原因:&quot;+retmes+&quot;.\n&quot;;
//					}

					//更新si_djb_tmp //先更新中间表标志后再调用接口 2016/05/27 flm
					sql = &quot;update v_si_djb_tmp set rzbz = &apos;0&apos;,rzr = null, rzsj = null,lsh=null where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and djh = &apos;&quot; + djh +&quot;&apos; and lsh = &apos;&quot;+lsh+&quot;&apos;&quot;;
					db.ExcecutSQL(sql);

					var yh_ywjk = new SBCW_YWJK_YH();
					var yhjk_msg = yh_ywjk.yhjk_PREPAY(db,ywjkbz,&quot;1&quot;,ls_dwbh,&quot;5&quot;,ldb_je,ls_hkrq,czyxm,tcqbm,lsh,sbh,zth );
					var ret = yhjk_msg.split(&quot;@&quot;)[0];
					var retmes = yhjk_msg.split(&quot;@&quot;)[1];
					if(ret!=&quot;NOERROR&quot;){
						db.Rollback();
						return &quot;2、调用业务接口prc_prepayCheck出错！流水号:&quot;+lsh+&quot;;出错码：&quot;+ret+&quot;出错原因:&quot;+retmes+&quot;.\n&quot;;
					}
				}
			}else{
				//如果是业务接口数据处理
				sql = &quot;select a.*,(je1+je2+je3+je4+je5+je6+je7+je8+je9+je10+je11+je12+je13+je14+je15) sumje from v_si_djb_tmp a where LSH=&quot;+lsh+&quot; and sbh = &apos;&quot;+sbh+&quot;&apos; and ZTH = &apos;&quot;+zth+&quot;&apos; and bac005 = &apos;E110&apos;&quot;;
				var sids = db.QuerySQL(sql);
				if (sids.getRowCount() &gt; 0) {	
					var sumje = 1.0 * sids.getStringAt(0,&quot;SUMJE&quot;);	
					//负数是业务回写进来的数据（银行回盘出错的记录）	
					if (sumje &lt; 0) {
						//调用业务接口
						var li_ret = iuo_ywjk.RJZ_Delete(db,sbh,zth,lsh,czyxm,ywjkbz);
						if (li_ret != &quot;0&quot;) {
							db.Rollback();
							return &quot;调用业务函数iuo_ywjk.RJZ_Delete失败！&quot;+li_ret;
						}
					}

					//更新SI_DJB_TMP
					sql = &quot;update v_si_djb_tmp set RZBZ=&apos;0&apos;,RZR=null,RZSJ=null,LSH=null where LSH=&quot;+lsh+&quot; and sbh = &apos;&quot;+sbh+&quot;&apos; and ZTH = &apos;&quot;+zth+&quot;&apos;&quot;;
					db.ExcecutSQL(sql);

					//修改银行回盘AC95标志
					sql = &quot;update ac95 set BAZ901=null,BAZ902=null,BAC004=null,BAC003=null,BAE011=null,BAE036=null,AAE076=null 
						where AAE076=&apos;&quot;+lsh+&quot;&apos; and zth=&apos;&quot;+zth+&quot;&apos; and aaa027=(select tcqbm from cw_sbsb where sbh=&apos;&quot;+thisorgid+&quot;&apos;)&quot;;
					db.ExcecutSQL(sql);
				}

				//征缴到账回退过程
				sql = &quot;select count(1) from ac95 where bae074 = &apos;&quot;+djh+&quot;&apos; and AAE076 = &apos;&quot;+lsh+&quot; &apos; and zth=&apos;&quot;+zth+&quot;&apos; and aaa027=(select tcqbm from cw_sbsb where sbh=&apos;&quot;+thisorgid+&quot;&apos;) and bac005 = &apos;A110&apos;&quot;;
				var zscou = db.GetSQL(sql);
				if(zscou &gt; 0){
					//调用业务接口
//					var yh_ywjk = new SBCW_YWJK_YH();
//					var yhjk_msg = yh_ywjk.yhjk_rollback(db,djh,czyxm,tcqbm,thisorgid ,thisaccid );
//					var ret = yhjk_msg.split(&quot;@&quot;)[0];
//					var retmes = yhjk_msg.split(&quot;@&quot;)[1];
//					if(ret!=&quot;NOERROR&quot;){
//						db.Rollback();
//						return &quot;2、调用业务接口prc_rollback出错！单据号:&quot;+djh+&quot;;出错原因:&quot;+retmes+&quot;.\n&quot;;
//					}
					var yh_ywjk = new SBCW_YWJK_YH();				
					var yhjk_msg = yh_ywjk.yhjk_PREPAY(db,ywjkbz,&quot;1&quot;,ls_dwbh,&quot;5&quot;,ldb_je,ls_hkrq,czyxm,tcqbm,lsh,sbh,zth );
					var ret = yhjk_msg.split(&quot;@&quot;)[0];
					var retmes = yhjk_msg.split(&quot;@&quot;)[1];
					if(ret!=&quot;NOERROR&quot;){
						db.Rollback();
						return &quot;3、调用业务接口prc_prepayCheck出错！流水号:&quot;+lsh+&quot;;出错码：&quot;+ret+&quot;出错原因:&quot;+retmes+&quot;.\n&quot;;
					}

					//更新si_djb_tmp
					sql = &quot;update v_si_djb_tmp set rzbz = &apos;0&apos;,rzr = null, rzsj = null,lsh=null where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and djh = &apos;&quot; + djh +&quot;&apos; and lsh = &apos;&quot;+lsh+&quot;&apos;&quot;;
					db.ExcecutSQL(sql);

					//更新ac95标志
					sql = &quot;update ac95 set BAZ901=null,BAZ902=null,BAC004=null,BAC003=null,BAE011=null,BAE036=null,AAE076=null 
						where bae074 = &apos;&quot;+djh+&quot;&apos; and AAE076=&apos;&quot;+lsh+&quot;&apos; and zth=&apos;&quot;+zth+&quot;&apos; and AAB034 = &apos;&quot;+tcqbm+&quot;&apos;&quot;;
					db.ExcecutSQL(sql);
				}
			}
			//end 调用接口回退代码

			//删除cw_rjzmxb对应流水号... //录单笔的数据
			if (ll_dylsh != &quot;&quot; && ll_dylsh != &quot;-1&quot;) {
				sql = &quot;delete from cw_rjzmxb where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and lsh = &apos;&quot;+ll_dylsh+&quot;&apos;&quot;;
				db.ExcecutSQL(sql);

				//删除cw_rjzb对应流水号...
				sql = &quot;delete from cw_rjzb where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and lsh = &apos;&quot;+ll_dylsh+&quot;&apos;&quot;;
				db.ExcecutSQL(sql);				
			}

			if(mkjmbz == &quot;3&quot;){
				//删除cw_rjzb,cw_rjzmxb
				sql = &quot;delete from cw_rjzmxb where org=&apos;&quot;+thisorgid+&quot;&apos; and acc=&apos;&quot;+thisaccid+&quot;&apos; and lsh=&quot;+lsh;
				retcnt += db.ExcecutSQL(sql);		
			}else{				
				//删除cw_rjzb,cw_rjzmxb
				sql = &quot;delete from cw_rjzmxb where org=&apos;&quot;+thisorgid+&quot;&apos; and acc=&apos;&quot;+thisaccid+&quot;&apos; and lsh=&quot;+lsh;
				db.ExcecutSQL(sql);
				//挂暂存数据回退不需要删除日记账，只需要修改日记账			
				sql = &quot;delete from cw_rjzb where org=&apos;&quot;+thisorgid+&quot;&apos; and acc=&apos;&quot;+thisaccid+&quot;&apos; and lsh=&quot;+lsh;
				retcnt += db.ExcecutSQL(sql);
			}		

		} //end for

		db.Commit();
		return retcnt;
	}
	catch (e) {
		if (db != null) db.Rollback();
		return &quot;删除失败！\n&quot;+e.toString()+sql;	
	}
	finally {
		if (db != null) db.Close();
	}
}

//打印单据权限检查
function Print()
{
	return 1;
}

//余额检查
function CheckNextQcye()
{
	var db = null;
	try {
		db = new pub.EADatabase();

		//调用f_check_next_qcye更新期初余额
		var pubfunc = new SBCW_PUBFUNC();
		var ret = pubfunc.f_check_next_qcye(db,thisorgid,thisaccid,kmbh,yy,mm);
		return ret;

	}
	catch (e) {
		if (db != null) db.Rollback();
		return e.toString();	
	}
	finally {
		if (db != null) db.Close();
	}
}

//余额检查
function yejc()
{      
	var db = null;
	try {   
		db = new pub.EADatabase();
		var yy = &quot;&quot;;
		var mm = &quot;&quot;;
		var sql = &quot;&quot;;
	        if(typ == 1) {
	               sql = &quot;update cw_rjzb set qcbz = 0 
	                       where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and kmbh = &apos;&quot;+kmbh+&quot;&apos; and qcbz is null&quot; ;
	               db.ExcecutSQL(sql);     
	               sql =  &quot;update cw_rjzb set ye=0 
	                       where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and kmbh = &apos;&quot;+kmbh+&quot;&apos; and qcbz=1 and  ye is null&quot; ; 
	               db.ExcecutSQL(sql);
	               sql = &quot;select substr(qdym,1,4) qdyy,substr(qdym,5) qdmm from cw_ztb where sbh =&apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos;&quot;;
	               var ds = db.QuerySQL(sql);
	               yy = ds.getStringAt(0,&quot;qdyy&quot;);
	               mm = ds.getStringAt(0,&quot;qdmm&quot;);        
	        }
	        else if(typ == 2) {
	                sql = &quot;update cw_rjzb set qcbz = 0 
	                where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and kmbh = &apos;&quot;+kmbh+&quot;&apos; and ( yy &gt; &quot;+dqyy+&quot; or ( yy = &quot;+dqyy+&quot; and mm &gt;= &quot;+dqmm+&quot; ) ) and qcbz is null&quot;;
	                db.ExcecutSQL(sql);
	                sql = &quot;	update cw_rjzb set ye=0 
	                where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and kmbh = &apos;&quot;+kmbh+&quot;&apos; and qcbz=1 and  ye is null&quot;;
	                db.ExcecutSQL(sql);
	                yy = dqyy;
	                mm = dqmm;
	        }
		//调用f_check_next_qcye更新期初余额
		var pubfunc = new SBCW_PUBFUNC();
		var ret = pubfunc.f_check_next_qcye(db,thisorgid,thisaccid,kmbh,yy,mm);
		db.Commit();
		return ret;

	}
	catch (e) {
		if (db != null) db.Rollback();
		return e.toString();	
	}
	finally {
		if (db != null) db.Close();
	}
}

//金额检查
function jejc()
{
	var db = null;
	try {
		db = new pub.EADatabase();
		var ds = &quot;&quot;;
		var ret = &quot;&quot;;
		var jfje1 = 0.0;
		var dfje1 = 0.0;
		var jdje = 0.0;
		var jdfx = &quot;&quot;;
		var sum_je = pub.EAFunc.Round(1.0*je,2);
		var sum_jefx = jefx;
		var sql = &quot;select count(*) from cw_rjzb where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and lsh = &quot;+lsh+&quot; and qcbz = 1&quot;;
		var cnt = 0;
		var m = 0;
		var n = 0;
		try{cnt = db.GetSQL(sql);}catch(e){return &quot;出错信息：查询日记账表出错\r\n&quot;+e.toString();}
		if(cnt &gt; 0) return 0;
		if(zy == &quot;&quot;) return &quot;出错信息：摘要不能为空&quot;;
		sql = &quot;select round(sum(decode(jefx,&apos;借&apos;,nvl(je,0),0)),2) jfje,round(sum(decode(jefx,&apos;贷&apos;,nvl(je,0),0)),2) dfje
	               from cw_rjzmxb
	               where sbh = &apos;&quot;+sbh+&quot;&apos; and zth = &apos;&quot;+zth+&quot;&apos; and lsh =&quot;+lsh;
	        ds = db.QuerySQL(sql);  
	        try{     
		        jfje1 = 1.0*ds.getStringAt(0,&quot;jfje&quot;);
		        dfje1 = 1.0*ds.getStringAt(0,&quot;dfje&quot;);
		}catch(e) {}
		jdje = jfje1 = dfje1; 
	        if(jdje &gt;= 0) {
		        jdje = pub.EAFunc.Round(jfje1 - dfje1,2);   	        
	                jdfx = &quot;借&quot;;
	        }else {
	                jdje = pub.EAFunc.Round(jfje1 - dfje1,2);   
	                jdfx = &quot;贷&quot;;
	        }
	        if(!(sum_je == 0) && !(jdje == 0)) {
	               if((sum_je != jdje) || (sum_jefx != jdfx)) {
	                      ret = zy+&quot;\r\n主表金额(sum_je)=&quot;+sum_jefx+&quot;,&quot;+sum_je+&quot;&lt;&gt;明细表金额(jdje)=&quot;+jdfx+&quot;,&quot;+jdje;
	                      return ret;
	               }
	        }
	        return 1;//返回1表示正常		
	}
	catch (e) {
		if (db != null) db.Rollback();
		return &quot;出错信息：&quot;+e.toString();	
	}
	finally {
		if (db != null) db.Close();
	}    
}



//获取打印日记帐发票的数据
function getRJZPrintData()
{
	var db = null;
	var xmlstr = &quot;&quot;;

	try {
		db = new pub.EADatabase();
		var sql = &quot;SELECT a.yy,a.mm,a.dd,a.pch,a.zy,a.kmbh,a.dwbh,a.dwmc,a.je,a.jefx,a.jsfs,a.fkrq,
			       b.mxxh,b.lxbh,b.lxxh,b.ym1,b.ym2,b.je mxje,b.jefx mxjefx,
			       nvl(c.rjlx,b.kmbh) rjlx,nvl(c.kxmc,cw_pack4.kmmc( a.sbh, a.zth, b.KMBH )) kxmc,
			       (select khyh from si_dwb d where a.sbh=d.sbh and a.zth=d.zth and a.dwbh=d.dwbh) dwkmyhmc,
			       kxmc rjlxmc,szztlx,decode(szztlx,&apos;单位缴&apos;,b.je,0) dwje,decode(szztlx,&apos;个人缴&apos;,b.je,0) grje
			    FROM CW_RJZB a, CW_RJZMXB b,(
			         SELECT b.RJLX,a.KXMC,a.LXBH,a.MXXH,szztlx
			         FROM CW_RJLXMXB a, CW_RJLXB b
			         WHERE a.ORG = &apos;&quot;+thisorgid+&quot;&apos; AND a.ACC = &apos;&quot;+thisaccid+&quot;&apos; 
			           and a.SBH = b.SBH and a.ZTH = b.ZTH and a.LXBH = b.LXBH
			    ) c
			   WHERE ( a.ORG = &apos;&quot;+thisorgid+&quot;&apos; AND  a.ACC = &apos;&quot;+thisaccid+&quot;&apos; ) 
			      and ( a.SBH = b.SBH and a.ZTH = b.ZTH and a.lsh=b.lsh)
			      and ( a.ORG = b.ORG and a.ACC = b.ACC )
			      and a.lsh=&apos;&quot;+printlsh+&quot;&apos;
			      and b.LXBH = c.LXBH(+)
			      and b.lxxh=c.mxxh(+)
			order by b.mxxh&quot;;

		var ds = db.QuerySQL(sql);
		return ds.GetXml();

	}
	catch(e) {
		if (db != null) db.Rollback();
		return e.toString();
	}
	finally {
		if (db != null) db.Close();
	}
}

ROW.DATDSC:
select &apos;&apos; SEL,&apos;&apos; flag,a.*  
from (
  SELECT &quot;CW_RJZB&quot;.YY, &quot;CW_RJZB&quot;.MM, &quot;CW_RJZB&quot;.DD,  &quot;CW_RJZB&quot;.PCH,&quot;CW_RJZB&quot;.PZLX,&quot;CW_RJZB&quot;.STR_PZH,  
    decode(ZY,&apos;期初余额&apos;,decode(to_char(MM)||to_char(DD),&apos;11&apos;,&apos;上年结转&apos;,&apos;上月结转&apos;),ZY) ZY,      
    decode(QCBZ,0,decode( JEFX, &apos;借&apos;, JE, NULL ),NULL) JFJE,   
    decode(QCBZ,0,decode( JEFX, &apos;贷&apos;, JE, NULL ),NULL) DFJE,   
    decode(&quot;CW_RJZB&quot;.&quot;QCBZ&quot;,1,&quot;CW_RJZB&quot;.&quot;YEFX&quot;,&apos;借&apos;) yefx,decode(QCBZ,1,YE,0.00) ye,JSFS,DJH,LSH,DYLSH,SXH,
    DWBH,DWMC,czlx,FKBZ,FKRXM,to_char(FKRQ,&apos;yyyy-mm-dd&apos;) FKRQ,FKYK,DYBZ,to_char(DYRQ,&apos;yyyy-mm-dd&apos;) DYRQ,DYR,
    CZYXM, LXBH,QCBZ,PZH,KMBH,KMBH||&apos;-&apos;||cw_pack4.kmmc(&quot;CW_RJZB&quot;.sbh,&quot;CW_RJZB&quot;.zth,&quot;CW_RJZB&quot;.KMBH) kmmc,JEFX
    ,mkjmbz,&quot;CW_RJZB&quot;.siseqno
  FROM CW_RJZB,(
    select distinct a.sbh,a.zth,a.siseqno from cw_rjzb a,cw_rjzmxb b 
    where a.sbh=b.sbh and a.zth=b.zth and a.lsh=b.lsh
      and a.org=&apos;[%SYS_ORGID]&apos; and a.acc=&apos;[%SYS_ACCID]&apos; 
      and a.yy=[%YYYY] and a.mm=[%MM]  
    ) c  
  where &quot;CW_RJZB&quot;.ORG = &apos;[%SYS_ORGID]&apos; 
    and &quot;CW_RJZB&quot;.ACC = &apos;[%SYS_ACCID]&apos; 
    and &quot;CW_RJZB&quot;.YY = &apos;[%YYYY]&apos; 
    and &quot;CW_RJZB&quot;.MM = &apos;[%MM]&apos;   
    /*AND &quot;CW_RJZB&quot;.KMBH = &apos;[%KMBH]&apos; */
    and &quot;CW_RJZB&quot;.sbh=c.sbh 
    and &quot;CW_RJZB&quot;.zth=c.zth and &quot;CW_RJZB&quot;.siseqno=c.siseqno
    [%FILTER]
  order by yy, mm,dd, sxh, lsh
) a

select dwlxbh from si_dwb 
where org=&apos;[%SYS_ORGID]&apos; and acc=&apos;[%SYS_ACCID]&apos; 
  and dwbh=&apos;[%DWBH]&apos;
