}

//加载日记帐数据
function loadRJZData(dat1,dat2,yhbm)
{	
	var kmbh = &quot;&quot;; //不做查询条件
	//加载日记帐
	var szbz = _this.GetCellText(sheet_param,1,6);
	var hdfs = _this.GetCellText(sheet_param,2,8);
	var yhzl = yhbm;//_this.GetCellText(sheet_param,2,6);
	var ywlx = _this.GetCellText(sheet_param,2,10);
	var pch = _this.GetCellText(sheet_param,1,4);
	var djh = _this.GetCellText(sheet_param,2,4);
	dat1 = dat1.replace(/-/g,&quot;&quot;);
	dat2 = dat2.replace(/-/g,&quot;&quot;);
	if (dat1 == &quot;&quot; || dat2 == &quot;&quot;) {
		alert(&quot;请选择日期范围！&quot;);
		return;
	}
	if (szbz == &quot;&quot;) { alert(&quot;请选择收支标志！&quot;); return; }
//	if (yhbm== &quot;&quot;) { alert(&quot;请选择银行！&quot;); return; }
	if (djh == &quot;&quot; && pch == &quot;&quot;) { alert(&quot;必须输入单据号或者批次号!&quot;); return }
	//清除前面的数据
	_this.LoadZXGFile(ZXGFILE0,-1,0);
	_this.SetFixedRow(sheet_rjz,1);	
	_this.SetColVisible(sheet_rjz,1,-1);
	_this.SetColVisible(sheet_rjz,5,-1);
	_this.SetColVisible(sheet_rjz,6,-1);
	_this.SetColVisible(sheet_rjz,7,-1);
	for (var rw=20;rw&lt;=27;rw++) {
		_this.SetColVisible(sheet_rjz,rw,-1);
	}
	_this.SetColVisible(sheet_rjz,30,-1);
	_this.SetColVisible(sheet_rjz,31,-1);

	if(szbz == &quot;1&quot;) {
		hdfs = &quot;&quot;;
	}

	_this.SetMainCellRange(sheet_rjz,1,0,_this.GetRowCount(sheet_rjz)-2,_this.GetColCount(sheet_rjz)-1);	
	_this.SetToolbarString(&quot;正在加载数据...&quot;);
//	alert(&quot;DAT1=&quot;+dat1+&quot;&DAT2=&quot;+dat2+&quot;&KMBH=&quot;+kmbh+&quot;&YWLX=&quot;+ywlx+&quot;&SZBZ=&quot;+szbz+&quot;&HDFS=&quot;+hdfs+&quot;&YHZL=&quot;+yhzl);
	var xml = _sql.query(&quot;RJZ&quot;,&quot;DAT1=&quot;+dat1+&quot;&DAT2=&quot;+dat2+&quot;&KMBH=&quot;+kmbh+&quot;&YWLX=&quot;+ywlx+&quot;&SZBZ=&quot;+szbz+&quot;&HDFS=&quot;+hdfs+&quot;&YHZL=&quot;+escape(yhzl)+&quot;&PCH=&quot;+pch+&quot;&DJH=&quot;+djh);
	_this.SetXmlDS(sheet_rjz,1,0,_this.GetRowCount(sheet_rjz)-2,_this.GetColCount(sheet_rjz)-1,xml);

	//计算合计数
	var jfje_sum = 0.00;
	var dfje_sum = 0.00;
	var ye_sum = 0.00;
	var jfbs = 0;
	var dfbs = 0;
	_this.SetToBoolBox(sheet_rjz,0,0);

	//做帐顺序号
//	_sql.querytods(&quot;RJZSXH&quot;,&quot;YY=&quot;+dat1.substring(0,4)+&quot;&MM=&quot;+dat1.substring(4,6)+&quot;&DD=&quot;+dat1.substring(6,8)+&quot;&KMBH=&quot;+kmbh);
	var sxh = 0;//_this.XMLDS_GetString(0,&quot;SXH&quot;);
	for (var r=_this.GetMainCellRangeRow1(sheet_rjz);r&lt;=_this.GetMainCellRangeRow2(sheet_rjz);r++) {
		var fx = _this.GetCellText(sheet_rjz,r,11);
		var lxbh = _this.GetCellText(sheet_rjz,r,29);
		var jfje = 1.0*_this.GetCellText(sheet_rjz,r,9);
		var dfje = 1.0*_this.GetCellText(sheet_rjz,r,10);

		if (jfje != 0 ) jfbs ++;
		if (dfje != 0 ) dfbs ++;
		jfje_sum += jfje;		
		dfje_sum += dfje;
		ye_sum += 1.0*_this.GetCellText(sheet_rjz,r,12);

		_this.SetToBoolBox(sheet_rjz,r,0);
		_this.SetToComboBox(&quot;&quot;,sheet_rjz,r,21,fkBZ);
		_this.SetToComboBox(&quot;&quot;,sheet_rjz,r,25,printBZ);
		if (_this.GetCellText(sheet_rjz,r,25) == 1) {
			_this.SetCellColor(sheet_rjz,r,25,0,0,255);
		}
		else if (_this.GetCellText(sheet_rjz,r,25) == 0) {
			_this.SetCellColor(sheet_rjz,r,25,255,0,0);
		}

		if (lxbh != &quot;&quot;) {
//			_this.SetCellText(sheet_rjz,r,15,lsh); lsh ++;
	 		_this.SetCellText(sheet_rjz,r,17,sxh); sxh ++;
	 		_this.SetCellText(sheet_rjz,r,28,G_USRNAM);
 		}

	}
	var jefx = (szbz == 2 ? &quot;贷&quot; : &quot;借&quot;);
	_this.SetCellText(sheet_rjz,_this.GetRowCount(sheet_rjz)-1,9,jfje_sum);
	_this.SetCellText(sheet_rjz,_this.GetRowCount(sheet_rjz)-1,10,dfje_sum);

	_this.SetCellText(sheet_param,1,13,&quot;借：&quot;+jfbs+&quot;笔  贷：&quot;+dfbs+&quot;笔&quot;);
	_this.SetCellText(sheet_param,2,13,&quot;0/&quot;+(jfbs+dfbs)+ &quot;  &quot;+jefx+&quot;：&quot;+(jfje_sum+dfje_sum).toFixed(2)+&quot;元&quot;);

	//加载日记帐明细
	var lsh = _this.GetCellText(sheet_rjz,1,15);
	var djh = _this.GetCellText(sheet_rjz,1,14);
	loadRJZMXData(djh,1);


	_this.AutoFixScreenWidth();
	_this.SetToolbarString(&quot;&quot;);

}

//日记帐明细
function loadRJZMXData(lsh,rjzRow)
{	
	_this.SetToolbarString(&quot;正在加载数据...&quot;);

	_this.LoadZXGFile(ZXGFILE1,-1,1);
	_this.LoadZXGFile(ZXGFILE2,-1,2);
	_this.SetFixedRow(sheet_rjzmx1,1);
	_this.SetFixedRow(sheet_rjzmx2,1);

	_this.SetColVisible(sheet_rjzmx1,5,-1);
	_this.SetColVisible(sheet_rjzmx1,6,-1);
	_this.SetColVisible(sheet_rjzmx2,5,-1);
	_this.SetColVisible(sheet_rjzmx2,6,-1);

	_this.SetMainCellRange(sheet_rjzmx1,1,0,_this.GetRowCount(sheet_rjzmx1)-2,_this.GetColCount(sheet_rjzmx1)-1);
	_this.SetMainCellRange(sheet_rjzmx2,1,0,_this.GetRowCount(sheet_rjzmx2)-2,_this.GetColCount(sheet_rjzmx2)-1);
	_sql.querytods(&quot;RJZMX&quot;,&quot;LSH=&quot;+lsh+&quot;&DWBH=&quot;+_this.GetCellText(sheet_rjz,rjzRow,18));
	var sumrow = _this.XMLDS_GetRowCount();
	var midrow = Math.ceil(sumrow / 2);
	//alert(&quot;sumrow=&quot;+sumrow+&quot;,midrow=&quot;+midrow);
	var row1 = _this.GetMainCellRangeRow1(sheet_rjzmx1);
	var row2 = _this.GetMainCellRangeRow2(sheet_rjzmx1);
	var n = 0;
	for (var i=0;i&lt;midrow;i++) {
		n ++;
		if (n &gt; row2) {
			_this.AppendRow(sheet_rjzmx1,n-1);
		}
		_this.SetCellText(sheet_rjzmx1,row1,0,_this.XMLDS_GetString(i,&quot;RJLX&quot;));
		_this.SetCellText(sheet_rjzmx1,row1,1,_this.XMLDS_GetString(i,&quot;KXMC&quot;));
		_this.SetCellText(sheet_rjzmx1,row1,2,_this.XMLDS_GetString(i,&quot;YM1&quot;));
		_this.SetCellText(sheet_rjzmx1,row1,3,_this.XMLDS_GetString(i,&quot;YM2&quot;));
		_this.SetCellText(sheet_rjzmx1,row1,4,_this.XMLDS_GetString(i,&quot;JE&quot;));
		_this.SetCellText(sheet_rjzmx1,row1,5,_this.XMLDS_GetString(i,&quot;ORG&quot;));
		_this.SetCellText(sheet_rjzmx1,row1,6,_this.XMLDS_GetString(i,&quot;ACC&quot;));
		_this.SetCellText(sheet_rjzmx1,row1,7,_this.GetCellText(sheet_rjz,rjzRow,15));
		_this.SetCellText(sheet_rjzmx1,row1,8,_this.XMLDS_GetString(i,&quot;LXBH&quot;));
		_this.SetCellText(sheet_rjzmx1,row1,9,_this.XMLDS_GetString(i,&quot;MXXH&quot;));
		row1 ++;
	}
	row1 = _this.GetMainCellRangeRow1(sheet_rjzmx2);
	row2 = _this.GetMainCellRangeRow2(sheet_rjzmx2);
	n = 0;
	for (var i=midrow;i&lt;sumrow;i++) {
	 	n ++;
		if (n &gt; row2) {
			_this.AppendRow(sheet_rjzmx2,n-1);
		}
		_this.SetCellText(sheet_rjzmx2,row1,0,_this.XMLDS_GetString(i,&quot;RJLX&quot;));
		_this.SetCellText(sheet_rjzmx2,row1,1,_this.XMLDS_GetString(i,&quot;KXMC&quot;));
		_this.SetCellText(sheet_rjzmx2,row1,2,_this.XMLDS_GetString(i,&quot;YM1&quot;));
		_this.SetCellText(sheet_rjzmx2,row1,3,_this.XMLDS_GetString(i,&quot;YM2&quot;));
		_this.SetCellText(sheet_rjzmx2,row1,4,_this.XMLDS_GetString(i,&quot;JE&quot;));
		_this.SetCellText(sheet_rjzmx2,row1,5,_this.XMLDS_GetString(i,&quot;ORG&quot;));
		_this.SetCellText(sheet_rjzmx2,row1,6,_this.XMLDS_GetString(i,&quot;ACC&quot;));
		_this.SetCellText(sheet_rjzmx2,row1,7,_this.GetCellText(sheet_rjz,rjzRow,15));
		_this.SetCellText(sheet_rjzmx2,row1,8,_this.XMLDS_GetString(i,&quot;LXBH&quot;));
		_this.SetCellText(sheet_rjzmx2,row1,9,_this.XMLDS_GetString(i,&quot;MXXH&quot;));

		row1 ++;
	}
	_this.SetToolbarString(&quot;&quot;);
	_this.AutoFixScreenWidth();

}

//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
	if (sheet == sheet_param) {
		var dat1 =  _this.GetCellText(sheet_param,1,2);
		var dat2 =  _this.GetCellText(sheet_param,2,2);
		var yhbm =  _this.GetCellText(sheet_param,2,6);
		cur_kmbh = _this.GetCellText(sheet_param,1,8);
//		if (dat1 !=&quot;&quot; && dat2 != &quot;&quot; && yhbm != &quot;&quot;) {
		if (dat1  !=&quot;&quot; && dat2 != &quot;&quot; && cur_kmbh != &quot;&quot;){
			loadRJZData(dat1,dat2,yhbm);
		}

	}
	else if (sheet == sheet_rjz) {
		if (col != 0) {
			_this.SetCellText(sheet_rjz,row,1,&quot;*&quot;); //设置标志列为修改*
			g_modifyflag = 1;
		}	
		else if (col == 0) {
			if (row == 0) {
				for (var r=_this.GetMainCellRangeRow1(sheet_rjz);r&lt;=_this.GetMainCellRangeRow2(sheet_rjz);r++) {
					_this.SetCellText(sheet_rjz,r,0,newvalue);
				}
			}
			//计算选中合计数
			var selSumje = 0.00;
			var jfje_sum = 0.00;
			var dfje_sum = 0.00;
			for (var r=_this.GetMainCellRangeRow1(sheet_rjz);r&lt;=_this.GetMainCellRangeRow2(sheet_rjz);r++) {
				var flg = _this.GetCellText(sheet_rjz,r,0);
				if (flg == 1) {
					var jfje = 1.0*_this.GetCellText(sheet_rjz,r,9);
					var dfje = 1.0*_this.GetCellText(sheet_rjz,r,10);
					jfje_sum += jfje;
					dfje_sum += dfje;
				}
			}
			if (jfje_sum != 0) selSumje = jfje_sum;
			if (dfje_sum != 0) selSumje = dfje_sum;
			_this.SetCellText(sheet_rjz,_this.GetRowCount(sheet_rjz)-1,14,selSumje);
		}
	}
}

//鼠标左键点击
function _thisOnMouseLClick(sheet,row,col)
{
	if (sheet == sheet_rjz && row &gt;= _this.GetMainCellRangeRow1(sheet_rjz) && row &lt;= _this.GetMainCellRangeRow2(sheet_rjz)) {
		var lsh = _this.GetCellText(sheet_rjz,row,15);
		var djh = _this.GetCellText(sheet_rjz,row,14);
		if (djh != &quot;&quot;) {
			var str = _this.GetCellText(sheet_param,2,13);
			var pos = str.indexOf(&quot;/&quot;);
			_this.SetCellText(sheet_param,2,13,row+str.substring(pos,str.length));
		}
		loadRJZMXData(djh,row);
		setDataRowTips(row);

		if (ctrlKey == true) { //按住Ctrl键多选
			selectOneRow(sheet_rjz,row);
		}
	}
	else if (sheet == sheet_param) {
		if (row == 1 && col == 11) {
			showKM(); //选择科目
		}
		else if (row == 1 && col == 12) {
			_thisOnCellModify(sheet,row,col,&quot;&quot;,&quot;&quot;); //查询		
		}
	}
}

//提示行信息
function setDataRowTips(row)
{
	_this.SetCellText(sheet_tips,0,2,_this.GetCellText(sheet_rjz,row,5));
	_this.SetCellText(sheet_tips,0,5,_this.GetCellText(sheet_rjz,row,18)+&quot;-&quot;+_this.GetCellText(sheet_rjz,row,19));
	_this.SetCellText(sheet_tips,0,7,_this.GetCellText(sheet_rjz,row,13));
	_this.SetCellText(sheet_tips,0,9,_this.GetCellText(sheet_rjz,row,29));

}

function showKM()
{
	var obj = new Object();
        obj.filter = &quot;xjyhbz &lt;&gt; 0&quot;;
        obj.mjbz = &quot;1&quot;;
        obj.bz = &quot;&quot;;
        obj.jb = 0;
        obj.kmbh = &quot;&quot;;
	var retVal = window.showModalDialog(&quot;show.sp?grdid=CW_KMSEL&quot;,obj,&quot;dialogWidth=600px;dialogHeight=500px&quot;);
	cur_kmbh = retVal;
	if (cur_kmbh != &quot;&quot; && cur_kmbh != &quot;undefined&quot; && cur_kmbh != undefined) {
		_this.SetCellText(sheet_param,1,8,cur_kmbh);
		_thisOnCellModify(sheet_param,1,8,&quot;&quot;,cur_kmbh);
	}	
}

//保存
function rjzSave()
{
	//检查是否已经结账
	var jz_yy = G_LOGDAT.split(&quot;-&quot;)[0];
	var jz_mm = G_LOGDAT.split(&quot;-&quot;)[1]*1;
	var jz_sbh = G_ORGID;
	var jz_zth = G_ACCID.replace(G_ORGID,&quot;&quot;);

	var param = new Object();
	param.yy = jz_yy;
	param.mm = jz_mm;
	param.sbh = jz_sbh;
	param.zth = jz_zth;
	var jz_retVal = invokeOSFuncExt(&quot;check_jz&quot;,param);
	var jz_ret = jz_retVal.split(&quot;@&quot;)[0];
	var jz_msg = jz_retVal.split(&quot;@&quot;)[1];
//	if(jz_ret != 1 ){
//		alert(jz_retVal);
//		return false;
//	}

	if (!beforeSaveCheck()) return;

	var param = new Object();
	param.rjzdat = &quot;&quot;;
	param.dat1 = _this.GetCellText(sheet_param,1,2);
	param.dat2 = _this.GetCellText(sheet_param,2,2);
	param.rjzxml = getRJZSaveXml();
	param.thisorgid = G_ORGID;
	param.thisaccid = G_ACCID;
	param.czyxm = G_USRNAM;
	param.kmbh = _this.GetCellText(sheet_param,1,8);
	var szbz = _this.GetCellText(sheet_param,1,6); //收支标志
	param.szbz= szbz;
	param.jefx = &quot;借&quot;;
	if (szbz == &quot;2&quot;) param.jefx = &quot;贷&quot;;

	var rjzdat = window.showModalDialog(&quot;show.sp?grdid=RJZ_XGRQ&quot;,&quot;&quot;,&quot;dialogWidth=350px;dialogHeight=200px&quot;);	
	if (rjzdat == &quot;2&quot;) return; //取消保存操作
	if (rjzdat != &quot;&quot; && rjzdat != &quot;undefined&quot;) {
		param.rjzdat = rjzdat;
	}

	try {
		var ret = invokeOSFuncExt(&quot;Save&quot;,param);
		if (ret == 1) {
			alert(&quot;存入日记帐成功！&quot;);
			g_modifyflag = 0;
			var dat1 = _this.GetCellText(sheet_param,1,2);
			var dat2 = _this.GetCellText(sheet_param,2,2);
			var yhbm =  _this.GetCellText(sheet_param,2,6);
			loadRJZData(dat1,dat2,yhbm);
		}
		else if (ret == 0) {
			alert(&quot;没有选中记录！&quot;);
		}
		else {
			alert(&quot;存入日记帐出错:&quot;+ret);
		}
	}
	catch (e) {
		alert(&quot;存入日记帐出错:&quot;+e.toString());
	}

}

function beforeSaveCheck()
{
	var kmbh = _this.GetCellText(sheet_param,1,8);
	if (kmbh == &quot;&quot;) {
		alert(&quot;请选择日记帐科目！&quot;);
		return false;
	}

	for (var r=_this.GetMainCellRangeRow1(sheet_rjz);r&lt;=_this.GetMainCellRangeRow2(sheet_rjz);r++) {
		var flag = _this.GetCellText(sheet_rjz,r,0);
		var yy = _this.GetCellText(sheet_rjz,r,2);
		var mm = _this.GetCellText(sheet_rjz,r,3);
		if (flag == &quot;1&quot; && yy != &quot;&quot;) {
			if ((yy+&quot;-&quot;+mm) != G_LOGDAT.substring(0,7)) {
				alert(&quot;日期出错(发生年月不是当前月)！&quot;);
				_this.SetCellFocus(sheet_rjz,r,8);
				return false;
			}
		}
	}
	return true;
}

//获取修改的数据xml
function getRJZSaveXml()
{
	var xml = &quot;&lt;?xml version = &apos;1.0&apos;?&gt;&lt;ROWSET&gt;\n&quot;;
	var num = 0;
	for (var r=_this.GetMainCellRangeRow1(sheet_rjz);r&lt;=_this.GetMainCellRangeRow2(sheet_rjz);r++) {
		var flag = _this.GetCellText(sheet_rjz,r,0);
		var yy = _this.GetCellText(sheet_rjz,r,2);
		if (flag == &quot;1&quot; && yy != &quot;&quot;) {
			num ++;
			xml += &quot;&lt;ROW num=\&quot;&quot;+num+&quot;\&quot;&gt;\n&quot;;
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_rjz,2)+&quot;&gt;&quot; + _this.GetCellText(sheet_rjz,r,2) +&quot;&lt;/&quot;+_this.GetColName(sheet_rjz,2)+&quot;&gt;\n&quot;;	//年
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_rjz,3)+&quot;&gt;&quot; + _this.GetCellText(sheet_rjz,r,3) +&quot;&lt;/&quot;+_this.GetColName(sheet_rjz,3)+&quot;&gt;\n&quot;;	//月
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_rjz,4)+&quot;&gt;&quot; + _this.GetCellText(sheet_rjz,r,4) +&quot;&lt;/&quot;+_this.GetColName(sheet_rjz,4)+&quot;&gt;\n&quot;;	//日
			//xml += &quot;&lt;&quot;+_this.GetColName(sheet_rjz,8)+&quot;&gt;&quot; + _this.GetCellText(sheet_rjz,r,8) +&quot;&lt;/&quot;+_this.GetColName(sheet_rjz,8)+&quot;&gt;\n&quot;;	//摘要
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_rjz,14)+&quot;&gt;&quot; + _this.GetCellText(sheet_rjz,r,14) +&quot;&lt;/&quot;+_this.GetColName(sheet_rjz,14)+&quot;&gt;\n&quot;;	//单据号
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_rjz,18)+&quot;&gt;&quot; + _this.GetCellText(sheet_rjz,r,18) +&quot;&lt;/&quot;+_this.GetColName(sheet_rjz,18)+&quot;&gt;\n&quot;;	//单位编号
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_rjz,32)+&quot;&gt;&quot; + _this.GetCellText(sheet_rjz,r,32) +&quot;&lt;/&quot;+_this.GetColName(sheet_rjz,32)+&quot;&gt;\n&quot;;	//银行
			xml += &quot;&lt;/ROW&gt;\n&quot;;		
		}
	}
	xml += &quot;&lt;/ROWSET&gt;&quot;;
	return xml;
}

function controlkeydown()
{
	altKey = event.altKey;
	ctrlKey = event.ctrlKey;
	shiftKey = event.shiftKey;

	if (shiftKey == true) {
		shiftSelectRow1 = _this.GetCurrentRow(sheet_rjz);
	}
}

function controlkeyup()
{
	if (shiftKey == true) {
		shiftSelectRow2 = _this.GetCurrentRow(sheet_rjz);
		for (var r=shiftSelectRow1;r&lt;=shiftSelectRow2;r++) {
			selectOneRow(sheet_rjz,r);
		}
	}

	altKey = false;
	ctrlKey = false;
	shiftKey = false;
}

//鼠标双击
function _thisOnMouseDClick(sheet,row,col)
{
	if (sheet == sheet_rjz && row &gt;= _this.GetMainCellRangeRow1(sheet_rjz) && row &lt;= _this.GetMainCellRangeRow2(sheet_rjz)) {
		selectOneRow(sheet_rjz,row);
	}
}

function selectOneRow(sheet,row)
{
	var flg = _this.GetCellText(sheet,row,0);
	if (flg == 1) {
		_this.SetCellText(sheet,row,0,0);
		setOneRowBkColor(sheet,row,255,255,255);		
	}
	else {
		_this.SetCellText(sheet,row,0,1);
		setOneRowBkColor(sheet,row,255,236,139);
	}
}

function setOneRowBkColor(sheet,row,r,g,b)
{
	for (var c=0;c&lt;_this.GetColCount(sheet);c++) {
		_this.SetCellBkColor(sheet,row,c,r,g,b);
	}
	_this.SetCellFocus(sheet,row,0);
	_this.Redraw();
}

