//保存日记帐修改
function Save()
{	
	var db = null;
	try {
		db = new pub.EADatabase();
		var ds = new pub.EAXmlDS(rjzxml);
		if (ds.getRowCount() &lt;= 0) return &quot;0&quot;;
		var my_old_pch = &quot;&quot;;
		for (var i=0;i&lt;ds.getRowCount();i++) {
			var yy = ds.getStringAt(i,&quot;YY&quot;);
			var mm = ds.getStringAt(i,&quot;MM&quot;);
			var dd = ds.getStringAt(i,&quot;DD&quot;);
			var dat = yy+mm+dd; //中间表的RQ
			//修改日期
			if (rjzdat != &quot;&quot;) {
				yy = rjzdat.split(&quot;-&quot;)[0];
				mm = rjzdat.split(&quot;-&quot;)[1];
				dd = rjzdat.split(&quot;-&quot;)[2];
			}
			var djh = ds.getStringAt(i,&quot;DJH&quot;);
			var dwbh = ds.getStringAt(i,&quot;DWBH&quot;);
			var aae008 = ds.getStringAt(i,&quot;AAE008&quot;);
			var sql = &quot;select sbh,zth,substr(rq,0,4) yy,substr(rq,5,2) mm,substr(rq,7,2) dd,pch,jsfs,djh,dwbh,max(dwmc) dwmc,max(zy) zy,
			       sum(je1) je1,sum(je2) je2,sum(je3) je3,sum(je4) je4,sum(je5) je5,sum(je6) je6,sum(je7) je7,
			       sum(je8) je8,sum(je9) je9,sum(je10) je10,sum(je11) je11,sum(je12) je12,sum(je13) je13,sum(je14) je14,sum(je15) je15,
			         sum(je1+je2+je3+je4+je5+je6+je7+je8+je9+je10+je11+je12+je13+je14+je15) sumje,
			         max(nvl(dwjc,dwmc)) dwjc,nvl(max(dwlxbh),&apos;0&apos;) dwlxbh,max(nvl(yhzh1,yhzh2)) yhzh,ym1,ym2,lxbh      
				FROM  v_si_djb_tmp 
				where djh=&apos;&quot;+djh+&quot;&apos; and dwbh=&apos;&quot;+dwbh+&quot;&apos; and org=&apos;&quot;+thisorgid+&quot;&apos; and acc=&apos;&quot;+thisaccid+&quot;&apos; and rzbz = &apos;0&apos; and zfbz = &apos;0&apos;
				group by sbh,zth,rq,djh,dwbh,jsfs,pch,ym1,ym2,lxbh&quot;;	
			var sids = db.QuerySQL(sql);
			//throw new Exception(sql);
			//pub.EAFunc.Log(&quot;rowcnt=&quot;+sids.getRowCount()+&quot; &quot;+sql);
			if (sids.getRowCount() &gt; 0) {
				var sbh = sids.getStringAt(0,&quot;SBH&quot;);
				var zth = sids.getStringAt(0,&quot;ZTH&quot;);
				var dwmc = sids.getStringAt(0,&quot;DWMC&quot;);
				var pch = sids.getStringAt(0,&quot;PCH&quot;);
				var sumje = 1.0 * sids.getStringAt(0,&quot;SUMJE&quot;);
				var zy = sids.getStringAt(0,&quot;ZY&quot;);
				var jsfs = sids.getStringAt(0,&quot;JSFS&quot;);
				var yspz_fjzs = &quot;&quot;;
				var yspz_fjpch = &quot;&quot;;
				var dwjc = sids.getStringAt(0,&quot;DWJC&quot;);
				var dwlxbh = sids.getStringAt(0,&quot;DWLXBH&quot;);
				var yhzh = sids.getStringAt(0,&quot;YHZH&quot;);		

				//查询si_dwb，单位不存在的话插入，存在的则更新
				sql = &quot;select * from si_dwb where sbh=&apos;&quot;+sbh+&quot;&apos; and zth=&apos;&quot;+zth+&quot;&apos; and upper(dwbh)=upper(&apos;&quot;+dwbh+&quot;&apos;)&quot;;
				var tmpds = db.QuerySQL(sql);
				if (tmpds.getRowCount() == 0) { //不存在插入
					sql = &quot;insert into si_dwb ( sbh, zth, org, acc, dwbh, dwmc, dwjc, dwlxbh, yhzh ) values (&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;,&apos;%s&apos;)&quot;
						.format([sbh,zth,thisorgid,thisaccid,dwbh,dwmc,dwjc,dwlxbh,yhzh]);
					db.ExcecutSQL(sql);
				}
				else { //更新 
					sql = &quot;update si_dwb set dwbh = upper(&apos;&quot;+dwbh+&quot;&apos;), dwmc = &apos;&quot;+dwmc+&quot;&apos;,dwjc = &apos;&quot;+dwjc+&quot;&apos;, dwlxbh = &apos;&quot;+dwlxbh+&quot;&apos;, yhzh = &apos;&quot;+yhzh+&quot;&apos;
