				//保存进日记帐表cw_rjzb			
				var lsh = db.GetSQL(&quot;select seq_rjz_lsh.nextval from dual&quot;); //流水号
				sql = &quot;select nvl(max(sxh),0)+1 from cw_rjzb where sbh=&apos;&quot;+sbh+&quot;&apos; and zth=&apos;&quot;+zth+&quot;&apos; and yy=&apos;&quot;+yy+&quot;&apos; and mm=&apos;&quot;+mm+&quot;&apos; and kmbh=&apos;&quot;+kmbh+&quot;&apos;&quot;;
				var sxh = db.GetSQL(sql); //顺序号
				jsfs = &quot;转帐&quot;;
				sql = &quot;insert into cw_rjzb(sbh,zth,org,acc,lsh,yy,mm,dd,sxh,czyxm,djh,OLD_PCH,zy,kmbh,lxbh,dwbh,dwmc,je,jefx,jsfs,yspz_fjzs,yspz_fjpch,yspz_lrrq,czysj)
					values(&apos;&quot;+sbh+&quot;&apos;,&apos;&quot;+zth+&quot;&apos;,&apos;&quot;+thisorgid+&quot;&apos;,&apos;&quot;+thisaccid+&quot;&apos;,&apos;&quot;+lsh+&quot;&apos;,&apos;&quot;+yy+&quot;&apos;,&apos;&quot;+mm+&quot;&apos;,&apos;&quot;+dd+&quot;&apos;,&apos;&quot;+sxh+&quot;&apos;,&apos;&quot;+czyxm+&quot;&apos;,&apos;&quot;+djh+&quot;&apos;,
					&apos;&quot;+pch+&quot;&apos;,&apos;&quot;+zy+&quot;&apos;,&apos;&quot;+kmbh+&quot;&apos;,&apos;&apos;,&apos;&quot;+dwbh+&quot;&apos;,&apos;&quot;+dwmc+&quot;&apos;,&apos;&quot;+sumje+&quot;&apos;,&apos;&quot;+jefx+&quot;&apos;,&apos;&quot;+jsfs+&quot;&apos;,&apos;&quot;+yspz_fjzs+&quot;&apos;,&apos;&quot;+yspz_fjpch+&quot;&apos;,sysdate,sysdate)&quot;;
				db.ExcecutSQL(sql);	

				//保存进日记帐明细表cw_rjzmxb
				var mxxh = 0;
				var oldlxbh = &quot;&quot;;
				var rjzlxbh = &quot;&quot;;
				for (var n=0;n&lt;sids.getRowCount();n++) {
					for (var k=1;k&lt;=15;k++) {
						var lxbh = sids.getStringAt(n,&quot;LXBH&quot;);	
						var je = sids.getStringAt(n,&quot;JE&quot;+k);	
						var lxxh = k;				
						if (je != &quot;0&quot; && je != &quot;0.00&quot; && je != &quot;&quot;) {
							mxxh ++;
							var ym1 = sids.getStringAt(n,&quot;YM1&quot;);
							var ym2 = sids.getStringAt(n,&quot;YM2&quot;);

							if (oldlxbh == &quot;&quot;) {
								oldlxbh = lxbh;
								rjzlxbh = oldlxbh;
							}
							if (lxbh == oldlxbh) {
								//lxxh ++;
							}
							else {
								//lxxh = 1;
								oldlxbh = lxbh;
								rjzlxbh += lxbh;
							}

							sql = &quot;insert into cw_rjzmxb(sbh,zth,org,acc,lsh,mxxh,lxbh,lxxh,ym1,ym2,je,jefx)values(&apos;&quot;+sbh+&quot;&apos;,&apos;&quot;+zth+&quot;&apos;,&apos;&quot;+thisorgid+&quot;&apos;,&apos;&quot;+thisaccid+&quot;&apos;,&apos;&quot;+lsh+&quot;&apos;,
									&apos;&quot;+mxxh+&quot;&apos;,&apos;&quot;+lxbh+&quot;&apos;,&apos;&quot;+lxxh+&quot;&apos;,&apos;&quot;+ym1+&quot;&apos;,&apos;&quot;+ym2+&quot;&apos;,&apos;&quot;+je+&quot;&apos;,&apos;&quot;+jefx+&quot;&apos;)&quot;;
							db.ExcecutSQL(sql);
						}
					}
				}
				//更新日记帐表的总金额
				sql = &quot;update cw_rjzb a set je=(select sum(je) from cw_rjzmxb b where a.sbh=b.sbh and a.zth=b.zth and a.lsh=b.lsh)
					where sbh=&apos;&quot;+sbh+&quot;&apos; and zth=&apos;&quot;+zth+&quot;&apos; and lsh=&apos;&quot;+lsh+&quot;&apos;&quot;;
				db.ExcecutSQL(sql);
				//更新日记帐表的类型编号
				sql = &quot;update cw_rjzb a set lxbh=&apos;&quot;+rjzlxbh+&quot;&apos; where sbh=&apos;&quot;+sbh+&quot;&apos; and zth=&apos;&quot;+zth+&quot;&apos; and lsh=&apos;&quot;+lsh+&quot;&apos;&quot;;
				db.ExcecutSQL(sql);


				//更新SI_DJB_TMP
//				sql = &quot;update v_si_djb_tmp set lsh=&apos;&quot;+lsh+&quot;&apos;,RZBZ=&apos;1&apos;,RZR=&apos;&quot;+czyxm+&quot;&apos;,RZSJ=sysdate 
//					where aae008=&apos;&quot;+aae008+&quot;&apos; and rq=&apos;&quot;+dat+&quot;&apos; and djh=&apos;&quot;+djh+&quot;&apos; and dwbh=&apos;&quot;+dwbh+&quot;&apos; 
//					and org=&apos;&quot;+thisorgid+&quot;&apos; and acc=&apos;&quot;+thisaccid+&quot;&apos;
//					and lsh is null and rzbz=&apos;0&apos;&quot;;

				sql = &quot;update v_si_djb_tmp set lsh=&apos;&quot;+lsh+&quot;&apos;,RZBZ=&apos;1&apos;,RZR=&apos;&quot;+czyxm+&quot;&apos;,RZSJ=sysdate 
					where djh=&apos;&quot;+djh+&quot;&apos; and dwbh=&apos;&quot;+dwbh+&quot;&apos; 
					and org=&apos;&quot;+thisorgid+&quot;&apos; and acc=&apos;&quot;+thisaccid+&quot;&apos;
					and lsh is null and rzbz=&apos;0&apos;&quot;;
				db.ExcecutSQL(sql);

				//更新AC95明细,入日记帐的记录才可以拷盘
				var tcqbm = db.GetSQL(&quot;select nvl(tcqbm,sbh) from cw_sbsb where sbh=&apos;&quot;+sbh+&quot;&apos;&quot;);
//				sql = &quot;update ac95 set AAE076=&apos;&quot;+lsh+&quot;&apos; where (aaa027=&apos;&quot;+thisorgid+&quot;&apos; or aaa027=&apos;&quot;+tcqbm+&quot;&apos;) 
//					and zth=&apos;&quot;+zth+&quot;&apos; and BAE074=&apos;&quot;+djh+&quot;&apos; and AAB001=&apos;&quot;+dwbh+&quot;&apos;
//					and bae419=&apos;&quot;+aae008+&quot;&apos; and aae076 is null&quot;;
				sql = &quot;update ac95 set AAE076=&apos;&quot;+lsh+&quot;&apos; where (aaa027=&apos;&quot;+thisorgid+&quot;&apos; or aaa027=&apos;&quot;+tcqbm+&quot;&apos;) 
					and zth=&apos;&quot;+zth+&quot;&apos; and BAE074=&apos;&quot;+djh+&quot;&apos; and AAB001=&apos;&quot;+dwbh+&quot;&apos;
					and aae076 is null&quot;;
				db.ExcecutSQL(sql);

			}
		}
		//调用f_check_next_qcye更新期初余额
		var pubfunc = new SBCW_PUBFUNC();
		var rjzyy = &quot;&quot;;
		var rjzmm = &quot;&quot;;
		if (rjzdat != &quot;&quot;) {
			rjzyy = rjzdat.split(&quot;-&quot;)[0];
			rjzmm = rjzdat.split(&quot;-&quot;)[1];
		}
		else {
			rjzyy = dat1.split(&quot;-&quot;)[0];
			rjzmm = dat1.split(&quot;-&quot;)[1];
		}
		pubfunc.f_check_next_qcye(db,thisorgid,thisaccid,kmbh,rjzyy,rjzmm);

		db.Commit();
		return &quot;1&quot;;
	}
	catch (e) {
		if (db != null) db.Rollback();
		return e.toString();	
	}
	finally {
		if (db != null) db.Close();
	}
}

ROW.DATDSC:
select * from (
select b.rjlx,b.kxmc,a.ym1,a.ym2,sum(decode(mxxh,&apos;1&apos;,je1,&apos;2&apos;,je2,&apos;3&apos;,je3,&apos;4&apos;,je4,&apos;5&apos;,je5,&apos;6&apos;,je6,&apos;7&apos;,je7,
       &apos;8&apos;,je8,&apos;9&apos;,je9,&apos;10&apos;,je10,&apos;11&apos;,je11,&apos;12&apos;,je12,&apos;13&apos;,je13,&apos;14&apos;,je14,&apos;15&apos;,je15,null)) je,
       a.sbh,a.zth,a.lxbh,b.mxxh
from v_si_djb_tmp a,(
       select b1.sbh,b1.zth,b1.lxbh,b1.rjlx,b2.mxxh,b2.kxmc,b2.fx 
       from cw_rjlxb b1,cw_rjlxmxb b2 
       where b1.sbh=b2.sbh and b1.zth=b2.zth and b1.lxbh=b2.lxbh
       ) b
where a.sbh=&apos;[%SYS_ORGID]&apos; and a.acc=&apos;[%SYS_ACCID]&apos;
  and a.djh=&apos;[%LSH]&apos;
  and a.dwbh=&apos;[%DWBH]&apos; 
  and a.rzbz=&apos;0&apos; and a.zbbz = &apos;0&apos;
  and nvl(a.zfbz,&apos;0&apos;) = &apos;0&apos;
  and a.sbh=b.sbh and a.zth=b.zth
  and a.lxbh=b.lxbh  
group by b.rjlx,b.kxmc,a.ym1,a.ym2,a.sbh,a.zth,a.lxbh,b.mxxh
order by b.mxxh
) where je&lt;&gt;0

select dwlxbh from si_dwb 
where org=&apos;[%SYS_ORGID]&apos; and acc=&apos;[%SYS_ACCID]&apos; 
  and dwbh=&apos;[%DWBH]&apos;
