ROW.GRDJSDS_VALUE:
var printType = &quot;1&quot;;

//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	_this.SetToBoolBox(sheet,3,2);
	_this.SetToBoolBox(sheet,5,2);
	_this.SetToCheckBox(&quot;&quot;,sheet,8,2,&quot;附件只打印养老保险&quot;,&quot;1&quot;,&quot;0&quot;);

	_this.SetCellText(sheet,3,2,&quot;1&quot;);

	_this.ShowToolBar(0);
	_this.AutoFixScreenWidth();

}

//确定
function queding()
{
	var obj = new Object();
	obj.result = printType;
	obj.onlyyl = _this.GetCellText(0,8,2);

	window.returnValue = obj;
	window.close();
}

//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
	if (row == 3) {
		_this.SetCellText(sheet,5,2,&quot;0&quot;);
		_this.SetCellText(sheet,3,2,&quot;1&quot;);
		printType = &quot;1&quot;;
	}
	else if (row == 5) {
		_this.SetCellText(sheet,3,2,&quot;0&quot;);
		_this.SetCellText(sheet,5,2,&quot;1&quot;);
		printType = &quot;2&quot;;
	}
}

//点击按钮
function _thisOnButtonCtrlClick(id,sheet,row,col)
{
	if (id == &quot;确定&quot;) {
		queding();
	}
	else if (id == &quot;取消&quot;) {
		var obj = new Object();
		obj.result = 0;
		obj.onlyyl = &quot;&quot;;

		window.returnValue = obj;
		window.close();
	}
}
