var _dat1 = &quot;&quot;;
var _dat2 = &quot;&quot;;
var _kmbh = &quot;&quot;;

//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	_this.SetToDateBox(&quot;&quot;,sheet,3,2,&quot;&quot;);
	_this.SetToDateBox(&quot;&quot;,sheet,3,4,&quot;&quot;);

	var rjlxList =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_CW_RJLXB&quot;),&quot;ID&quot;,&quot;FULLNAME&quot;);
	_this.SetToComboBox(&quot;&quot;,sheet,3,6,rjlxList);
	var dwlxList =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_SI_DWLXB&quot;),&quot;ID&quot;,&quot;NAME&quot;);
	_this.SetToComboBox(&quot;&quot;,sheet,7,6,dwlxList);

	//接收传入参数
	try {
		var objParam = window.dialogArguments;
		var dat = objParam.yyyy+&quot;-&quot;+objParam.mm+&quot;-01&quot;;
		_sql.querytods(&quot;GETDAT&quot;,&quot;DAT=&quot;+dat);
		_this.SetCellText(sheet,3,2,_this.XMLDS_GetString(0,&quot;DAT1&quot;));
		_this.SetCellText(sheet,3,4,_this.XMLDS_GetString(0,&quot;DAT2&quot;));
		_dat1 = _this.XMLDS_GetString(0,&quot;DAT1&quot;);
		_dat2 = _this.XMLDS_GetString(0,&quot;DAT2&quot;);
		_kmbh = objParam.kmbh;
		var kmmc = objParam.kmmc;
		_kmmc = kmmc.replace(objParam.kmbh+&quot;-&quot;,&quot;&quot;);
		if(_kmbh == -1) {
			_this.SetCellText(sheet,4,2,&quot;&quot;);
		}
		else {
			_this.SetCellText(sheet,4,2,objParam.kmbh);
		}
		//1021001001―养老收入户存款―活期―农行-0587
		_this.SetCellText(sheet,4,4,kmmc);
	}
	catch (e) {}
}

