function showKM()
{
	var obj = new Object();
        obj.filter = &quot;xjyhbz &lt;&gt; 0&quot;;
        obj.mjbz = &quot;1&quot;;
        obj.bz = &quot;&quot;;
        obj.jb = 0;
        obj.kmbh = &quot;&quot;;
	var retVal = window.showModalDialog(&quot;show.sp?grdid=CW_KMSEL&quot;,obj,&quot;dialogWidth=600px;dialogHeight=500px&quot;);
	if (retVal != &quot;&quot; && retVal != &quot;undefined&quot; && retVal != undefined) {
		_this.SetCellText(0,4,2,retVal);
		_thisOnCellModify(0,4,2,&quot;&quot;,retVal);
	}
	setTimeout(&quot;setStatus()&quot;,500);
}

//选择单位
function showDW()
{
	var obj = new Object();
        obj.filter = &quot;&quot;;
	var retVal = window.showModalDialog(&quot;show.sp?grdid=CW_DWSEL&quot;,obj,&quot;dialogWidth=600px;dialogHeight=500px&quot;);
	if (retVal != &quot;&quot; && retVal != &quot;undefined&quot; && retVal != undefined) {
		var dwbh = retVal.split(&quot;~&quot;)[0];
		var dwmc = retVal.split(&quot;~&quot;)[1];
		_this.SetCellText(0,5,2,dwbh);
		_this.SetCellText(0,5,4,dwmc);
	}
	setTimeout(&quot;setStatus()&quot;,500);

}

//鼠标左键点击
function _thisOnMouseLClick(sheet,row,col)
{
	if (row == 4 && col == 7) {
		if (clickbz == 0) return;
		clickbz = 0;
		showKM();
	}
	else if (row == 5 && col == 7) {
		if (clickbz == 0) return;
		clickbz = 0;
		showDW();
	}
}

//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
	if (row == 3 && (col == 2 || col == 4)) {
		var dat1 = _this.GetCellText(sheet,3,2);
		var dat2 = _this.GetCellText(sheet,3,4);
		var dat11 = dat1.replace(/-/g,&quot;&quot;);
		var dat22 = dat2.replace(/-/g,&quot;&quot;);
		if (dat11 &gt; dat22) _this.SetCellText(sheet,3,4,dat1);
	}
	else if (row == 4 && col == 2) {
		if (newvalue == &quot;&quot;) { _this.SetCellText(0,4,4,&quot;&quot;); return; }
		_sql.querytods(&quot;KMINFO&quot;,&quot;KMBH=&quot;+newvalue);
		if (_this.XMLDS_GetRowCount() &gt; 0) {
			_this.SetCellText(0,4,4,_this.XMLDS_GetString(0,&quot;KMMC&quot;));
		}
		else {
			alert(&quot;没有找到该科目[&quot;+newvalue+&quot;]，请检查！&quot;);
			_this.SetCellText(sheet,row,col,oldvalue);
		}
	}
	else if (row == 5 && col == 2) {
		if (newvalue == &quot;&quot;) { _this.SetCellText(0,5,4,&quot;&quot;); return; }
		_sql.querytods(&quot;DWINFO&quot;,&quot;DWBH=&quot;+newvalue);
		if (_this.XMLDS_GetRowCount() &gt; 0) {
			_this.SetCellText(0,5,4,_this.XMLDS_GetString(0,&quot;DWMC&quot;));
		}
		else {
			alert(&quot;没有找到该单位[&quot;+newvalue+&quot;]，请检查！&quot;);
			_this.SetCellText(sheet,row,col,oldvalue);
		}
	}
	else if (row == 7 && (col == 2 || col == 4)) {
		if ( !IsNumber( newvalue,null ) ) {
			alert(&quot;输入格式不正确！&quot;);
			_this.SetCellText(sheet,row,col,oldvalue);
		}
		var mny1 = 1.0*_this.GetCellText(0,7,2);
		var mny2 = 1.0*_this.GetCellText(0,7,4);
		if (mny1 &gt; mny2) {
			_this.SetCellText(0,7,4,mny1);
		}
	}

}

//单元格焦点变动
function _thisOnCellFocusChange(sheet,rowold,colold,rownew,colnew)
{
	if (rowold == 3 && colold == 2) {
		_thisOnCellModify(sheet,rowold,colold,&quot;&quot;,&quot;&quot;);
	}
}



//处理选择单位双击后会再次触发单击事件再次弹出选择单位的问题
var clickbz = -1;
function setStatus()
{
	clickbz = 1;
}
