var ZXGFILE0 = &quot;&quot;;
var ZXGFILE1 = &quot;&quot;;

var param_dat1 = &quot;&quot;;
var param_dat2 = &quot;&quot;;
var param_kmbh = &quot;&quot;;
var param_rjkx = &quot;&quot;;
var kmList;

//数据加载完毕
function _thisOnpost_loaddata(sheet)
{

	try { param_dat1 = DAT1; param_dat2 = DAT2; param_kmbh = KMBH; } catch(e) {}
	try { param_rjkx = RJKX; } catch(e) {}

	initLayout();

	_this.SetToCheckBox(&quot;&quot;,0,1,6,&quot;精确匹配&quot;,&quot;1&quot;,&quot;0&quot;);

	kmList =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_ACCITEM&where=mjbz=1 and XJYHBZ&lt;&gt;0 and QLBZ&lt;&gt;1 and nvl(RJZKM_LRBZ,&apos;1&apos;) = &apos;1&apos;&quot;),&quot;ID&quot;,&quot;NAME&quot;);
	var rjlxList =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_CW_RJLXB&quot;),&quot;ID&quot;,&quot;FULLNAME&quot;);
	_this.SetListValue(rjlxList,&quot;%&quot;,&quot;全部&quot;);

	_this.SetToComboBox(&quot;&quot;,0,1,5,kmList);	
	_this.SetToComboBox(&quot;&quot;,0,1,8,rjlxList);

	_this.SetToDateBox(&quot;&quot;,0,1,1,param_dat1);
	_this.SetToDateBox(&quot;&quot;,0,1,3,param_dat2);
	_this.SetCellText(0,1,5,param_kmbh);
	_this.SetCellText(0,1,8,param_rjkx);

	if (param_rjkx == &quot;&quot;) _this.SetCellText(0,1,8,&quot;%&quot;);

	param_dat1 = _this.GetCellText(0,1,1);
	param_dat2 = _this.GetCellText(0,1,3);
	param_kmbh = _this.GetCellText(0,1,5);
	param_rjkx = _this.GetCellText(0,1,8);



	loadSheetData();

	_this.AutoFixScreenWidth();	
}



//页面布局初始化
function initLayout()
{
	_this.SplitSheet(0,&quot;V&quot;,&quot;8%&quot;);

	ZXGFILE0 = _this.SaveTempZXGFile(0); 
	ZXGFILE1 = _this.SaveTempZXGFile(1);
}

//加载统计明细
function loadSheetData()
{
	_this.SetToolbarString(&quot;正在加载数据...&quot;);
	_this.loadZXGFile(ZXGFILE1,-1,1);	
	_this.SetFixedRow(1,4);	

	_this.SetMainCellRange(1,4,1,_this.GetRowCount(1)-1,_this.GetColCount(1)-1);

	var jqpp = _this.GetCellText(0,1,6); //科目是否精确匹配
	if (jqpp == 1) jqpp = &quot;&quot;;
	else jqpp = &quot;%25&quot;;

	var xml = _sql.query(&quot;MAIN&quot;,&quot;DAT1=&quot;+param_dat1+&quot;&DAT2=&quot;+param_dat2+&quot;&KMBH=&quot;+param_kmbh+&quot;&RJLX=&quot;+param_rjkx+&quot;&JQPP=&quot;+jqpp);
	_this.SetXmlDS(1,4,1,_this.GetRowCount(1)-2,_this.GetColCount(1)-1,xml);


	_this.SetToolbarString(&quot;&quot;);
	_this.AutoFixScreenWidth();

}

//点击按钮
function _thisOnButtonCtrlClick(id,sheet,row,col)
{
	if ( id== &quot;统计&quot; ) {
		loadSheetData();
	}

}


//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
	var dat1 = _this.GetCellText(0,1,1);
	var dat2 = _this.GetCellText(0,1,3);
	var dat11 = dat1.replace(/-/g,&quot;&quot;);
	var dat22 = dat2.replace(/-/g,&quot;&quot;);
	if (dat11 &gt; dat22) {
		_this.SetCellText(0,1,3,dat1);
	}

	param_dat1 = _this.GetCellText(0,1,1);
	param_dat2 = _this.GetCellText(0,1,3);
	param_kmbh = _this.GetCellText(0,1,5);
	param_rjkx = _this.GetCellText(0,1,8);

}

//单元格焦点变动
function _thisOnCellFocusChange(sheet,rowold,colold,rownew,colnew)
{
	_thisOnCellModify(sheet,rowold,colold,&quot;&quot;,&quot;&quot;);
}



//选择所有科目
function setKMBHNULL()
{
	_this.SetCellText(0,1,5,&quot;%&quot;);
}

var sbz = 1;
//录入科目
function setkmbh()
{
	if (sbz == 1) {
		_this.SetToStandardCell(0,1,5);
		_this.SetCellFocus(0,1,5);
		sbz = 2;
	}
	else {
		_this.SetToComboBox(&quot;&quot;,0,1,5,kmList);	
		sbz = 1;
	}
	_this.SetRedraw(1);

}

//保存
function SaveInput()
{
	if (!confirm(&quot;保存当前修改记录，是否继续？&quot;)) return;
	var param = Object();
	var row1 = _this.GetMainCellRangeRow1(1);
	var row2 = _this.GetMainCellRangeRow2(1);
	var col1 = _this.GetMainCellRangeCol1(1);
	var col2 = _this.GetMainCellRangeCol2(1);
	param.xml = _this.GetXml(1,row1,col1,row2,col2);
	param.sbh = G_ORGID;
	param.zth = G_ACCID.replace(G_ORGID,&quot;&quot;);

	try {
		var ret = invokeOSFuncExt(&quot;Save&quot;,param);
		alert(ret);
	}
	catch (e) {
		alert(&quot;保存失败！&quot;+e.toString());
	}
}

ROW.GRDOSDS_VALUE:
var pub = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);

function Save()
{
	var db = null;
	var sql = &quot;&quot;;

	try {
		db = new pub.EADatabase();
		var ds = new pub.EAXmlDS(xml);
		for (var i=0;i&lt;ds.getRowCount();i++) {
			var lsh = ds.getStringAt(i,&quot;LSH&quot;);
			var hm = ds.getStringAt(i,&quot;HM&quot;);
			var zh = ds.getStringAt(i,&quot;ZH&quot;);
			var khh = ds.getStringAt(i,&quot;KHH&quot;);
			var sssh = ds.getStringAt(i,&quot;SSSH&quot;);
			var ssds = ds.getStringAt(i,&quot;SSDS&quot;);

			if (lsh != &quot;&quot; && (hm != &quot;&quot; || zh != &quot;&quot; || khh != &quot;&quot; || sssh != &quot;&quot; || ssds != &quot;&quot;)) {
				sql = &quot;update cw_rjzb set hm=&apos;%s&apos;,zh=&apos;%s&apos;,khh=&apos;%s&apos;,sssh=&apos;%s&apos;,ssds=&apos;%s&apos; where sbh=&apos;%s&apos; and zth=&apos;%s&apos; and lsh=&apos;%s&apos;&quot;.format([hm,zh,khh,sssh,ssds,sbh,zth,lsh]);
				db.ExcecutSQL(sql);
			}			
		}

		db.Commit();

		return &quot;保存成功！&quot;;
	}
	catch (e) {
		if (db != null) db.Rollback();
		return e.toString();
	}
	finally {
		if (db != null) db.Close();
	}
}
