//数据加载完毕
function _thisOnpost_loaddata(sheet)
{
	initLayout();
	addToolButtons();

	ZXGFILE0 = _this.SaveTempZXGFile(0); 
	ZXGFILE1 = _this.SaveTempZXGFile(1);

	var yymmList =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_YYYY_MM&quot;),&quot;ID&quot;,&quot;NAME&quot;);
	//var kmList =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_ACCITEM&where=mjbz=1 and XJYHBZ&lt;&gt;0 and QLBZ&lt;&gt;1 and nvl(RJZKM_LRBZ,&apos;1&apos;) = &apos;1&apos;&quot;),&quot;ID&quot;,&quot;NAME&quot;);
	var logyymm = G_LOGDAT.substring(0,7).replace(&quot;-&quot;,&quot;&quot;); //登录年月
	var kmList =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_ACCITEM&where=mjbz=1 and XJYHBZ&lt;&gt;0 and QLBZ&lt;&gt;1 and nvl(RJZKM_LRBZ,&apos;1&apos;) = &apos;1&apos; and nvl(jssj,&apos;999999&apos;)&gt;=&apos;&quot;+logyymm+&quot;&apos; and nvl(kssj,&apos;190001&apos;)&lt;=&apos;&quot;+logyymm+&quot;&apos;&quot;),&quot;ID&quot;,&quot;NAME&quot;);
	//_this.SetToComboBox(&quot;&quot;,sheet_param,1,2,yymmList);
	_this.SetToSelectBox(&quot;&quot;,sheet_param,1,2,&quot;V_CW_YYYY_MM&quot;,&quot;&quot;);
	_this.SetCellText(sheet_param,1,2,G_LOGDAT.substring(0,7));
	_this.SetToComboBox(&quot;&quot;,sheet_param,1,5,kmList);

	_this.SetToSelectBox(&quot;&quot;,sheet_ctrl,15,3,&quot;V_CW_YYYY_MM&quot;,&quot;&quot;);
	_this.SetToSelectBox(&quot;&quot;,sheet_ctrl,17,3,&quot;V_CW_YYYY_MM&quot;,&quot;&quot;);

	_this.SetAttribe(&quot;SHEET_0&quot;,_this.ATTR_SHEET_SELECTFLAG,_this. SELECTFLAG_SINGLEROW );
	_this.SetAttribe(&quot;SHEET_1&quot;,_this.ATTR_SHEET_SELECTFLAG,_this. SELECTFLAG_SINGLEROW );

	bfbzList =_this.CreateListValueByXMLDS(_this.HttpCall(homeurl+&quot;/XmlDB.sp?bind=V_CW_BFBZ&quot;),&quot;ID&quot;,&quot;NAME&quot;);
	cur_zth = G_ACCID.replace(G_ORGID,&quot;&quot;);

	_this.SetColVisible(sheet_kzb,2,-1);
	_this.SetColVisible(sheet_kzb,3,-1);
	_this.SetColVisible(sheet_kzb,4,-1);
	_this.SetColVisible(sheet_kzmxb,2,-1);
	_this.SetColVisible(sheet_kzmxb,3,-1);
	_this.SetColVisible(sheet_kzmxb,4,-1);

	loadData();
}

