function loadData()
{
	var yymm = _this.GetCellText(sheet_param,1,2);
	var kmbh = _this.GetCellText(sheet_param,1,5);
	var dwbh = _this.GetCellText(sheet_param,1,7);
	if (yymm != &quot;&quot;) {
		loadKZBData(yymm,kmbh,dwbh);
	}
}

//加载数据
function loadKZBData(yymm,kmbh,dwbh)
{
	_this.SetToolbarString(&quot;正在加载数据...&quot;);
	_this.SetRedraw(0);

	//清除前面的数据
	_this.LoadZXGFile(ZXGFILE0,-1,sheet_kzb);
	_this.SetFixedRow(sheet_kzb,1);	

	_this.SetMainCellRange(sheet_kzb,1,2,_this.GetRowCount(sheet_kzb)-5,_this.GetColCount(sheet_kzb)-1);	

	//加载控制表
	var xml = _sql.query(&quot;KZB&quot;,&quot;YYYY=&quot;+yymm.substring(0,4)+&quot;&DWBH=&quot;+dwbh+&quot;&KMBH=&quot;+kmbh);
	_this.SetXmlDS(sheet_kzb,1,2,_this.GetRowCount(sheet_kzb)-5,_this.GetColCount(sheet_kzb)-1,xml);

	_this.SetToBoolBox(sheet_kzb,0,1);
	_this.SetCellText(sheet_kzb,0,1,&quot;1&quot;);
	for (var r=_this.GetMainCellRangeRow1(sheet_kzb);r&lt;=_this.GetMainCellRangeRow2(sheet_kzb);r++) {
		var lsh = _this.GetCellText(sheet_kzb,r,4);
		var nmqs = _this.GetCellText(sheet_kzb,r,25); //年未清算
		var jybz = _this.GetCellText(sheet_kzb,r,16); //结余标志
		var shbz = _this.GetCellText(sheet_kzb,r,27); //审核标志
		if (lsh != &quot;&quot;) {			
			_this.SetToBoolBox(sheet_kzb,r,1);
			_this.SetCellText(sheet_kzb,r,1,&quot;1&quot;);
			if (nmqs == 0) {
				_this.SetCellText(sheet_kzb,r,25,&quot;末清算&quot;);
				_this.SetCellColor(sheet_kzb,r,25,255,0,0);
			}
			else if (nmqs == 1) {
				_this.SetCellText(sheet_kzb,r,25,&quot;已清算&quot;);
				_this.SetCellColor(sheet_kzb,r,25,0,0,255);
			}
			if (jybz == 1) _this.SetCellShowText(sheet_kzb,r,16,&quot;超支&quot;);
			if (jybz == 2) _this.SetCellShowText(sheet_kzb,r,16,&quot;结余&quot;);
		}
		if (shbz == 1) {
			_this.SetCellShowText(sheet_kzb,r,27,&quot;已审核&quot;);
			_this.SetCellColor(sheet_kzb,r,27,0,0,255);
		}
		else {
			_this.SetCellShowText(sheet_kzb,r,27,&quot;未审核&quot;);
			_this.SetCellColor(sheet_kzb,r,27,255,0,0);
		}
	}

	//加载控制明细
	var xh = _this.GetCellText(sheet_kzb,1,4);
	if (xh != &quot;&quot;) {
		loadKZMXData(yymm,xh);
	}

	_this.SetColVisible(sheet_kzb,2,-1);
	_this.SetColVisible(sheet_kzb,3,-1);
//	_this.SetColVisible(sheet_kzb,4,-1);	

	_this.AutoFixScreenWidth();
	_this.SetToolbarString(&quot;&quot;);
	_this.SetRedraw(1);
}

//明细表
function loadKZMXData(yymm,xh)
{
	//清除前面的数据
	_this.LoadZXGFile(ZXGFILE1,-1,sheet_kzmxb);
	_this.SetFixedRow(sheet_kzmxb,2);	

	_this.SetMainCellRange(sheet_kzmxb,2,2,_this.GetRowCount(sheet_kzmxb)-2,_this.GetColCount(sheet_kzmxb)-1);	

	//加载控制表
	var xml = _sql.query(&quot;KZMXB&quot;,&quot;YYYY=&quot;+yymm.substring(0,4)+&quot;&XH=&quot;+xh);
	_this.SetXmlDS(sheet_kzmxb,2,2,_this.GetRowCount(sheet_kzmxb)-2,_this.GetColCount(sheet_kzmxb)-1,xml);

	for (var r=_this.GetMainCellRangeRow1(sheet_kzmxb);r&lt;=_this.GetMainCellRangeRow2(sheet_kzmxb);r++) {
		var lsh = _this.GetCellText(sheet_kzmxb,r,4);
		var dybz = _this.GetCellText(sheet_kzmxb,r,26); //打印标志
		var bkbz = _this.GetCellText(sheet_kzmxb,r,30); //拨款标志
		if (lsh != &quot;&quot;) {
			_this.SetToBoolBox(sheet_kzmxb,r,1);
			_this.SetToComboBox(&quot;&quot;,sheet_kzmxb,r,7,bfbzList);
		}
		if (dybz == 0) {
			_this.SetCellShowText(sheet_kzmxb,r,26,&quot;末打印&quot;);
			_this.SetCellColor(sheet_kzmxb,r,26,255,0,0);
		}
		else if (dybz == 1) {
			_this.SetCellShowText(sheet_kzmxb,r,26,&quot;已打印&quot;);
			_this.SetCellColor(sheet_kzmxb,r,26,0,0,255);
		}
		if (bkbz == 0) {
			_this.SetCellShowText(sheet_kzmxb,r,30,&quot;末拨&quot;);
			_this.SetCellColor(sheet_kzmxb,r,30,255,0,0);
		}
		else if (bkbz == 1) {
			_this.SetCellShowText(sheet_kzmxb,r,30,&quot;已拨&quot;);
			_this.SetCellColor(sheet_kzmxb,r,30,0,0,255);
		}
	}

	//20170215 lyh modify 隐藏选择列
	_this.SetColVisible(sheet_kzmxb,1,-1);
	_this.SetColVisible(sheet_kzmxb,2,-1);
	_this.SetColVisible(sheet_kzmxb,3,-1);
	_this.SetColVisible(sheet_kzmxb,4,-1);

}

//修改单元格内容
function _thisOnCellModify(sheet,row,col,oldvalue,newvalue)
{
	if (sheet == sheet_param) {
		if (row == 1 && col == 7) { //查找单位
			searchDW(newvalue);
		}
		else if (row == 1 && col == 2) {
			if (newvalue.substring(0,4) != oldvalue.substring(0,4)) {
				loadData();
			}
		}
	}
	else if (sheet == sheet_kzb) {
		footerSum(); //计算合计数
		if (col == 1) {
			setKzbRowEdit(row,newvalue);
		}
		if (row == 0 && col == 1) { //全选
			for (var r=_this.GetMainCellRangeRow1(sheet_kzb);r&lt;=_this.GetMainCellRangeRow2(sheet_kzb);r++) {
				_this.SetCellText(sheet_kzb,r,1,newvalue);
				setKzbRowEdit(r,newvalue);
			}
		}
	}
	else if (sheet == sheet_kzmxb) {
		if (col == 1 && newvalue == 1) {
			_this.SetCellText(sheet_kzb,_this.GetCurrentRow(sheet_kzb),1,1);
		}
	}
}

//鼠标左键点击
function _thisOnMouseLClick(sheet,row,col)
{
	if (sheet == sheet_param) {
		if (row == 1 && col == 8) {
			//选择单位	
			selectYH();	
		}
		else if (row == 1 && col == 10) { //查询
			loadData();
		}
	}
	else if (sheet == sheet_kzb && row&gt;=_this.GetMainCellRangeRow1(sheet_kzb) && row&lt;=_this.GetMainCellRangeRow2(sheet_kzb)) {
		var yyyy = _this.GetCellText(sheet_kzb,row,5);
		var xh = _this.GetCellText(sheet_kzb,row,4);
		if (xh != &quot;&quot;) {
			loadKZMXData(yyyy,xh);
		}		
	}
}

//选择银行
function selectYH()
{
	var retVal = window.showModalDialog(&quot;show.sp?grdid=CW_YHSEL&quot;,null,&quot;dialogWidth=800px;dialogHeight=600px&quot;);
	if (retVal != &quot;&quot; && retVal != &quot;undefined&quot; && retVal != undefined) {
		var dwbh = retVal.split(&quot;~&quot;)[0];
		var dwmc = retVal.split(&quot;~&quot;)[1];
		_this.SetCellText(sheet_param,1,7,dwbh+&quot; &quot;+dwmc);
		_thisOnCellModify(sheet_param,1,7,&quot;&quot;,dwbh+&quot; &quot;+dwmc);
	}

}

function searchDW(keyval)
{
	var str = keyval.split(&quot; &quot;);
	var bh = str[0];
	var mc = str[0];
	if (str.length &gt; 1) {
		mc = str[1];
	}
	var pos = 0;
	for (var r=_this.GetMainCellRangeRow1(sheet_kzb);r&lt;=_this.GetMainCellRangeRow2(sheet_kzb);r++) {
		var dwbh = _this.GetCellText(sheet_kzb,r,6);
		var dwmc = _this.GetCellText(sheet_kzb,r,7); 
		pos += 1*_this.GetRowHeight(sheet_kzb,r);
		if (dwbh.indexOf(bh) &gt; -1 || dwmc.indexOf(mc) &gt; -1) {
			//_this.SetCellText(sheet_kzb,r,1,&quot;1&quot;);
			_this.ScrollToPos(sheet_kzb,pos-30,200);
			_this.SetCellFocus(sheet_kzb,r,6);
			break;
		}
	}
	if (pos == 0) {
		alert(&quot;没有找到！&quot;);
	}
}

//计算控制表底下的合计金额
function footerSum()
{
	var sum_shq_jyje = 0.00; //审核前结余合计
	var sum_shq_czje = 0.00; //审核前超支合计
	var sum_jyje = 0.00; 	//审核后结余合计
	var sum_czje = 0.00;	//审核后超支合计
	for (var r=_this.GetMainCellRangeRow1(sheet_kzb);r&lt;=_this.GetMainCellRangeRow2(sheet_kzb);r++) {
		var bz = _this.GetCellText(sheet_kzb,r,1);
		var shbz = _this.GetCellText(sheet_kzb,r,27);
		var jybz = _this.GetCellText(sheet_kzb,r,16); //结余标志
		var jyje = 1.0 * _this.GetCellText(sheet_kzb,r,17); //结余金额
		var zje = 1.0 * _this.GetCellText(sheet_kzb,r,8); //统筹年初总指标金额
		var bl = 1.0 * _this.GetCellText(sheet_kzb,r,18); //比率
		if (bz == 1) {
			if (jybz == &quot;2&quot;) sum_shq_jyje += jyje; //审核前结余合计
			if (jybz == &quot;1&quot;) sum_shq_czje += jyje; //审核前超支合计

			//审核后结余合计
//			sum(if( bz = 1, if ( jybz = &apos;2&apos; , if (shbz = &apos;1&apos; , if ( bl &gt; 0.1, zje * (bl - 0.1) ,0 ) , 0 ) , 0 ) , 0 ) 
//			+ if( bz = 1, if ( jybz = &apos;2&apos; , if (shbz = &apos;2&apos; , if ( bl &gt; 0.1, zje * (0.05 + bl - 0.1),zje * bl * 0.5 ) , 0 ) , 0 ) , 0 ) 
//			+ if( bz = 1, if ( jybz = &apos;2&apos; , if (shbz = &apos;3&apos; , zje * bl , 0 ) , 0 ) , 0 ) )
			if (jybz == &quot;2&quot; && shbz == &quot;1&quot; && bl &gt; 0.1) {
				sum_jyje += zje * (bl - 0.1);
			}
			else if (jybz == &quot;2&quot; && shbz == &quot;2&quot;) {
				if (bl &gt; 0.1) sum_jyje += zje * (0.05 + bl - 0.1);
				else sum_jyje += zje * bl * 0.5;
			}
			else if (jybz == &quot;2&quot; && shbz == &quot;3&quot;) {
				sum_jyje += zje * bl;
			}

			//审核后超支合计
//			sum(if (bz = 1 , if ( shbz &lt;&gt; &apos;0&apos; , if ( jybz = &apos;1&apos;,jyje,0),0 ),0 )  )			
			if (shbz != &quot;0&quot; && jybz == &quot;1&quot;) {
				sum_czje += jyje;
			}
		}
	}

	var rw = _this.GetMainCellRangeRow2(sheet_kzb) + 2;
	_this.SetCellText(sheet_kzb,rw,9,sum_shq_jyje);
	_this.SetCellText(sheet_kzb,rw+1,9,sum_shq_czje);
	_this.SetCellText(sheet_kzb,rw,12,sum_jyje);
	_this.SetCellText(sheet_kzb,rw+1,12,sum_czje);

	var shqbl = &quot;&quot;; //审核前比率
	var shhbl = &quot;&quot;; //审核后比率
	if (sum_shq_czje &gt; 0) {
		shqbl = (sum_shq_jyje / sum_shq_czje).toFixed(2);
	}
	_this.SetCellText(sheet_kzb,rw+2,9,shqbl);
	if (sum_czje &gt; 0) {
		shqbl = (sum_jyje / sum_czje).toFixed(2);
	}
	_this.SetCellText(sheet_kzb,rw+2,12,shhbl);


}

//点击菜单项
function _thisOnMenuItemSelect(sheet,row,col,menuitemid)
{
	//生成拨款单 20170215 lyh modify 屏蔽
//	if (menuitemid == &quot;bkd_selected&quot; || menuitemid == &quot;bkd_plsc&quot; ) {
//		GenBkdData(menuitemid);
//	}


	//生成月指标数据
	if (menuitemid == &quot;zb_scmm&quot;) {
		scYzb();
	}
	//保存年初指标
	else if (menuitemid == &quot;zb_savenc&quot;) {
		saveNczb();
	}
	//调整年初指标
	else if (menuitemid == &quot;zb_tznc&quot;) {
		tzNczb();
	}
	//获取业务数据
	else if (menuitemid == &quot;yw_getdata&quot;) {
		getYWData(1);
	}
	//获取(准)公务员数据
	else if (menuitemid == &quot;yw_gwydata&quot;) {
		getYWData(2);
	}
	//删除医疗数据20170215 lyh modify 屏蔽
//	else if (menuitemid == &quot;yl_delAllData&quot;) {
//		DelYLData(1);
//	}
//	else if (menuitemid == &quot;yl_delMMData&quot;) {
//		DelYLData(2);
//	}
//	else if (menuitemid == &quot;btnrjz1&quot;) {
//		GenRjzData(1);
//	}
//	else if (menuitemid == &quot;btnrjz2&quot;) {
//		GenRjzData(2);
//	}

}

//is_xtbh2 = string(integer(g_sh),&apos;000&apos;) +g_zth
function getXtbh2()
{
	var zth = G_ACCID.replace(G_ORGID,&quot;&quot;);
	if (1 * G_ORGID &gt;= 100) return G_ORGID + zth;
	else if (1 * G_ORGID &gt;= 10) return &quot;0&quot; + G_ORGID + zth;
	else return &quot;00&quot; + G_ORGID + zth;
}

//生成拨款单
function bkd(){
	var mod = &quot;bkd_plsc&quot;
	GenBkdData(mod);
}

//生成拨款单
function GenBkdData(mod) 
{
	var ls_rq = _this.GetCellText(sheet_param,1,2);

	if(_this.GetCellText(sheet_param,1,5) == &quot;&quot; || _this.GetCellText(sheet_param,1,5) == &quot;undefined&quot; || _this.GetCellText(sheet_param,1,5) == undefined ){
		alert(&quot;请选择科目&quot;);
		return false;
	}

	var param = new Object();
	param.xtbh2 = getXtbh2();
	//alert(param.xtbh2 );
	param.thisorgid = G_ORGID;
	param.thisaccid = G_ACCID;
	param.czyxm = G_USRNAM;
	param.bfny = _this.GetCellText(sheet_param,1,2);
	param.gentype = mod;
	param.kmbh = _this.GetCellText(sheet_param,1,5);
	param.zth  = cur_zth;

	if (!confirm(&quot;生成选中单位&quot; + ls_rq.split(&quot;-&quot;)[0] + &quot;年&quot; + ls_rq.split(&quot;-&quot;)[1] + &quot;月拨款单，是否继续？&quot;)) return;

//	//生成选中单位数据
//	if (mod == &quot;bkd_selected&quot;) {
//		if (!confirm(&quot;生成选中单位数据&quot; + ls_rq.split(&quot;-&quot;)[0] + &quot;年&quot; + ls_rq.split(&quot;-&quot;)[1] + &quot;月拨款单，是否继续？&quot;)) return;
//	}
//	//批量生成所选单位
//	else if (mod == &quot;bkd_plsc&quot; ) {
//		if (!confirm(&quot;批量生成所选单位&quot; + ls_rq.split(&quot;-&quot;)[0] + &quot;年&quot; + ls_rq.split(&quot;-&quot;)[1] + &quot;拨款单，是否继续？&quot;)) return;
//	}

	try {
		param.kzmxbXml = getKzmxbSaveXml();
		param.kzbXml = getKzbSaveXml();

//		alert(param.kzbXml);
//		alert(param.kzmxbXml );

		var ret = invokeOSFuncExt(&quot;GenBKD&quot;,param);
		alert(ret);
	}
	catch (e) {
		alert(&quot;生成拨款单出错:&quot;+e.toString());
	}
}

//获取选中的数据xml
function getKzbSaveXml()
{
	var xml = &quot;&lt;?xml version = &apos;1.0&apos;?&gt;&lt;ROWSET&gt;\n&quot;;
	var num = 0;
	for (var r=_this.GetMainCellRangeRow1(sheet_kzb);r&lt;=_this.GetMainCellRangeRow2(sheet_kzb);r++) {
		var flag = _this.GetCellText(sheet_kzb,r,1);
		var lsh = _this.GetCellText(sheet_kzb,r,4);
		if (flag == &quot;1&quot; && lsh != &quot;&quot;) {
			num ++;
			xml += &quot;&lt;ROW num=\&quot;&quot;+num+&quot;\&quot;&gt;\n&quot;;
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_kzb,2)+&quot;&gt;&quot; + _this.GetCellText(sheet_kzb,r,2) +&quot;&lt;/&quot;+_this.GetColName(sheet_kzb,2)+&quot;&gt;\n&quot;;	//所编号SBH
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_kzb,3)+&quot;&gt;&quot; + _this.GetCellText(sheet_kzb,r,3) +&quot;&lt;/&quot;+_this.GetColName(sheet_kzb,3)+&quot;&gt;\n&quot;;	//帐套号ZTH
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_kzb,4)+&quot;&gt;&quot; + _this.GetCellText(sheet_kzb,r,4) +&quot;&lt;/&quot;+_this.GetColName(sheet_kzb,4)+&quot;&gt;\n&quot;;	//流水号XH
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_kzb,5)+&quot;&gt;&quot; + _this.GetCellText(sheet_kzb,r,5) +&quot;&lt;/&quot;+_this.GetColName(sheet_kzb,5)+&quot;&gt;\n&quot;;	//年YY
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_kzb,6)+&quot;&gt;&quot; + _this.GetCellText(sheet_kzb,r,6) +&quot;&lt;/&quot;+_this.GetColName(sheet_kzb,6)+&quot;&gt;\n&quot;;	//单位编号DWBH
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_kzb,7)+&quot;&gt;&quot; + _this.GetCellText(sheet_kzb,r,7) +&quot;&lt;/&quot;+_this.GetColName(sheet_kzb,7)+&quot;&gt;\n&quot;;	//单位名称DWMC
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_kzb,8)+&quot;&gt;&quot; + _this.GetCellText(sheet_kzb,r,8) +&quot;&lt;/&quot;+_this.GetColName(sheet_kzb,8)+&quot;&gt;\n&quot;;	//统筹年初总指标
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_kzb,9)+&quot;&gt;&quot; + _this.GetCellText(sheet_kzb,r,9) +&quot;&lt;/&quot;+_this.GetColName(sheet_kzb,9)+&quot;&gt;\n&quot;;	//个帐年初总指标
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_kzb,10)+&quot;&gt;&quot; + _this.GetCellText(sheet_kzb,r,10) +&quot;&lt;/&quot;+_this.GetColName(sheet_kzb,10)+&quot;&gt;\n&quot;;	//公务员年初总指标
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_kzb,11)+&quot;&gt;&quot; + _this.GetCellText(sheet_kzb,r,11) +&quot;&lt;/&quot;+_this.GetColName(sheet_kzb,11)+&quot;&gt;\n&quot;;	//准公务员年初总指标
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_kzb,12)+&quot;&gt;&quot; + _this.GetCellText(sheet_kzb,r,12) +&quot;&lt;/&quot;+_this.GetColName(sheet_kzb,12)+&quot;&gt;\n&quot;;	//调整统筹年初金额
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_kzb,13)+&quot;&gt;&quot; + _this.GetCellText(sheet_kzb,r,13) +&quot;&lt;/&quot;+_this.GetColName(sheet_kzb,13)+&quot;&gt;\n&quot;;	//调整个帐年初金额
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_kzb,27)+&quot;&gt;&quot; + _this.GetCellText(sheet_kzb,r,27) +&quot;&lt;/&quot;+_this.GetColName(sheet_kzb,27)+&quot;&gt;\n&quot;;	//审核标志
			xml += &quot;&lt;/ROW&gt;\n&quot;;		
		}
	}
	xml += &quot;&lt;/ROWSET&gt;&quot;;
	return xml;
}



//获取选中的数据xml
function getKzmxbSaveXml()
{
	var xml = &quot;&lt;?xml version = &apos;1.0&apos;?&gt;&lt;ROWSET&gt;\n&quot;;
	var num = 0;
	for (var r=_this.GetMainCellRangeRow1(sheet_kzmxb);r&lt;=_this.GetMainCellRangeRow2(sheet_kzmxb);r++) {
		var flag = _this.GetCellText(sheet_kzmxb,r,1);
		var lsh = _this.GetCellText(sheet_kzmxb,r,4);
		if (flag == &quot;1&quot; && lsh != &quot;&quot;) {
			num ++;
			xml += &quot;&lt;ROW num=\&quot;&quot;+num+&quot;\&quot;&gt;\n&quot;;
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_kzmxb,2)+&quot;&gt;&quot; + _this.GetCellText(sheet_kzmxb,r,2) +&quot;&lt;/&quot;+_this.GetColName(sheet_kzmxb,2)+&quot;&gt;\n&quot;;	//所编号SBH
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_kzmxb,3)+&quot;&gt;&quot; + _this.GetCellText(sheet_kzmxb,r,3) +&quot;&lt;/&quot;+_this.GetColName(sheet_kzmxb,3)+&quot;&gt;\n&quot;;	//帐套号ZTH
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_kzmxb,4)+&quot;&gt;&quot; + _this.GetCellText(sheet_kzmxb,r,4) +&quot;&lt;/&quot;+_this.GetColName(sheet_kzmxb,4)+&quot;&gt;\n&quot;;	//流水号XH
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_kzmxb,5)+&quot;&gt;&quot; + _this.GetCellText(sheet_kzmxb,r,5) +&quot;&lt;/&quot;+_this.GetColName(sheet_kzmxb,5)+&quot;&gt;\n&quot;;	//年YY
			xml += &quot;&lt;&quot;+_this.GetColName(sheet_kzmxb,6)+&quot;&gt;&quot; + _this.GetCellText(sheet_kzmxb,r,6) +&quot;&lt;/&quot;+_this.GetColName(sheet_kzmxb,6)+&quot;&gt;\n&quot;;	//月MM
//			xml += &quot;&lt;&quot;+_this.GetColName(sheet_kzmxb,7)+&quot;&gt;&quot; + _this.GetCellText(sheet_kzmxb,r,7) +&quot;&lt;/&quot;+_this.GetColName(sheet_kzmxb,7)+&quot;&gt;\n&quot;;	//拨付标志BFBZ
//			xml += &quot;&lt;&quot;+_this.GetColName(sheet_kzmxb,20)+&quot;&gt;&quot; + _this.GetCellText(sheet_kzmxb,r,20) +&quot;&lt;/&quot;+_this.GetColName(sheet_kzmxb,20)+&quot;&gt;\n&quot;;	//单据号DJH
//			xml += &quot;&lt;&quot;+_this.GetColName(sheet_kzmxb,30)+&quot;&gt;&quot; + _this.GetCellText(sheet_kzmxb,r,30) +&quot;&lt;/&quot;+_this.GetColName(sheet_kzmxb,30)+&quot;&gt;\n&quot;;	//拨款标志BKBZ
			xml += &quot;&lt;/ROW&gt;\n&quot;;		
		}
	}
	xml += &quot;&lt;/ROWSET&gt;&quot;;
	return xml;
}

//打印拨款单
function printBKD()
{
	window.open(&quot;show.sp?grdid=CW_RJZ_BKD&quot;);
}

//打印控制表
function printKZB()
{
	var yymm = _this.GetCellText(sheet_param,1,2);
	var yyyy = yymm.split(&quot;-&quot;)[0];
	var mm = yymm.split(&quot;-&quot;)[1];
	window.open(&quot;show.sp?grdid=YL_ZEHZB&YYYY=&quot;+yyyy+&quot;&MM=&quot;+mm);
}

//鼠标双击
function _thisOnMouseDClick(sheet,row,col)
{
	if (sheet == sheet_kzb && row &gt;= _this.GetMainCellRangeRow1(sheet_kzb) && row &lt;= _this.GetMainCellRangeRow2(sheet_kzb)) {
		var sel = _this.GetCellText(sheet_kzb,row,1);
		var newvalue = &quot;&quot;;
		if (sel == 0) newvalue = 1; else newvalue = 0;
		_this.SetCellText(sheet_kzb,row,1,newvalue);
		setKzbRowEdit(row,newvalue);

	}
}

//设置控制表选中行可编辑
function setKzbRowEdit(row,editFlg)
{
	var flg = 1;
	if (editFlg == 1) flg = 0;
	else flg = 1;
	for (var c=8;c&lt;=13;c++) {
		_this.SetCellLock(sheet_kzb,row,c,flg);
	}
}

//生成月指标数据
function scYzb()
{
	var param = new Object();
	param.thisorgid = G_ORGID;
	param.thisaccid = G_ACCID;
	param.czyxm = G_USRNAM;

	if (!confirm(&quot;生成月指标数据，是否继续？&quot;)) return;

	try {
		param.kzbXml = getKzbSaveXml();

		var ret = invokeOSFuncExt(&quot;SaveYzb&quot;,param);
		alert(ret);

		//加载控制明细
		var crow = _this.GetCurrentRow(sheet_kzb);
		if (crow &lt; 1) crow = 1;
		var xh = _this.GetCellText(sheet_kzb,crow,4);
		var yymm = _this.GetCellText(sheet_kzb,crow,5);
		if (xh != &quot;&quot;) {
			loadKZMXData(yymm,xh);
		}
	}
	catch (e) {
		alert(&quot;生成月指标数据:&quot;+e.toString());
	}
}

