//调整年初指标
function tzNczb()
{
	var param = new Object();
	param.thisorgid = G_ORGID;
	param.thisaccid = G_ACCID;
	param.czyxm = G_USRNAM;

	if (!confirm(&quot;调整年初指标，是否继续？&quot;)) return;

	for (var r=_this.GetMainCellRangeRow1(sheet_kzb);r&lt;=_this.GetMainCellRangeRow2(sheet_kzb);r++) {
		var flag = _this.GetCellText(sheet_kzb,r,1);
		var shbz = _this.GetCellText(sheet_kzb,r,27);
		var ls_dwmc  = _this.GetCellText(sheet_kzb,r,7);
		if (flag == &quot;1&quot; && shbz == &quot;1&quot;) {
			if (!confirm(&quot;该单位[&quot;+ls_dwmc+&quot;]已经调整过年初指标金额,是否要重新调整?&quot;)) return;
		}
	}

	try {
		param.kzbXml = getKzbSaveXml();

		var ret = invokeOSFuncExt(&quot;SaveTzNczb&quot;,param);
		alert(ret);
	}
	catch (e) {
		alert(&quot;调整年初指标出错:&quot;+e.toString());
	}

}

//获取业务数据
//typ = 1 获取业务数据
//typ = 2 获取(准)公务员数据
function getYWData(typ)
{
	var param = new Object();
	param.thisorgid = G_ORGID;
	param.thisaccid = G_ACCID;
	param.czyxm = G_USRNAM;

	var cnt = 0;
	for (var r=_this.GetMainCellRangeRow1(sheet_kzb);r&lt;=_this.GetMainCellRangeRow2(sheet_kzb);r++) {
		if (_this.GetCellText(sheet_kzb,r,1) == &quot;1&quot;) cnt ++;
	}
	if (cnt == 0) {
		alert(&quot;没有选择数据！&quot;);
		return;
	}

//	20170301 lyh modify 屏蔽
//	param.dwbh = _this.GetCellText(sheet_kzb,_this.GetCurrentRow(sheet_kzb),6);
//	param.dwmc = _this.GetCellText(sheet_kzb,_this.GetCurrentRow(sheet_kzb),7);

	var yymm = _this.GetCellText(sheet_param,1,2);
	if(yymm.indexOf(&quot;-&quot;) &gt; 0){
		param.yyyy = yymm.split(&quot;-&quot;)[0];
		param.mm = yymm.split(&quot;-&quot;)[1];	
	}else{
		param.yyyy = yymm.substring(0,4);
		param.mm   = yymm.substring(4,6)
	}

	if (typ == 2) {
		if (!confirm(&quot;获取所选单位(准)公务员&quot;+yymm+&quot;数据，是否继续？&quot;)) return;
		try {
			param.kzbXml = getKzbSaveXml();
			var ret = invokeOSFuncExt(&quot;getZgwyData&quot;,param);
			alert(ret);
		}
		catch (e) {
			alert(&quot;获取(准)公务员数据出错:&quot;+e.toString());
		}
	}
	else {
//		20170301 LYH MODIFY 	屏蔽
//		var retVal = window.showModalDialog(&quot;show.sp?grdid=RJZ_GETPARAM&quot;,param,&quot;dialogWidth=500px;dialogHeight=400px&quot;);
//		if (retVal != &quot;&quot; && retVal != &quot;undefined&quot; && retVal != undefined) {
//			if (retVal == 0 || retVal == &quot;&quot;) return;			
//		}
//		if (retVal == &quot;&quot; || retVal == &quot;undefined&quot; || retVal == undefined) return;

		try {			
			if (!confirm(&quot;获取所选单位的&quot;+yymm+&quot;业务数据,是否继续?&quot;)) return;
			param.kzbXml = getKzbSaveXml();

//			param.getType = retVal;
//			if (retVal == 1) {
//				param.kzbXml = getKzmxbSaveXml();
//			}
//			else if (retVal == 2) {
//				param.kzbXml = getKzbSaveXml();
//			}

			var ret = invokeOSFuncExt(&quot;getYWData&quot;,param);
			alert(ret);
		}
		catch (e) {
			alert(&quot;获取业务数据出错:&quot;+e.toString());
		}

	}
}

//生成医疗数据
function GenYLData()
{
	try {			
		var yymm = _this.GetCellText(sheet_param,1,2);
		if (!confirm(&quot;生成&quot;+yymm.substring(0,4)+&quot;年的医疗数据，是否继续？&quot;)) return;
		var ret = invokeOSFunc(&quot;GenYLData&quot;,&quot;thisorgid=&quot;+G_ORGID+&quot;&thisaccid=&quot;+G_ACCID+&quot;&yymm=&quot;+yymm);
		alert(ret);
		loadData();
	}
	catch (e) {
		alert(&quot;生成医疗数据出错:&quot;+e.toString());
	}

}

