}

//生成日记账
function btnrjz(){
 	GenRjzData(1);
}

//存入日记帐
function GenRjzData(typ)
{
	//拨付年月
	var bfny = _this.GetCellText(sheet_param,1,2);

	//日记账年月
	var rjzdat = _this.GetCellText(sheet_ctrl,4,2);
	if (rjzdat == &quot;&quot;) {
		alert(&quot;请选择日记帐时间 ！&quot;);
		return;
	}
	var yy = rjzdat.split(&quot;-&quot;)[0];
	var mm = rjzdat.split(&quot;-&quot;)[1];
	var dd = rjzdat.split(&quot;-&quot;)[2];

	if (mm == 12) {
		alert(&quot;12月份不需要存日记账，需审核后，在明年的1月份再存日记账！&quot;);
		return;
	}
	var kmbh = _this.GetCellText(sheet_param,1,5);
	if (kmbh == &quot;&quot;) {
		alert(&quot;请选择科目！&quot;);
		return;
	}

//	20170216 lyh modify 
	if (!confirm(&quot;生成所选单位【&quot;+bfny+&quot;】拨付数据，到账日期：【&quot;+rjzdat+&quot;】日记帐，是否继续？&quot;)) return;

//	if (typ == 1) {
//		if (!confirm(&quot;只生成该单位拨付年月：【&quot;+_this.GetCellText(sheet_kzb,_this.GetCurrentRow(sheet_kzb),7)+&quot;】&quot;+rjzdat+&quot;日记帐，是否继续？&quot;)) return;	
//	}
//	else if (typ == 2) {
//		if (!confirm(&quot;批量生成所选单位&quot;+rjzdat+&quot;日记帐，是否继续？&quot;)) return;				
//	}

	try {	
		var param = new Object();
		param.thisorgid = G_ORGID;
		param.thisaccid = G_ACCID;
		param.czyxm = G_USRNAM;
		param.genType = typ;
		param.rjzXml = getRJZSaveXml(typ);
//		alert(param.rjzXml);
//		return false;
		param.rjzdat = rjzdat;
		param.rjzyy = yy;
		param.rjzmm = mm;
		param.rjzdd = dd;
		param.rjzkmbh = kmbh;
		param.bfny  = bfny;

		var ret = invokeOSFuncExt(&quot;SaveToRjz&quot;,param);
		alert(ret);
	}
	catch (e) {
		alert(&quot;存入日记帐出错:&quot;+e.toString());
	}

}

//获取修改的数据xml
function getRJZSaveXml(typ)
{
	var xml = &quot;&lt;?xml version = &apos;1.0&apos;?&gt;&lt;ROWSET&gt;\n&quot;;
	var num = 0;
	var sheet = sheet_kzb;
	if (typ == 2) sheet = sheet_kzmxb;
	var row1 = _this.GetMainCellRangeRow1(sheet);
	var row2 = _this.GetMainCellRangeRow2(sheet);
//	20170221 lyh modify
//	if (typ == 1) {
//		row1 = _this.GetCurrentRow(sheet_kzb);
//		row2 = row1;
//	}
	for (var r=row1;r&lt;=row2;r++) {
		var flag = _this.GetCellText(sheet,r,1);
		var xh = _this.GetCellText(sheet,r,4);
		if (flag == &quot;1&quot; && xh != &quot;&quot;) {
			num ++;
			xml += &quot;&lt;ROW num=\&quot;&quot;+num+&quot;\&quot;&gt;\n&quot;;
			xml += &quot;&lt;XH&gt;&quot; + _this.GetCellText(sheet,r,4) +&quot;&lt;/XH&gt;\n&quot;;	//流水号
			xml += &quot;&lt;YY&gt;&quot; + _this.GetCellText(sheet,r,5) +&quot;&lt;/YY&gt;\n&quot;;	//年
			xml += &quot;&lt;MM&gt;&quot; + _this.GetCellText(sheet,r,6) +&quot;&lt;/MM&gt;\n&quot;;	//月 sheet_kzb不需要
			xml += &quot;&lt;BKDID&gt;&quot; + _this.GetCellText(sheet,r,27) +&quot;&lt;/BKDID&gt;\n&quot;;	//拨款单据号 sheet_kzb不需要
			xml += &quot;&lt;/ROW&gt;\n&quot;;		
		}
	}
	xml += &quot;&lt;/ROWSET&gt;&quot;;

	return xml;
}

//月指标做账
function dwdzbcz(){

	var daydat = _this.GetCellText(sheet_param,1,2);
	var row = _this.GetCurrentRow(sheet_kzb);
//	var currow = _this.GetCurrentRow(sheet_yhrjzb);
//	alert(row);
	if (row &lt;= 0) return false;

	var param = new Object();
	param.dwbh = _this.GetCellText(sheet_kzb,row,6);
	param.xh   = _this.GetCellText(sheet_kzb,row,4);
	param.daydat = daydat ;

	if(_this.GetCellText(sheet_kzb,row,4) == &quot;&quot;) return false;

	var retVal = window.showModalDialog(&quot;show.sp?grdid=RJZ_YJSYZBRQ&quot;,param,&quot;dialogWidth=1500px;dialogHeight=800px&quot;);
}

//点击按钮
function _thisOnButtonCtrlClick(id,sheet,row,col)
{
	if (id == &quot;统计&quot;) {
		var ym1 = _this.GetCellText(sheet_ctrl,15,3);
		var ym2 = _this.GetCellText(sheet_ctrl,17,3);
		if (ym1 == &quot;&quot; || ym2 == &quot;&quot;) {
			alert(&quot;请输入统计年月范围！&quot;);
			return;
		} 
		//20170222 lyh modify 统一时间日期格式 yyyymm
		if(ym1.indexOf(&quot;-&quot;) &gt;= 0){
			ym1 = ym1.replace(&quot;-&quot;,&quot;&quot;);
		}
		if(ym2.indexOf(&quot;-&quot;) &gt;= 0){
			ym2 = ym2.replace(&quot;-&quot;,&quot;&quot;);
		}

		window.open(&quot;show.sp?grdid=YLFTJ&DAT1=&quot;+ym1+&quot;&DAT2=&quot;+ym2);
	}
}

//年终清算
function zekzNzqs(){
	//拨付年月
	var bfny = _this.GetCellText(sheet_param,1,2);

	//生成年终清算数据
	var param = new Object();
