	param.thisaccid = G_ACCID;
	param.zth = cur_zth;
	param.czyxm = G_USRNAM;
	param.bfny  = bfny;

	var ret = invokeOSFuncExt(&quot;SaveNzqs&quot;,param);

	window.open(&quot;show.sp?grdid=RJZ_NZQS&quot;);

}

ROW.GRDOSDS_VALUE:
var pub = new JavaPackage(&quot;com.xlsgrid.net.pub&quot;);

//生成拨款单
function GenBKD()
{
	var db = null;
	var sql = &quot;&quot;;
	try {
		db = new pub.EADatabase();

		var kzmxbds = new pub.EAXmlDS(kzmxbXml);
		var kzbds = new pub.EAXmlDS(kzbXml);
		var mainds = null;
		if (gentype == &quot;bkd_selected&quot;) { //生成选中单位数据
			mainds = kzmxbds;
		}
		else {
			mainds = kzbds;
		}
		//20170220 lyh add 截取拨付年月
		var bfyy = &quot;&quot;;
		var bfmm = &quot;&quot;;
		if( bfny.indexOf(&quot;-&quot;) &gt; -1 ){
			//bfny.split(&quot;-&quot;)[1];
			bfyy = bfny.split(&quot;-&quot;)[0];
			bfmm = bfny.split(&quot;-&quot;)[1];
		}else{
			bfyy = bfny.substring(0,4);
			bfmm = bfny.substring(4,6);
		}


		//20170215 kyh modify 险种类型=31 该模块只用于基本医疗的月结算总额控制
		var is_xzlx = &quot;31&quot;;
//		var tmpds  = db.QuerySQL(&quot;select max(dzxz) xz from cw_dfdsb t where t.org = &apos;&quot;+thisorgid+&quot;&apos; and t.acc = &apos;&quot;+thisaccid+&quot;&apos;&quot;);
//		if (tmpds.getRowCount() == 0) {
//			return &quot;没有设置是四险统一征缴还是医疗征缴&quot;;
//		}
//		else {
//			is_xzlx = tmpds.getStringAt(0,&quot;XZ&quot;);
//		}

		var ls_yhbm = &quot;&quot;;
		var ls_aae008 = &quot;&quot;;
		var ls_aae010 = &quot;&quot;;
		var ls_aae009 = &quot;&quot;;
		var ls_sbshengfen = &quot;&quot;;
		var ls_sbdishi = &quot;&quot;;
		//20170215 lyh modify 不再查询AEF3 查询CW_BKDYHB 		
//		sql = &quot;select AAB001,AAB341,AAB026,AAB025,BAE101,BAE102 
//			from aef3  
//			where BAE007 = &apos;&quot;+xtbh2+&quot;&apos; and BAE541 = 0 and BAE511 = 0 
//			and AAE338 = &apos;-1&apos;and BAE201 = &apos;-1&apos; and BAE202 = &apos;-1&apos; 
//			and aae002= &apos;1&apos; and aae140 = &apos;&quot;+is_xzlx+&quot;&apos; and aac001 = &apos;1&apos;&quot;;		

		sql = &quot;SELECT jgbh AAB001,khyh AAB341,yhzh AAB026,yhhm AAB025,bae101,bae102 FROM cw_bkdyhb WHERE sbh = &apos;&quot;+thisorgid+&quot;&apos; AND zth = &apos;&quot;+zth+&quot;&apos; AND xzlx = &apos;&quot;+is_xzlx+&quot;&apos; AND bz = &apos;10&apos; AND kmbh = &apos;&quot;+kmbh+&quot;&apos;&quot;;
		var tmpds = db.QuerySQL(sql);
		if (tmpds.getRowCount() == 0) {
			return &quot;没有查询到社保开户行，请先设置社保开户行！&quot;;
		}
		else {
			ls_yhbm = tmpds.getStringAt(0,&quot;AAB001&quot;);
			ls_aae008 = tmpds.getStringAt(0,&quot;AAB341&quot;);
			ls_aae010 = tmpds.getStringAt(0,&quot;AAB026&quot;);
			ls_aae009 = tmpds.getStringAt(0,&quot;AAB025&quot;);
			ls_sbshengfen = tmpds.getStringAt(0,&quot;BAE101&quot;);
			ls_sbdishi = tmpds.getStringAt(0,&quot;BAE102&quot;);
		}

		for (var i=0;i&lt;mainds.getRowCount();i++) {
			var yy = mainds.getStringAt(i,&quot;YY&quot;);
			var lsh = mainds.getStringAt(i,&quot;XH&quot;);
			var bkbz = &quot;&quot;;			// kzmxbds.getStringAt(i,&quot;BKBZ&quot;);
			//20170215 lyh modify 只要拨付年月
			var mm = bfmm;	//mainds.getStringAt(i,&quot;MM&quot;);			

//			if (gentype == &quot;bkd_selected&quot;) {
//				mm = mainds.getStringAt(i,&quot;MM&quot;);
//			}
//			else {
//				mm = bfny.split(&quot;-&quot;)[1];
//			}

			var yymm = yy+mm;
			var dwbh = &quot;&quot;;
			var dwmc = &quot;&quot;;
			var ls_djh = &quot;&quot;;//kzmxbds.getStringAt(i,&quot;DJH&quot;);

			//获取单位编号，单位名称
			//20170215 lyh modify 直接获取，不再查询表数据
			dwbh = mainds.getStringAt(i,&quot;DWBH&quot;);
			dwmc = mainds.getStringAt(i,&quot;DWMC&quot;);

//			sql = &quot;select dwbh,dwmc from cw_zekzb where org = &apos;&quot;+thisorgid+&quot;&apos; and acc = &apos;&quot;+thisaccid+&quot;&apos; and yy = &quot;+yy+&quot; and xh = &quot;+lsh;
//			var tmpds = db.QuerySQL(sql);
//			if (tmpds.getRowCount() &lt;= 0) {
//				return &quot;流水号&quot;+lsh+&quot;没有找到单位信息!&quot;;
//			}
//			else {
//				
//			}

			if (bkbz == &quot;1&quot;) {
				return &quot;该单位&quot;+dwmc+yymm+&quot;已经生成过拨款数据，不允许再拨款！请到删除拨款单后再重新生成!&quot;;
			}

			//拨款金额合计 + 统筹 + 个帐金额 + gwy +zgwy
			var ldb_je = 0.00;
			var ldb_gz_je = 0.00;
			var ldb_cwgwy_je = 0.00;
			var ldb_cwzgwy_je = 0.00;

			sql = &quot;select nvl(je,0) je,nvl(gz_je,0) gz_je,nvl(gwy_je,0) gwy_je,nvl(zgwy_je,0) zgwy_je,djh,bkbz
				from cw_zekzmxb 
				where org = &apos;&quot;+thisorgid+&quot;&apos; and acc = &apos;&quot;+thisaccid+&quot;&apos; and yy = &quot;+yy+&quot; and mm = &quot;+mm+&quot; and xh = &quot;+lsh+&quot; and bfbz = &apos;0&apos;&quot;;
			var mxds = db.QuerySQL(sql);
			if (mxds.getRowCount() &gt; 0) {
				ldb_je = mxds.getStringAt(0,&quot;JE&quot;);
				ldb_gz_je = mxds.getStringAt(0,&quot;GZ_JE&quot;);
				ldb_cwgwy_je = mxds.getStringAt(0,&quot;GWY_JE&quot;);
				ldb_cwzgwy_je = mxds.getStringAt(0,&quot;ZGWY_JE&quot;);
				ls_djh  = mxds.getStringAt(0,&quot;DJH&quot;);
				bkbz  = mxds.getStringAt(0,&quot;BKBZ&quot;);

				//获取单位开户行
				var yhmc = &quot;&quot;;
				var yhhm = &quot;&quot;;
				var yhzh = &quot;&quot;;
				var dwshengfen = &quot;&quot;;
				var dwsishi = &quot;&quot;;

//				20170215 lyh modify 改为查询CW_BKDYHB
//				sql = &quot;SELECT AAB341 YHMC, AAB026 YHHM, AAE339 YHZH, BAE101 DWSHENGFEN, BAE102 DWSISHI
//				  FROM AEF3 A
//				  WHERE AAE140 = &apos;&quot;+is_xzlx+&quot;&apos;
//					AND BAE007 = &apos;&quot;+xtbh2+&quot;&apos;
//					AND AAB001 = &apos;&quot;+dwbh+&quot;&apos;
//					AND AAE002 = &apos;2&apos;
//					AND BAE541 = 0
//					AND BAE511 = 0
//					AND AAE338 = &apos;-1&apos;
//					AND BAE201 = &apos;-1&apos;
//					AND BAE202 = &apos;-1&apos;&quot;;

				sql = &quot;select khyh YHMC,yhzh YHZH,yhhm YHHM,bae101 DWSHENGFEN,bae102 DWSISHI FROM cw_bkdyhb WHERE sbh = &apos;&quot;+thisorgid+&quot;&apos; AND zth = &apos;&quot;+zth+&quot;&apos; AND xzlx = &apos;&quot;+is_xzlx+&quot;&apos; AND bz = &apos;21&apos; AND jgbh = &apos;&quot;+dwbh+&quot;&apos;&quot;;
				var tmpds = db.QuerySQL(sql);
				if (tmpds.getRowCount() &lt;= 0) {
					return &quot;查询不到单位【&quot;+dwbh+dwmc+&quot;】的开户行!&quot;;
				}
				else {
					yhmc = tmpds.getStringAt(0,&quot;YHMC&quot;);
					yhhm = tmpds.getStringAt(0,&quot;YHHM&quot;);
					yhzh = tmpds.getStringAt(0,&quot;YHZH&quot;);
					dwshengfen = tmpds.getStringAt(0,&quot;DWSHENGFEN&quot;);
					dwsishi = tmpds.getStringAt(0,&quot;DWSISHI&quot;);
				}

				var ldb_sum_je = 0.00;
				ldb_sum_je = pub.EAFunc.Round(ldb_je,2) + pub.EAFunc.Round(ldb_gz_je,2) + pub.EAFunc.Round(ldb_cwgwy_je,2) + pub.EAFunc.Round(ldb_cwzgwy_je,2);

				//获取拨款单号
				var ll_aae400 = db.GetSQL(&quot;select SEQ_AEF3_AAE400.nextval from dual&quot;);
				var ldb_rs = 0;
				//插入主表
				sql = &quot;insert into aef3(AAE400,BAE007,AAB001,AAE140,AAE002,BAE541,BAE511,AAE401,AAE338,AAB025,AAB026,AAB340,BAE101,BAE102,AAE009,AAE010,AAB341,BAE201,BAE202,AAE403,AAE339,BAE552,BAE553,BAE031,BAE554,AAE600,org,acc)
					values (&apos;&quot;+ll_aae400+&quot;&apos;,&apos;&quot;+xtbh2+&quot;&apos;,&apos;&quot;+dwbh+&quot;&apos;,&apos;&quot;+is_xzlx+&quot;&apos;,&apos;&quot;+yymm+&quot;&apos;,&apos;&quot;+ldb_rs+&quot;&apos;,&apos;&quot;+ldb_sum_je+&quot;&apos;,to_char(sysdate,&apos;yyyymmdd&apos;),
						 &apos;2&apos;,&apos;&quot;+ls_aae009+&quot;&apos;,&apos;&quot;+ls_aae010+&quot;&apos;,&apos;&quot;+ls_aae008+&quot;&apos;,&apos;&quot;+ls_sbshengfen+&quot;&apos;,&apos;&quot;+ls_sbdishi+&quot;&apos;,
						 &apos;&quot;+dwmc+&quot;&apos;,&apos;&quot;+yhhm+&quot;&apos;,&apos;&quot;+yhmc+&quot;&apos;,&apos;&quot;+dwshengfen+&quot;&apos;,&apos;&quot;+dwsishi+&quot;&apos;,&apos;&quot;+czyxm+&quot;&apos;,&apos;&quot;+yhzh+&quot;&apos;,&apos;0&apos;,&apos;0&apos;,&apos;0&apos;,&apos;0&apos;,&apos;&quot;+ls_djh+&quot;&apos;,&apos;&quot;+thisorgid+&quot;&apos;,&apos;&quot;+thisaccid+&quot;&apos;)&quot;;	
				db.ExcecutSQL(sql);

				var bfmx = &quot;医疗保险费&quot;;

				//插入明细表
				sql = &quot;insert into aef4 (AAE400, AAE140, AAE405, AAE406, AAE501, AAE407, AAE408, BAE002, AAE401,org,acc)
					values (&apos;&quot;+ll_aae400+&quot;&apos;,&apos;&quot;+is_xzlx+&quot;&apos;,&apos;&quot;+yymm+&quot;&apos;,&apos;&quot;+yymm+&quot;&apos;,&apos;&quot;+bfmx+&quot;&apos;,&apos;&quot;+ldb_rs+&quot;&apos;,&apos;&quot;+ldb_sum_je+&quot;&apos;,&apos;1&apos;,1,&apos;&quot;+thisorgid+&quot;&apos;,&apos;&quot;+thisaccid+&quot;&apos;)&quot;;
				db.ExcecutSQL(sql);	

				//更新总额控制表拨款单信息
				sql = &quot;update cw_zekzmxb set bkdid = &apos;&quot;+ll_aae400+&quot;&apos;,bk_czyxm = &apos;&quot;+czyxm+&quot;&apos;,bk_czsj=sysdate,bkbz = &apos;1&apos; ,bk_je = &quot;+ldb_sum_je
					+ &quot; where org=&apos;&quot;+thisorgid+&quot;&apos; and sbh = &apos;&quot;+thisorgid+&quot;&apos; and acc = &quot;+thisaccid+&quot; and yy = &quot;+yy+&quot; and mm = &quot;+mm+&quot; and xh = &quot;+lsh+&quot; and bfbz = &apos;0&apos;&quot;;
				db.ExcecutSQL(sql);
			}
		}
		db.Commit();

		return &quot;生成拨款单成功！&quot;;
	}
	catch (e) {
		if (db != null) db.Rollback();
		return e.toString();	
	}
	finally {
		if (db != null) db.Close();
	}
}

//保存年初指标
function SaveNczb()
{
	var db = null;
	var sql = &quot;&quot;;
	try {
		db = new pub.EADatabase();

		var kzbds = new pub.EAXmlDS(kzbXml);
		for (var i=0;i&lt;kzbds.getRowCount();i++) {
			var sbh = kzbds.getStringAt(i,&quot;SBH&quot;);
			var zth = kzbds.getStringAt(i,&quot;ZTH&quot;);
			var yy = kzbds.getStringAt(i,&quot;YY&quot;);
			var lsh = kzbds.getStringAt(i,&quot;XH&quot;);
			var zje = nvl(kzbds.getStringAt(i,&quot;ZJE&quot;),&quot;null&quot;);
       			var gz_zje = nvl(kzbds.getStringAt(i,&quot;GZ_ZJE&quot;),&quot;null&quot;);
			var gwy_zje = nvl(kzbds.getStringAt(i,&quot;GWY_ZJE&quot;),&quot;null&quot;);
			var zgwy_zje = nvl(kzbds.getStringAt(i,&quot;ZGWY_ZJE&quot;),&quot;null&quot;);			

			sql = &quot;update cw_zekzb set zje=&quot;+zje+&quot;,gz_zje=&quot;+gz_zje+&quot;,gwy_zje=&quot;+gwy_zje+&quot;,zgwy_zje=&quot;+zgwy_zje+&quot;,ncfp_czyxm=&apos;&quot;+czyxm+&quot;&apos;,ncfp_czsj=sysdate&quot;
				+ &quot; where sbh=&apos;&quot;+sbh+&quot;&apos; and zth=&apos;&quot;+zth+&quot;&apos; and acc=&apos;&quot;+thisaccid+&quot;&apos; and org=&apos;&quot;+thisorgid+&quot;&apos; and xh=&quot;+lsh+&quot; and yy=&quot;+yy;
			db.ExcecutSQL(sql);
		}
		db.Commit();

		return &quot;分配年初指标成功!&quot;;
	}
	catch (e) {
		if (db != null) db.Rollback();
		return e.toString();	
	}
	finally {
		if (db != null) db.Close();
	}
}

function nvl(je,defje) 
{
	if (je == &quot;&quot; || je == null) {
		return defje;
	}
	return je;
}

//生成月指标数据
function SaveYzb()
{
	var db = null;
	var sql = &quot;&quot;;
	var ret = 0;
	try {
		db = new pub.EADatabase();

		var kzbds = new pub.EAXmlDS(kzbXml);
		for (var i=0;i&lt;kzbds.getRowCount();i++) {
			var sbh = kzbds.getStringAt(i,&quot;SBH&quot;);
			var zth = kzbds.getStringAt(i,&quot;ZTH&quot;);
			var yy = kzbds.getStringAt(i,&quot;YY&quot;);
			var lsh = kzbds.getStringAt(i,&quot;XH&quot;);
			var zje = 1.0 * nvl(kzbds.getStringAt(i,&quot;ZJE&quot;),0);
       			var gz_zje = 1.0 * nvl(kzbds.getStringAt(i,&quot;GZ_ZJE&quot;),0);
			var gwy_zje = 1.0 * nvl(kzbds.getStringAt(i,&quot;GWY_ZJE&quot;),0);
			var zgwy_zje = 1.0 * nvl(kzbds.getStringAt(i,&quot;ZGWY_ZJE&quot;),0);
			var sumje = zje + gz_zje + gwy_zje + zgwy_zje;
			if (sumje &gt; 0) {
				var ldb_zje1 = pub.EAFunc.Round(zje / 12, 2);
				var ldb_zje2 = pub.EAFunc.Round(gz_zje / 12, 2);
				var ldb_zje3 = pub.EAFunc.Round(gwy_zje / 12, 2);
				var ldb_zje4 = pub.EAFunc.Round(zgwy_zje / 12, 2);	

				//先清除原来的数据
				sql = &quot;delete from CW_ZEKZMXB where org=&apos;&quot;+thisorgid+&quot;&apos; and acc=&apos;&quot;+thisaccid+&quot;&apos; and sbh=&apos;&quot;+sbh+&quot;&apos; and zth=&apos;&quot;+zth+&quot;&apos; and yy=&quot;+yy+&quot; and xh=&quot;+lsh;
				db.ExcecutSQL(sql);

				//分到12个月里
				for (var m=1;m&lt;=12;m++) {
					if (m == 12) {
						ldb_zje1 = zje - (pub.EAFunc.Round(zje / 12, 2) * 11);
						ldb_zje2 = gz_zje - (pub.EAFunc.Round(gz_zje / 12, 2) * 11);
						ldb_zje3 = gwy_zje - (pub.EAFunc.Round(gwy_zje / 12, 2) * 11);
						ldb_zje4 = zgwy_zje - (pub.EAFunc.Round(zgwy_zje / 12, 2) * 11);
					}
					sql = &quot;INSERT INTO CW_ZEKZMXB(ORG,ACC,SBH, ZTH, XH, YY, MM, BFBZ, JE, gz_je, YWJE, DYBZ, BKBZ,gwy_je,zgwy_je) 
						VALUES(&apos;&quot;+thisorgid+&quot;&apos;,&apos;&quot;+thisaccid+&quot;&apos;,&apos;&quot;+sbh+&quot;&apos;,&apos;&quot;+zth+&quot;&apos;,&quot;+lsh+&quot;, &quot;+yy+&quot;, &quot;+m+&quot;, &apos;0&apos;, &quot;+ldb_zje1+&quot;,&quot;+ldb_zje2+&quot;, 0, &apos;0&apos;, &apos;0&apos;,&quot;+ldb_zje3+&quot;,&quot;+ldb_zje4+&quot;)&quot;;
					ret += db.ExcecutSQL(sql);
				}
			}

		}
		db.Commit();

		return &quot;生成拨付数据成功!记录数：&quot;+ret;
	}
	catch (e) {
		if (db != null) db.Rollback();
		return e.toString();	
	}
	finally {
		if (db != null) db.Close();
	}
}

//调整年初指标
function SaveTzNczb()
{
	var db = null;
	var sql = &quot;&quot;;
	try {
		db = new pub.EADatabase();

		var kzbds = new pub.EAXmlDS(kzbXml);
		for (var i=0;i&lt;kzbds.getRowCount();i++) {
			var sbh = kzbds.getStringAt(i,&quot;SBH&quot;);
			var zth = kzbds.getStringAt(i,&quot;ZTH&quot;);
			var yy = kzbds.getStringAt(i,&quot;YY&quot;);
			var lsh = kzbds.getStringAt(i,&quot;XH&quot;);
			var tz_zje = nvl(kzbds.getStringAt(i,&quot;TZ_ZJE&quot;),&quot;null&quot;);
			var tz_gz_zje = nvl(kzbds.getStringAt(i,&quot;TZ_GZ_ZJE&quot;),&quot;null&quot;);

			if (tz_zje != &quot;null&quot; && tz_zje != &quot;&quot; && (1*tz_zje) &gt; 0) {
				sql = &quot;update cw_zekzb set 
					tz_zje = &quot;+tz_zje+&quot;,
					tz_gz_zje = &quot;+tz_gz_zje+&quot; ,
					bz = decode( nvl(tz_zje,tz_gz_zje),null,&apos;修改前备份年终统筹金额:&apos;||zje||&apos;，个帐金额&apos;|| gz_zje,0,&apos;修改前备份年终金额:&apos;||zje||&apos;，个帐金额&apos;|| gz_zje, bz),
					xg_czyxm = &apos;&quot;+czyxm+&quot;&apos;,
					xg_czsj = sysdate
					where xh = &quot;+lsh;
				return sql;
				db.ExcecutSQL(sql);
			}
		}
		db.Commit();

		return &quot;调整年初指标成功!&quot;;
	}
	catch (e) {
		if (db != null) db.Rollback();
		return e.toString();	
	}
	finally {
		if (db != null) db.Close();
	}
}

